custombutton.initcode:
const {LightweightThemeManager} = Cu.import("resource://gre/modules/LightweightThemeManager.jsm", {});

function $(aId) document.getElementById(aId);
function $$(aSelector, aNode)  (aNode || document).querySelector(aSelector);
function $$$(aSelector, aNode) (aNode || document).querySelectorAll(aSelector);
function $create(aTagName) document.createElement(aTagName);

// Open solid color theme web site
function solidColorTheme() {
  switchToTabHavingURI("https://loucypher.makes.org/thimble/solid-color-theme_", true);
}

// Open Add-ons Manager
function openAddonsManager(aView) {
  if ("toEM" in window) {
    toEM(aView);  // SeaMonkey
  } else if ("openAddonsMgr" in window) {
    openAddonsMgr(aView); // Thunderbird
  } else {
    BrowserOpenAddonsMgr(aView);  // Firefox
  }
}

// Open themes list in Add-ons Manager
// If Lightweight Themes Manager is installed and enabled, open it instead
function openManager() {
  AddonManager.getAddonByID("lwthemes-manager@loucypher", function(aAddon) {
    if (aAddon && aAddon.isActive)
      switchToTabHavingURI("chrome://lwthemes/content/", true);
    else
      openAddonsManager("addons://list/theme");
  });
}

// Enable theme
function setLocalTheme(aEvent) {
  var currentTheme = LightweightThemeManager.currentTheme;
  if (currentTheme && currentTheme.id === aEvent.target._theme.id)
    LightweightThemeManager.setLocalTheme();
  else {
    LightweightThemeManager.setLocalTheme(aEvent.target._theme);
    LightWeightThemeWebInstaller._removePreviousNotifications();
  }
}

// Preview theme
function previewTheme(aEvent) {
  LightweightThemeManager.previewTheme(aEvent.target._theme);
}

// Reset theme preview
function resetPreview(aEvent) {
  LightweightThemeManager.resetPreview();
}

// Get theme home page or AMO page
function getThemeURL(aTheme) {
  const { id: id, homepageURL: homepageURL, updateURL: updateURL } = aTheme;
  const AMO_URL = "https://addons.mozilla.org";
  if (homepageURL)
    return homepageURL;
  if (id === "1")
    return AMO_URL + "/addon/personas-plus/";
  if (updateURL) {
    if (updateURL.match(/getpersonas.com/) || updateURL.match(/\?src\=gp$/))
      return AMO_URL + "/persona/" + id;
    if (updateURL.match(/addons.mozilla.org/))
      return AMO_URL + "/addon/" + id;
  }
  return null;
}

// Theme menuitem
function addMenuItem(aTheme) {
  const {id: id, name: name, iconURL: iconURL} = aTheme;
  var menuitem = $create("menuitem");
  menuitem._theme = aTheme;
  var themeURL = getThemeURL(aTheme);
  //LOG(themeURL);
  if (themeURL)
    menuitem.value = themeURL;
  menuitem.setAttribute("type", "radio");
  //menuitem.setAttribute("name", "themeGroup");
  var currentTheme = LightweightThemeManager.currentTheme;
  if (currentTheme && currentTheme.id === id)
    menuitem.setAttribute("checked", "true");
  menuitem.setAttribute("label", name);
  return menuitem;
}

// Populate menupopup with installed themes
function populate(aNode) {
  while (popup.lastChild.localName != "menuseparator")
    aNode.removeChild(aNode.lastChild);

  var themes = LightweightThemeManager.usedThemes;
  themes = themes.sort(function(a, b) {
    return a.name.localeCompare(b.name);
  });
  for (var i in themes)
    aNode.appendChild(addMenuItem(themes[i]));
}

// Context menu for when you click on theme menuitems 
var context = $create("menupopup");
context.id = this.id + "-popup";
context.onclick = function onclick(aEvent) {
  if (aEvent.button === 0 || typeof inspectObject !== "function")
    return;
  aEvent.preventDefault();
  //LOG(aEvent.currentTarget.localName);
  switch (aEvent.button) {
    case 1: inspectObject(aEvent.target); break;
    case 2: inspectObject(aEvent.currentTarget.triggerNode);
  }
  closeMenus(aEvent.currentTarget.triggerNode);
};
context.ThemeUtils = {
  // Open theme home page or AMO page
  homepage: function themeUtils_homepage(aTheme) {
    var url = getThemeURL(aTheme);
    switchToTabHavingURI(url, true);
  },

  // Uninstal theme
  remove: function themeUtils_removeTheme(aTheme) {
    var msg = "Are you sure you want to remove " + aTheme.name + "?";
    if (custombuttons.confirmBox(self.name, msg, "Yes", "No"))
      LightweightThemeManager.forgetUsedTheme(aTheme.id);
  },

  // Open theme in Add-ons Manager
  info: function themeUtils_themeInfo(aTheme) {
    var id = aTheme.id + "@personas.mozilla.org";
    LightweightThemeManager.getAddonByID(id, function(aAddon) {
      openAddonsManager("addons://detail/" + encodeURIComponent(id));
    });
  },

  // Inspect theme object with DOM Inspector
  inspectTheme: function themeUtils_inspectTheme(aTheme) {
    inspectObject(aTheme);
  },

  // Inspect theme add-on object with DOM Inspector
  inspectAddon: function themeUtils_inspectAddon(aTheme) {
    var id = aTheme.id + "@personas.mozilla.org";
    LightweightThemeManager.getAddonByID(id, inspectObject);
  }
};

[{
  label: "Visit Home Page",
  command: "this.parentNode.ThemeUtils.homepage(this.parentNode.triggerNode._theme);",
  tooltip: true
},{
  label: "Inspect Theme",
  command: "this.parentNode.ThemeUtils.inspectTheme(this.parentNode.triggerNode._theme);",
  inspector: true
},{
  label: "Inspect Add-on",
  command: "this.parentNode.ThemeUtils.inspectAddon(this.parentNode.triggerNode._theme);",
  inspector: true
},{
  label: "Remove Theme",
  command: "this.parentNode.ThemeUtils.remove(this.parentNode.triggerNode._theme);"
},{
  label: "More Info\u2026",
  command: "this.parentNode.ThemeUtils.info(this.parentNode.triggerNode._theme);"
}].forEach(function(aItem) {
  //LOG(aItem.label);
  if (aItem.inspector && typeof inspectObject !== "function")
    return;
  var menuitem = context.appendChild($create("menuitem"));
  menuitem.setAttribute("label", aItem.label);
  menuitem.setAttribute("oncommand", aItem.command);
  if (aItem.tooltip)
    menuitem.setAttribute("onmouseenter",
                          "this.tooltipText = this.parentNode.triggerNode.value;");
});

if ($(context.id))
  $(context.id).parentNode.removeChild($(context.id));

document.getElementById("mainPopupSet").appendChild(context);

var manageMenu = $create("menuitem");
manageMenu.openManager = openManager.bind();
manageMenu.setAttribute("oncommand", "openManager(this);");
manageMenu.setAttribute("label", "Manage\u2026");
manageMenu.setAttribute("image", this.image);
manageMenu.setAttribute("context", "toolbar-context-menu");
manageMenu.className = "menuitem-iconic";

var solidColorMenu = $create("menuitem");
solidColorMenu.solidColorTheme = solidColorTheme.bind();
solidColorMenu.setAttribute("oncommand", "solidColorTheme();");
solidColorMenu.setAttribute("label", "Solid Color Theme");
solidColorMenu.setAttribute("context", "toolbar-context-menu");

var menuseparator = $create("menuseparator")
menuseparator.setAttribute("context", "toolbar-context-menu");

var popup = this.appendChild($create("menupopup"));
popup.appendChild(manageMenu);
popup.appendChild(solidColorMenu);
popup.appendChild(menuseparator);
popup.populate = populate.bind();
popup.setLocalTheme = setLocalTheme.bind();
popup.previewTheme = previewTheme.bind();
popup.resetPreview = resetPreview.bind();
popup.setAttribute("onpopupshowing", "populate(this);");
popup.setAttribute("onmouseover", "previewTheme(event);");
popup.setAttribute("onmouseout", "resetPreview(event);");
popup.setAttribute("oncommand", "setLocalTheme(event);");
popup.setAttribute("context", context.id);

this.type = "menu";
this.onDestroy = function onButtonDestroy(aReason) {
  if (aReason === "delete")
    $(context.id).parentNode.removeChild($(context.id));
}
