custombutton.initcode:
var that = this.CBBackupAllButtons = {
	_UID: "__cb_backup_all_buttons" + self.id.slice(20),
	_exportType: null,
	_autoBackTimer: 0,
	_startupBackTimer: 0,
	_autoBackCheck: 0,
	_nsIFile: Components.Constructor("@mozilla.org/file/local;1", "nsIFile", "initWithPath"),
	_XMLExportFolder: null,

	get _curTime() { // getTime() in hours
		return parseInt((new Date()).getTime() / 3600000, 10);
	},

	alertCallback: {
		observe: function(aSubject, aTopic, aData) {
			if (aTopic !== "alertclickcallback")
				return;
			that._getExpStorage(that._whichStorage).reveal();
		}
	},

	_getExpStorage: function(aWhich) {
		let dir;
		try {
			dir = new that._nsIFile(that["_" + aWhich]);
			if (!dir.exists() || !dir.isDirectory())
				dir = Services.dirsvc.get("Home", Ci.nsIFile);
		} catch (aErr) {
			dir = Services.dirsvc.get("Home", Ci.nsIFile);
			dir.append("Custom Buttons Backups");
			if (!dir.exists() || !dir.isDirectory())
				dir.create(Ci.nsIFile.DIRECTORY_TYPE, 0x1FF);
		}
		return dir;
	},

	Init: function() {
		if (!("OS" in window))
			Cu.import("resource://gre/modules/osfile.jsm");
		if (!("Services" in window))
			Cu.import("resource://gre/modules/Services.jsm");

		that.InitPrefs();

		if (!(that._UID in window)) {
			window[that._UID] = {
				Initialized: false,
				StartupBackupDone: false,
				SavePrefFile: false,
			};
		}

		self.onclick = function(aE) {
			if (aE.button === 0 && !aE.shiftKey && !aE.ctrlKey && !aE.altKey) {
				aE.preventDefault();
				that.TogglePopup();
			}
			aE.stopPropagation();
		};

		for (let p in that.DefPrefs) {
			if (that.DefPrefs.hasOwnProperty(p))
				that["_" + p] = that.gPref(p);
		}

		if (!(window[that._UID]["Initialized"])) {
			window[that._UID]["Initialized"] = true;
			that.HandlePrefListener();
		}

		that.CreateOptionsMenu();
		that.HandleScheduledBackups();
		that.HandleStartupBack();

		addDestructor(function(aReason) {
			if (aReason === "update" || aReason === "delete" || aReason === "destructor") {
				that.HandlePrefListener(true);
				let selfEls = document.getElementsByAttribute("cb_id", that._UID);
				Array.prototype.slice.call(selfEls).forEach(function(aEl) {
					aEl && aEl.parentNode.removeChild(aEl);
				});
				window[that._UID]["Initialized"] = false;
				that.HandleScheduledBackups(true);
			}
			if (aReason === "delete" || aReason === "destructor") {
				clearTimeout(that._autoBackTimer);
				clearTimeout(that._startupBackTimer);
				clearInterval(that._autoBackCheck);
				delete window[that._UID];
			}
			if (aReason === "delete") {
				(Services.prefs.getBranch(that.Branch)).deleteBranch("");
				Services.prefs.savePrefFile(null);
			}
		});
	},

	TogglePopup: function() {
		let pop = document.getElementById(that._UID + "_CBBackupAllButtonsPopup");
		if (pop) {
			if (pop.state === "open")
				pop.hidePopup();
			else
				pop.openPopup(self, "after_start", 0, 0, false, false, null);
		}
	},

	HandleStartupBack: function() {
		clearTimeout(that._startupBackTimer);
		if (that._zStartupBUType === 0 || window[that._UID]["StartupBackupDone"])
			return true;
		let curTime = parseInt(that._curTime, 10);
		if (!window[that._UID]["StartupBackupDone"] &&
			that._zStartupBUElapsedHours !== 0 &&
			that._zStartupBULastTime !== 0) {
			if ((curTime - that._zStartupBULastTime) < that._zStartupBUElapsedHours)
				return true;
		}
		that._startupBackTimer = setTimeout(function() {
			switch (that._zStartupBUType) {
				case 1:
					that.HandleOverlay("HTML", "zStartupBUExpLoc");
					break;
				case 2:
					that.HandleOverlay("XML", "zStartupBUExpLoc");
					break;
				case 3:
					that.HandleOverlay("Overlay", "zStartupBUExpLoc");
					break;
			}
			that.sPref("zStartupBULastTime", curTime);
			window[that._UID]["StartupBackupDone"] = true;
		}, that._zStartupBUDelay * 1000);
	},

	HandleScheduledBackups: function(aRem) {
		clearTimeout(that._autoBackTimer);
		if (aRem)
			return true;
		let tmp;
		let oneDay = 24 * 60 * 60 * 1000;
		let today = new Date().getDay();
		if (that._zAutoBUType === 0)
			return true;
		if (!that.HandleAutoBackDays(today, true)) {
			clearInterval(that._autoBackCheck);
			that._autoBackCheck = setInterval(function() {
				that.HandleScheduledBackups();
			}, 300000);
			return true;
		}
		let now = new Date().getTime();
		let autoTime = new Date();
		autoTime.setSeconds(0);
		let waitTime = 0;
		autoTime.setHours(that._zAutoBUHours);
		autoTime.setMinutes(that._zAutoBUMinutes);
		waitTime = autoTime.getTime() - now;
		if (waitTime <= 0) {
			tmp = autoTime.getTime();
			tmp += oneDay;
			autoTime.setTime(tmp);
			waitTime = autoTime.getTime() - now;
		}
		that._autoBackTimer = setTimeout(function() {
			switch (that._zAutoBUType) {
				case 1:
					that.HandleOverlay("HTML", "zAutoBUExpLoc");
					break;
				case 2:
					that.HandleOverlay("XML", "zAutoBUExpLoc");
					break;
				case 3:
					that.HandleOverlay("Overlay", "zAutoBUExpLoc");
					break;
			}
		}, waitTime);
	},

	CreateOptionsMenu: function() {
		var popSet = document.getElementById("mainPopupSet");
		var m = document.getElementById(that._UID + "_CBBackupAllButtonsPopup");
		if (!!popSet && !m) {
			let pop = that.getHelp("MenuPopup")
				.replace(/_U_I_D_/g, that._UID)
				.replace(/_T_H_A_T_/g, "document.getElementById('" + self.id + "').CBBackupAllButtons");
			popSet.appendChild(that.parseXML(pop));
		}
	},

	HandleOptionsMenu: function(aNode) {
		Array.prototype.slice.call(aNode.getElementsByTagName("dialogheader")).forEach(function(aH) {
			aH && aH.setAttribute("style", "margin: 3px 0 3px 0 !important;padding: 2px 0 2px 5px " +
				"!important;border:none !important;font-size:smaller !important;");
		});
		Array.prototype.slice.call(aNode.getElementsByAttribute("type", "radio")).forEach(function(aR) {
			if (!!aR && aR.hasAttribute("_custom_ts"))
				aR.setAttribute("tooltiptext", that._zTimeStampForFilesCustom);
			aR && aR.setAttribute("checked", parseInt(aR.value, 10) === that["_" + aR.parentNode.getAttribute("_pref")]);
		});
		let days = that._zAutoBUDays.split(",").map(function(aVal) {
			if (aVal !== "")
				return parseInt(aVal, 10);
		});
		Array.prototype.slice.call(aNode.getElementsByAttribute("type", "checkbox")).forEach(function(aC) {
			aC && (aC.setAttribute("checked", days.indexOf(parseInt(aC.value, 10)) !== -1));
		});
		Array.prototype.slice.call(aNode.getElementsByTagName("textbox")).forEach(function(aT) {
			if (aT) {
				let pref = aT.getAttribute("_pref");
				aT.value = that["_" + pref]
				aT.onchange = function(aE) {
					setTimeout(function() {
						that.sPref(pref, parseInt(aT.value, 10));
					}, 10);
				};
			}
		});
	},

	HandleAutoBackDays: function(aDayNum, aCheck) {
		let days = that._zAutoBUDays.split(",").map(function(aVal) {
			if (aVal !== "")
				return parseInt(aVal, 10);
		});
		if (aCheck) {
			if (days.indexOf(aDayNum) !== -1)
				return true;
			else
				return false;
		} else {
			if (days.indexOf(aDayNum) !== -1) {
				while (days.indexOf(aDayNum) !== -1) {
					days.splice(days.indexOf(aDayNum), 1);
				}
			} else
				days.push(aDayNum);
			that.sPref("zAutoBUDays", days.join(","));
		}
	},

	SetCustomTimestamp: function() {
		let msg = "Set a custom timestamp format for files/folders names.\n" +
			"YYYY: year\nMM: month\nDD: day\nhh: hour\nmm: minute\nss: second\n\n" +
			"Note: The choosen format will be automatically stripped of ilegal characters for file names.";
		let value = that.promptme(msg, that._zTimeStampForFilesCustom);
		if (value)
			that.sPref("zTimeStampForFilesCustom", value);
	},

	SetBackupsCustomPath: function(aType) {
		let type;
		switch (aType) {
			case "zStartupBUExpLoc":
				type = "Startup backups";
				break;
			case "zAutoBUExpLoc":
				type = "Scheduled backups";
				break;
			case "zManualBUExpLoc":
				type = "Manual backups";
				break;
		}
		let msg = type + "\n\nFull path to an EXISTENT custom location to store exported Custom Buttons.\n" +
			"Example Windows: C:\\Users\\UserName\\Custom Buttons Backups\n" +
			"Example Linux: /home/username/Custom Buttons Backups";
		let value = that.promptme(msg, that["_" + aType]);
		if (value)
			that.sPref(aType, value);
	},

	SetHTMLBaseName: function() {
		let value = that.promptme("Set a custom base name for HTML files.", that._zHTMLDefaultFileName);
		if (value)
			that.sPref("zHTMLDefaultFileName", value);
	},

	HandleOverlay: function(aType, aWhichStorage) {
		that._whichStorage = aWhichStorage;
		let overlayFile = OS.Path.join(OS.Constants.Path.profileDir, "custombuttons", "buttonsoverlay.xul");
		that._exportType = aType;
		if (that._exportType === "Overlay") {
			let dateForFileNames = that._getTimeStamp(new Date().getTime());
			OS.File.copy(overlayFile,
				OS.Path.join(that._getExpStorage(that._whichStorage).path,
					dateForFileNames + "-buttonsoverlay.xul"));
			that.notifyme("Custom Buttons overlay backed up",
				"Click here to open export location.",
				that.getImg("AlertImg"),
				that.alertCallback);
			return true;
		}
		OS.File.read(overlayFile).then(function onSuccess(aReadData) {
			let data = new TextDecoder().decode(aReadData).replace(/>\s+</g, "><");
			let overlayDoc = (new DOMParser).parseFromString(data, "application/xml").documentElement;
			let allPalettes = overlayDoc.getElementsByTagName("toolbarpalette");
			let allUsedPalettes = [];
			for (let i = allPalettes.length - 1; i >= 0; i--) {
				if (allPalettes[i].childNodes.length > 0)
					allUsedPalettes.push(allPalettes[i]);
			}
			setTimeout(function() {
				Array.prototype.slice.call(allUsedPalettes).forEach(function(aPalette) {
					that.saveAllButtonsFromOverlay(aPalette, aPalette.id);
				});
			}, 200);
		}, function onFailure(aRejectReason) {
			Cu.reportError(self.name + ": " + [aType, aWhichStorage, aRejectReason, aRejectReason.stack].join("\n"));
		});
	},

	saveAllButtonsFromOverlay: function(aPalette, aPaletteName) {
		if (!that._exportType || that._exportType === "Overlay")
			return true;
		let paletteStorage = OS.Path.join(that._getExpStorage(that._whichStorage).path, aPaletteName);
		OS.File.makeDir(paletteStorage, {
			ignoreExisting: true
		});
		var dateForFileNames = that._getTimeStamp(new Date().getTime());
		if (that._exportType === "XML") {
			that._XMLExportFolder = OS.Path.join(paletteStorage, "XML Output " + dateForFileNames);
			OS.File.makeDir(that._XMLExportFolder, {
				ignoreExisting: true
			});
		}
		var outPut = [];
		Array.prototype.slice.call(aPalette.childNodes).forEach(function(aBtn) {
			let img = aBtn.getAttribute("image");
			img || (img = that.getImg(aBtn.getAttribute("cb-stdicon")));
			that.img_to_b64_cback(img, function(aImg) {
				if (that._exportType !== "Images") {
					let name = aBtn.getAttribute("label") || aBtn.getAttribute("name") || "";
					(name === "") && (name = "Name me!!");
					let id = aBtn.getAttribute("id").slice(20),
						init = aBtn.getAttribute("cb-init"),
						key = aBtn.getAttribute("accelkey") || "",
						code = aBtn.getAttribute("cb-oncommand"),
						help = aBtn.getAttribute("Help"),
						mode = aBtn.getAttribute("mode") || "";
					let URI = that.createURI(name, aImg, code, help, init, key, mode);
					switch (that._exportType) {
						case "HTML":
							outPut.push("<li><span><span><input class=\"spoiler_button\" type=\"button\"" +
								" value=\"Show Source\" onclick=\"self.spr(this);\"><a href=\"" + URI +
								"\"><img alt=\"\" src=\"" + aImg + "\">" + name + "</a><label>#ID:" + id +
								"</label></span><span><span class=\"spoiler\" style=\"display: none;\">" +
								"</span></span></span></li>");
							break;
						case "XML":
							that.saveXML(name, id, URI, aImg);
							break;
					}
				} else
					outPut.push(aImg);
			});
		});
		let buttonCount = outPut.length;
		setTimeout(function() {
			if (that._exportType === "XML") {
				that.notifyme("All buttons exported as XML", "Click here to open export location.",
					that.getImg("AlertImg"),
					that.alertCallback);
			} else if (that._exportType === "Images") {
				if (!("Task" in window))
					Cu.import("resource://gre/modules/Task.jsm");
				if (!("Downloads" in window))
					Cu.import("resource://gre/modules/Downloads.jsm");
				let imgs = outPut.filter(function(v, i, a) { // Remove duplicated
					return a.indexOf(v) === i;
				});
				let imgPath = OS.Path.join(paletteStorage, "Saved Images " + dateForFileNames);
				OS.File.makeDir(imgPath, {
					ignoreExisting: true
				});
				let count = imgs.length;
				Array.prototype.slice.call(imgs).forEach(function(aImg) {
					if (aImg.search(/^data:image/) !== -1) {
						let imgExt;
						count--;
						switch (aImg.substring(0, aImg.indexOf(";"))) {
							case "data:image/svg+xml":
								imgExt = ".svg";
								break;
							case "data:image/x-icon":
								imgExt = ".ico";
								break;
							case "data:image/gif":
								imgExt = ".gif";
								break;
							case "data:image/jpeg":
								imgExt = ".jpg";
								break;
							case "data:image/bmp":
								imgExt = ".bmp";
								break;
							default:
								imgExt = ".png";
						}
						var geckoVer = parseInt(Services.appinfo.platformVersion, 10) >= 27; // If Gecko version is v27 or +.
						Task.spawn(function() {
							let doDownload = function(aURL, aPath) {
								return (geckoVer ? Downloads.fetch(aURL, aPath) : Downloads.simpleDownload(aURL, aPath));
							};
							yield doDownload(aImg, OS.Path.join(imgPath, "Image-" + that.padding(count, 3) + imgExt));
						}).then(null, Cu.reportError);
					}
				});
				that.notifyme("All Custom Buttons images saved", "Click here to open export location.",
					that.getImg("AlertImg"),
					that.alertCallback);
			} else if (that._exportType !== "XML" && that._exportType !== "Images") {
				outPut = outPut.sort(function sortByString(a, b) {
					return a.localeCompare(b);
				});
				var html = that.getHelp("HTMLForExport");
				html = html
					.replace("<!--_T_I_T_L_E_-->", "There are a total of " + buttonCount + " <title>")
					.replace("<!--_B_U_T_T_O_N_L_I_S_T_-->", new Date() + "\n" + "<ol>" + "\n" + outPut.join("\n"));
				setTimeout(function() {
					OS.File.writeAtomic(OS.Path.join(paletteStorage,
							that._zHTMLDefaultFileName + dateForFileNames + ".html"),
						html, {
							tmpPath: paletteStorage + ".tmp",
							encoding: "utf-8"
						});
					that.notifyme("All buttons exported as HTML", "Click here to open export location.",
						that.getImg("AlertImg"),
						that.alertCallback);
				}, 500);
			}
		}, parseInt(buttonCount * 10, 10));
	},

	saveXML: function(aName, aId, aURI, aImg) {
		if (!aURI || !/^custombutton\:\/\//.test(aURI))
			return;
		var cbXML = aURI.replace(/^custombutton\:\/\//, "");
		var data = unescape(cbXML);
		var header = "<custombutton xmlns:cb=\"http://xsms.nm.ru/custombuttons/\"" +
			" xmlns:html=\"http://www.w3.org/1999/xhtml\">\n" +
			"<html:head>\n" +
			"	<html:title>\x3C![CDATA[" + that.escHTML(aName) + "]]\x3e</html:title>\n" +
			"	<html:link rel=\"shortcut icon\" href=\"" + aImg + "\"/>\n" +
			"	<html:style type=\"text/css\">\x3C![CDATA[" + that.getHelp("StyleForXMLFiles") +
			"]]\x3e</html:style>\n" +
			"</html:head>\n" +
			"<html:body>\n" +
			"	<html:div id=\"wrapper\">\n" +
			"		<html:div id=\"button\">\n" +
			"			<html:a href=\"" + aURI + "\" rel=\"nofollow\" title=\"Install " +
			that.escHTML(aName) + "\">\n" +
			"				\x3C![CDATA[Install this button]]\x3e\n" +
			"			</html:a>\n" +
			"		</html:div>\n" +
			"		<html:a id=\"about\" href=\"https://addons.mozilla.org/addon/custom-buttons/\">\n" +
			"			\x3C![CDATA[About CustomButtons]]\x3e\n" +
			"		</html:a>\n" +
			"		<html:div id=\"credits\">\n" +
			"			<html:a href=\"http://custombuttons.sourceforge.net/forum/viewtopic.php?t=3647\">\n" +
			"				\x3C![CDATA[XML created with]]\x3e\n" +
			"				<html:br/>\n" +
			"				\x3C![CDATA[Backup all Custom Buttons]]\x3e\n" +
			"			</html:a>\n" +
			"		</html:div>\n" +
			"	</html:div>\n" +
			"</html:body>";
		data = data
			.replace(/<custombutton xmlns\:cb=\"http\:\/\/xsms\.nm\.ru\/custombuttons\/\">/, header);
		let filePath = OS.Path.join(that._XMLExportFolder, that.legalize(aName) + "-ID" + aId + ".xml");
		OS.File.writeAtomic(filePath, data, {
			tmpPath: filePath + ".tmp",
			encoding: "utf-8"
		});
	},

	/**
	 * Using a parsed string for the template variable it's infinitely faster than calling
	 * the template file with XMLHttpRequest.
	 */
	createURI: function(aName, aImg, aCode, aHelp, aInit, aKey, aMode) {
		let template = (new DOMParser).parseFromString('<?xml version="1.0"?><custombutton xmlns:cb=' +
			'"http://xsms.nm.ru/custombuttons/"><name/><image/><mode/><initcode/><code/><accelke' +
			'y/><help/><attributes/></custombutton>', "application/xml");
		let sTxt = custombutton.buttonSetText;
		sTxt(template, "name", aName, 0);
		sTxt(template, "image", aImg, 1);
		sTxt(template, "mode", aMode, 0);
		sTxt(template, "initcode", that.escCDATA(aInit), 1);
		sTxt(template, "code", that.escCDATA(aCode), 1);
		sTxt(template, "accelkey", aKey, 1);
		sTxt(template, "help", that.escCDATA(aHelp), 1);
		return "custombutton:\x2F\x2F" + escape(new XMLSerializer().serializeToString(template));
	},

	escCDATA: function(aStr) { // Unescaped CDATA tags can break buttons in XML format.
		return aStr
			.replace(/]]\x3e/g, "\\" + "x5D" + "\\" + "x5D" + "\\" + "x3e")
			.replace(/]]&gt\;/g, "\\" + "x5D" + "\\" + "x5D" + "\\" + "x3e");
	},

	img_to_b64_cback: function(aURL, aCallback) {
		if (!aURL)
			aCallback(that.getImg("DefaultImg"));
		else if (aURL.search(/^data:image/) === 0)
			aCallback(aURL);
		else {
			let xhr = new XMLHttpRequest,
				nsIMIMEService = Cc["@mozilla.org/mime;1"].getService(Ci.nsIMIMEService);
			try {
				xhr.open("GET", aURL, true);
			} catch (aErr) {
				aCallback(that.getImg("DefaultImg"));
				return true;
			}
			xhr.responseType = "arraybuffer";
			xhr.onload = function() {
				let mediaType,
					dataURI,
					binary,
					bytes;
				bytes = new Uint8Array(xhr.response);
				binary = [].map.call(bytes, function(aByte) {
					return String.fromCharCode(aByte);
				}).join("");
				mediaType = xhr.getResponseHeader("Content-Type");
				let base64 = btoa(binary);
				if (mediaType.indexOf("image/") !== 0) {
					try {
						mediaType = nsIMIMEService.getTypeFromURI(Services.io.newURI(aURL, null, null));
					} catch (aError) {
						mediaType = "image/png";
					}
				}
				dataURI = "data:" + mediaType + ";base64," + base64;
				aCallback(dataURI);
			};
			xhr.onerror = function() {
				aCallback(that.getImg("DefaultImg"));
			};
			xhr.send(null);
		}
	},

	_getTimeStamp: function(aDate) {
		let ts;
		switch (that._zTimeStampForFiles) {
			case 0:
				ts = that.legalize(that._zTimeStampForFilesCustom); // Custom
				break;
			case 1:
				ts = "YYYY MM-DD hh.mm.ss"; // ISO8601
				break;
			case 2:
				ts = "YYYY DD.MM hh.mm.ss"; // European
				break;
		}
		let dte = new Date(parseInt(aDate));
		let YYYY = String(dte.getFullYear());
		let MM = String(dte.getMonth() + 1);
		if (MM.length === 1) {
			MM = "0" + MM;
		}
		let DD = String(dte.getDate());
		if (DD.length === 1)
			DD = "0" + DD;
		let hh = String(dte.getHours());
		if (hh.length === 1)
			hh = "0" + hh;
		let mm = String(dte.getMinutes());
		if (mm.length === 1)
			mm = "0" + mm;
		let ss = String(dte.getSeconds());
		if (ss.length === 1)
			ss = "0" + ss;
		ts = ts.replace("YYYY", YYYY);
		ts = ts.replace("MM", MM);
		ts = ts.replace("DD", DD);
		ts = ts.replace("hh", hh);
		ts = ts.replace("mm", mm);
		ts = ts.replace("ss", ss);
		return ts;
	},

	HandlePrefListener: function(aRem) {
		var prefs = that.DefPrefs;
		for (let key in prefs) {
			let pref = that.Branch + key;
			if (prefs.hasOwnProperty(key)) {
				if (aRem)
					Services.prefs.removeObserver(pref, that.PrefListener, false);
				else
					Services.prefs.addObserver(pref, that.PrefListener, false);
			}
		}
	},

	PrefListener: {
		observe: function(aSubject, aTopic, aPStr) {
			if (aTopic === "nsPref:changed") {
				window[that._UID]["SavePrefFile"] = true;
				let prefName = aPStr.slice(that.Branch.length);
				that["_" + prefName] = that.gPref(prefName);
				switch (prefName) {
					case "zHTMLDefaultFileName":
						if (that.gPref(prefName) === "") {
							that.notifyme("Default name for HTML files can't be blank!!!",
								"Value has been reseted to its default.",
								that.getImg("WarningImg"));
							Services.prefs.clearUserPref(that.Branch + prefName);
						}
						break;
					case "zAutoBUType":
					case "zAutoBUDays":
					case "zAutoBUHours":
					case "zAutoBUMinutes":
						that.HandleScheduledBackups();
						break;
				}
			}
		}
	},

	getImg: function(aSrc) {
		switch (aSrc) {
			case "custombuttons-stdicon-1":
				return "chrome://custombuttons/skin/button.png";
			case "custombuttons-stdicon-2":
				return "chrome://custombuttons/skin/stdicons/rbutton.png";
			case "custombuttons-stdicon-3":
				return "chrome://custombuttons/skin/stdicons/gbutton.png";
			case "custombuttons-stdicon-4":
				return "chrome://custombuttons/skin/stdicons/bbutton.png";
			default:
				return that.getHelp(aSrc);
		}
	},

	getHelp: function(aPlaceholder) { // Get specific data, surrounded by "placeholders", from the Help tab.
		return document.getElementById(self.id).getAttribute("Help").split("<!--" + aPlaceholder + "-->")[1];
	},

	promptme: function(aMsg, aVal) {
		let retVal = {
			value: aVal
		};
		if (Services.prompt.prompt(null, (self.label || self.name), aMsg, retVal, null, {}))
			return retVal.value;
		else
			return false;
		return false;
	},

	$CNS: function(aElName, aAttrs, aNS) {
		var el = document.createElementNS((aNS ? aNS : xulns), aElName);
		if (aElName === "menuitem")
			el.setAttribute("class", "menuitem-iconic");
		if (aAttrs) {
			Object.keys(aAttrs).forEach(function(n) {
				if (aAttrs[n] !== "")
					el.setAttribute(n, aAttrs[n]);
			});
		}
		return el;
	},

	parseXML: function(aXML) { // Return parsed XML
		aXML = aXML.replace(/>\s+</g, "><"); // Linearize XML
		return (new DOMParser).parseFromString(aXML, "application/xml").documentElement;
	},

	padding: function(aNum, aLength) {
		var str = '' + aNum;
		while (str.length < aLength)
			str = '0' + str;
		return str;
	},

	legalize: function(aStr) { // Removes illegal characters in file names.
		return aStr.replace(/[\/:\"\*\?<>\|\\]/g, "");
	},

	escHTML: function(aStr) {
		aStr = String(aStr)
			.replace(/&/g, "&amp;")
			.replace(/</g, "&lt;")
			.replace(/>/g, "&gt;")
			.replace(/"/g, "&quot;")
			.replace(/'/g, "&apos;");
		return aStr;
	},

	notifyme: function(aTitle, aMsg, aImg, aCallback) { // Notification function
		let origin = (Services.prefs.getPrefType("ui.alertNotificationOrigin") !== 0) ?
			Services.prefs.getIntPref("ui.alertNotificationOrigin") :
			null;
		let win = Services.ww.openWindow(null, "chrome://global/content/alerts/alert.xul",
			"_blank", "chrome,titlebar=no,popup=yes", null);
		win.arguments = [aImg, aTitle, aMsg, !!aCallback, "", origin, "", "", null, (!!aCallback ? aCallback : null)];
	},

};

this.CBBackupAllButtons.Branch = "CB.CBBackupAllButtons.Id" + self.id.slice(20) + ".";

this.CBBackupAllButtons.DefPrefs = {
	zManualBUExpLoc: "",
	zHTMLDefaultFileName: "Custom Buttons ",
	zAutoBUExpLoc: "",
	zAutoBUType: 0,
	zAutoBUDays: "0,1,2,3,4,5,6",
	zAutoBUHours: 12,
	zAutoBUMinutes: 30,
	zStartupBUExpLoc: "",
	zStartupBUType: 0,
	zStartupBUDelay: 5,
	zStartupBUElapsedHours: 23,
	zStartupBULastTime: 0,
	zTimeStampForFilesCustom: "YYYY-MM-DD hh.mm.ss",
	zTimeStampForFiles: 1,
};

this.CBBackupAllButtons.gPref = function(aKey) {
	let branch = Services.prefs.getBranch(self.CBBackupAllButtons.Branch);
	switch (typeof self.CBBackupAllButtons.DefPrefs[aKey]) {
		case "boolean":
			return branch.getBoolPref(aKey);
			break;
		case "number":
			return branch.getIntPref(aKey);
			break;
		case "string":
			return branch.getComplexValue(aKey, Ci.nsISupportsString).data;
			break;
	}
	return null;
};

this.CBBackupAllButtons.sPref = function(aKey, aVal) {
	let branch = Services.prefs.getBranch(self.CBBackupAllButtons.Branch);
	switch (typeof aVal) {
		case "boolean":
			branch.setBoolPref(aKey, aVal);
			break;
		case "number":
			branch.setIntPref(aKey, aVal);
			break;
		case "string":
			let str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
			str.data = aVal;
			branch.setComplexValue((aKey), Ci.nsISupportsString, str);
			break;
	}
};

this.CBBackupAllButtons.InitPrefs = function() {
	let branch = Services.prefs.getDefaultBranch(self.CBBackupAllButtons.Branch),
		defaults = that.DefPrefs;
	for (let key in defaults) {
		if (defaults.hasOwnProperty(key)) {
			let val = defaults[key];
			switch (typeof val) {
				case "boolean":
					branch.setBoolPref(key, val);
					break;
				case "number":
					branch.setIntPref(key, val);
					break;
				case "string":
					let str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
					str.data = val;
					branch.setComplexValue((key),
						Ci.nsISupportsString, str);
					break;
			}
		}
	}
};

this.CBBackupAllButtons.Init();
