custombutton.initcode:
const _UID = "browser-console-sidebar-tab" + (self.id).split("custombuttons")[1],
	_CSS = "__css_" + _UID,
	USE_CSS_PREF = "extensions.custombuttons.BrowserConsoleOnSidebarTabBar.StyleSheetEnabled";

custombuttons.isPref(USE_CSS_PREF, true);

(tmp => {
	var modules = "resource://gre/modules/";
	var {
		Promise
	} = Cu.import(modules + "Promise.jsm", tmp);
	var {
		devtools
	} = Cu.import(modules + "devtools/Loader.jsm", tmp);
	var {
		DebuggerServer
	} = Cu.import(modules + "devtools/dbg-server.jsm", tmp);
	var {
		DebuggerClient
	} = Cu.import(modules + "devtools/dbg-client.jsm", tmp);

	var hud = devtools.require("devtools/webconsole/hudservice");
	var url = "chrome://browser/content/devtools/webconsole.xul";

	var link = document.createElementNS(xhtmlns, "link");
	link.setAttribute("rel", "shortcut icon");
	link.setAttribute("href", self.image);

	function connect() {
		var deferred = Promise.defer();

		if (!DebuggerServer.initialized) {
			DebuggerServer.init();
			DebuggerServer.addBrowserActors();
		}
		DebuggerServer.allowChromeProcess = true;

		var client = new DebuggerClient(DebuggerServer.connectPipe());
		client.connect(() => {
			client.getProcess().then(response => {
				deferred.resolve({
					form: response.form,
					client: client,
					chrome: false
				});
			}, deferred.reject);
		});
		return deferred.promise;
	}

	addEventListener("DOMContentLoaded", e => {
		var win = e.target.defaultView,
			loc = win.location;
		if (!loc || loc.href != url) return;
		connect()
			.then(connection => devtools.TargetFactory.forRemoteTab(connection))
			.then(target => {
				hud.openBrowserConsole(target, win, win).then(browserConsole => {
					win.onbeforeunload = e => {
						e.returnValue = "";
						browserConsole.chromeWindow = {
							close: () => {},
							document: win.document
						};
					}
					var root = win.document.documentElement;
					root.setAttribute("title", "Browser Console");
					root.appendChild(link.cloneNode(false));
				}, Cu.reportError);
			}, Cu.reportError);

	}, false, document.getElementById("browser"));

	self.onclick = function(aEvt) {
		aEvt.preventDefault();
		if (aEvt.button === 0 && !aEvt.shiftKey && !aEvt.ctrlKey && !aEvt.altKey) {
			aEvt.preventDefault();
			var wpb = SidebarUI.browser.contentDocument.getElementById("web-panels-browser");
			wpb && wpb.currentURI.spec === url ? SidebarUI.hide() : openWebPanel("Browser Console", url);
		} else if (aEvt.button === 1 && !aEvt.shiftKey && !aEvt.ctrlKey && !aEvt.altKey) {
			var tab = gBrowser.visibleTabs.find(tab => tab.linkedBrowser.currentURI.spec === url);
			gBrowser.selectedTab = tab || gBrowser.addTab(url);
		} else if (aEvt.button === 0 && !aEvt.shiftKey && aEvt.ctrlKey && !aEvt.altKey) {
			Services.prefs.setBoolPref(USE_CSS_PREF, !Services.prefs.getBoolPref(USE_CSS_PREF));
			toggleCSS();
		}
		aEvt.stopPropagation();
	};
})({});

function getHelp(aPlaceholder) { // Get specific data, surrounded by "placeholders", from the Help tab.
	return document.getElementById(self.id).getAttribute("Help").split("<!--" + aPlaceholder + "-->")[1];
}

//START// Load style sheets
var css = getHelp("StyleSheet");

function styleSheet(aCSS, aRemFlag) {
	var sss = Cc["@mozilla.org/content/style-sheet-service;1"]
		.getService(Ci.nsIStyleSheetService);
	var ios = Cc["@mozilla.org/network/io-service;1"]
		.getService(Ci.nsIIOService);
	var uri = ios.newURI("data:text/css," + encodeURIComponent(aCSS), null, null);
	if (sss.sheetRegistered(uri, sss.USER_SHEET))
		sss.unregisterSheet(uri, sss.USER_SHEET);
	if (aRemFlag)
		return;
	sss.loadAndRegisterSheet(uri, sss.USER_SHEET);
	window[_CSS] = aCSS;
}

function deleteCSS() {
	if ((_CSS in window)) {
		styleSheet(window[_CSS], true);
		delete window[_CSS];
	}
}

function toggleCSS() {
	if (Services.prefs.getBoolPref(USE_CSS_PREF)) {
		if (!(_CSS in window))
			styleSheet(css);
		else if (window[_CSS] !== css) {
			styleSheet(window[_CSS], true);
			setTimeout(function() {
				styleSheet(css);
			}, 500);
		}
	} else {
		deleteCSS();
	}
}
//END// Load style sheets

this.onDestroy = function(aReason) {
	self.antiReInit = false;
	if (aReason === "delete" || aReason === "update")
		deleteCSS();
};

this.tooltipText = this.label + "\n L = Browser Console On Sidebar \n M = Browser Console On New Tab" +
"\n Ctrl + L = Toggle StyleSheet";

toggleCSS();

custombutton.help:
Inside the Help tab of this button you will find the style sheet to customize the console.

Left click = Open Browser Console in Sidebar
Middle click = Open Browser Console in New Tab
[,]
