custombutton.initcode:
if (self.parentNode && /^wrapper-/.test(self.parentNode.id))
	return;

var $ = this.MultiSearch = {
	/**
	 *  Change this button image here.
	 */
	get customImage() {
		return $.defaultImage; // Comment this line.
		// return "link to an image of your choice"; // Un-comment this line and add a link to an image.
	},

	init: function() {
		// If button is located inside App Button or in Vertical Toolbar Addon
		// Put it right here due to those sheets of Palemoon and SeaMonkey.
		if (self.parentNode && (self.parentNode.id.match("PanelUI") ||
				self.parentNode.id.match("vertical-") ||
				self.parentNode.id === "aios-toolbar")) {
			if (self.parentNode.id.match("PanelUI")) {
				$.notify("Multi Search button does not work inside the App Menu. Place it inside any other toolbar.");
				self.disabled = true;
				setTimeout(function() {
					$.notify("", true);
				}, 20000);
			}
			self.orient = "vertical";
		} else
			self.orient = "horizontal";

		if (!("Services" in window))
			Cu.import("resource://gre/modules/Services.jsm");

		$.initPrefs();

		if (!($._UID in window)) {
			window[$._UID] = {
				CustomizableUI: false,
				DDOneOffFlag: false,
				HideOneOffFlag: false,
				DragEndFlag: false,
				StyleSheet: false,
				Initialized: false,
				ListenerForOneOffCtxMenu: false,
				ListenerForManageEnginesMenu: false,
				SelfImage: false,
				FrameScriptLoaded: false,
				Selection: "",
				Preferences: {},
			};
		}

		for (let p in $.DefPrefs) {
			if ($.DefPrefs.hasOwnProperty(p))
				window[$._UID]["Preferences"][p] = $.gPref(p);
		}

		$._ = window[$._UID]["Preferences"];

		if (!window[$._UID]["RandomID"]) {
			window[$._UID]["RandomID"] = parseInt(Math.random() * 10000, 10);
		}
		$._randomID = window[$._UID]["RandomID"];

		if (!window[$._UID]["FrameScriptLoaded"]) {
			window[$._UID]["FrameScriptLoaded"] = true;
			$._frameScript = "data:application/javascript," + encodeURIComponent($.getHelp("%FrameScript%")
				.replace(/%Search_Image_ID%/g, $._searchImageMsgID)
				.replace(/%Get_Sel_ID%/g, $._getSelectionMsgID)
				.replace(/%D_AND_D_FEEDBACK%/g, !$._.pGlobalDaDFeedback)
				.replace(/%Cleanup_ID%/g, $._cleanUpMsgID)
				.replace(/%Response_ID%/g, $._responseMsgID));
			$._globalMM.loadFrameScript($._frameScript, true);
			$._globalMM.addMessageListener($._responseMsgID, $._messageResponse);
		}

		if ($._.pUseEngineImageOnButton) {
			// Setting the style here instead of using CSS is infinitelly faster.
			// This minimizes the interface "dancing" every time the CSS is reloaded.
			let selfImage = document.getAnonymousElementByAttribute(self, "class", "toolbarbutton-icon");
			if (!selfImage) {
				let btn = document.getAnonymousElementByAttribute(self, "anonid", "button");
				selfImage = document.getAnonymousElementByAttribute(btn, "class", "toolbarbutton-icon");
			}
			selfImage.style.setProperty("max-height", $._.pButtonIconHeight + "px", "important");
			selfImage.style.setProperty("width", "100%", "important");
		} else
			self.image = $.customImage;

		$.CSSLoader();
		$.handlePrefObserver();
		$.checkIfOptIsAssigned();
		$.createElements();

		if ($._.pOneOffButtons)
			($.SP.getPrefType("browser.search.hiddenOneOffs") === 0) && $.sCV("browser.search.hiddenOneOffs", "");

		$.maxMinWidth = $._.pEnginesPerRow * 39;

		self.setAttribute("overflows", $._.pAllowOverlow);

		self.tooltipText = "";
		self.setAttribute("tooltip", $._UID + "-dynamic-tooltip");

		// Wait for Search service to be ready to do some things.
		$.SS.init(function onInitComplete() {
			// Set up page context menu
			if ($._.pSearchImageContextMenu) {
				if (!$.$($._UID + "-search_image_menuitem"))
					$.sbiContext.init();
			} else
				$.sbiContext.remove();

			if ($._.pSearchSelectionContextMenu) {
				if (!$.$($._UID + "-context_search_selection"))
					$.pageContext.init();
			} else
				$.pageContext.remove();

			self.image = $.getButtonImage();

			try {
				if (!$.sP)
					$.createPanels();
			} finally {
				if ($._.pOneOffButtons && $.oODetached) {
					setTimeout(function() {
						var autoCP = $.aCP;
						if (!!autoCP) {
							try {
								// autoCP.style.setProperty("-moz-binding",
								// 	"url(chrome://browser/content/urlbarBindings.xml" +
								// 	"#browser-autocomplete-result-popup)", "important");
								autoCP.style.MozBinding = "url(chrome://browser/content/urlbarBindings.xml" +
									"#browser-autocomplete-result-popup) !important;";
							} finally {
								setTimeout(function() {
									try {
										// To keep "CustomPopupAutoComplete" panel open after selecting a suggestion.
										$.tB.oninput = function() {
											setTimeout(function() {
												($.tB.value !== "") && $.searchPanelActions.keepItUp($.tB, $.tB);
											}, 50);
										};
										autoCP.textContent = "";
										$.searchPanelActions.attachOneOffBtns();
										autoCP.hidden = false; // Needed for Palemoon. ¬¬
									} catch (aErr) {
										$.reportError(aErr);
									}
								}, 500);
							}
						}
					}, 500);
				}
			}
			var aboutEngName = "aboutConfig";
			var aboutEng = $.SS.getEngineByName(aboutEngName);
			if ($._.pAboutConfigEngine) {
				if (!aboutEng) {
					$.SS.addEngineWithDetails(
						aboutEngName,
						"data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAC7E" +
						"lEQVQ4jXWTwWsaaxTFP2jXgXYhNJSCdBaGLLoJEVx1VSj0DyguGwiYIC6EKgg67pJQVFITyE" +
						"zF6CLJDGjqNKCosZUig34Tg3zquNA+ZqHgogN2IbiZ8zbN46XF3/aecy73ci8hSwgGg49Kpd" +
						"KHUqn0IRgMPlqmuwfP80+vr6/L1Wr1Szgcft1sNtFsNhGNRl8XCoUv1Wq1zPP806UBoijK3W" +
						"4XjDGUSqU+YwyMMVxdXfUZY+j1eojH4/JfxkAg8KxcLn9cW1t72+/3MRwOLcMwoGkaNE2DYR" +
						"gYDodWr9fD+vr6W1VVP/I8/+y/AErp18lkgtFohOl0ajUaDSSTSQiCAEEQkEwm0Wg0MJ1OLc" +
						"MwMB6PQSn9eud/eHh42DVNE6Zpot1u4/T0FDzP/+NyuUIulysUjUZ/pNNp3N7e4k6XSCS6hJ" +
						"CHxOv1rvh8vjeKoowXiwVkWbZEUfxFCFn935RPRFH8JcuytVgsoCjK2OfzvfF6vSukVqsJpm" +
						"liPp9jNpshl8shFospf+4pFospuVwOs9kM8/kcpmmiVqsJJJvNplRVBaUU/X4fxWIRsix/+z" +
						"NAluVvxWIRuq6DUgpVVZHNZlPEZrO92NjYeJ9KpSaapoFSCk3TEAqFXHfmUCjk0jQNrVYLNz" +
						"c3ODk5mWxubr632WwvCCGEXF5eflYUBeVyGa1WC+1229J1HZ1OR+t0Opqu62i32xalFJVKBY" +
						"qiIJ1Of75r8CASibBMJoOLiwsUCgWrXq+DMYbBYIDBYADGGOr1OhRFsSRJQjabRSQSYYSQB4" +
						"QQQhwOx8utra3vHMeFM5kMzs7OIEkS8vk88vk8JEnC+fk50uk0VldXw9vb298dDsfLvy5yb2" +
						"/vUzwex9HREfx+/09BECCKIvx+/8/j42MkEgkEAoFPS3/Bbre/8ng8o93d3Y7T6Xy3v7+Pg4" +
						"MDOJ3Odzs7Ox2PxzOy2+2vlgb8ZoUQ8pjjuOdut7vidrsrHMc9J4Q8/l27x78wUO831tEJLg" +
						"AAAABJRU5ErkJggg==",
						"",
						"aboutConfig",
						"GET",
						"https://duckduckgo.com/?q={searchTerms}");
					aboutEng = $.SS.getEngineByName(aboutEngName);
				}
				aboutEng = aboutEng.wrappedJSObject;
				aboutEng.getSubmission = function(aData, aResponseType) {
					var url = 'about:config?filter=' + aData;
					var submission = {
						uri: {
							spec: url
						},
						postData: null
					};
					return submission;
				};
			} else
				aboutEng && $.SS.removeEngine(aboutEng);

			if ($._.pDragAndDropForOneOffButtons) {
				let sP = $.sP;
				try {
					setTimeout(function() {
						$._initializingSearchPanel = true;
						sP && sP.style.setProperty("opacity", "0", "important");
						sP && sP.openPopup(self, "", 0, 0, false, null, null);
						setTimeout(function() {
							sP && sP.style.removeProperty("opacity");
							sP && sP.hidePopup();
							$._initializingSearchPanel = false;
						}, 1000);
					}, 1000);
				} catch (aErr) {
					$.reportError(aErr);
				}
			}
		});

		$._.pOneOffButtons &&
			self.setAttribute("ondragenter", $.that + "dragAndDropHandler.handleEvent(event);");
		self.setAttribute("ondragover", $.that + "dragAndDropHandler.handleEvent(event);");
		self.setAttribute("ondragleave", $.that + "dragAndDropHandler.handleEvent(event);");
		self.setAttribute("ondrop", $.that + "dragAndDropHandler.handleEvent(event);");

		self.oncontextmenu = function(aE) {
			if (aE.shiftKey || $.accel(aE) || aE.altKey)
				return false;
		};

		// Fix for Linux.
		// To avoid opening the search panel when trying to open the "manage engines" popup.
		self.onmousedown = function(aE) {
			if (aE.button === 0 && aE.shiftKey && $.accel(aE) && !aE.altKey)
				aE.preventDefault();
		};

		self.onclick = function(aE) {
			if (aE.type !== "click")
				return;
			switch (aE.button) {
				case 0:
					aE.preventDefault();
					if ($._.pBtnActLeft !== 0 && !aE.shiftKey && !$.accel(aE) && !aE.altKey) {
						$._clickActions($._.pBtnActLeft, aE);
					} else if ($._.pBtnActLeftCtrl !== 0 && !aE.shiftKey && $.accel(aE) && !aE.altKey) {
						$._clickActions($._.pBtnActLeftCtrl, aE);
					} else if ($._.pBtnActLeftShift !== 0 && aE.shiftKey && !$.accel(aE) && !aE.altKey) {
						$._clickActions($._.pBtnActLeftShift, aE);
					} else if ($._.pBtnActLeftCtrlShift !== 0 && aE.shiftKey && $.accel(aE) && !aE.altKey) {
						$._clickActions($._.pBtnActLeftCtrlShift, aE);
					}
					break;
				case 1:
					aE.preventDefault();
					if ($._.pBtnActMiddle !== 0 && !aE.shiftKey && !$.accel(aE) && !aE.altKey) {
						$._clickActions($._.pBtnActMiddle, aE);
					}
					break;
				case 2:
					if ($._.pBtnActRightCtrl !== 0 && !aE.shiftKey && $.accel(aE) && !aE.altKey) {
						aE.preventDefault();
						$._clickActions($._.pBtnActRightCtrl, aE);
					} else if ($._.pBtnActRightShift !== 0 && aE.shiftKey && !$.accel(aE) && !aE.altKey) {
						aE.preventDefault();
						$._clickActions($._.pBtnActRightShift, aE);
					} else if ($._.pBtnActRightCtrlShift !== 0 && aE.shiftKey && $.accel(aE) && !aE.altKey) {
						aE.preventDefault();
						$._clickActions($._.pBtnActRightCtrlShift, aE);
					}
					break;
			}
			aE.stopPropagation();
		};

		if ("CustomizableUI" in window) {
			if (!window[$._UID]["CustomizableUI"]) {
				window[$._UID]["CustomizableUI"] = true;
				CustomizableUI.addListener({
					onCustomizeEnd: function(aWindow) {
						var thisBtn = aWindow.document.getElementById(self.id);
						setTimeout(function() {
							aWindow[$._UID]["CustomizableUI"] && thisBtn.MultiSearch.selfDestroy();
						}, Math.floor(Math.random() * (1 + 2000 - 200)) + 200);
					}
				});
			}
		}

		if (!window[$._UID]["DragEndFlag"]) {
			window[$._UID]["DragEndFlag"] = true;
			window.addEventListener("dragend", function() {
				try {
					$.dragAndDropHandler.feedbackHide();
					$.aCP && $.aCP.hidePopup();
					$.sP && $.sP.hidePopup();
				} catch (aError) {}
			}, false);
		}

		let searchBar = $.$("search-container");
		if (searchBar && searchBar.parentNode.id !== "BrowserToolbarPalette") {
			!!$.$($._UID + "-dad-Door-Hanger") && $.notify("", true);
			searchBar.style.visibility = $._.pHideDefaultSearchbar ? "collapse" : "visible";
		} else {
			$.notify("For certain features of " + self.name + " button to work properly, " +
				Services.appinfo.name + "'s default search bar needs to be placed inside any toolbar. " +
				"You can hide the default search bar from the options window of this button. " +
				"This notification will self-destruct in 30 seconds.");
			setTimeout(function() {
				$.notify("", true);
			}, 30000);
		}

		addDestructor(function(aReason) {
			if (aReason === "update" || aReason === "delete") {
				let selfEls = document.getElementsByAttribute("cb_id", $._UID);
				Array.prototype.slice.call(selfEls).forEach(function(aEl) {
					aEl && aEl.parentNode.removeChild(aEl);
				});
				$.styleSheet(window[$._UID]["StyleSheet"], true);
				if (typeof window[$._UID] === "object") {
					window[$._UID]["StyleSheet"] = false;
					window[$._UID]["HideOneOffFlag"] = false;
					window[$._UID]["DDOneOffFlag"] = false;
				}
			}
			if (aReason === "delete" || aReason === "destructor") {
				$._mm.sendAsyncMessage($._cleanUpMsgID);
				$._globalMM.removeDelayedFrameScript($._frameScript);
				$._globalMM.removeMessageListener($._responseMsgID, $._messageResponse);
			}
			if (aReason === "delete") {
				($.SP.getBranch($.Branch)).deleteBranch("");
				Services.prefs.savePrefFile(null);
				delete window[$._UID];
			}
		});
	},

	checkIfOptIsAssigned: function() {
		if ($._.pBtnActLeft !== 9 &&
			$._.pBtnActMiddle !== 9 &&
			$._.pBtnActLeftCtrl !== 9 &&
			$._.pBtnActLeftShift !== 9 &&
			$._.pBtnActLeftCtrlShift !== 9 &&
			$._.pBtnActRightCtrl !== 9 &&
			$._.pBtnActRightShift !== 9 &&
			$._.pBtnActRightCtrlShift !== 9)
			$.sPref("pBtnActRightCtrl", 9);
	},

	_clickActions: function(aAction, aE) {
		$.getSearchVal();
		switch (aAction) {
			case 1: // Open search panel
				let p = $.sP;
				p && p.state === "open" ?
					p.hidePopup() :
					p.openPopup(self, "", 0, 0, false, null, null);
				break;
			case 2: // Do a site search (Selection)
				setTimeout(function() {
					$.openSearch($._selection, "site");
				}, 100);
				break;
			case 3: // Do a web search (Selection)
				setTimeout(function() {
					$.openSearch($._selection, "web");
				}, 100);
				break;
			case 4: // Manage search engines
				$.searchEngines.openEnginesMenu();
				break;
			case 5: // Open Google images
				openUILink($._.pSearchByImageBaseURL + "/imghp?sbi=1", aE, false, true);
				break;
			case 6: // Switch search mode
				$.sPref("pSearchMode", !$._.pSearchMode);
				setTimeout(function() {
					$.alertCallback.opt = {
						footer: null,
						centerScreen: false,
					};
					$.notifyme($._.pSearchMode ? "Site Search mode enabled" : "Web Search mode enabled",
						"",
						$._.pSearchMode ? $.getHelp("%SiteSearch%") : $.getHelp("%WebSearch%"),
						$.alertCallback);
				}, 100);
				break;
			case 7: // Do a site search (Clipboard content)
				setTimeout(function() {
					$.openSearch(gClipboard.read(), "site");
				}, 100);
				break;
			case 8: // Do a web search (Clipboard content)
				setTimeout(function() {
					$.openSearch(gClipboard.read(), "web");
				}, 100);
				break;
			case 9: // Open options window
				$.openOptions();
				break;
		}
	},

	oneOffsCtxPopShowing: function() {
		$.oODetached && $.aCP.openAutocompletePopup($.tB, $.tB);
	},

	oneOffsCtx: function(aE) {
		let el = document.popupNode;
		switch (aE.target.getAttribute("_value")) {
			case "set-default":
				$.SS.defaultEngine = el.engine;
				$.selfDestroy();
				break;
			case "delete-oneoff":
				$.oneOffsHiddenBtn(el.engine.name, el.engine.iconURI.spec);
				if (!window[$._UID]["ListenerForOneOffCtxMenu"]) {
					window[$._UID]["ListenerForOneOffCtxMenu"] = true;
					$.sP.addEventListener("popuphiding", function(aE) {
						$.selfDestroy();
						window[$._UID]["ListenerForOneOffCtxMenu"] = false;
					}, false);
				}
				break;
		}
	},

	initPrefs: function() {
		var branch = $.SP.getDefaultBranch($.Branch),
			defaults = self.MultiSearch.DefPrefs;
		for (var key in defaults) {
			if (defaults.hasOwnProperty(key)) {
				var val = defaults[key];
				switch (typeof val) {
					case "boolean":
						branch.setBoolPref(key, val);
						break;
					case "number":
						branch.setIntPref(key, val);
						break;
					case "string":
						var str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
						str.data = val;
						branch.setComplexValue((key),
							Ci.nsISupportsString, str);
						break;
				}
			}
		}
	},

	_messageResponse: {
		receiveMessage: function(aMsg) {
			if (aMsg.name !== $._responseMsgID)
				return;
			switch (aMsg.data.id) {
				case 1: // Current page selection
					// "Playing with fire" here.
					// $._selection is set asynchronously BUT is called synchronously.
					// Lets see how it goes.
					$._selection = $._.pAutoFillWithSelection ? aMsg.data.selStr : "";
					break;
			}
		},
	},

	populatePositioningTooltip: function(aE, aN) {
		aN.textContent = "";
		let box = aN.appendChild($.$CNS("hbox"));
		let pos = aE.target.triggerNode.getAttribute("value");
		let isWide = /^end_/.test(pos) || /^start_/.test(pos);
		box.appendChild($.$CNS("spacer", {
			flex: 1
		}));
		box.appendChild($.$CNS("label", {
			value: "Preview",
			style: "font-weight:bold;",
		}));
		box.appendChild($.$CNS("spacer", {
			flex: 1
		}));
		aN.appendChild($.$CNS("image", {
			src: $.getHelp("%" + pos + "%"),
			width: isWide ? 180 : 130,
			height: isWide ? 91 : 122,
		}));
	},

	populateDynamicTooltip: function(aE, aN) {
		if (!aN)
			return false;
		aN.textContent = "";

		var grid = aN.appendChild($.$CNS("grid"));
		var cols = grid.appendChild($.$CNS("columns"));
		cols.appendChild($.$CNS("column", {
			flex: 2
		}));
		cols.appendChild($.$CNS("column", {
			flex: 3
		}));
		cols.appendChild($.$CNS("column", {
			flex: 1
		}));
		var rows = grid.appendChild($.$CNS("rows"));
		var AR = function(aRowsNode, aLabel1, aLabel2, aLabel3, aEmpty) {
			var row = $.$CNS("row");
			if (aEmpty) {
				row.appendChild($.$CNS("label", {
					value: " ",
					width: 0,
				}));
			} else {
				row.appendChild($.$CNS("label", {
					value: aLabel1,
					style: "font-weight: bold;",
				}));
				row.appendChild($.$CNS("label", {
					value: aLabel2,
					style: "font-weight: bold;",
				}));
				var desc = $.$CNS("description", {
					width: 150,
				});
				desc.textContent = aLabel3; // Use of "textContent" to achieve word wrap. ¬¬
				row.appendChild(desc);
			}
			aRowsNode.appendChild(row);
		};
		var btn = aE.target.triggerNode;
		AR(rows, btn.name, "→", "ID = " + btn.id.slice(20));
		AR(rows, "Search mode", "→", $._.pSearchMode ?
			"Site Search" :
			"Web Search");
		AR(rows, null, null, null, true);
		let clickAction = function(aAction) {
			switch (aAction) {
				case 1:
					return "Open search panel";
				case 2:
					return "Do a site search (Selection)";
				case 3:
					return "Do a web search (Selection)";
				case 4:
					return "Manage search engines";
				case 5:
					return "Open Google images";
				case 6:
					return "Switch search mode";
				case 7:
					return "Do a site search (Clipboard content)";
				case 8:
					return "Do a web search (Clipboard content)";
				case 9:
					return "Open options window";
			}
		};

		if ($._.pBtnActLeft !== 0)
			AR(rows, "Left click", "→", clickAction($._.pBtnActLeft));
		if ($._.pBtnActLeftCtrl !== 0)
			AR(rows, $.accel() + " + Left click", "→", clickAction($._.pBtnActLeftCtrl));
		if ($._.pBtnActLeftShift !== 0)
			AR(rows, "Shift + Left click", "→", clickAction($._.pBtnActLeftShift));
		if ($._.pBtnActLeftCtrlShift !== 0)
			AR(rows, $.accel() + " + Shift + Left click", "→", clickAction($._.pBtnActLeftCtrlShift));
		if ($._.pBtnActMiddle !== 0)
			AR(rows, "Middle click", "→", clickAction($._.pBtnActMiddle));
		if ($._.pBtnActRightCtrl !== 0)
			AR(rows, $.accel() + " + Right click", "→", clickAction($._.pBtnActRightCtrl));
		if ($._.pBtnActRightShift !== 0)
			AR(rows, "Shift + Right click", "→", clickAction($._.pBtnActRightShift));
		if ($._.pBtnActRightCtrlShift !== 0)
			AR(rows, $.accel() + " + Shift + Right click", "→", clickAction($._.pBtnActRightCtrlShift));
		AR(rows, null, null, null, true);
		let desc = $.$CNS("description", {
			style: "font-weight:bold;",
		});
		// Use of "textContent" to achieve word wrap. ¬¬
		desc.textContent = "Read this button help for very important usage notes.\n" +
			"Right click on button, \"Button Help\" item.";
		aN.appendChild(desc);
		return true;
	},

	createPanels: function() {
		var autoCPanel = $.$CNS("panel", {
			type: "autocomplete",
			id: $._UID + "-CustomPopupAutoComplete",
			cb_id: $._UID,
			noautofocus: true,
			minwidth: $.maxMinWidth,
			maxwidth: $.maxMinWidth,
			hidden: true,
			onclick: "event.stopPropagation();",
			onpopupshowing: "event.stopPropagation();",
			level: "floating",
		});
		if ($._.pOneOffButtons && $.oODetached)
			!!$.aCP || $.popSet.appendChild(autoCPanel);
		else if ($._.pOneOffButtons && !$.oODetached)
			!!$.aCP && $.aCP.parentNode.removeChild($.aCP);
		var mP = self.appendChild($.$CNS("panel", {
			id: $._UID + "-search_panel",
			// If I set "position" to an "arrow" panel, the arrow is not correctly pointing to the anchor. ¬¬
			// Sooo, I apply the "arrowpanel" binding to the panel with CSS and move on.
			// Sacrificing the "position" attribute is not an option at all.
			// type: "arrow",
			arrowposition: $._.pSearchPanelPosition, // I don't think that this in necessary, but it seems to not "hurt".
			position: $._.pSearchPanelPosition,
			// When I click outside, I WANT TO ACTIVATE WHAT'S OUTSIDE!! Not wait for the freaking panel to close!!!
			consumeoutsideclicks: false,
			orient: "vertical",
			flip: ($._.pFlipAttribute ? "both" : "slide"),
			onclick: "event.stopPropagation();",
			onpopupshown: "this.handleOpenState(event, true);event.stopPropagation();",
			onpopuphidden: "this.handleOpenState(event, false);event.stopPropagation();",
			ondragover: "event.preventDefault();event.stopPropagation();",
			oncontextmenu: "event.preventDefault();event.stopPropagation();",
			onpopuphiding: $.that + "searchPanelActions.onPopupHiding(event);event.stopPropagation();",
			onpopupshowing: $.that + "searchPanelActions.prePopupShowing();" +
				$.that + "searchPanelActions.onPopupShowing(event);event.stopPropagation();"
		}));
		setTimeout(function() {
			mP.handleOpenState = function(aE, aState) {
				if (aState)
					self.setAttribute("open", aState);
				else
					self.removeAttribute("open");
				aE.stopPropagation();
			};
			mP.textContent = "";
			var tBAttrs = {
				id: $._UID + "-searchbox",
				cb_id: $._UID,
				width: $.maxMinWidth,
				minwidth: $.maxMinWidth,
				maxwidth: $.maxMinWidth,
				placeholder: $.SS.currentEngine.name,
				minresultsforpopup: 0,
				oncontextmenu: "event.stopPropagation();",
				onkeyup: $.that + "searchPanelActions.onKeyUp(event);event.stopPropagation();",
				onkeydown: $._.pTabNavForOneOffButtons ?
					$.that + "searchPanelActions.onKeyDown(event);event.stopPropagation();" : "",
				onkeypress: $.that + "searchPanelActions.onKeyPress(event);event.stopPropagation();",
				onpaste: $.that + "searchPanelActions.showClearSearchboxBtn();",
				oncommand: "event.preventDefault();event.stopPropagation();"
			};
			if ($._.pSearchHistory || $.SP.getBoolPref("browser.search.suggest.enabled")) {
				tBAttrs["type"] = "autocomplete";
				tBAttrs["autocompletepopup"] = (($._.pOneOffButtons && $.oODetached) ?
					$._UID + "-CustomPopupAutoComplete" :
					"PopupAutoComplete");
				tBAttrs["autocompletesearch"] = "search-autocomplete";
				tBAttrs["autocompletesearchparam"] = ($._.pSearchHistory ? "searchbar-history" : "");
				tBAttrs["timeout"] = $._.pAutoCompleteTimeout;
				tBAttrs["completeselectedindex"] = $._.pCompleteSelectedIndex;
				tBAttrs["tabscrolling"] = $._.pTabScrolling;
				tBAttrs["maxrows"] = $._.pMaxRowsForSuggestions;
				tBAttrs["showcommentcolumn"] = $._.pShowCommentColumn;
			}
			let tB = mP.appendChild($.$CNS("textbox", tBAttrs));
			tB.appendChild($.$CNS("image", {
				id: $._UID + "-current-engine-image",
				src: $.SS.currentEngine.iconURI.spec,
				oncontextmenu: "event.preventDefault();event.stopPropagation();",
			}));
			let clearBtn = tB.appendChild($.$CNS("image", {
				id: $._UID + "-clear-searchbox",
				src: "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAAiklEQ" +
					"VQYlW3PRwpCQRAFwFqJ4QrmsBFF738HMSPyQV2LeoXvwh4Zw1s2NT2veWWOtt90MZWhBzaoZqiOP" +
					"W6YQAc7lFijh2GgEku08tcJFzhnqPLdZ4BTgBKH6PiTES4ZPEaNjzRiQ9pUZJ3fB/YytIjv+tjGb" +
					"JWOmeEeKC9eC3zFOA2naP7p3U7oCVcYJ1GZKOnkAAAAAElFTkSuQmCC",
				tooltiptext: "Clear search box",
				oncontextmenu: "event.preventDefault();event.stopPropagation();",
				collapsed: true,
			}));
			clearBtn.onclick = function(aE) {
				aE.preventDefault();
				if (aE.button !== 0 && aE.originalTarget !== this)
					return;
				$.tB.value = "";
				$.tB.focus();
				$.searchPanelActions.showClearSearchboxBtn();
				$.$("PopupAutoComplete").hidePopup();
				$.aCP && $.aCP.hidePopup();
				aE.stopPropagation();
			};
			tB.appendChild($.$CNS("image", {
				id: $._UID + "-search-button",
				src: $.customImage,
				tooltiptext: "Search with: " + $.SS.currentEngine.name,
				oncontextmenu: "event.preventDefault();event.stopPropagation();",
				onclick: $.that + "searchPanelActions.onKeyPress(event);event.stopPropagation();",
			}));
			if ($._.pOneOffButtons && !$.oODetached) {
				mP.appendChild($.$CNS("vbox", {
					id: $._UID + "-one-off-items",
					context: $._UID + "_one_off_context_popup",
					minwidth: $.maxMinWidth,
					maxwidth: $.maxMinWidth,
				}));
			}
			$._.pOneOffButtons && $.searchPanelActions.attachOneOffBtns();
		}, 100);
	},

	searchPanelActions: {
		showClearSearchboxBtn: function() {
			setTimeout(function() {
				$.clearBtn && ($.clearBtn.collapsed = $.tB.value === "");
			}, 10);
		},
		prePopupShowing: function() {
			$._.pAutoFillWithSelection && $.getSearchVal();
			if (!$.oODetached || !window[$._UID]["DDOneOffFlag"])
				return true;
			if (!!$.aCP && $.aCP.popupOpen)
				$.aCP.hidePopup();
			window[$._UID]["DDOneOffFlag"] = false;
		},
		onPopupShowing: function(aE) {
			if ($.sP.state === "open" || window[$._UID]["DDOneOffFlag"] || $._initializingSearchPanel)
				return; // Workaround for SeaMonkey's panels. Holly $%&?$%&¿!!!
			setTimeout(function() {
				$.tB.value = $._selection;
				$.tB.focus();
				$.tB.select();
				if ($._.pAutoTriggerSuggestions && $.tB.value !== "") {
					var nsIDOMWindowUtils = window.QueryInterface(Ci.nsIInterfaceRequestor)
						.getInterface(Ci.nsIDOMWindowUtils);
					nsIDOMWindowUtils.sendKeyEvent("keypress", 35, 0, null, null);
					nsIDOMWindowUtils.sendKeyEvent("keyup", 35, 0, null, null);
					setTimeout(function() {
						nsIDOMWindowUtils.sendKeyEvent("keypress", 0, 32, null, null);
						nsIDOMWindowUtils.sendKeyEvent("keyup", 0, 32, null, null);
					}, 100);
				}
				$.searchPanelActions.showClearSearchboxBtn();
				setTimeout(function() {
					($.tB && $.tB.value !== "") && $.searchPanelActions.keepItUp($.tB, $.tB);
				}, 50);
			}, 100);
		},
		onPopupHiding: function(aE) {
			if (!!$.sP && $.sP.state === "open")
				return; // Again, for SeaMonkey's panels. Holly $%&?$%&¿!!!
			window[$._UID]["DDOneOffFlag"] = false;
			$.tB.value = "";
			setTimeout(function() {
				if (!!$.aCP && $.aCP.popupOpen) // "Needed" in case AutoCompletePanel stays open after Search Panel is closed.
					$.aCP.hidePopup();
			}, 100);
			$.searchPanelActions.showClearSearchboxBtn();
			$._selection = "";
			$._.pRememberOffButtons || $.getSelectedOneOff(true); // The true arg. clears selected buttons.
		},
		onKeyDown: function(aE) {
			if (aE.keyCode === KeyEvent.DOM_VK_TAB && aE.type !== "click") {
				if ($._.pAttachOneOffBoxToMainPanel) {
					let aCP1 = $.aCP,
						aCP2 = document.getAnonymousElementByAttribute($.tB, "anonid", "popup") ||
						$.$("PopupAutoComplete");
					// Had to use closePopup because that aberration of SeaMonkey!!! ¬¬
					if (aCP1) {
						try {
							aCP1.closePopup();
						} catch (aErr) {
							aCP1.hidePopup();
						}
					}
					if (aCP2) {
						try {
							aCP2.closePopup();
						} catch (aErr) {
							aCP2.hidePopup();
						}
					}
				}
				let list = Array.prototype.slice.call($.$($._UID + "-one-off-items").getElementsByTagName("button")).filter(e =>
					!e.classList.contains("dummy"));

				if (!list)
					return;

				if ($.accel(aE))
					return;

				let selectedButton = $.getSelectedOneOff();
				if (selectedButton) {
					// TAB cycles though the list of one-off buttons.
					selectedButton.removeAttribute("selected");
					let idx = list.indexOf(selectedButton);
					let btn;
					if (aE.shiftKey) {
						selectedButton = list[(idx === 0) ? list.length - 1 : idx - 1];
					} else {
						selectedButton = list[(idx === list.length - 1) ? 0 : idx + 1];
					}

				} else {
					selectedButton = list[0];
				}
				selectedButton.setAttribute("selected", "true");

				aE.preventDefault();
				aE.stopPropagation();
			}
			if (aE.keyCode === KeyEvent.DOM_VK_ESCAPE) {
				$.getSelectedOneOff(true); // The true arg. clears selected buttons.
			}
		},
		onKeyPress: function(aE) {
			if (aE.keyCode === KeyEvent.DOM_VK_RETURN || aE.type === "click") { // Enter key or click
				$._oneOffBtnEngine = $.getSelectedOneOff() ? $.getSelectedOneOff().engine : null;
				if (!aE.shiftKey && !$.accel(aE) && !aE.altKey) { // No modifiers
					$.openSearch($.tB.value, $._.pSearchMode ? "site" : "web", $._oneOffBtnEngine ? $._oneOffBtnEngine : "TextBox");
					$.sP.hidePopup();
					$.getSelectedOneOff(true);
				} else if (!aE.shiftKey && $.accel(aE) && !aE.altKey) { // Ctrl or Cmd
					$.openSearch($.tB.value, $._.pSearchMode ? "web" : "site", $._oneOffBtnEngine ? $._oneOffBtnEngine : "TextBox");
					$.sP.hidePopup();
					$.getSelectedOneOff(true);
				}
			}
			aE.stopPropagation();
		},
		onKeyUp: function(aE) {
			// No Enter key nor Del key
			if ($._.pOneOffButtons && $.oODetached && aE.keyCode !== 13 && aE.keyCode !== 46) {
				setTimeout(function() {
					if ($.tB.value !== "")
						$.searchPanelActions.keepItUp($.tB, $.tB);
				}, 50);
			} else if (aE.keyCode === 46 && !aE.shiftKey && $.accel(aE) && !aE.altKey) {
				$.tB.value = "";
				$.tB.focus();
				(!!$.aCP && $.aCP.popupOpen) && $.aCP.hidePopup();
				$.$("PopupAutoComplete").hidePopup();
				$.getSelectedOneOff(true); // The true arg. clears selected buttons.
			}
			$.searchPanelActions.showClearSearchboxBtn();
			aE.stopPropagation();
		},
		keepItUp: function(aInput, aAnchor) {
			var autoCP = $.aCP;
			if (autoCP && !autoCP.popupOpen) {
				autoCP.width = $.maxMinWidth;
				autoCP.minWidth = $.maxMinWidth;
				autoCP.maxWidth = $.maxMinWidth;
				autoCP.height = 0; // Workaround to hide empty autocomplete results.
				autoCP.hidden = false;
				autoCP.addEventListener("popupshown", function() {
					setTimeout(function() {
						autoCP.removeAttribute("height");
					}, 100);
				}, false);
				// Directly using "openPopup" is "glitchy".
				try {
					autoCP.openAutocompletePopup(aInput, aAnchor);
				} catch (aErr) {
					$.reportError(aErr);
					// autoCP.openPopup(aAnchor, "after_start", 0, 0, false, false);
				}
			} else
				return;
		},
		attachOneOffBtns: function() {
			let oneOffsBox = $.$CNS("vbox", {
				id: $._UID + "-one-off-items",
				context: $._UID + "_one_off_context_popup",
				minwidth: $.maxMinWidth,
				maxwidth: $.maxMinWidth,
			});
			let aCP = $.aCP;
			if ($.oODetached && aCP) {
				aCP.textContent = "";
				setTimeout(function() {
					$.$($._UID + "-one-off-items") || aCP.appendChild(oneOffsBox);
				}, 100);
			}

			setTimeout(function() { // Finally, build the list of one-off buttons.
				var list = $.$($._UID + "-one-off-items");
				if (!list)
					return true;
				list.textContent = "";
				var hiddenList;
				try {
					let pref = $.gCV("browser.search.hiddenOneOffs");
					hiddenList = pref ? pref.split(",") : [];
				} catch (e) {
					hiddenList = [];
				}
				var engs = $.SS.getVisibleEngines().filter(e =>
					e.name !== $.SS.currentEngine.name &&
					hiddenList.indexOf(e.name) === -1);
				if (!engs.length)
					return;
				var rowCount = Math.ceil(engs.length / $._.pEnginesPerRow);
				list.setAttribute("height", rowCount * 33 + "px");
				var dummyItems = $._.pEnginesPerRow - (engs.length % $._.pEnginesPerRow || $._.pEnginesPerRow);
				let btnList = [];
				let onMouseDown = function(aE) { // Fix for linux.
					aE.preventDefault();
					aE.stopPropagation();
				};
				let onCLick = function(aE) {
					aE.preventDefault();
					let oO = aE.target;
					if (oO.nodeName !== "button")
						return true;
					$.getSelectedOneOff(true); // The true arg. clears selected buttons.
					if (aE.button === 2 || aE.originalTarget.localName !== "button")
						return;
					if (!aE.shiftKey && !$.accel(aE) && !aE.altKey && aE.button === 0) {
						$.openSearch($.tB.value, $._.pSearchMode ? "site" : "web", oO.engine);
						$.sP.hidePopup();
					} else if (!aE.shiftKey && $.accel(aE) && !aE.altKey && aE.button === 0) {
						$.openSearch($.tB.value, $._.pSearchMode ? "web" : "site", oO.engine);
						$.sP.hidePopup();
					} else if (!aE.shiftKey && !$.accel(aE) && !aE.altKey && aE.button === 1 ||
						aE.shiftKey && $.accel(aE) && !aE.altKey && aE.button === 0) {
						$.oneOffsHiddenBtn(oO.engine.name, oO.getAttribute("image"));
						if (!window[$._UID]["HideOneOffFlag"]) {
							window[$._UID]["HideOneOffFlag"] = true;
							$.sP.addEventListener("popuphiding", function() {
								$.selfDestroy();
							}, false);
						}
					}
					aE.stopPropagation();
				};
				let onDragendLeave = function(aE) {
					aE.preventDefault();
					let oO = aE.target;
					if (oO.nodeName !== "button")
						return true;
					($._.pDaDFeedbackType !== 0) && $.dragAndDropHandler.feedbackHide();
					oO.removeAttribute("selected");
					aE.stopPropagation();
				};
				let onDrop = function(aE) {
					// aE.preventDefault();
					let oO = aE.target;
					if (oO.nodeName !== "button")
						return true;
					oO.removeAttribute("selected");
					var data = aE.dataTransfer.getData("text/plain");
					if (!data)
						data = aE.dataTransfer.getData("text/x-moz-text-internal");
					if (data) {
						var searchValue = $.shorten(data, 900);
						setTimeout(function() {
							$.searchModeSwitch(searchValue, aE, oO.engine);
						}, 100);
					}
					var panel = $.oODetached ? $.aCP : $.sP;
					if (panel.state === "open")
						panel.hidePopup();
					$.dragAndDropHandler.feedbackHide();
					aE.stopPropagation();
				};
				let onDragover = function(aE) {
					aE.preventDefault();
					let oO = aE.target;
					if (oO.nodeName !== "button")
						return true;
					if ($._.pDaDFeedbackType !== 0) {
						switch ($._.pSearchMode) {
							case true: // Search mode is "Site"
								if (!aE.shiftKey && !$.accel(aE) && !aE.altKey) // No modifiers
									$.dragAndDropHandler.feedbackShow(aE, "Search current site with: " +
									oO.engine.name);
								if (!aE.shiftKey && $.accel(aE) && !aE.altKey) // Control or Command
									$.dragAndDropHandler.feedbackShow(aE, "Search the web with: " +
									oO.engine.name);
								break;
							case false: // Search mode is "Web"
								if (!aE.shiftKey && !$.accel(aE) && !aE.altKey) // No modifiers
									$.dragAndDropHandler.feedbackShow(aE, "Search the web with: " +
									oO.engine.name);
								if (!aE.shiftKey && $.accel(aE) && !aE.altKey) // Control or Command
									$.dragAndDropHandler.feedbackShow(aE, "Search current site with: " +
									oO.engine.name);
								break;
						}
					}
					oO.setAttribute("selected", true);
					aE.stopPropagation();
				};
				try {
					var btn;
					var i = 0,
						iLen = engs.length;
					for (; i < iLen; ++i) {
						var engine = engs[i],
							uri = $.defaultImage;
						if (engine.iconURI)
							uri = $.CB_$ImageURLForResolution(window, engine.iconURI.spec);
						btn = $.$CNS("button", {
							label: engine.name,
							id: "multi-search-one-off-item-" + engine.name.replace(/ /g, '-'),
							image: uri,
							class: "multi-search-one-off-item",
							tooltiptext: "Search with: " + engine.name,
							width: 38
						});
						btn.engine = engine;
						// btn.addEventListener("mousedown", onMouseDown, false);
						// btn.addEventListener("click", onCLick, false);
						// if ($._.pDragAndDropForOneOffButtons) {
						// 	btn.addEventListener("dragend", onDragendLeave, false);
						// 	btn.addEventListener("dragleave", onDragendLeave, false);
						// 	btn.addEventListener("drop", onDrop, false);
						// 	btn.addEventListener("dragover", onDragover, false);
						// }
						if ((i + 1) % $._.pEnginesPerRow === 0)
							btn.classList.add("last-of-row");
						if (i >= engs.length + dummyItems - $._.pEnginesPerRow)
							btn.classList.add("last-row");
						btnList.push(btn);
					}
					while (dummyItems) {
						btn = $.$CNS("button", {
							class: "multi-search-one-off-item dummy last-row",
							width: 38
						});
						if (!--dummyItems)
							btn.classList.add("last-of-row");
						btnList.push(btn);
					}
				} finally { // Using grid fixes absolutely all graphical problems with SeaMonkey.
					let breakPoint = 0;
					let grid = list.appendChild($.$CNS("grid"));

					let cols = grid.appendChild($.$CNS("columns"));
					while (breakPoint !== $._.pEnginesPerRow) {
						breakPoint++;
						cols.appendChild($.$CNS("column", {
							flex: 1
						}));
					}
					let rows = grid.appendChild($.$CNS("rows"));
					let createRow = function(aArr) {
						let row = rows.appendChild($.$CNS("row"));
						let a = 0,
							aLen = aArr.length;
						for (; a < aLen; a++) {
							row.appendChild(aArr[a]);
						}
					};
					let btnsRow = [];
					let b = 0,
						bLen = btnList.length;
					for (; b < bLen; b++) {
						breakPoint++;
						btnsRow.push(btnList[b]);
						if ((btnsRow.length === $._.pEnginesPerRow) || (b + 1) === btnList.length) {
							breakPoint = 0;
							createRow(btnsRow);
							btnsRow = [];
							continue;
						}
					}
				}
				list.addEventListener("mousedown", onMouseDown, false);
				list.addEventListener("click", onCLick, false);
				if ($._.pDragAndDropForOneOffButtons) {
					list.addEventListener("dragend", onDragendLeave, false);
					list.addEventListener("dragleave", onDragendLeave, false);
					list.addEventListener("drop", onDrop, false);
					list.addEventListener("dragover", onDragover, false);
				}
			}, 200);
		}
	},

	searchEngines: {
		engMIOnClick: function(aE) {
			let mi = aE.target;
			if (aE.button === 2 || !mi.hasOwnProperty("engine"))
				return false;
			window[$._UID]["ListenerForManageEnginesMenu"] = true;
			if (!aE.shiftKey && !$.accel(aE) && !aE.altKey && aE.button === 0)
				$.SS.defaultEngine = mi.engine;
			else if (!aE.shiftKey && !$.accel(aE) && !aE.altKey && aE.button === 1 ||
				!aE.shiftKey && $.accel(aE) && !aE.altKey && aE.button === 0) {
				$.oneOffsHiddenBtn(mi.getAttribute("label"), mi.getAttribute("src"));
			}
			aE.stopPropagation();
		},
		openEnginesMenu: function() {
			let ePop = $.popSet.appendChild($.$CNS("menupopup", {
				id: $._UID + "-manage-engines-mpop",
				cb_id: $._UID,
				orient: "vertical",
				onpopuphiding: "event.stopPropagation();",
				onpopupshowing: "event.stopPropagation();",
				oncommand: "event.preventDefault();event.stopPropagation();",
				onclick: "event.preventDefault();" + $.that +
					"searchEngines.engMIOnClick(event);event.stopPropagation();"
			}));
			ePop.addEventListener("popuphiding", function remPop() {
				if (window[$._UID]["ListenerForManageEnginesMenu"]) {
					$.selfDestroy();
					window[$._UID]["ListenerForManageEnginesMenu"] = false;
				}
				this.parentNode.removeChild(this);
			}, false);
			ePop.appendChild($.$CNS("menuseparator"));
			ePop.appendChild($.$CNS("menuitem", {
				label: "Manage Search Engines…",
				onclick: "event.stopPropagation();",
				oncommand: $.that + "searchEngines.openSearchEngineManager();event.stopPropagation();"
			}));
			var engs = $.SS.getVisibleEngines();
			var a = engs.length - 1;
			for (; a >= 0; --a) {
				let name = engs[a].name,
					uri = $.defaultImage;
				if (engs[a].iconURI)
					uri = $.CB_$ImageURLForResolution(window, engs[a].iconURI.spec);
				let engMI = $.$CNS("menuitem", {
					label: name,
					id: $._UID + name,
					tooltiptext: "Left click = Set as default engine.\n" +
						"Middle of Ctrl + Left click = Hide/Show engine \"One-Off\" button.",
					style: ((name === $.SS.currentEngine.name) ? "font-weight:bold!important;" : ""),
					src: uri
				});
				$.oneOffsHiddenBtn(name, null, true) && engMI.classList.add("one-off-is-hidden");
				engMI.engine = engs[a];
				ePop.insertBefore(engMI, ePop.firstChild);
			}
			var addengines = getBrowser().mCurrentBrowser.engines;
			if (addengines && addengines.length > 0) {
				var insLoc = ePop.firstChild;
				while (insLoc.nextSibling && insLoc.localName !== "menuseparator") {
					insLoc = insLoc.nextSibling;
				}
				if (insLoc.localName !== "menuseparator")
					insLoc = ePop.firstChild;
				var sep = $.$CNS("menuseparator", {
					class: "addengine-separator"
				});
				ePop.insertBefore(sep, insLoc);
				var i = 0,
					iLen = addengines.length;
				let newEngMIOnClick = function(aE) {
					$.SS.addEngine(aE.target.getAttribute("uri"), Ci.nsISearchEngine.DATA_XML,
						aE.target.getAttribute("src"), false, null);
					$.searchEngines.hideNewEngine(aE.target.engineInfo);
					window[$._UID]["ListenerForManageEnginesMenu"] = true;
					aE.stopPropagation();
				};
				for (; i < iLen; i++) {
					var engineInfo = addengines[i];
					var labelStr = engineInfo.title;
					var newEngMI = $.$CNS("menuitem", {
						class: "addengine-item",
						label: "Add \"" + labelStr + "\"",
						tooltiptext: engineInfo.uri,
						uri: engineInfo.uri,
						src: (engineInfo.icon ? engineInfo.icon : ""),
						title: engineInfo.title,
					});
					newEngMI.engineInfo = engineInfo;
					newEngMI.addEventListener("command", newEngMIOnClick, false);
					ePop.insertBefore(newEngMI, insLoc);
				}
			}
			ePop.openPopup(self, "after_start", 0, 0, false, null, null);
		},
		openSearchEngineManager: function() {
			var window = Services.wm.getMostRecentWindow("Browser:SearchManager");
			if (window)
				window.focus();
			if (/about:preferences/.test(openPreferences.toString()))
				openPreferences("paneSearch");
			else {
				let app = $.appID("SM") ? "communicator" : "browser";
				openDialog("chrome://" + app + "/content/search/engineManager.xul",
					"_blank", "chrome,dialog,modal,centerscreen,resizable");
			}
		},
		hideNewEngine: function(aEngine) {
			var allbrowsers = getBrowser().mPanelContainer.childNodes;
			var tab = 0,
				tabLen = allbrowsers.length;
			for (; tab < tabLen; tab++) {
				var browser = getBrowser().getBrowserAtIndex(tab);
				if (browser.engines) {
					// XXX This will need to be changed when engines are identified by
					// URL rather than title; see bug 335102.
					var removeTitle = aEngine.title;
					var i = 0,
						iLen = browser.engines.length;
					for (; i < iLen; i++) {
						if (browser.engines[i].title === removeTitle) {
							if (!browser.hiddenEngines)
								browser.hiddenEngines = [];
							browser.hiddenEngines.push(browser.engines[i]);
							browser.engines.splice(i, 1);
							break;
						}
					}
				}
			}
		}
	},

	searchImageSwitch: function(aImgSource, aImage) {
		var src,
			convertToB64;
		switch (aImgSource) {
			case "content":
				convertToB64 = true;
				src = aImage;
				break;
			case "localfile":
				convertToB64 = true;
				src = "file:///" + aImage;
				break;
			case "contextmenu":
				convertToB64 = false;
				src = gContextMenu.imageURL || gContextMenu.mediaURL;
				break;
			default:
				return true;
		}
		if (!src)
			return;
		setTimeout(function() {
			if (convertToB64) {
				$.img_to_b64_cback(src, function(aImg) {
					if (!aImg)
						return true;
					$.searchImage(aImg);
				});
			} else
				$.searchImage(src);
		}, 100);
	},

	searchImage: function(aImageURL) {
		if (aImageURL.indexOf("data:") === 0) {
			if (aImageURL.length >= $._.pSearchByImageMaxSize) {
				$.alertCallback.opt = {
					footer: null,
					centerScreen: true,
				};
				$.notifyme(self.name,
					"Image is too big to perform a search by image!!!",
					$.getHelp("%WarningImg%"),
					$.alertCallback);
				return true;
			}
			var base64Offset = aImageURL.indexOf(",");
			if (base64Offset !== -1) {
				var inlineImage = aImageURL.substring(base64Offset + 1)
					.replace(/\+/g, "-")
					.replace(/\//g, "_")
					.replace(/\./g, "=");
				$._mm.sendAsyncMessage($._searchImageMsgID, {
					inlineImage: inlineImage,
					TabOpeningForImg: $._.pTabOpeningForImg,
					searchByImageBaseURL: $._.pSearchByImageBaseURL,
				});
			}
		} else {
			var query = $._.pSearchByImageBaseURL + "/searchbyimage?" +
				"image_url=" + encodeURIComponent(aImageURL);
			$.openPage(query, $._.pTabOpeningForImg);
		}
	},

	checkContentType: function(aFile, aLocalFile) {
		var file = (aLocalFile ? aFile : (Services.io.newFileURI(aFile).spec)),
			cType;
		try {
			cType = Cc["@mozilla.org/mime;1"].getService(Ci.nsIMIMEService)
				.getTypeFromURI(Services.io.newURI(file, null, null));
		} catch (aError) {
			// $.reportError(aError);
			return false;
		}
		if (/^image\//.test(cType))
			return true;
		else
			return false;
	},

	img_to_b64_cback: function(aURL, aCallback) {
		if (!aURL)
			aCallback(false);
		else {
			let xhr = new XMLHttpRequest(),
				nsIMIMEService = Cc["@mozilla.org/mime;1"].getService(Ci.nsIMIMEService);
			try {
				xhr.open("GET", aURL, true);
			} catch (aErr) {
				aCallback(false);
				return true;
			}
			xhr.responseType = "arraybuffer";
			xhr.onload = function() {
				let mediaType,
					dataURI,
					binary,
					bytes;
				bytes = new Uint8Array(xhr.response);
				binary = [].map.call(bytes, function(aByte) {
					return String.fromCharCode(aByte);
				}).join("");
				mediaType = xhr.getResponseHeader("Content-Type");
				let base64 = btoa(binary);
				if (mediaType.indexOf("image/") !== 0) {
					try {
						mediaType = nsIMIMEService.getTypeFromURI(Services.io.newURI(aURL, null, null));
					} catch (aError) {
						mediaType = "image/png";
					}
				}
				dataURI = "data:" + mediaType + ";base64," + base64;
				aCallback(dataURI);
			};
			xhr.onerror = function() {
				aCallback(false);
			};
			xhr.send(null);
		}
	},

	getButtonImage: function() {
		if ($.SS.isInitialized && $._.pUseEngineImageOnButton)
			return $.SS.currentEngine.iconURI.spec;
		return $.customImage;
	},

	openOptions: function() {
		var em = Services.ww.getWindowEnumerator();
		var winName = $._UID + "_settings_window";
		var index = 1;
		while (em.hasMoreElements()) {
			let win = em.getNext();
			if (win.name === winName) {
				win.focus();
				return;
			}
			index++;
		}
		let storage = {
			button: self,
		};
		var x = $.getHelp("%OptionsWindow%").split("%U_I_D%").join($._UID);
		x = x.split("%A_C_C_E_L%").join($.accel());
		x = x.split("%APP_NAME%").join(Services.appinfo.name);
		x = x.split("%B_R_A_N_C_H%");
		var url = "data:application/vnd.mozilla.xul+xml;text/plain," + encodeURIComponent(x.join($.Branch));
		let optWin = openDialog(url, winName, "chrome, dependant, dialog, centerscreen, close, alwaysRaised");
		optWin._storage = storage;
	},

	getOverrideURLs: function() { // Boolean return of URLs/Hostsnames to override
		var URLList = $._.pOverrideHosts.split(",");
		var curURL;
		if (URLList) {
			for (var url in URLList) {
				if (URLList[url].length > 0) {
					curURL = (gBrowser.currentURI.spec === URLList[url]);
					if (curURL)
						return true;
					else
						continue;
				}
			}
		}
		var hostList = $._.pOverrideHosts.split(",");
		if (hostList) {
			for (var name in hostList) {
				if (hostList[name].length > 0) {
					curURL = (gBrowser.currentURI.host === hostList[name]);
					if (curURL)
						return true;
					else
						continue;
				}
			}
		}
		return false;
	},

	oneOffsHiddenBtn: function(aEngName, aImg, aCheck) {
		var hiddenList;
		try {
			var pref = $.gCV("browser.search.hiddenOneOffs");
			hiddenList = pref ? pref.split(",") : [];
		} catch (e) {
			hiddenList = [];
		}
		var isHidden = false,
			index;
		var i = hiddenList.length - 1;
		for (; i >= 0; i--) {
			if (hiddenList[i] === aEngName) {
				isHidden = true;
				index = i;
			}
		}
		if (aCheck) {
			return isHidden;
		} else {
			isHidden
				? hiddenList.splice(index, 1) : hiddenList.push(aEngName);
			$.sCV("browser.search.hiddenOneOffs", hiddenList.join(","));
			$.alertCallback.opt = {
				footer: null,
				centerScreen: false,
			};
			$.notifyme(aEngName + " \"One-Off\" button",
				"is been " + (isHidden ? "SHOWN." : "HIDDEN."),
				aImg,
				$.alertCallback);
		}
		return false;
	},

	openSearch: function(aSTerms, aType, aEngine) {
		var searchTerms = aSTerms.trim();
		if (!searchTerms && $._.pUseClipboard)
			searchTerms = gClipboard.read();
		var engine = false;
		var submission;
		if ($._.pUseSearchAlias && aEngine === "TextBox") {
			var offset = aSTerms.indexOf(" ");
			if (offset > 0) {
				var alias = aSTerms.substr(0, offset);
				var aliasExistence = function() {
					return ($.SS.getEngineByAlias(alias));
				};
				searchTerms = (aliasExistence() === null) ? searchTerms : aSTerms.substr(offset + 1);
				engine = $.SS.getEngineByAlias(alias);
			}
			if (!engine)
				engine = $.SS.defaultEngine;
		} else if (typeof aEngine === "object" && aEngine !== "undefined") {
			engine = aEngine;
		} else {
			engine = $.SS.defaultEngine;
		}
		if ($._.pSearchHistory && searchTerms !== "" &&
			!PrivateBrowsingUtils.isWindowPrivate(window)) {
			var searchParam = searchTerms;
			if (searchParam.match(/^site:/))
				searchParam = searchTerms.substr(0, searchTerms.indexOf(" "));
			(Cu.import("resource://gre/modules/FormHistory.jsm", {})).FormHistory.update({
				op: "bump",
				fieldname: $.tB.getAttribute("autocompletesearchparam"),
				value: searchParam
			}, {
				handleError: function(aErr) {
					$.reportError("Saving search term to form history failed: " + aErr.message);
				}
			});
		}
		if (aType === "site") {
			searchTerms = "site:" + $.getDomainOfAddress(gBrowser.selectedBrowser.currentURI) +
				" " + ($._.pUseQuotationMarks ? "\"" + searchTerms + "\"" : searchTerms);
			if (searchTerms.slice(0, 9) === "site:null")
				return;
		} else if (aType === "web")
			searchTerms = ($._.pUseQuotationMarks ? "\"" + searchTerms + "\"" : searchTerms);
		if (engine) {
			if (!searchTerms || searchTerms === "\"\"")
				return;
			submission = engine.getSubmission(searchTerms);
		} else {
			$.alertme("No search engine has been found.");
			return;
		}
		$.openPage(submission.uri.spec, $._.pTabOpening);
	},

	openPage: function(aURL, aWhere) {
		switch (aWhere) {
			case 0: // Current
				gBrowser.loadURI(aURL);
				break;
			case 1: // Next to current
				let pos = gBrowser.selectedTab._tPos || gBrowser.tabContainer.selectedIndex; // Firefox || SeaMonkey
				gBrowser.moveTabTo(gBrowser.selectedTab = gBrowser.addTab(aURL), pos + 1);
				break;
			case 2: // New tab in background
				gBrowser.addTab(aURL);
				break;
			case 3: // New tab in foreground
				gBrowser.selectedTab = gBrowser.addTab(aURL);
				break;
		}
	},

	getSearchVal: function() {
		$._mm.sendAsyncMessage($._getSelectionMsgID);
	},

	searchModeSwitch: function(aSerchVal, aE, aEngine) {
		switch ($._.pSearchMode) {
			case true:
				(!$.accel(aE) && !aE.shiftKey && !aE.altKey) && $.openSearch(aSerchVal, "site", aEngine);
				($.accel(aE) && !aE.shiftKey && !aE.altKey) && $.openSearch(aSerchVal, "web", aEngine);
				break;
			case false:
				(!$.accel(aE) && !aE.shiftKey && !aE.altKey) && $.openSearch(aSerchVal, "web", aEngine);
				($.accel(aE) && !aE.shiftKey && !aE.altKey) && $.openSearch(aSerchVal, "site", aEngine);
				break;
		}
	},

	hotkeyPressed: function(aAnchor, aE) {
		setTimeout(function() {
			$._clickActions($._.pHotkeyAction, aE);
		}, 10);
	},

	getSelectedOneOff: function(aForceDeselect) {
		let list = Array.prototype.slice.call($.$($._UID + "-one-off-items").getElementsByTagName("button")).filter(e =>
			!e.classList.contains("dummy"));
		if (!list)
			return null;

		for (let button = list[0]; button; button = list[list.indexOf(button) + 1]) {
			if (button.hasAttribute("selected")) {
				if (aForceDeselect) {
					button.removeAttribute("selected");
					continue;
				}
				return button;
			}
		}
		return null;
	},

	handlePrefObserver: function(aRem) {
		var prefs = $.DefPrefs;
		for (let key in prefs) {
			let pref = $.Branch + key;
			if (prefs.hasOwnProperty(key)) {
				if (aRem)
					Services.prefs.removeObserver(pref, $.prefObserver, false);
				else
					Services.prefs.addObserver(pref, $.prefObserver, false);
			}
		}
	},

	prefObserver: {
		observe: function(aSubject, aTopic, aPStr) {
			if (aTopic === "nsPref:changed") {
				let prefName = aPStr.slice($.Branch.length);
				try {
					window[$._UID]["Preferences"][prefName] = $.gPref(prefName);
				} finally {
					$._ = window[$._UID]["Preferences"];
				}
			}
		}
	},

	// Search by image context menu handler
	sbiContext: {
		init: function() {
			let insPoint = $.$("context-copyimage");
			let sbiMI = $.$CNS("menuitem", {
				id: $._UID + "-search_image_menuitem",
				cb_id: $._UID,
				label: "Search this image on Google Images",
				accesskey: "e",
				image: $.getHelp("%CameraImage%"),
				oncommand: $.that + "searchImageSwitch('contextmenu');event.stopPropagation()",
			});
			insPoint.parentNode.insertBefore(sbiMI, insPoint.nextSibling);
			let contextMenuListener = function() {
				var context = $.$($._UID + "-search_image_menuitem");
				if (gContextMenu && context)
					context.hidden = (!gContextMenu.onImage);
			};
			self.addEventListener("load", function _() {
				self.removeEventListener("load", _, false);
				var ctxM = $.$("contentAreaContextMenu");
				if (ctxM) {
					ctxM.addEventListener("popupshowing", contextMenuListener, false);
				}
				addDestructor(function(aReason) {
					if (aReason === "update" || aReason === "delete") {
						ctxM.removeEventListener("popupshowing", contextMenuListener, false);
					}
				});
			}, false);
		},

		remove: function() {
			var mItem = $.$($._UID + "-search_image_menuitem");
			if (mItem && mItem.parentNode) {
				mItem.parentNode.removeChild(mItem);
			}
		}
	},

	// Search selection context menu handler
	pageContext: {
		init: function() {
			let insPoint = $.$("context-searchselect");
			let item = $.$CNS("menu", {
				id: $._UID + "-context_search_selection",
				cb_id: $._UID,
				image: $.customImage,
				label: "Multi Search",
			});
			item.appendChild($.$CNS("menupopup", {
				orient: "vertical",
				onpopupshowing: "event.stopPropagation();",
				onpopuphiding: "event.stopPropagation();",
				oncommand: "event.stopPropagation();",
				onclick: $.that + "pageContext.doSearch(event);event.stopPropagation();",
			}));
			insPoint.parentNode.insertBefore(item, insPoint.nextSibling);
			$.pageContext.populateMenu(item);
		},

		populateMenu: function(aN) {
			let node = aN.firstChild;
			if (!node)
				return true;
			node.textContent = "";
			node.appendChild($.$CNS("menuitem", {
				label: "Search on " + $.SS.currentEngine.name,
				image: $.SS.currentEngine.iconURI.spec,
			}));
			var hiddenList;
			try {
				let pref = $.gCV("browser.search.hiddenOneOffs");
				hiddenList = pref ? pref.split(",") : [];
			} catch (e) {
				hiddenList = [];
			}
			let engs = $.SS.getVisibleEngines().filter(aEng =>
				aEng.name !== $.SS.currentEngine.name);
			if ($._.pShowOnlyOneOffsInContext) {
				engs = engs.filter(aEng =>
					hiddenList.indexOf(aEng.name) === -1);
			}
			if (!engs.length)
				return true;
			node.appendChild($.$CNS("menuseparator"));
			var i = 0,
				iLen = engs.length;
			let mi;
			for (; i < iLen; ++i) {
				var eng = engs[i],
					uri = $.defaultImage;
				if (eng.iconURI)
					uri = $.CB_$ImageURLForResolution(window, eng.iconURI.spec);
				mi = node.appendChild($.$CNS("menuitem", {
					label: eng.name,
					image: uri,
					tooltiptext: "Search with: " + eng.name,
				}));
				mi.engine = eng;
			}
		},

		doSearch: function(aE) {
			$.getSearchVal();
			setTimeout(function() {
				$.searchModeSwitch($._selection, aE, aE.target.engine);
			}, 100);
		},

		remove: function() {
			var mItem = $.$($._UID + "-context_search_selection");
			if (mItem && mItem.parentNode) {
				mItem.parentNode.removeChild(mItem);
			}
		}
	},

	$: function(aId) { // Return elements by Id
		return document.getElementById(aId);
	},

	getDomainOfAddress: function(aUri) {
		if (aUri.schemeIs("chrome") || aUri.schemeIs("file")) {
			return null;
		}
		while (aUri.schemeIs("view-source")) {
			aUri = Services.io.newURI(aUri.path, null, null);
		}
		try {
			return aUri.host;
		} catch (aError) {
			return null;
		}
	},

	shorten: function(aStr, aLimit) {
		aStr = aStr.replace(/[\n\r]+/g, " "); // Replace newlines with space.
		if (encodeURIComponent(aStr).length > aLimit) // Remove duplicate whitespace.
			aStr = aStr.replace(/\s{2,}/g, ' ');
		aStr = aStr.trim(); // White space trimming.
		aStr = aStr.slice(0, aLimit); // Cut string to aLimit. Default 900 characters.
		return aStr;
	},

	CB_$ImageURLForResolution: function(aWindow, aURL, aWidth = 16, aHeight = 16) {
		var width = Math.round(aWidth * aWindow.devicePixelRatio);
		var height = Math.round(aHeight * aWindow.devicePixelRatio);
		return aURL + (/#/.test(aURL) ? "&" : "#") +
			"-moz-resolution=" + width + "," + height;
	},

	alertme: function(aMsg) {
		Services.prompt.alert(null, self.name, aMsg);
	},

	getHelp: function(aPlaceholder) { // Get specific data, surrounded by "placeholders", from the Help tab.
		return self.getAttribute("Help").split("<!--" + aPlaceholder + "-->")[1];
	},

	/**
	 * nsIAlertsService works whenever it f***ing wants!!!!
	 * Possible culprit, the source of the image. ¬¬
	 * The origin can be changed in the callback too!!
	 */
	notifyme: function(aTitle, aMsg, aImg, aCallback) { // Notification function
		let origin = (Services.prefs.getPrefType("ui.alertNotificationOrigin") !== 0) ?
			Services.prefs.getIntPref("ui.alertNotificationOrigin") :
			null;
		let win = Services.ww.openWindow(null, "chrome://global/content/alerts/alert.xul",
			"_blank", "chrome,titlebar=no,popup=yes", null);
		win.arguments = [aImg, aTitle, aMsg, !!aCallback, "", origin, "", "", null, (!!aCallback ? aCallback : null)];
	},

	getNotificationFooter: function(aType) {
		switch (aType) {
			case 1: // Open storage.
				return "𝗖𝗹𝗶𝗰𝗸 𝗺𝗲 𝘁𝗼 𝗼𝗽𝗲𝗻 𝗲𝘅𝗽𝗼𝗿𝘁 𝗹𝗼𝗰𝗮𝘁𝗶𝗼𝗻";
			default:
				return "𝗖𝗹𝗶𝗰𝗸 𝗺𝗲 𝘁𝗼 𝗰𝗹𝗼𝘀𝗲 𝗺𝗲";
		}
	},

	alertCallback: {
		opt: {
			footer: null,
			centerScreen: null,
		},
		observe: function(aSubject, aTopic, aData) {
			let alt = Services.wm.getMostRecentWindow("alert:alert");
			let tBox = alt.document.getElementById("alertTextBox");
			if (aTopic === "alertshow") {
				tBox.appendChild($.$CNS("label", {
					flex: 1,
					value: $.getNotificationFooter(this.opt.footer),
				}));
				if (this.opt.centerScreen) {
					alt.moveTo((screen.availWidth / 2) - (alt.outerWidth / 2),
						(screen.availHeight / 2) - (alt.outerHeight / 2));
				}
				setTimeout(function() {
					alt.close();
				}, (this.opt.centerScreen ? 10000 : 3000));
			}
			if (aTopic === "alertclickcallback") {
				alt.close();
			}
		}
	},

	$CNS: function(aElName, aAttrs, aNS) { // Creates elements
		var el = aNS ?
			document.createElementNS(aNS, aElName) :
			document.createElement(aElName);
		if (aAttrs)
			Object.keys(aAttrs).forEach(function(n) {
				if (aAttrs[n] !== "")
					el.setAttribute(n, aAttrs[n]);
			});
		aElName === "menu" && el.classList.add("menu-iconic");
		aElName === "menuitem" && el.classList.add("menuitem-iconic");
		return el;
	},

	appID: function(aId) { // App ID boolean return.
		var app = {
			"SM": "{92650c4d-4b8e-4d2a-b7eb-24ecf4f6b63a}",
			"FX": "{ec8030f7-c20a-464f-9b0e-13a3a9e97384}",
			"TB": "{3550f703-e582-4d05-9a08-453d09bdfdc6}",
			"PM": "{8de7fcbb-c55c-4fbe-bfc5-fc555c87dbc4}"
		};
		return (Services.appinfo.ID === app[aId]);
	},

	/**
	 * getComplexValue
	 * @param  {string} aKey Full preference name.
	 * @return {string}      Preference value.
	 */
	gCV: function(aKey) { // Get Complex Value (Char Pref)
		return $.SP.getComplexValue(aKey, Ci.nsISupportsString).data;
	},

	/**
	 * setComplexValue
	 * @param  {string} aKey Full preference name.
	 * @param  {string} aVal Preference value.
	 */
	sCV: function(aKey, aVal) { // Set Complex Value (Char Pref)
		var str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
		str.data = aVal;
		$.SP.setComplexValue(aKey, Ci.nsISupportsString, str);
	},

	/**
	 * Doorhanger notifications
	 * @param  {string} aMsg Message to display.
	 * @param  {boolean} aDel Remove the notification.
	 */
	notify: function(aMsg, aDel) {
		if (!("PopupNotifications" in window))
			Cu.import("resource://gre/modules/PopupNotifications.jsm");
		var nt = PopupNotifications.show(gBrowser.selectedBrowser, $._UID +
			"-dad-Door-Hanger", aMsg, self.id, null, null, null);
		(aDel) && nt.remove();
	},

	styleSheet: function(aCSS, aRemFlag) {
		var sss = Cc["@mozilla.org/content/style-sheet-service;1"].getService(Ci.nsIStyleSheetService);
		var uri = Services.io.newURI("data:text/css," + encodeURIComponent(aCSS), null, null);
		if (sss.sheetRegistered(uri, sss.USER_SHEET))
			sss.unregisterSheet(uri, sss.USER_SHEET);
		if (aRemFlag)
			return;
		sss.loadAndRegisterSheet(uri, sss.USER_SHEET);
	},

	CSSLoader: function() {
		var css = $.getHelp("%StyleSheet%")
			.split("%U_I_D%").join($._UID);

		if (!window[$._UID]["StyleSheet"]) {
			window[$._UID]["StyleSheet"] = css;
			$.styleSheet(css);
		} else if (window[$._UID]["StyleSheet"] !== css) {
			// Use of try...finally to avoid using a setTimeout. (?)
			try {
				$.styleSheet(window[$._UID]["StyleSheet"], true);
			} finally {
				window[$._UID]["StyleSheet"] = css;
				$.styleSheet(css);
			}
		}
	},

	createElements: function(aInit) {
		let mPop = $.popSet;
		// Dynamic tooltip for button
		if (mPop && !($.$($._UID + "-dynamic-tooltip"))) {
			mPop.appendChild($.$CNS("tooltip", {
				id: $._UID + "-dynamic-tooltip",
				cb_id: $._UID,
				level: "top",
				onpopupshowing: $.that + "populateDynamicTooltip(event, this);event.stopPropagation();",
				onpopuphiding: "event.stopPropagation();",
				flex: 1,
			}));
		}
		// Initialize Drag&Drop handler
		if (mPop && !$.dAndDTT) {
			mPop.appendChild($.$CNS("panel", {
				id: $._UID + "-drag-and-drop-tooltip",
				// If I set "position" to an "arrow" panel, the arrow is not correctly pointing to the anchor. ¬¬
				// Sooo, I apply the "arrowpanel" binding to the panel with CSS and move on.
				// Sacrificing the "position" attribute is not an option at all.
				// type: "arrow",
				cb_id: $._UID,
				arrowposition: "after_start", // I don't think that this in necessary, but it seems to not "hurt".
				position: "after_start",
				flip: "slide",
				consumeoutsideclicks: false,
				orient: "vertical",
				level: "top",
			})).appendChild($.$CNS("description", {
				class: "tooltip-label",
				style: "font-weight:bold;"
			}));
		}
		// One-Offs context menu
		if (mPop && !$.ctxOneOffPop) {
			let ctx = mPop.appendChild($.$CNS("menupopup", {
				id: $._UID + "_one_off_context_popup",
				cb_id: $._UID,
				orient: "vertical",
				level: "parent",
				onpopuphiding: "event.stopPropagation();",
				onpopupshowing: $.that + "oneOffsCtxPopShowing();event.stopPropagation();",
				oncommand: $.that + "oneOffsCtx(event);event.stopPropagation();",
				onclick: "event.stopPropagation();",
			}));
			ctx.appendChild($.$CNS("menuitem", {
				label: "Set as default",
				_value: "set-default",
			}));
			ctx.appendChild($.$CNS("menuitem", {
				label: "Delete One Off button",
				_value: "delete-oneoff",
			}));
		}
		$.createHotkeys();
	},

	createHotkeys: function() {
		if (!$._.pCustomHotkey1Enabled && !$._.pCustomHotkey2Enabled && !$._.pCustomHotkey3Enabled)
			return true;
		let hotKeys = [{
			id: "key" + $._UID + "_global_hotkey_1",
			combo: $._.pCustomHotkey1,
			enabled: $._.pCustomHotkey1Enabled && $._.pCustomHotkey1Action !== 0,
			cmd: $.that + "_clickActions(" + $._.pCustomHotkey1Action + ", event);event.stopPropagation();",
		}, {
			id: "key" + $._UID + "_global_hotkey_2",
			combo: $._.pCustomHotkey2,
			enabled: $._.pCustomHotkey2Enabled && $._.pCustomHotkey2Action !== 0,
			cmd: $.that + "_clickActions(" + $._.pCustomHotkey2Action + ", event);event.stopPropagation();",
		}, {
			id: "key" + $._UID + "_global_hotkey_3",
			combo: $._.pCustomHotkey3,
			enabled: $._.pCustomHotkey3Enabled && $._.pCustomHotkey3Action !== 0,
			cmd: $.that + "_clickActions(" + $._.pCustomHotkey3Action + ", event);event.stopPropagation();",
		}];
		var keyset = $.$($._UID + "_keyset");
		var popSet = $.popSet;
		try {
			if (popSet && !keyset) { // Using "my own" keyset seems to minimize hotkeys erratic behaivor.
				keyset = popSet.parentNode.appendChild($.$CNS("keyset", {
					id: $._UID + "_keyset",
					cb_id: $._UID,
				}));
			}
		} finally {
			for (var i = hotKeys.length - 1; i >= 0; i--) {
				let hk = hotKeys[i];
				if (!hk["enabled"])
					continue;
				let combo = $.getCombo(hk["combo"].trim());
				let key = combo.substring(combo.lastIndexOf("-") + 1, combo.length);
				let modifiers = combo.slice(0, -(key.length + 1)).split("-").join(",");
				if (key && modifiers && keyset) {
					let attr = key.length === 1 ? "key" : "keycode";
					let hotKey = keyset.appendChild($.$CNS("key", {
						id: hk["id"],
						modifiers: modifiers,
						oncommand: hk["cmd"],
					}));
					hotKey.setAttribute(attr, key.length === 1 ? key : "VK_" + key); // Set "keycode" or "key" attribute.
				}
			}
		}
	},

	getCombo: function(aHKStr) {
		var prefix = "";
		if (aHKStr.search("Alt+") !== -1)
			prefix += "alt-";
		if (aHKStr.search("Ctrl+") !== -1)
			prefix += "control-";
		if (aHKStr.search("Shift+") !== -1)
			prefix += "shift-";
		if (aHKStr.search("Meta+") !== -1)
			prefix += "meta-";
		return prefix + aHKStr.substring(aHKStr.lastIndexOf("+") + 1, aHKStr.length);
	},

	// Drag&Drop handler
	dragAndDropHandler: {
		handleEvent: function(aE) {
			var types = aE.dataTransfer.types;
			var data;
			var urlArr;
			var searchValue;
			switch (aE.type) {
				case "dragenter": // ondragenter
					aE.preventDefault();
					if (aE.target !== self)
						return;
					setTimeout(function() {
						if ($._.pDragAndDropForOneOffButtons && $._needsToOpenPanel) {
							window[$._UID]["DDOneOffFlag"] = true;
							if ($.oODetached) {
								$.searchPanelActions.keepItUp($.tB, self);
							} else {
								if ($.sP.state === "closed")
									$.sP.openPopup(self, "bottomcenter topleft", 0, 0, false, null, null);
							}
						}
					}, 100);
					aE.stopPropagation();
					break;
				case "dragover": // ondragover
					if ($._.pDaDFeedbackType !== 0) {
						if (types.contains("application/x-moz-file")) { // If what is being dragged IS a local file.
							$._needsToOpenPanel = false;
							$.checkContentType(aE.dataTransfer.mozGetDataAt("application/x-moz-file", 0), false) ?
								$.dragAndDropHandler.feedbackShow(aE, "Search local image file with Google Images") :
								$.dragAndDropHandler.feedbackShow(aE, "The file that is being dragged is not an image file " +
									"or is not a supported image format.");
							self.image = $.getHelp("%CameraImage%");
						} else if (types.contains("text/html") ||
							types.contains("text/plain") ||
							types.contains("text/x-moz-text-internal")) { // If what is being dragged is NOT a local file.
							data = aE.dataTransfer.getData('text/html');
							var rex = /src="?([^"\s]+)"?\s*/;
							urlArr = rex.exec(data);
							if (urlArr) {
								// if ($.isImgURL(urlArr[1].toLowerCase() || urlArr[1].indexOf("data:") === 0)) {
								if ($.imgPattern.test(urlArr[1]) || urlArr[1].indexOf("data:") === 0) {
									$._needsToOpenPanel = false;
									$.dragAndDropHandler.feedbackShow(aE, "Search image in content with Google Images");
									self.image = $.getHelp("%CameraImage%");
								} else {
									$._needsToOpenPanel = true;
									data = aE.dataTransfer.getData("text/plain");
									if (!data)
										data = aE.dataTransfer.getData("text/x-moz-text-internal");
									if (data) {
										switch ($._.pSearchMode) {
											case true: // Search mode is "Site"
												if (!aE.shiftKey && !$.accel(aE) && !aE.altKey) // No modifiers
													this.feedbackShow(aE, "Search the current site with: " +
													$.SS.currentEngine.name);
												if (!aE.shiftKey && $.accel(aE) && !aE.altKey) // Control or Command
													this.feedbackShow(aE, "Search the web with: " +
													$.SS.currentEngine.name);
												self.image = $.getButtonImage();
												break;
											case false: // Search mode is "Web"
												if (!aE.shiftKey && !$.accel(aE) && !aE.altKey) // No modifiers
													this.feedbackShow(aE, "Search the web with: " +
													$.SS.currentEngine.name);
												if (!aE.shiftKey && $.accel(aE) && !aE.altKey) // Control or Command
													this.feedbackShow(aE, "Search the current site with: " +
													$.SS.currentEngine.name);
												self.image = $.getButtonImage();
												break;
											default:
												break;
										}
									}
								}
							} else {
								data = aE.dataTransfer.getData("text/plain");
								if (!data)
									data = aE.dataTransfer.getData("text/x-moz-text-internal");
								if (data) {
									switch ($._.pSearchMode) {
										case true: // Search mode is "Site"
											if (!aE.shiftKey && !$.accel(aE) && !aE.altKey) // No modifiers
												$.dragAndDropHandler.feedbackShow(aE, "Search the current site with: " +
												$.SS.currentEngine.name);
											if (!aE.shiftKey && $.accel(aE) && !aE.altKey) // Control or Command
												$.dragAndDropHandler.feedbackShow(aE, "Search the web with: " +
												$.SS.currentEngine.name);
											self.image = $.getButtonImage();
											break;
										case false: // Search mode is "Web"
											if (!aE.shiftKey && !$.accel(aE) && !aE.altKey) // No modifiers
												$.dragAndDropHandler.feedbackShow(aE, "Search the web with: " +
												$.SS.currentEngine.name);
											if (!aE.shiftKey && $.accel(aE) && !aE.altKey) // Control or Command
												$.dragAndDropHandler.feedbackShow(aE, "Search the current site with: " +
												$.SS.currentEngine.name);
											self.image = $.getButtonImage();
											break;
									}
								}
							}
						}
					}
					aE.preventDefault();
					break;
				case "dragleave": // ondragleave
					$._needsToOpenPanel = true;
					this.feedbackHide();
					self.image = $.getButtonImage();
					aE.preventDefault();
					aE.stopPropagation();
					break;
				case "drop": // ondrop
					if (types.contains("application/x-moz-file")) {
						let files = aE.dataTransfer.files;
						for (var i = files.length - 1; i >= 0; i--) {
							if (files[i].type.indexOf("image/") === 0)
								$.searchImageSwitch("localfile", files[i].mozFullPath);
						}
					} else if (types.contains("text/html") || types.contains("text/plain") ||
						types.contains("text/x-moz-text-internal")) {
						data = aE.dataTransfer.getData('text/html');
						var rexsrc = /src="?([^"\s]+)"?\s*/;
						urlArr = rexsrc.exec(data);
						if (urlArr) {
							// if ($.isImgURL(urlArr[1].toLowerCase()) || urlArr[1].indexOf("data:") === 0) {
							if ($.imgPattern.test(urlArr[1]) || urlArr[1].indexOf("data:") === 0) {
								let img;
								try {
									if (urlArr[1].indexOf("view-source:file:") === 0)
										img = urlArr[1].split("view-source:")[1];
									else if (urlArr[1].indexOf("view-source:jar:") === 0)
										img = decodeURIComponent(urlArr[1]).split("view-source:")[1];
									else if (urlArr[1].indexOf("chrome://") === 0 ||
										urlArr[1].indexOf("jar:") === 0)
										img = decodeURIComponent(urlArr[1]);
									else
										img = urlArr[1];
								} finally {
									$.searchImageSwitch("content", img);
								}
							} else {
								data = aE.dataTransfer.getData("text/plain");
								if (!data)
									data = aE.dataTransfer.getData("text/x-moz-text-internal");
								if (data) {
									searchValue = $.shorten(data, 900);
									setTimeout(function() {
										$.searchModeSwitch(searchValue, aE);
									}, 100);
								}
							}
						} else {
							data = aE.dataTransfer.getData("text/plain");
							if (!data)
								data = aE.dataTransfer.getData("text/x-moz-text-internal");
							if (data) {
								searchValue = $.shorten(data, 900);
								setTimeout(function() {
									$.searchModeSwitch(searchValue, aE);
								}, 100);
							}
						}
					}
					this.feedbackHide();
					self.image = $.getButtonImage();
					if (!!$.sP && $.sP.state === "open")
						$.sP.hidePopup();
					if (!!$.aCP && $.aCP.popupOpen)
						$.aCP.hidePopup();
					$._needsToOpenPanel = true;
					aE.preventDefault();
					break;
				default:
					break;
			}
		},

		feedbackShow: function(aE, aMsg) {
			switch ($._.pDaDFeedbackType) {
				case 2:
					$.appID("SM") ?
						($.$("statusbar-display").label = aMsg) :
						gBrowser.getStatusPanel().label = aMsg;
					break;
				default:
					this.handleTT(aE, aMsg);
					break;
			}
		},

		feedbackHide: function() {
			switch ($._.pDaDFeedbackType) {
				case 2:
					$.appID("SM") ?
						($.$("statusbar-display").label = "") :
						gBrowser.getStatusPanel().label = "";
					break;
				default:
					this.handleTT(null, null, true);
					break;
			}
		},

		handleTT: function(aE, aMsg, aDel) {
			if (!aDel) {
				if (!aE || !aMsg)
					return;
				$.dAndDTT.firstChild.setAttribute("value", aMsg);
				$.dAndDTT.openPopup(aE.target, "", 0, 0, false, null, null);
			} else {
				$.dAndDTT.hidePopup();
			}
		},

		removeDandDFeedback: function(aE) { // Drag and Drop feedback "removal"
			let canvas = document.createElementNS("http://www.w3.org/1999/xhtml", "canvas");
			canvas.width = canvas.height = 0;
			aE.dataTransfer.setDragImage(canvas, 0, 0);
		}
	},

	reportError: function(aError) {
		try {
			console.error(aError);
		} catch (aErr) {
			Cu.reportError(["Multi Menu" + ": " + self.id, aError, aError.stack].join("\n"));
		}
	},

	isImgURL: function(aURL) {
		let exts = [".png", ".jpg", ".tif", ".gif", ".bmp", ".jpeg"];
		let itsImg = false;
		try {
			for (let i = exts.length - 1; i >= 0; i--) {
				if (aURL.indexOf(exts[i]) !== -1) {
					itsImg = true;
					break;
				}
			}
		} finally {
			return itsImg;
		}
	},

	// Preferences handling functions
	prefHandling: {
		nsIFile: Components.Constructor("@mozilla.org/file/local;1", "nsIFile", "initWithPath"),
		resetP: function() {
			var msg = "You're about to reset Multi Search preferences to their default values​​.\t\t\n" +
				"This process is immediate and irreversible.\t\n" +
				"It is advisable to make a backup of the preferences before proceeding.\t\n\n" +
				"Do you want to continue?\n";
			var cBox = custombuttons.confirmBox(self.name, msg, "Yes", "Cancel");
			if (cBox) {
				($.SP.getBranch($.Branch)).deleteBranch("");
				$.selfDestroy();
			} else
				return;
		},
		exportP: function() {
			var out = [],
				prefList = ($.SP.getBranch($.Branch)).getChildList("", {});
			var value;
			for (var pref in prefList) {
				switch ($.SP.getPrefType($.Branch + prefList[pref])) {
					case 0:
						break;
					case 32:
						value = $.gPref(prefList[pref]);
						break;
					case 64:
						value = $.gPref(prefList[pref]);
						break;
					case 128:
						value = $.gPref(prefList[pref]) ? "true" : "false";
						break;
				}
				out.push(prefList[pref] + "|" + value);
			}
			var file = new this.nsIFile(Services.dirsvc.get("Desk", Ci.nsIFile).path);
			file.append("MultiSearchPrefs.js");
			file.createUnique(Ci.nsIFile.NORMAL_FILE_TYPE, 0x1B6);
			var data = out.join("\n");
			OS.File.writeAtomic(file.path, data, {
				tmpPath: file.path + ".tmp",
				encoding: "utf-8"
			});
			$.alertCallback.opt = {
				footer: null,
				centerScreen: false,
			};
			$.notifyme("Multi Search Preferences",
				"Successfully exported to your Desktop.",
				$.getHelp("%WebSearch%"),
				$.alertCallback);
		},
		importP: function() {
			var msg = "You're about to import new preferences to Multi Search.\t\t\n" +
				"This process is immediate and irreversible.\t\n" +
				"It is advisable to make a backup of the preferences before proceeding.\t\n\n" +
				"Do you want to continue?\n";
			var cBox = custombuttons.confirmBox(self.name, msg, "Yes", "Cancel");
			if (cBox) {
				var file = new this.nsIFile(Services.dirsvc.get("Desk", Ci.nsIFile).path);
				var fp = Cc["@mozilla.org/filepicker;1"].createInstance(Ci.nsIFilePicker);
				fp.init(window, "Import Multi Search Preferences",
					Ci.nsIFilePicker.modeOpen);
				fp.appendFilter("MultiSearchPrefs*.js", "MultiSearchPrefs*.js");
				fp.appendFilter("", "*.*");
				fp.displayDirectory = file;
				if (fp.show() === Ci.nsIFilePicker.returnOK) {
					if (fp.file && fp.file.exists()) {
						file = fp.file.parent.QueryInterface(Ci.nsIFile);
					}
				} else
					return;
				OS.File.read(fp.file.path).then(function onSuccess(aReadData) {
					var prefFile = new TextDecoder().decode(aReadData);
					prefFile = prefFile.replace(/\r\n/g, "\n").split("\n");
					for (var p in prefFile) {
						var prefName = $.Branch + prefFile[p].split("|")[0],
							prefValue = prefFile[p].split("|")[1];
						if ($.SP.getPrefType(prefName)) {
							switch ($.SP.getPrefType(prefName)) {
								case 0:
									break;
								case 32:
									var str = Cc["@mozilla.org/supports-string;1"]
										.createInstance(Ci.nsISupportsString);
									str.data = prefValue;
									$.SP.setComplexValue((prefName), Ci.nsISupportsString, str);
									break;
								case 64:
									$.SP.setIntPref(prefName, prefValue);
									break;
								case 128:
									$.SP.setBoolPref(prefName, ((prefValue === "true") ? "true" : ""));
									break;
							}
						}
					}
					setTimeout(function() {
						$.selfDestroy();
					}, 1000);
				}, function onFailure(aRejectReason) {
					$.reportError(aRejectReason);
				});
			} else
				return;
		}
	},

	selfDestroy: function() {
		setTimeout(function() {
			self.destroy("update");
			setTimeout(function() {
				self.init();
			}, 100);
		}, 100);
	},

	_UID: "__multi_search_button" + self.id.slice(20),
	that: "document.getElementById('" + self.id + "').MultiSearch.",
	imgPattern: /\.(gif|jpg|jpeg|tiff|png|ico|svg|bmp)$/i,
	SP: Services.prefs,
	SS: Services.search,
	isOSX: Services.appinfo.OS.toLowerCase().startsWith("darwin"),
	_globalMM: Cc["@mozilla.org/globalmessagemanager;1"].getService(Ci.nsIMessageListenerManager),
	_randomID: null,
	_frameScript: null,
	_initializingSearchPanel: null,
	_oneOffBtnEngine: null,
	_needsToOpenPanel: true,
	accel: function(aE) {
		if (aE)
			return $.isOSX ? aE.metaKey : aE.ctrlKey;
		return $.isOSX ? "Command" : "Control";
	},
	get oODetached() { // Where to create the one-off buttons box.
		// Attached to the main panel or inside "CustomPopupAutoComplete".
		if ($._.pAttachOneOffBoxToMainPanel || $.appID("SM") ||
			(!$._.pSearchHistory && !$.SP.getBoolPref("browser.search.suggest.enabled")))
			return false;
		else
			return true;
		return false;
	},
	get dAndDTT() {
		return $.$($._UID + "-drag-and-drop-tooltip");
	},
	get aCP() {
		return $.$($._UID + "-CustomPopupAutoComplete");
	},
	get clearBtn() {
		return $.$($._UID + "-clear-searchbox");
	},
	get sP() {
		return $.$($._UID + "-search_panel");
	},
	get tB() {
		return $.$($._UID + "-searchbox");
	},
	get _cleanUpMsgID() {
		return "CB:MultiSearch:Cleanup:" + self.id + "-" + $._randomID;
	},
	get _searchImageMsgID() {
		return "CB:MultiSearch:SearchImage:" + self.id + "-" + $._randomID;
	},
	get _getSelectionMsgID() {
		return "CB:MultiSearch:GetSelection:" + self.id + "-" + $._randomID;
	},
	get _responseMsgID() {
		return "CB:MultiSearch:Response:" + self.id + "-" + $._randomID;
	},
	get _selection() {
		return window[$._UID]["Selection"];
	},
	set _selection(aStr) {
		window[$._UID]["Selection"] = aStr;
	},
	get ctxOneOffPop() {
		return $.$($._UID + "_one_off_context_popup");
	},
	get popSet() {
		return $.$("mainPopupSet");
	},
	get _mm() {
		let mm = null;
		try {
			mm = typeof gBrowser === "object" ?
				gBrowser.selectedTab.linkedBrowser.messageManager : // Everything else.
				getBrowser().messageManager; // Thunderbird. %$/&$% you!!!!
		} catch (aErr) {}
		return mm;
	},
};

