<!--%FrameScript%-->
(function(aContext) {
	"use sctrict";
	let Ci = Components.interfaces;
	let removeDandDFeedback = %D_AND_D_FEEDBACK%;
	let DAndDListener = function(aE) {
		let canvas = content.document.createElementNS("http://www.w3.org/1999/xhtml", "canvas");
		canvas.width = canvas.height = 0;
		aE.dataTransfer.setDragImage(canvas, 0, 0);
	};
	var MultiSearch = {
		init: function() {
			aContext.addMessageListener("%Get_Sel_ID%", this);
			aContext.addMessageListener("%Search_Image_ID%", this);
			aContext.addMessageListener("%Cleanup_ID%", this);
			removeDandDFeedback && addEventListener("dragstart", DAndDListener, false);
		},

		unInit: function() {
			aContext.removeMessageListener("%Get_Sel_ID%", this);
			aContext.removeMessageListener("%Search_Image_ID%", this);
			aContext.removeMessageListener("%Cleanup_ID%", this);
			removeEventListener("dragstart", DAndDListener, false);
		},

		receiveMessage: function(aMsg) {
			switch (aMsg.name) {
				case "%Get_Sel_ID%":
					this._getSelection(true);
					break;
				case "%Search_Image_ID%":
					this.searchImage(aMsg.data);
					break;
				case "%Cleanup_ID%":
					this.unInit();
					break;
			}
		},

		searchImage: function(aData) {
			let form = content.document.createElement("form");
			let target = (aData.TabOpeningForImg === 0) ? "_self" : "_blank";
			form.setAttribute("method", "POST");
			form.setAttribute("action", aData.searchByImageBaseURL + "/searchbyimage/upload");
			form.setAttribute("enctype", "multipart/form-data");
			form.setAttribute("target", target);
			form.setAttribute("accept-charset", "utf-8");
			this.addParamsToForm(form, "image_content", aData.inlineImage);
			this.addParamsToForm(form, "filename", "");
			this.addParamsToForm(form, "image_url", "");
			content.document.body.appendChild(form);
			form.submit();
		},

		addParamsToForm: function(aForm, aKey, aValue) {
			let hiddenField = content.document.createElement("input");
			hiddenField.setAttribute("type", "hidden");
			hiddenField.setAttribute("name", aKey);
			hiddenField.setAttribute("value", aValue);
			aForm.appendChild(hiddenField);
		},

		_getSelection: function(aSendResponse) {
			let fWin = {};
			let fEl = Services.focus.getFocusedElementForWindow(content, true, fWin);
			fWin = fWin.value;
			let sel = fWin.getSelection().toString();
			if (!sel && fEl instanceof Ci.nsIDOMNSEditableElement) {
				if (fEl instanceof Ci.nsIDOMHTMLTextAreaElement ||
					(fEl instanceof Ci.nsIDOMHTMLInputElement &&
						fEl.mozIsTextField(true))) {
					sel = fEl.editor.selection.toString();
				}
			}
			if (aSendResponse) {
				sendAsyncMessage("%Response_ID%", {
					id: 1, // ID 1
					selStr: sel,
				});
			} else
				return sel;
		},
	};
	MultiSearch.init();
})(this);
<!--%FrameScript%-->


