custombutton.initcode:
/**
 * [prefName description][Required]
 * Preference name. Can be a Boolean, Integer, or String pref. type.
 * @type {String}: The full name of a preference.
 */
var prefName = "accessibility.typeaheadfind"; // Boolean
//var prefName = "browser.newtab.url"; // String
/**
 * [switchButtonImage description][Optional]
 * Use a custom image for the button that represents the current pref. value.
 * @type {Boolean}: true or false
 */
var switchButtonImage = true;
/**
 * [showNotification description][Optional]
 * Show a notification after switching a preference.
 * @type {Boolean}: true or false
 */
var showNotification = true;
/**
 * [boolEnabledImage description][Optional]
 * Image for enabled Boolean pref. type ONLY.
 * @type {String}: Link to an image.
 */
var boolEnabledImage = "chrome://custombuttons/skin/stdicons/gbutton.png";
/**
 * [boolDisabledImage description][Optional]
 * Image for disabled Boolean pref. type ONLY.
 * @type {String}: Link to an image.
 */
var boolDisabledImage = "chrome://custombuttons/skin/stdicons/rbutton.png";
/**
 * [prefValues description][Optional]
 * Array of pref. values. For Integer or String pref. type ONLY.
 * @type {Array} : List of strings or integers separated by a comma.
 * It has to have the exact same length (amount of values) as the prefImages variable.
 */
var prefValues = ["about:home", "https://www.google.com", "https://www.bing.com"];
/**
 * [prefImages description][Optional]
 * Array of images. For Integer or String pref. type ONLY.
 * @type {Array} : List of links to images separated by a comma.
 * It has to have the exact same length (amount of images) as the prefValues variable.
 */
var prefImages = ["chrome://custombuttons/skin/stdicons/bbutton.png",
	"https://www.google.com.ar/images/branding/product/ico/googleg_lodp.ico",
	"http://www.bing.com/sa/simg/bing_p_rr_teal_min.ico"
];

/**
 * From here down DO NOT edit anything!!!
 */

this.onclick = function(aE) {
	if (aE.button !== 2)
		aE.preventDefault();
	if (aE.button === 0 && !aE.shiftKey && !accel(aE) && !aE.altKey) {
		self.setPref(false, false);
	} else if (aE.button === 0 && !aE.shiftKey && accel(aE) && !aE.altKey ||
		aE.button === 1 && !aE.shiftKey && !accel(aE) && !aE.altKey) {
		self.setPref(true, false);
	} else if (aE.button === 0 && aE.shiftKey && !accel(aE) && !aE.altKey) {
		self.setPref(false, true);
	}
	aE.stopPropagation();
};

this.setPref = function(aPrompt, aInverse) {
	if (Services.prefs.prefIsLocked(prefName)) {
		self.alertCallback.options = {
			footer: 2,
			centerScreen: true,
		};
		notifyme(prefName,
			"This preference is 𝗹𝗼𝗰𝗸𝗲𝗱.",
			getHelp("%WarningImg%"),
			self.alertCallback);
		return;
	}
	var value,
		changed = false;
	let i = 0,
		iLen = prefValues.length;
	switch (self._prefType) {
		case 0:
			self.alertCallback.options = {
				footer: 1,
				centerScreen: true,
			};
			notifyme(prefName,
				"This preference 𝗱𝗼𝗲𝘀𝗻'𝘁 𝗲𝘅𝗶𝘀𝘁𝘀.",
				getHelp("%WarningImg%"),
				self.alertCallback);
			return;
		case 32:
			if (iLen > 0 && !aPrompt) {
				for (; i < iLen; i++) {
					if (prefValues[i] === Services.prefs.getComplexValue(prefName, Ci.nsISupportsString).data)
						value = aInverse ? prefValues[i - 1] : prefValues[i + 1];
				}
				if (!value)
					value = aInverse ? prefValues[iLen - 1] : prefValues[0];
				self._nsISS.data = value.trim();
				Services.prefs.setComplexValue(prefName, Ci.nsISupportsString, self._nsISS);
			} else {
				value = promptme(prefName, Services.prefs.getComplexValue(prefName, Ci.nsISupportsString).data);
				if (value) {
					changed = true;
					self._nsISS.data = value.trim();
					Services.prefs.setComplexValue(prefName, Ci.nsISupportsString, self._nsISS);
				}
			}
			break;
		case 64:
			if (iLen > 0 && !aPrompt) {
				for (; i < iLen; i++) {
					if (prefValues[i] === Services.prefs.getIntPref(prefName, true))
						value = aInverse ? prefValues[i - 1] : prefValues[i + 1];
				}
				if (!value)
					value = aInverse ? prefValues[iLen - 1] : prefValues[0];
				Services.prefs.setIntPref(prefName, value);
			} else {
				value = promptme(prefName, Services.prefs.getIntPref(prefName, true));
				if (value) {
					changed = true;
					Services.prefs.setIntPref(prefName, value);
				}
			}
			break;
		case 128:
			Services.prefs.setBoolPref(prefName, !Services.prefs.getBoolPref(prefName));
			setTimeout(function() {
				setSelfImage();
			}, 100);
			break;
	}
	setSelfImage();
	setSelfTooltip();
	if (showNotification && changed) {
		self.alertCallback.options = {
			footer: 0,
			centerScreen: false,
		};
		notifyme(prefName,
			"𝗖𝘂𝗿𝗿𝗲𝗻𝘁 𝘃𝗮𝗹𝘂𝗲: " + String(getPref(prefName)),
			getHelp("%AlertImg%"),
			self.alertCallback);
	}
	Services.prefs.savePrefFile(null);
};

function dealWithNonExistentPref() {
	let opts = ["Boolean", "Integer", "String"];
	let selected = {};
	let result = Services.prompt.select(null, self.name, "Choose the preference type.", opts.length,
		opts, selected);
	if (result) {
		switch (selected.value) {
			case 0:
				Services.prefs.setBoolPref(prefName, false);
				break;
			case 1:
				Services.prefs.setIntPref(prefName, 0);
				break;
			case 2:
				self._nsISS.data = "";
				Services.prefs.setComplexValue(prefName, Ci.nsISupportsString, self._nsISS);
				break;
		}
		setTimeout(function() {
			self.init();
		}, 100);
	} else
		return;
}

function setSelfTooltip() {
	let pVal = getPref(prefName);
	self.tooltipText = self.name + "\t ID: " + self.id.slice(20) +
		"\n\n\t𝗣𝗿𝗲𝗳. 𝗻𝗮𝗺𝗲: " + prefName +
		"\n\t𝗣𝗿𝗲𝗳. 𝘃𝗮𝗹𝘂𝗲: " + (pVal === null ? "𝗣𝗿𝗲𝗳𝗲𝗿𝗲𝗻𝗰𝗲 𝗱𝗼𝗲𝘀𝗻'𝘁 𝗲𝘅𝗶𝘀𝘁𝘀." : pVal) + "\n" +
		"\n\t𝗟𝗲𝗳𝘁 𝗰𝗹𝗶𝗰𝗸: Switch pref." +
		"\n\t𝗦𝗵𝗶𝗳𝘁 + 𝗟𝗲𝗳𝘁 𝗰𝗹𝗶𝗰𝗸: Switch pref. (Inverse)" +
		"\n\t𝗠𝗶𝗱𝗱𝗹𝗲 𝗼𝗿 " + accel() + " + 𝗟𝗲𝗳𝘁 𝗰𝗹𝗶𝗰𝗸: Set pref. prompt\n\n" +
		"\t𝗥𝗲𝗮𝗱 𝘁𝗵𝗶𝘀 𝗯𝘂𝘁𝘁𝗼𝗻 𝗵𝗲𝗹𝗽 𝗳𝗼𝗿 𝘃𝗲𝗿𝘆 𝗶𝗺𝗽𝗼𝗿𝘁𝗮𝗻𝘁 𝘂𝘀𝗮𝗴𝗲 𝗻𝗼𝘁𝗲𝘀.\t\t\n" +
		"\tRight click on button, 𝗕𝘂𝘁𝘁𝗼𝗻 𝗛𝗲𝗹𝗽 item.\n\n";
}

function setSelfImage() {
	if (self._prefType === 0 || !switchButtonImage)
		return true;
	let prefVal;
	switch (self._prefType) {
		case 128:
			if (Services.prefs.getBoolPref(prefName))
				self.image = boolEnabledImage;
			else
				self.image = boolDisabledImage;
			break;
		default:
			let img = null;
			try {
				img = prefImages[prefValues.indexOf(getPref(prefName))];
			} catch (aErr) {}
			img && (self.image = img);
	}
}

function getPref(aKey) {
	switch (self._prefType) {
		case 128:
			return Services.prefs.getBoolPref(aKey);
		case 64:
			return Services.prefs.getIntPref(aKey);
		case 32:
			return Services.prefs.getComplexValue(aKey, Ci.nsISupportsString).data;
		default:
			return null;
	}
}

function promptme(aMsg, aVal) {
	let retVal = {
		value: aVal
	};
	if (Services.prompt.prompt(null, self.name, aMsg, retVal, null, {}))
		return retVal.value;
	else
		return false;
	return false;
}

function accel(aE) {
	if (aE)
		return self._isOSX ? aE.metaKey : aE.ctrlKey;
	return self._isOSX ? "𝗖𝗼𝗺𝗺𝗮𝗻𝗱" : "𝗖𝗼𝗻𝘁𝗿𝗼𝗹";
}

function $CNS(aElName, aAttrs, aNameSpace) { // Creates elements
	var el = aNameSpace ?
		document.createElementNS(aNameSpace, aElName) :
		document.createElement(aElName);
	if (aAttrs)
		Object.keys(aAttrs).forEach(function(n) {
			if (aAttrs[n] !== "")
				el.setAttribute(n, aAttrs[n]);
		});
	return el;
}

function getHelp(aPlaceholder) { // Get specific data, surrounded by "placeholders", from the Help tab.
	return document.getElementById(self.id).getAttribute("Help").split("<!--" + aPlaceholder + "-->")[1];
}

function notifyme(aTitle, aMsg, aImg, aCallback) { // Notification function
	let origin = (Services.prefs.getPrefType("ui.alertNotificationOrigin") !== 0) ?
		Services.prefs.getIntPref("ui.alertNotificationOrigin") :
		null;
	let win = Services.ww.openWindow(null, "chrome://global/content/alerts/alert.xul",
		"_blank", "chrome,titlebar=no,popup=yes", null);
	win.arguments = [aImg, aTitle, aMsg, !!aCallback, "", origin, "", "", null, (!!aCallback ? aCallback : null)];
}

function getNotificationFooter(aType) {
	switch (aType) {
		case 1: // Non existent pref.
			return "𝗖𝗹𝗶𝗰𝗸 𝗺𝗲 𝘁𝗼 𝗰𝗿𝗲𝗮𝘁𝗲";
		case 2: // Locked pref.
			return "𝗖𝗹𝗶𝗰𝗸 𝗺𝗲 𝘁𝗼 𝘂𝗻𝗹𝗼𝗰𝗸";
		default:
			return "𝗖𝗹𝗶𝗰𝗸 𝗺𝗲 𝘁𝗼 𝗰𝗹𝗼𝘀𝗲 𝗺𝗲";
	}
}

this.alertCallback = {
	options: {
		footer: null,
		centerScreen: null
	},
	observe: function(aSubject, aTopic, aData) {
		let alt = Services.wm.getMostRecentWindow("alert:alert");
		let tBox = alt.document.getElementById("alertTextBox");
		if (aTopic === "alertshow") {
			tBox.appendChild($CNS("label", {
				flex: 1,
				value: getNotificationFooter(this.options.footer),
			}));
			if (this.options.centerScreen) {
				alt.moveTo((screen.availWidth / 2) - (alt.outerWidth / 2),
					(screen.availHeight / 2) - (alt.outerHeight / 2));
			}
		}
		if (aTopic === "alertclickcallback") {
			alt.close();
			switch (this.options.footer) {
				case 1: // Non existent pref.
					dealWithNonExistentPref(prefName);
					break;
				case 2: // Locked pref.
					Services.prefs.unlockPref(prefName);
					setTimeout(function() {
						if (Services.prefs.prefIsLocked(prefName)) {
							self.alertCallback.options = {
								footer: 0,
								centerScreen: false,
							};
							notifyme(prefName,
								"This preference 𝗰𝗼𝘂𝗹𝗱 𝗻𝗼𝘁 be 𝘂𝗻𝗹𝗼𝗰𝗸𝗲𝗱.",
								getHelp("%ErrorImg%"),
								self.alertCallback);
						} else {
							self.alertCallback.options = {
								footer: 0,
								centerScreen: false,
							};
							notifyme(prefName,
								"This preference has been 𝘂𝗻𝗹𝗼𝗰𝗸𝗲𝗱.",
								getHelp("%AlertImg%"),
								self.alertCallback);
						}
					}, 100);
					break;
			}
		}
	}
};

this._prefName = prefName;
this._prefValues = prefValues;
this._prefType = Services.prefs.getPrefType(prefName);
this._nsISS = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
this._isOSX = Services.appinfo.OS.toLowerCase().startsWith("darwin");

setSelfImage();
setSelfTooltip();
