custombutton.initcode:
// To avoid initialization while moving the button in "Australis customize mode". 
if (self.parentNode && /^wrapper-/.test(self.parentNode.id))
	return;

var buttonImage = "data:image/png;base64,";

var $ = this.SpeedDial = {
	Init: function() {
		if (self.parentNode && self.parentNode.id.match("PanelUI") || self.parentNode.id.match("vertical-"))
			self.orient = "vertical";
		else // If button is placed on any other toolbar
			self.orient = "horizontal";

		if (!("OS" in window))
			Cu.import("resource://gre/modules/osfile.jsm");
		if (!("Services" in window))
			Cu.import("resource://gre/modules/Services.jsm");

		$.InitPrefs();

		if (!($._UID in window)) {
			window[$._UID] = {
				CustomizableUI: false,
				StyleSheet: false,
			};
		}

		for (var p in $.DefPrefs) {
			if ($.DefPrefs.hasOwnProperty(p))
				$["_" + p] = $.gPref(p);
		}

		if (!window[$._UID]["Initialized"]) {
			window[$._UID]["Initialized"] = true;
			$.HandlePrefObserver();
		}

		$.CSSLoader();
		$.createItems();

		(function dropmarkerVisibility() {
			if (self.parentNode &&
				(!self.parentNode.id.match("PanelUI") || !self.parentNode.id.match("vertical-"))) {
				let dropMarker = document.getAnonymousElementByAttribute(self, "class",
					"toolbarbutton-menu-dropmarker");
				if (dropMarker)
					dropMarker.setAttribute("hidden", !$._pShowDropmarker);
				else
					setTimeout(function() {
						dropmarkerVisibility();
					}, 100);
			}
		})();

		if ("CustomizableUI" in window) {
			if (!window[$._UID]["CustomizableUI"]) {
				window[$._UID]["CustomizableUI"] = true;
				CustomizableUI.addListener({
					onCustomizeEnd: function(aWindow) {
						let thisBtn = aWindow.document.getElementById(self.id);
						setTimeout(function() {
							aWindow[$._UID]["CustomizableUI"] && thisBtn.init();
						}, 100);
					}
				});
			}
		}

		let selfPop = $.selfPop;

		if (selfPop && $._pOpenOnMouseover) {
			self.timer = 0;
			self.onmouseover = function(aE) {
				aE.preventDefault();
				clearTimeout(self.timer);
				self.timer = setTimeout(function() {
					selfPop.openPopup(self, "", 0, 0, false, false);
				}, $._pMouseoverDelay);
				aE.stopPropagation();
			};
			// self.onmouseout = function(aE) {
			// 	aE.preventDefault();
			// 	if (!aE.relatedTarget) {
			// 		clearTimeout(self.timer);
			// 		self.timer = setTimeout(function() {
			// 			selfPop.hidePopup();
			// 		}, $._pMouseoverDelay);
			// 	}
			// 	aE.stopPropagation();
			// };
		} else {
			self.onmouseover = null;
			// self.onmouseout = null;
		}

		self.type = "menu";
		self.image = buttonImage;
		$._pUnanchoredPanel || self.setAttribute("popup", $._UID + "-main-panel");
		self.tooltipText = "";
		self.setAttribute("tooltip", $._UID + "_dynamic_tooltip");
		self.onmousedown = function(aE) {
			if ($.accel(aE))
				aE.preventDefault();
		};
		self.onclick = function(aE) {
			if (aE.button === 0 && !aE.shiftKey && !$.accel(aE) && !aE.altKey && $._pUnanchoredPanel) {
				aE.preventDefault();
				$.openMenuAtScreen();
			}
			if ((aE.button === 0 && !aE.shiftKey && $.accel(aE) && !aE.altKey) ||
				(aE.button === 1 && !aE.shiftKey && !$.accel(aE) && !aE.altKey)) {
				aE.preventDefault();
				$.optPop && $.optPop.openPopup(self, "after_start", 0, 0, false, null, null);
			}
		};

		$.loadFromFile();

		addDestructor(function(aReason) {
			if (aReason === "update" || aReason === "delete") {
				self.removeAttribute("popup");
				if (self.menuModified) {
					self.image = buttonImage;
					self.menuModified = false;
				}
				// Delete all elements created by this button.
				let selfEls = document.getElementsByAttribute("cb_id", $._UID);
				Array.prototype.slice.call(selfEls).forEach(function(aEl) {
					aEl.parentNode.removeChild(aEl);
				});
			}
			if (aReason === "delete") {
				(Services.prefs.getBranch($.Branch)).deleteBranch("");
				Services.prefs.savePrefFile(null);
				delete window[$._UID];
			}
		});
	},

	openMenuAtScreen: function() {
		let pop = $.selfPop;
		let popW = parseInt(getComputedStyle(pop).width, 10),
			popH = parseInt(getComputedStyle(pop).height, 10);
		let posX = (screen.availWidth / 2) - (popW / 2),
			posY = (screen.availHeight / 2) - (popH / 2);
		if (popW === 0 || popH === 0) {
			pop.style.setProperty("opacity", "0", "important");
			pop && pop.openPopupAtScreen(posX, posY, false);
			setTimeout(function() {
				pop.hidePopup();
				$.openMenuAtScreen();
			}, 100);
		} else {
			pop.style.removeProperty("opacity", "0", "important");
			pop && pop.openPopupAtScreen(posX, posY, false);
		}
	},

	loadFromFile: function() {
		let path = OS.Path.join($.storagePath, $.bookFileName);
		OS.File.read(path).then(function onSuccess(aReadData) {
			let data = new TextDecoder().decode(aReadData);
			if (data === "") {
				$.createDummyFile(path);
				return;
			}
			data = JSON.parse(data);
			if (data.length === 0) {
				$.createDummyFile(path);
				return;
			}
			$.createMenu(data);
			if (self.menuModified) {
				self.image = buttonImage;
				self.menuModified = false;
			}
		}, function onFailure(aRejectReason) {
			$.createDummyFile(path);
		});
	},

	createDummyFile: function(aPath) {
		OS.File.writeAtomic(aPath, $.dummyItems, {
			tmpPath: aPath + ".tmp",
			encoding: "utf-8"
		}).then(function onSuccess() {
			$.loadFromFile();
		}, function onFailure(aRejectReason) {
			Cu.reportError([self.name + ": " + self.id, aRejectReason, aRejectReason.stack].join("\n"));
		});
	},

	createItems: function() {
		let popSet = $.popSet;
		if (popSet && !$.selfPop) {
			let p = popSet.appendChild($.$CNS("menupopup", {
				id: $._UID + "-main-panel",
				cb_id: $._UID,
				orient: "vertical",
				position: $._pPopupPosition,
				flip: $._pFlipAttribute ? "both" : "slide",
				oncommand: "event.stopPropagation();",
				context: $._UID + "-context-popup",
				consumeoutsideclicks: false,
				onpopupshown: "document.getElementById('" + self.id +
					"').setAttribute('open', true);event.stopPropagation();",
				onpopuphidden: "document.getElementById('" + self.id +
					"').setAttribute('open', false);event.stopPropagation();",
				onmouseup: "if (document.getElementById('" + self.id +
					"').SpeedDial.accel(event)) event.preventDefault();event.stopPropagation();",
				onclick: "document.getElementById('" + self.id +
					"').SpeedDial.openMyBookmarks(event);event.stopPropagation();",
				onpopupshowing: ($._pUseAccelkeys ?
					"document.getElementById('" + self.id +
					"').SpeedDial.keypressHandler('startListening');event.stopPropagation();" :
					"event.stopPropagation();"),
				onpopuphiding: ($._pUseAccelkeys ?
					"document.getElementById('" + self.id +
					"').SpeedDial.keypressHandler('stopListening');event.stopPropagation();" :
					"event.stopPropagation();"),
			}));
			if ($._pConvertToPanel) {
				p.style.MozAppearance = "none";
				p.style.MozBinding = "url('chrome://global/content/bindings/popup.xml#arrowpanel')";
				p.style.background = "transparent";
				p.style.border = "none";
				p.style.MozImageRegion = "auto";
			}
		}
		if (popSet && !$.optPop) {
			popSet.appendChild($.$CNS("menupopup", {
				id: $._UID + "-options-popup",
				cb_id: $._UID,
				oncommand: "document.getElementById('" + self.id +
					"').SpeedDial.handleOptionsPopup(event);event.stopPropagation();",
				onclick: "event.stopPropagation();",
				onpopupshowing: "document.getElementById('" + self.id +
					"').SpeedDial.populateOptionsMenu(this);event.stopPropagation();",
			}));
		}
		if (popSet && !$.ctxPop) {
			let ctx = popSet.appendChild($.$CNS("menupopup", {
				id: $._UID + "-context-popup",
				cb_id: $._UID,
				onclick: "event.stopPropagation();",
				oncommand: "document.getElementById('" + self.id +
					"').SpeedDial.onContextCommand(event);event.stopPropagation();",
				onpopupshowing: "document.getElementById('" + self.id +
					"').SpeedDial.onContextShowing(event);event.stopPropagation();",
				onpopuphiding: "event.stopPropagation();",
			}));
			ctx.appendChild($.$CNS("menuitem", {
				label: "Add new item",
				"data-action": "add",
			}));
			ctx.appendChild($.$CNS("menuitem", {
				label: "Edit item",
				"data-action": "edit",
			}));
			ctx.appendChild($.$CNS("menuitem", {
				label: "Remove item",
				"data-action": "delete",
			}));
		}
		if (popSet && !$.dynamicTT) {
			popSet.appendChild($.$CNS("tooltip", {
				id: $._UID + "_dynamic_tooltip",
				cb_id: $._UID,
				onpopupshowing: "document.getElementById('" + self.id +
					"').SpeedDial.populateDynamicTooltip(event, this);event.stopPropagation();",
				onpopuphiding: "event.stopPropagation();",
				flex: 1,
			}));
		}
	},

	setButtonState: function(aState) {
		self.setAttribute("open", aState);
	},

	onContextShowing: function(aE) {
		if (document.popupNode.nodeName !== "menuitem")
			aE.preventDefault();
	},

	onContextCommand: function(aE) {
		let el = document.popupNode;
		if (el.nodeName !== "menuitem")
			return true;
		switch (aE.target.getAttribute("data-action")) {
			case "add":
				$.addEditItems(true);
				break;
			case "edit":
				$.addEditItems(false);
				break;
			case "delete":
				el && el.parentNode.removeChild(el);
				$.setBtnImage();
				break;
		}
		aE.stopPropagation();
	},

	addEditItems: function(aNew) {
		var em = Services.ww.getWindowEnumerator();
		var winName = "speed_dial_add_edit_items_window_" + self.id;
		var index = 1;
		while (em.hasMoreElements()) {
			let win = em.getNext();
			if (win.name === winName) {
				win.focus();
				return;
			}
			index++;
		}
		let xul = $.getHelp("%AddEditWindow%");
		xul = xul.replace(/>\s+</g, "><");
		xul = xul.split("%S_E_L_F_I_D%").join(self.id);
		let storage = {
			button: self,
			popup_node: document.popupNode,
			is_new: aNew,
		};
		let url = "data:application/vnd.mozilla.xul+xml;text/plain," + encodeURIComponent(xul);
		let win = openDialog(url, winName, "chrome, resizable, dependant, dialog, centerscreen, close, alwaysRaised");
		win._storage = storage;
	},

	createItemFromCurrentTab: function() {
		let URI = gBrowser.currentURI;
		var done = function(aFavURI) {
			if (!aFavURI)
				aFavURI = gBrowser.selectedTab.image;
			if (!aFavURI)
				aFavURI = "";
			let name = gBrowser.selectedTab.label;
			let vBox = $.selfPop.getElementsByTagName("vbox")[0];
			let itm = $.$CNS("menuitem", {
				label: gBrowser.selectedTab.label.substr(0, 20) || "Name me!!!",
				tooltiptext: gBrowser.selectedTab.label || "",
				image: ($._pUseImagesOnItems ? (aFavURI || "") : ""),
				"data-url": URI.spec || "",
			});
			itm.classList.add($._UID + "-bookmark-items",
				($._pUseImagesOnItems ? "menuitem-iconic" : "menuitem-noniconic"));
			vBox.appendChild(itm);
			$.setBtnImage();
		};
		try {
			let asyncFavicons = Cc["@mozilla.org/browser/favicon-service;1"].getService(Ci.mozIAsyncFavicons);
			asyncFavicons.getFaviconURLForPage(Services.io.newURI(URI, null, null), done);
		} catch (aErr) {
			done();
		}
	},

	createMenu: function(aDataArr) {
		let pop = $.selfPop;
		pop.textContent = "";

		aDataArr.sort(function(a, b) {
			return a["label"].localeCompare(b["label"]);
		});

		let mBox = pop.appendChild($.$CNS("hbox"));
		let vBox;
		let vBox1 = mBox.appendChild($.$CNS("vbox"));
		let vBox2 = mBox.appendChild($.$CNS("vbox"));
		let vBox3 = mBox.appendChild($.$CNS("vbox"));
		let vBox4 = mBox.appendChild($.$CNS("vbox"));
		let vBox5 = mBox.appendChild($.$CNS("vbox"));
		let itemsPerCol = Math.ceil(aDataArr.length / $._pNumberOfColumns);
		var i = 0,
			iLen = aDataArr.length;
		for (; i < iLen; i++) {
			switch ($._pNumberOfColumns) {
				case 1:
					vBox = vBox1;
					break;
				case 2:
					vBox = (i < itemsPerCol) ?
						vBox1 : vBox2;
					break;
				case 3:
					vBox = (i < itemsPerCol) ?
						vBox1 : (i >= itemsPerCol && i < itemsPerCol * 2) ?
						vBox2 : vBox3;
					break;
				case 4:
					vBox = (i < itemsPerCol) ?
						vBox1 : (i >= itemsPerCol && i < itemsPerCol * 2) ?
						vBox2 : (i >= itemsPerCol * 2 && i < itemsPerCol * 3) ?
						vBox3 : vBox4;
					break;
				case 5:
					vBox = (i < itemsPerCol) ?
						vBox1 : (i >= itemsPerCol && i < itemsPerCol * 2) ?
						vBox2 : (i >= itemsPerCol * 2 && i < itemsPerCol * 3) ?
						vBox3 : (i >= itemsPerCol * 3 && i < itemsPerCol * 4) ?
						vBox4 : vBox5;
					break;
			}
			let img = aDataArr[i]["image"] || "";
			let itm = $.$CNS("menuitem", {
				id: (($._pUseAccelkeys && aDataArr[i]["accesskey"]) ?
					$._UID + "-menuitem-" + aDataArr[i]["accesskey"] :
					""),
				label: aDataArr[i]["label"] || "Name Me!!!",
				tooltiptext: aDataArr[i]["tooltiptext"] || "",
				image: ($._pUseImagesOnItems ?
					(img === "default" ?
						$.getHelp("%DefaultImage%") :
						img) :
					"") || "",
				accesskey: ($._pUseAccelkeys ? (aDataArr[i]["accesskey"] || "") : ""),
				"data-url": aDataArr[i]["data-url"] || "",
			});
			itm.classList.add($._UID + "-bookmark-items",
				($._pUseImagesOnItems ? "menuitem-iconic" : "menuitem-noniconic"));
			vBox.appendChild(itm);
		}
	},

	populateDynamicTooltip: function(aE, aN) {
		aN.textContent = "";

		if (aE.target.triggerNode.id === self.id) {
			var grid = aN.appendChild($.$CNS("grid"));
			var cols = grid.appendChild($.$CNS("columns"));
			cols.appendChild($.$CNS("column", {
				flex: 2
			}));
			cols.appendChild($.$CNS("column", {
				flex: 3
			}));
			cols.appendChild($.$CNS("column", {
				flex: 1
			}));
			var rows = grid.appendChild($.$CNS("rows"));
			// AppendRow
			let AR = function(aRowsNode, aLabel1, aLabel2, aLabel3, aEmpty) {
				let row = $.$CNS("row");
				if (aEmpty) {
					row.appendChild($.$CNS("label", {
						value: " ",
						width: 0,
					}));
				} else {
					row.appendChild($.$CNS("label", {
						value: aLabel1,
						style: "font-weight: bold;",
					}));
					row.appendChild($.$CNS("label", {
						value: aLabel2,
						style: "font-weight: bold;",
					}));
					let desc = $.$CNS("description", {
						maxwidth: 250,
					});
					desc.textContent = aLabel3; // Use of "textContent" to achieve word wrap. 
					row.appendChild(desc);
				}
				aRowsNode.appendChild(row);
			};

			let btn = aE.target.triggerNode;
			AR(rows, btn.name, "", "ID = " + btn.id.slice(20));
			AR(rows, null, null, null, true);
			AR(rows, "Left click", "", "Open/Close main menu");
			AR(rows, "Middle click or " + $.accel() + " + Left click", "", "Open Options menu");
			aN.appendChild($.$CNS("separator")); // Empty space
			let desc2 = $.$CNS("description", {
				style: "font-weight:bold;",
			});
			// Use of "textContent" to achieve word wrap. 
			desc2.textContent = "Read this button help for very important usage notes.\n" +
				"Right click on button, \"Button Help\" item.";
			aN.appendChild(desc2);
			if (self.menuModified && self.image === $.getHelp("%Siren16x16%")) {
				aN.appendChild($.$CNS("separator")); // Empty space
				let desc1 = $.$CNS("description", {
					style: "color:red;font-weight:bold;",
				});
				// Use of "textContent" to achieve word wrap. 
				desc1.textContent = "Remember to save the changes to the modified menu.\n" +
					"Open the \"Options\" menu and select the \"Save to main file\" item.";
				aN.appendChild(desc1);
			}
			return true;
		}
	},

	populatePositioningTooltip: function(aE, aN) {
		aN.textContent = "";
		let box = aN.appendChild($.$CNS("hbox"));
		let pos = aE.target.triggerNode.getAttribute("value");
		let isWide = /^end_/.test(pos) || /^start_/.test(pos);
		box.appendChild($.$CNS("spacer", {
			flex: 1
		}));
		box.appendChild($.$CNS("label", {
			value: "Preview",
			style: "font-weight:bold;",
		}));
		box.appendChild($.$CNS("spacer", {
			flex: 1
		}));
		aN.appendChild($.$CNS("image", {
			src: $.getHelp(pos),
			width: isWide ? 180 : 130,
			height: isWide ? 91 : 122,
		}));
	},

	populateOptionsMenu: function(aN) {
		aN.textContent = "";
		aN.appendChild($.$CNS("dialogheader", {
			title: "Options popup",
			tooltiptext: "Multi Snippets Inserter options popup.",
			style: "margin: 0 0 7px 0;padding: 2px 0 2px 5px;border:none;font-size:smaller;",
		}));
		aN.appendChild($.$CNS("menuitem", {
			label: "Add current tab",
			tooltiptext: "Create a new item from current tab.",
			_value: "add-from-tab",
		}));
		aN.appendChild($.$CNS("menuseparator"));
		aN.appendChild($.$CNS("menuitem", {
			label: "Save to main file",
			style: self.menuModified ? "color:red;font-weight:bold;" : "",
			tooltiptext: "Save all changes made to the main menu into the items file.",
			_value: "saveToFile",
		}));
		aN.appendChild($.$CNS("menuitem", {
			label: "Reload main file",
			tooltiptext: "Reload the items file.\n\nThis will discard any modification made to the main menu!!!",
			_value: "loadFromFile",
		}));
		aN.appendChild($.$CNS("menuitem", {
			label: "Edit items file",
			tooltiptext: "Edit the current items file in any text editor.",
			_value: "editFile",
		}));
		aN.appendChild($.$CNS("menuitem", {
			label: "Backup items file",
			tooltiptext: "Make a backup of the current loaded items file.",
			_value: "backup",
		}));
		aN.appendChild($.$CNS("menuitem", {
			label: "Open storage folder",
			tooltiptext: "Open the folder where all items files are stored.",
			_value: "open-storage",
		}));
		aN.appendChild($.$CNS("menuseparator"));
		aN.appendChild($.$CNS("menuitem", {
			label: "More options",
			tooltiptext: "Open " + self.name + " options window.",
			_value: "open-options",
		}));
	},

	handleOptionsPopup: function(aE) {
		switch (aE.target.getAttribute("_value")) {
			case "add-from-tab":
				$.createItemFromCurrentTab();
				break;
			case "saveToFile":
				$.saveToFile();
				break;
			case "loadFromFile":
				let cBox = custombuttons.confirmBox(self.name, "Are you sure that you want to " +
					"reload the main menu?\n" +
					"You will loose all changes made to the menu!!!\nOn the other hand, if you edited the " +
					"items file manually, the menu will be updated with those changes aplied.\n" +
					"This process is immediate and irreversible.\n\n" +
					"Click 'Yes' to proceed.\n" +
					"Click 'Cancel' to abort.\n", "Yes", "Cancel");
				if (cBox) {
					$.loadFromFile();
				} else
					return;
				break;
			case "editFile":
				$.bookFile.launch();
				break;
			case "backup":
				let dest = OS.Path.join($.storagePath, $.bookFileName + "-" +
					$._getTimeStamp(new Date().getTime()) + ".json");
				OS.File.copy($.bookFile.path, dest);
				break;
			case "import":
				$.importNewItems();
				break;
			case "open-storage":
				new $._nsIFile($.storagePath).reveal();
				break;
			case "open-options":
				$.openOptions();
				break;
		}
	},

	saveToFile: function() {
		let itemsArray = [];
		try {
			Array.prototype.slice.call($.selfPop.getElementsByTagName("menuitem")).forEach(function(aMI) {
				let img = aMI.getAttribute("image");
				if (img === $.getHelp("%DefaultImage%"))
					img = "default";
				itemsArray.push({
					label: aMI.getAttribute("label") || "Name me!!!",
					tooltiptext: aMI.getAttribute("tooltiptext") || "",
					image: ($._pUseImagesOnItems ? (img || "") : ""),
					accesskey: ($._pUseAccelkeys ? (aMI.getAttribute("accesskey") || "") : ""),
					"data-url": aMI.getAttribute("data-url") || "",
				});
			});
		} finally {
			let path = $.bookFile.path;
			OS.File.writeAtomic(path, JSON.stringify(itemsArray, null, "\t"), {
				tmpPath: path + ".tmp",
				encoding: "utf-8"
			}).then(function onSuccess() {
				$.selfDestroy();
			}, function onFailure(aRejectReason) {
				Cu.reportError([self.name + ": " + self.id, aRejectReason, aRejectReason.stack].join("\n"));
			});
		}
	},

	_getTimeStamp: function(aDate) {
		let ts;
		switch ($._pTimeStampForBackups) {
			case 0:
				ts = $.legalize($._pTimeStampForBackupsCustom); // Custom
				break;
			case 1:
				ts = "YYYY MM-DD hh.mm.ss"; // ISO8601
				break;
			case 2:
				ts = "YYYY DD.MM hh.mm.ss"; // European
				break;
		}
		let dte = new Date(parseInt(aDate));
		let YYYY = String(dte.getFullYear());
		let MM = String(dte.getMonth() + 1);
		if (MM.length === 1) {
			MM = "0" + MM;
		}
		let DD = String(dte.getDate());
		if (DD.length === 1)
			DD = "0" + DD;
		let hh = String(dte.getHours());
		if (hh.length === 1)
			hh = "0" + hh;
		let mm = String(dte.getMinutes());
		if (mm.length === 1)
			mm = "0" + mm;
		let ss = String(dte.getSeconds());
		if (ss.length === 1)
			ss = "0" + ss;
		ts = ts.replace("YYYY", YYYY);
		ts = ts.replace("MM", MM);
		ts = ts.replace("DD", DD);
		ts = ts.replace("hh", hh);
		ts = ts.replace("mm", mm);
		ts = ts.replace("ss", ss);
		return ts;
	},

	keypressHandler: function(aCond) {
		var tAPref = "accessibility.typeaheadfind",
			tAFBPref = "accessibility.typeaheadfind.autostart";
		var tmp1 = $.Branch + "-tmp1",
			tmp2 = $.Branch + "-tmp2";
		let gBP = Services.prefs.getBoolPref,
			sBP = Services.prefs.setBoolPref;
		if (aCond === "startListening") {
			self.open = true;
			window.removeEventListener("keypress", $.onKeyPress, true);
			setTimeout(function() {
				window.addEventListener("keypress", $.onKeyPress, true);
				($.appID("SM")) ?
				(sBP(tmp1, gBP(tAPref)) | sBP(tAPref, false) | sBP(tmp2, gBP(tAFBPref)) | sBP(tAFBPref, false)) :
				(sBP(tmp1, gBP(tAPref)) | sBP(tAPref, false));
				$.selfPop.focus();
			}, 100);
		} else if (aCond === "stopListening") {
			self.open = false;
			window.removeEventListener("keypress", $.onKeyPress, true);
			($.appID("SM")) ?
			((Services.prefs.getPrefType(tmp1) !== 0) && (sBP(tAPref, gBP(tmp1)) | Services.prefs.clearUserPref(tmp1)) | (sBP(tAFBPref, gBP(tmp2)) | Services.prefs.clearUserPref(tmp2))) :
			(Services.prefs.getPrefType(tmp1) !== 0) && (sBP(tAPref, gBP(tmp1)) | Services.prefs.clearUserPref(tmp1));
		}
	},

	onKeyPress: function(aE) {
		aE.preventDefault();
		var pressedKey = String.fromCharCode(aE.charCode);
		var panelItem = $.$($._UID + "-menuitem-" + pressedKey);
		if (!panelItem)
			return true;
		setTimeout(function() {
			$.openMyBookmarks(aE, panelItem);
			if ($._pHidePanelOnItemHotkeyPressed)
				$.accel(aE) || $.selfPop.hidePopup();
			else
				$.accel(aE) && $.selfPop.hidePopup();
		}, 50);
		aE.stopPropagation();
	},

	CSSLoader: function() {
		try {
			$.styleSheet(window[$._UID]["StyleSheet"], true);
			delete window[$._UID]["StyleSheet"];
		} catch (aErr) {}

		if (!$._pUseStylesheet)
			return true;

		let css;
		try {
			css = "@namespace url(" + xulns + ");" + $.getHelp("%StyleSheet%")
				.split("%U_I_D%").join($._UID)
				.replace(/%self_id%/g, self.id)
				.replace(/\n/g, "");
		} catch (aErr) {
			css = false;
		}
		if (!css)
			return;

		if (!window[$._UID]["StyleSheet"]) {
			window[$._UID]["StyleSheet"] = css;
			$.styleSheet(css);
		} else if (window[$._UID]["StyleSheet"] && window[$._UID]["StyleSheet"] !== css) {
			$.styleSheet(window[$._UID]["StyleSheet"], true);
			window[$._UID]["StyleSheet"] = css;
			$.styleSheet(css);
		}
	},

	styleSheet: function(aCSS, aRemFlag) {
		var sss = Cc["@mozilla.org/content/style-sheet-service;1"].getService(Ci.nsIStyleSheetService);
		var uri = Services.io.newURI("data:text/css," + encodeURIComponent(aCSS), null, null);
		if (sss.sheetRegistered(uri, sss.USER_SHEET))
			sss.unregisterSheet(uri, sss.USER_SHEET);
		if (aRemFlag)
			return;
		sss.loadAndRegisterSheet(uri, sss.USER_SHEET);
	},

	openMyBookmarks: function(aE, aKeyPressedTarget) {
		if (aE.button === 2)
			return true;
		aE.preventDefault();
		var URL;
		var urlOverwrite = $._pDrasticTabOverwrite ?
			(gBrowser.selectedBrowser.currentURI.schemeIs("about") ||
				gBrowser.selectedBrowser.currentURI.schemeIs("javascript")) :
			$.getOverrideURLs();
		if ("charCode" in aE) {
			URL = aKeyPressedTarget.getAttribute("data-url");
			if (!URL)
				return true;
			if (urlOverwrite && !gBrowser.selectedBrowser.webProgress.isLoadingDocument)
				gBrowser.loadURI(URL);
			else
				$._pTabsInBackground ?
				gBrowser.addTab(URL, null, null, null, false, false) :
				(gBrowser.selectedTab = gBrowser.addTab(URL, null, null, null, false, false));
		} else {
			URL = aE.target.getAttribute("data-url");
			if (!URL)
				return true;
			if ((!aE.shiftKey && !$.accel(aE) && !aE.altKey && aE.button === 0) ||
				(!aE.shiftKey && $.accel(aE) && !aE.altKey && aE.button === 0)) { // Left Click
				if (urlOverwrite && !gBrowser.selectedBrowser.webProgress.isLoadingDocument)
					gBrowser.loadURI(URL);
				else
					$._pTabsInBackground ?
					gBrowser.addTab(URL, null, null, null, false, false) :
					(gBrowser.selectedTab = gBrowser.addTab(URL, null, null, null, false, false));
			} else if (!aE.shiftKey && !$.accel(aE) && !aE.altKey && aE.button === 1) { // Middle Click or Ctrl/Cmd + Left Click
				if (urlOverwrite && !gBrowser.selectedBrowser.webProgress.isLoadingDocument)
					gBrowser.loadURI(URL);
				else
					$._pTabsInBackground ?
					(gBrowser.selectedTab = gBrowser.addTab(URL, null, null, null, false, false)) :
					gBrowser.addTab(URL, null, null, null, false, false);
			}
		}
		aE.stopPropagation();
	},

	legalize: function(aStr) { // Removes illegal characters in file names.
		return aStr.replace(/[\/:\"\*\?<>\|\\]/g, "");
	},

	getHelp: function(aPlaceholder) { // Get specific data, surrounded by "placeholders", from the Help tab.
		return $.$(self.id).getAttribute("Help").split("<!--" + aPlaceholder + "-->")[1];
	},

	appID: function(aId) { // App ID boolean return.
		let app = {
			"SM": "{92650c4d-4b8e-4d2a-b7eb-24ecf4f6b63a}",
			"FX": "{ec8030f7-c20a-464f-9b0e-13a3a9e97384}",
			"TB": "{3550f703-e582-4d05-9a08-453d09bdfdc6}"
		};
		return (Services.appinfo.ID === app[aId]);
	},

	$: function(aId) {
		return document.getElementById(aId);
	},

	getOverrideURLs: function() { // Boolean return of URLs/Hostsnames to override
		let URLList = $._pOverrideURLs.replace(/\s+/gm, "").split(",").filter(aSize =>
			aSize !== "");
		if (URLList) {
			for (let url in URLList) {
				if (URLList[url].length > 0) {
					if (gBrowser.currentURI.spec === URLList[url])
						return true;
					else
						continue;
				}
			}
		}
		let hostList = $._pOverrideHosts.replace(/\s+/gm, "").split(",").filter(aSize =>
			aSize !== "");
		if (hostList) {
			for (let name in hostList) {
				if (hostList[name].length > 0) {
					if (gBrowser.currentURI.host === hostList[name])
						return true;
					else
						continue;
				}
			}
		}
		return false;
	},

	togglePopup: function() {
		if ($._pUnanchoredPanel)
			$.openMenuAtScreen();
		else {
			let panel = $.selfPop;
			panel.state === "open" ?
				panel.hidePopup() :
				panel.openPopup(self, $._pPopupPosition, 0, 0, false, null, null);
		}
	},

	selfDestroy: function() {
		setTimeout(function() {
			self.destroy("update");
			setTimeout(function() {
				self.init();
			}, 100);
		}, 100);
	},

	openOptions: function() {
		var em = Services.ww.getWindowEnumerator();
		var winName = "speed_dial_settings_window_" + self.id;
		var index = 1;
		while (em.hasMoreElements()) {
			let win = em.getNext();
			if (win.name === winName) {
				win.focus();
				return;
			}
			index++;
		}
		let xul = $.getHelp("%OptionsWindow%");
		let storage = {
			button: self
		};
		xul = xul.replace(/>\s+</g, "><");
		xul = xul.split("%S_E_L_F_I_D%").join(self.id);
		xul = xul.split("%B_R_A_N_C_H%").join($.Branch);
		let url = "data:application/vnd.mozilla.xul+xml;text/plain," + encodeURIComponent(xul);
		let win = openDialog(url, winName, "chrome, resizable, dependant, dialog, centerscreen, close, alwaysRaised");
		win._storage = storage;
	},

	setBtnImage: function() {
		let mMod = self.menuModified; // Stored so I can use the original value twice.
		if (!mMod)
			self.menuModified = true;
		if (!mMod) {
			$.notifyme(self.name,
				"Remember to save the changes made to the main menu.",
				$.getHelp("%Siren48x48%"),
				$.alertCallback);
		}
		self.image = $.getHelp("%Siren16x16%");
	},

	notifyme: function(aTitle, aMsg, aImg, aCallback) { // Notification function
		let origin = (Services.prefs.getPrefType("ui.alertNotificationOrigin") !== 0) ?
			Services.prefs.getIntPref("ui.alertNotificationOrigin") :
			null;
		let win = Services.ww.openWindow(null, "chrome://global/content/alerts/alert.xul",
			"_blank", "chrome,titlebar=no,popup=yes", null);
		win.arguments = [aImg, aTitle, aMsg, !!aCallback, "", origin, "", "", null, (!!aCallback ? aCallback : null)];
	},

	alertCallback: { // Thanks to srazzano!!!
		observe: function(aSubject, aTopic, aData) {
			let alt = Services.wm.getMostRecentWindow("alert:alert");
			if (aTopic === "alertshow") {
				var altH = alt.outerHeight,
					altW = alt.outerWidth,
					scrH = screen.availHeight,
					scrW = screen.availWidth;
				let a = (scrW / 2) - (altW / 2);
				let b = (scrH / 2) - (altH / 2);
				alt.moveTo(a, b);
				setTimeout(function() {
					alt.close();
				}, 3000);
			}
			if (aTopic === "alertclickcallback") {
				alt.close();
			}
		}
	},

	img_to_b64_cback: function(aURL, aCallback) {
		if (!aURL)
			aCallback(false);
		else {
			let xhr = new XMLHttpRequest(),
				nsIMIMEService = Cc["@mozilla.org/mime;1"].getService(Ci.nsIMIMEService);
			try {
				xhr.open("GET", aURL, true);
			} catch (aErr) {
				aCallback(false);
				return true;
			}
			xhr.responseType = "arraybuffer";
			xhr.onload = function() {
				let mediaType,
					dataURI,
					binary,
					bytes;
				bytes = new Uint8Array(xhr.response);
				binary = [].map.call(bytes, function(aByte) {
					return String.fromCharCode(aByte);
				}).join("");
				mediaType = xhr.getResponseHeader("Content-Type");
				let base64 = btoa(binary);
				if (mediaType.indexOf("image/") !== 0) {
					try {
						mediaType = nsIMIMEService.getTypeFromURI(Services.io.newURI(aURL, null, null));
					} catch (aError) {
						mediaType = "image/png";
					}
				}
				dataURI = "data:" + mediaType + ";base64," + base64;
				aCallback(dataURI);
			};
			xhr.onerror = function() {
				aCallback(false);
			};
			xhr.send(null);
		}
	},

	/**
	 * NEVER CHANGE TO "BRANCH OBSERVER"!!!!
	 */
	HandlePrefObserver: function(aRem) {
		var prefs = $.DefPrefs;
		for (let key in prefs) {
			let pref = $.Branch + key;
			if (prefs.hasOwnProperty(key)) {
				if (aRem)
					Services.prefs.removeObserver(pref, $.prefObserver);
				else
					Services.prefs.addObserver(pref, $.prefObserver, false);
			}
		}
	},

	prefObserver: {
		observe: function(aSubject, aTopic, aPStr) {
			if (aTopic === "nsPref:changed") {
				$["_" + aPStr] = $.gPref(aPStr);
			}
		}
	},

	$CNS: function(aElName, aAttrs, aNS) { // Creates elements
		// Holy crap!!! createElementNS is absolute "sheet"!!
		// "key" elements are useless for one!! 
		var el = aNS ?
			document.createElementNS(aNS, aElName) :
			document.createElement(aElName);
		if (aAttrs)
			Object.keys(aAttrs).forEach(function(n) {
				if (aAttrs[n] !== "")
					el.setAttribute(n, aAttrs[n]);
			});
		return el;
	},

	accel: function(aE) {
		let isOSX = Services.appinfo.OS.toLowerCase().startsWith("darwin");
		if (aE)
			return isOSX ? aE.metaKey : aE.ctrlKey;
		return isOSX ? "Command" : "Control";
	},

