var $ = {
	onLoad: function() {
		isNew || this.populateFields();
		this.setDynamicLabels();
		this.label_tbox.focus();
		this.setImagePreview();
		window.addEventListener("focus", this, false);
		window.addEventListener("blur", this, false);
		window.document.documentElement.setAttribute("title", isNew ?
			"Add new bookmark - Button #ID: " + cb_btn.id.slice(20) :
			"Edit bookmark - Button #ID: " + cb_btn.id.slice(20));
		this.handleToBase64Btn();
	},
	handleEvent: function(aE) {
		switch (aE.type) {
			case "focus":
				cb_btn.style.setProperty("outline", "4px groove green", "important");
				cb_btn.style.setProperty("outline-offset", "-3px", "important");
				break;
			case "blur":
				cb_btn.style.setProperty("outline", "2px dotted green", "important");
				cb_btn.style.setProperty("outline-offset", "-3px", "important");
				break;
		}
	},
	onUnLoad: function() {
		cb_btn.style.removeProperty("outline", "4px groove green", "important");
		cb_btn.style.removeProperty("outline-offset", "-3px", "important");
		cb_btn._storage = {};
	},
	onAccept: function() {
		if (isNew) {
			let itm = this.$CNS("menuitem", {
				"label": this.label_tbox.value,
				"tooltiptext": this.tooltip_tbox.value,
				"data-url": this.url_tbox.value,
				"image": this.image_tbox.value,
				"accesskey": this.accesskey_tbox.value,
			});
			itm.classList.add(cb_btn.SpeedDial._UID + "-bookmark-items",
				(cb_btn.SpeedDial.gPref("pUseImagesOnItems") ? "menuitem-iconic" : "menuitem-noniconic"));
			node.parentNode.appendChild(itm);
			this.setBtnImage();
		} else
			this.saveAttributes();
	},
	setDynamicLabels: function() {
		this.accept_button.label = isNew ? "Add new bookmark" : "Save bookmark";
	},
	$CNS: function(aElName, aAttrs, aNS) {
		var el = document.createElement(aElName); // createElementNS not working here.
		if (aAttrs)
			Object.keys(aAttrs).forEach(function(n) {
				if (aAttrs[n] !== "")
					el.setAttribute(n, aAttrs[n])
			});
		return el;
	},
	img_to_b64: function() {
		let imgURL = this.image_tbox.value;
		if (imgURL.search(/^data:image/) === 0 || imgURL === "")
			return true;
		cb_btn.SpeedDial.img_to_b64_cback(imgURL, function(aImgData) {
			aImgData && ($.image_tbox.value = aImgData);
		});
		setTimeout(function() {
			$.handleToBase64Btn();
		}, 100);
	},
	saveAttributes: function() {
		this.label_tbox.value !== "" ?
			node.setAttribute("label", this.label_tbox.value) :
			node.setAttribute("label", "Name me!!!");
		this.tooltip_tbox.value !== "" ?
			node.setAttribute("tooltiptext", this.tooltip_tbox.value) :
			node.setAttribute("tooltiptext", "");
		this.url_tbox.value !== "" ?
			node.setAttribute("data-url", this.url_tbox.value) :
			node.setAttribute("data-url", "");
		this.image_tbox.value !== "" ?
			node.setAttribute("image", this.image_tbox.value) :
			node.removeAttribute("image");
		this.accesskey_tbox.value !== "" ?
			node.setAttribute("accesskey", this.accesskey_tbox.value) :
			node.removeAttribute("accesskey");
		this.setBtnImage();
	},
	setBtnImage: function() {
		window.setTimeout(function() {
			cb_btn.SpeedDial.setBtnImage();
			close();
		}, 200);
	},
	populateFields: function() {
		this.label_tbox.value = node.getAttribute("label") || "";
		this.tooltip_tbox.value = node.getAttribute("tooltiptext") || "";
		this.url_tbox.value = node.getAttribute("data-url") || "";
		this.image_tbox.value = node.getAttribute("image") || "";
		this.accesskey_tbox.value = node.getAttribute("accesskey") || "";
	},
	handleToBase64Btn: function() {
		if (this.image_tbox.value === "" || this.image_tbox.value.search(/^data:image/) === 0)
			this.to_base64_btn.disabled = true;
		else
			this.to_base64_btn.disabled = false;
	},
	setImagePreview: function() {
		this.image_preview.src = this.image_tbox.value;
		this.handleToBase64Btn();
	},
	selectLocalImage: function() {
		var fp = Cc["@mozilla.org/filepicker;1"].createInstance(Ci.nsIFilePicker);
		fp.init(window, "Select an image file", 0);
		fp.appendFilters(fp.filterImages);
		var res = fp.show();
		if (res == fp.returnOK) {
			this.image_tbox.value = fp.fileURL.spec;
			this.setImagePreview();
		}
	},
	get label_tbox() {
		return $id("label_tbox");
	},
	get tooltip_tbox() {
		return $id("tooltip_tbox");
	},
	get url_tbox() {
		return $id("url_tbox");
	},
	get image_tbox() {
		return $id("image_tbox");
	},
	get accesskey_tbox() {
		return $id("accesskey_tbox");
	},
	get to_base64_btn() {
		return $id("to_base64_btn");
	},
	get image_preview() {
		return $id("image_preview");
	},
	get accept_button() {
		return $id("accept_button");
	},
};
]]></script>
	<keyset>
		<key keycode="VK_ESCAPE" oncommand="close();"/>
	</keyset>
