			<grid>
				<columns>
					<column />
					<column flex="1"/>
				</columns>
				<rows>
					<row align="center">
						<label value="Label"/>
						<textbox id="label_tbox" flex="1" class="required" placeholder="A name for the item."/>
					</row>
					<row align="center">
						<label value="Tooltip"/>
						<textbox id="tooltip_tbox" flex="1" class="optional" placeholder="A tooltip for the item."/>
					</row>
					<row align="center">
						<label value="URL"/>
						<textbox id="url_tbox" flex="1" class="required" placeholder="The URL."/>
					</row>
					<row align="center">
						<label value="Image"/>
						<hbox align="center">
							<textbox id="image_tbox" oninput="$.setImagePreview();" flex="1" class="optional" placeholder="A link to an image."/>
							<image id="image_preview" width="16" height="16" />
							<button label="Browse..." align="left" oncommand="$.selectLocalImage();"/>
							<button id="to_base64_btn" label="to Base64" align="left" oncommand="$.img_to_b64();"/>
						</hbox>
					</row>
					<row align="center">
						<label value="Accesskey"/>
						<textbox id="accesskey_tbox" flex="1" maxlength="1" class="optional" placeholder="Any alpha-numeric character."/>
					</row>
				</rows>
			</grid>
		</groupbox>
