this.SpeedDial.Branch = "CB.SpeedDial.Id" + self.id.slice(20) + ".";

this.SpeedDial.DefPrefs = {
	pDrasticTabOverwrite: false,
	pFlipAttribute: true,
	pHidePanelOnItemHotkeyPressed: true,
	pMouseoverDelay: 200,
	pNumberOfColumns: 4,
	pOpenOnMouseover: false,
	pOverrideHosts: "",
	pOverrideURLs: "about:blank,about:newtab,about:home",
	pShowDropmarker: true,
	pTabsInBackground: false,
	pUseImagesOnItems: true,
	pUseAccelkeys: true,
	pConvertToPanel: true,
	pUseStylesheet: true,
	pUnanchoredPanel: true,
	pPopupPosition: "after_start",
	pTimeStampForBackups: 1,
	pTimeStampForBackupsCustom: "YYYY-MM-DD hh.mm.ss",
};

this.SpeedDial.InitPrefs = function() {
	var branch = Services.prefs.getDefaultBranch($.Branch),
		defaults = self.SpeedDial.DefPrefs;
	for (var key in defaults) {
		if (defaults.hasOwnProperty(key)) {
			var val = defaults[key];
			switch (typeof val) {
				case "boolean":
					branch.setBoolPref(key, val);
					break;
				case "number":
					branch.setIntPref(key, val);
					break;
				case "string":
					var str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
					str.data = val;
					branch.setComplexValue((key),
						Ci.nsISupportsString, str);
					break;
			}
		}
	}
};

this.SpeedDial.gPref = function(aKey) {
	var branch = Services.prefs.getBranch($.Branch);
	switch (typeof self.SpeedDial.DefPrefs[aKey]) {
		case "boolean":
			return branch.getBoolPref(aKey);
		case "number":
			return branch.getIntPref(aKey);
		case "string":
			return branch.getComplexValue(aKey, Ci.nsISupportsString).data;
	}
	return null;
};

this.SpeedDial.sPref = function(aKey, aVal) {
	var branch = Services.prefs.getBranch($.Branch);
	switch (typeof aVal) {
		case "boolean":
			branch.setBoolPref(aKey, aVal);
			break;
		case "number":
			branch.setIntPref(aKey, aVal);
			break;
		case "string":
			var str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
			str.data = aVal;
			branch.setComplexValue((aKey), Ci.nsISupportsString, str);
			break;
	}
};

this.SpeedDial.Init();

custombutton.help:
𝗜𝗺𝗽𝗼𝗿𝘁𝗮𝗻𝘁 𝗻𝗼𝘁𝗲𝘀
⚫ Create periodic backups of your items file. Open the options menu and click 𝗕𝗮𝗰𝗸𝘂𝗽 𝗶𝘁𝗲𝗺𝘀 𝗳𝗶𝗹𝗲.
⚫ The image for this button can be changed by editing the variable called 𝗯𝘂𝘁𝘁𝗼𝗻𝗜𝗺𝗮𝗴𝗲 inside the Initialization tab. This is not a whim, there is a "programmatic reason" to use that variable and not the usual place to set a CB button image.
⚫ If the button image changes to a siren, it means that you have made some changes to the main menu and they need to be saved. Just open the options menu and click 𝗦𝗮𝘃𝗲 𝘁𝗼 𝗺𝗮𝗶𝗻 𝗳𝗶𝗹𝗲.

𝗖𝗹𝗶𝗰𝗸 𝗼𝗻 𝗶𝘁𝗲𝗺𝘀
𝗟𝗲𝗳𝘁 𝗰𝗹𝗶𝗰𝗸: Open bookmark on new tab.
𝗠𝗶𝗱𝗱𝗹𝗲 𝗰𝗹𝗶𝗰𝗸 𝗼𝗿 𝗖𝘁𝗿𝗹/𝗖𝗺𝗱 + 𝗟𝗲𝗳𝘁 𝗰𝗹𝗶𝗰𝗸: Open bookmark on new tab without closing menu.
𝗥𝗶𝗴𝗵𝘁 𝗰𝗹𝗶𝗰𝗸: Context menu that allows to Add/Edit/Delete items.

𝗔𝗯𝗼𝘂𝘁 𝘀𝗵𝗼𝗿𝘁𝗰𝘂𝘁 𝗸𝗲𝘆𝘀
⚫ Only 1 digit characters (0-9, a-z, A-Z or any other character that can be entered with a keyboard).
⚫ Case sensitive. This means that "a" and "A" are two different shortcut keys.
⚫ There can not be 2 identical shortcut keys. Doh!
⚫ Shortcut keys will be reflected in the item's label.
⚫ 𝗠𝗼𝗿𝗲 𝗶𝗻𝗳𝗼: https://developer.mozilla.org/en-US/docs/Mozilla/Tech/XUL/Attribute/accesskey

