				"tooltiptext": this.tooltip_tbox.value,
				"data-url": this.url_tbox.value,
				"image": this.image_tbox.value,
				"accesskey": this.accesskey_tbox.value,
			});
			itm.classList.add(cb_btn.SpeedDial._UID + "-bookmark-items",
				(cb_btn.SpeedDial.gPref("pUseImagesOnItems") ? "menuitem-iconic" : "menuitem-noniconic"));
			node.parentNode.appendChild(itm);
			this.setBtnImage();
		} else
			this.saveAttributes();
	},
	setDynamicLabels: function() {
		this.accept_button.label = isNew ? "Add new bookmark" : "Save bookmark";
	},
	$CNS: function(aElName, aAttrs, aNS) {
		var el = document.createElement(aElName); // createElementNS not working here.
		if (aAttrs)
			Object.keys(aAttrs).forEach(function(n) {
				if (aAttrs[n] !== "")
					el.setAttribute(n, aAttrs[n])
			});
		return el;
	},
	img_to_b64: function() {
		let imgURL = this.image_tbox.value;
		if (imgURL.search(/^data:image/) === 0 || imgURL === "")
			return true;
		cb_btn.SpeedDial.img_to_b64_cback(imgURL, function(aImgData) {
			aImgData && ($.image_tbox.value = aImgData);
		});
		setTimeout(function() {
			$.handleToBase64Btn();
		}, 100);
	},
	saveAttributes: function() {
		this.label_tbox.value !== "" ?
			node.setAttribute("label", this.label_tbox.value) :
			node.setAttribute("label", "Name me!!!");
