			node.setAttribute("tooltiptext", this.tooltip_tbox.value) :
			node.setAttribute("tooltiptext", "");
		this.url_tbox.value !== "" ?
			node.setAttribute("data-url", this.url_tbox.value) :
			node.setAttribute("data-url", "");
		this.image_tbox.value !== "" ?
			node.setAttribute("image", this.image_tbox.value) :
			node.removeAttribute("image");
		this.accesskey_tbox.value !== "" ?
			node.setAttribute("accesskey", this.accesskey_tbox.value) :
			node.removeAttribute("accesskey");
		this.setBtnImage();
	},
