this.tooltipText = "Catch me if you can!"; this.isJumping = false; this.tries = 0;
function getRndNode(aNodeList) { var idx = Math.floor(Math.random() * aNodeList.length); var node = aNodeList[idx]; if (isHiddenNode(node)) { return getRndNode(aNodeList); } else { return node; } }
function isHiddenNode(aNode) { return aNode.hidden || aNode.collapsed || aNode.hasAttribute("inactive") || (getComputedStyle(aNode, null). getPropertyValue("visibility") == "collapsed") || (getComputedStyle(aNode, null). getPropertyValue("display") == "none"); }
this.stopJumping = function(aGaveUp) { this.isJumping = false; if (aGaveUp) { this.image = this.win; custombuttons.alertBox("Mr. Bunny WINS!", "You just got pwned!"); this.winner = true; this.image = this.anim; } else { custombuttons.alertBox("Congratulations!", "You catched Mr. Bunny in " + ((this.tries == 1) ? (this.tries + " try.") : (this.tries + " tries."))); this.image = this.stat; this.winner = false; } this.tooltipText = "Let's play again."; this.tries = 0; }
this.jump = function() { var toolbox = document.getElementById("navigator-toolbox"); var toolbars = toolbox.getElementsByTagName("toolbar"); var toolbar = getRndNode(toolbars); var children = toolbar.children; var node = getRndNode(children);
if (node != this) { if ((node != this.nextSibling) && !isHiddenNode(node)) { toolbar.insertBefore(this, node); } else { toolbar.appendChild(this); } if ((this.tries % 25 == 0) && (this.tries > 0)) { var giveup = custombuttons.confirmBox(this.label, "You have tried " + this.tries + " times.\n" + "Give up now?", "Yes", "No"); if (giveup) { return this.stopJumping(true); } } } else { if (!this.isJumping && (tries <= 1)) { this.tries = 0; return this.jump(); } return this.stopJumping(); } this.isJumping = true; }
this.jump();
this.onmouseover = function() { this.image = (this.isJumping || this.winner) ? this.image : this.anim; }
this.onmouseout = function() { this.image = (this.isJumping || this.winner) ? this.anim : this.stat; }
