get updateURL() { return this.baseURL + encodeURIComponent(self.name.replace(/(\/|:)/g, "-")) + ".xml"; },
openUpdateURL: function updater_openUpdateURL() { switchToTabHavingURI(this.updateURL); },
get stdIcon() { var stdIcon = parseInt(self.cbStdIcon.match(/\d/)); switch (stdIcon) { case 4: return "chrome://custombuttons/skin/stdicons/bbutton.png"; case 3: return "chrome://custombuttons/skin/stdicons/gbutton.png"; case 2: return "chrome://custombuttons/skin/stdicons/rbutton.png"; default: return "chrome://custombuttons/skin/button.png"; } },
get bsyIcon() { switch (Application.id) { case "{ec8030f7-c20a-464f-9b0e-13a3a9e97384}": if (Application.version >= "4") { return "chrome://browser/skin/tabbrowser/connecting.png"; } else { return "chrome://global/skin/icons/loading_16.png"; } case "{92650c4d-4b8e-4d2a-b7eb-24ecf4f6b63a}": return "chrome://communicator/skin/icons/loading.gif"; default: return "chrome://custombuttons/skin/button.png"; } },
// Check if the URI is Custom Button URI (custombutton://...) isValidCbURI: function updater_isValidCbURI(aURL) { if (!aURL) return false; return /^custombutton\:\/\//.test(aURL); },
