custombutton.initcode:
var $ = this.CBBackupAllButtons = {
	Init: function() {
		self.type = "menu";
		// If button is located inside App Button or in Vertical Toolbar Addon
		// Put it right here due to those sheets of Palemoon and SeaMonkey.
		if (self.parentNode && (self.parentNode.id.match("PanelUI") ||
				self.parentNode.id.match("vertical-") ||
				self.parentNode.id === "aios-toolbar")) {
			self.orient = "vertical";
		} else
			self.orient = "horizontal";

		if (!("OS" in window))
			Cu.import("resource://gre/modules/osfile.jsm");
		if (!("Services" in window))
			Cu.import("resource://gre/modules/Services.jsm");

		$.InitPrefs();

		if (!($._UID in window)) {
			window[$._UID] = {
				StartupBackupDone: false,
				Initialized: false,
				SavePrefFile: false,
				/**
				 * Have to store all timeOuts/intervals in window because infinite initialization on SeaMonhey!!!!
				 */
				_autoBackTimer: null,
				_startupBackTimer: null,
				_autoBackCheck: null,
			};
		}

		self.setAttribute("popup", $._UID + "_CBBackupAllButtonsPopup");

		for (let p in $.DefPrefs) {
			if ($.DefPrefs.hasOwnProperty(p))
				$["_" + p] = $.gPref(p);
		}

		if (!window[$._UID]["Initialized"]) {
			window[$._UID]["Initialized"] = true;
			$.HandlePrefObserver();
		}

		$.CreateMainMenu();
		$.HandleScheduledBackups();
		$.HandleStartupBack();

		if ($._pShowDetailedTooltip) {
			self.tooltipText = "";
			self.setAttribute("tooltip", $._UID + "_dynamic_tooltip");
		} else {
			self.removeAttribute("tooltip");
			self.tooltipText = self.name;
		}

		(function dropmarkerVisibility() {
			if (self.parentNode && (!self.parentNode.id.match("PanelUI") || !self.parentNode.id.match("vertical-"))) {
				let dM = document.getAnonymousElementByAttribute(self, "class", "toolbarbutton-menu-dropmarker");
				if (dM)
					dM.setAttribute("hidden", !$._pShowDropmarker);
				else
					setTimeout(function() {
						dropmarkerVisibility();
					}, 100);
			}
		})();

		addDestructor(function(aReason) {
			if (aReason === "update" || aReason === "delete") {
				let selfEls = document.getElementsByAttribute("cb_id", $._UID);
				Array.prototype.slice.call(selfEls).forEach(function(aEl) {
					aEl && aEl.parentNode.removeChild(aEl);
				});
				clearTimeout(window[$._UID]["_autoBackTimer"]);
				clearTimeout(window[$._UID]["_startupBackTimer"]);
				clearInterval(window[$._UID]["_autoBackCheck"]);
			}
			if (aReason === "delete") {
				(Services.prefs.getBranch($.Branch)).deleteBranch("");
				delete window[$._UID];
				Services.prefs.savePrefFile(null);
			}
		});
	},

	TogglePopup: function() {
		let pop = $.mPop;
		if (pop) {
			if (pop.state === "open")
				pop.hidePopup();
			else
				pop.openPopup(self, "after_start", 0, 0, false, false, null);
		}
	},

	HandleStartupBack: function() {
		clearTimeout(window[$._UID]["_startupBackTimer"]);
		if ($._pStartupBUType === 0 || window[$._UID]["StartupBackupDone"])
			return true;
		let curTime = parseInt($._curTime, 10);
		if (!window[$._UID]["StartupBackupDone"] &&
			$._pStartupBUElapsedHours !== 0 &&
			$._pStartupBULastTime !== 0) {
			if ((curTime - $._pStartupBULastTime) < $._pStartupBUElapsedHours)
				return true;
		}
		window[$._UID]["_startupBackTimer"] = setTimeout(function() {
			switch ($._pStartupBUType) {
				case 1:
					$.HandleOverlay("HTML", "pStartupBUExpLoc", $._pStartupBUZipFiles);
					break;
				case 2:
					$.HandleOverlay("XML", "pStartupBUExpLoc", $._pStartupBUZipFiles);
					break;
				case 3:
					$.HandleOverlay("Overlay", "pStartupBUExpLoc", $._pStartupBUZipFiles);
					break;
			}
			$.sPref("pStartupBULastTime", curTime);
			window[$._UID]["StartupBackupDone"] = true;
		}, $._pStartupBUDelay * 1000);
	},

	HandleScheduledBackups: function(aRem) {
		clearTimeout(window[$._UID]["_autoBackTimer"]);
		if (aRem)
			return true;
		let tmp;
		let oneDay = 24 * 60 * 60 * 1000;
		let today = new Date().getDay();
		if ($._pAutoBUType === 0)
			return true;
		if (!$.HandleAutoBackDays(today, true)) {
			clearInterval(window[$._UID]["_autoBackCheck"]);
			window[$._UID]["_autoBackCheck"] = setInterval(function() {
				$.HandleScheduledBackups();
			}, 300000);
			return true;
		}
		let now = new Date().getTime();
		let autoTime = new Date();
		autoTime.setSeconds(0);
		let waitTime = 0;
		autoTime.setHours($._pAutoBUHours);
		autoTime.setMinutes($._pAutoBUMinutes);
		waitTime = autoTime.getTime() - now;
		if (waitTime <= 0) {
			tmp = autoTime.getTime();
			tmp += oneDay;
			autoTime.setTime(tmp);
			waitTime = autoTime.getTime() - now;
		}
		window[$._UID]["_autoBackTimer"] = setTimeout(function() {
			switch ($._pAutoBUType) {
				case 1:
					$.HandleOverlay("HTML", "pAutoBUExpLoc", $._pAutoBUZipFiles);
					break;
				case 2:
					$.HandleOverlay("XML", "pAutoBUExpLoc", $._pAutoBUZipFiles);
					break;
				case 3:
					$.HandleOverlay("Overlay", "pAutoBUExpLoc", $._pAutoBUZipFiles);
					break;
			}
		}, waitTime);
	},

	populateDynamicTooltip: function(aE, aN) {
		aN.textContent = "";

		if (aE.target.triggerNode.id === self.id) {
			var grid = aN.appendChild($.$CNS("grid"));
			var cols = grid.appendChild($.$CNS("columns"));
			cols.appendChild($.$CNS("column", {
				flex: 2
			}));
			cols.appendChild($.$CNS("column", {
				flex: 3
			}));
			cols.appendChild($.$CNS("column", {
				flex: 1
			}));
			var rows = grid.appendChild($.$CNS("rows"));
			// AppendRow
			let AR = function(aRowsNode, aLabel1, aLabel2, aLabel3, aEmpty) {
				let row = $.$CNS("row");
				if (aEmpty) {
					row.appendChild($.$CNS("label", {
						value: " ",
						width: 0,
					}));
				} else {
					row.appendChild($.$CNS("label", {
						value: aLabel1,
						style: "font-weight: bold;",
					}));
					row.appendChild($.$CNS("label", {
						value: aLabel2,
						style: "font-weight: bold;",
					}));
					let desc = $.$CNS("description", {
						maxwidth: 250,
					});
					desc.textContent = aLabel3; // Use of "textContent" to achieve word wrap. 
					row.appendChild(desc);
				}
				aRowsNode.appendChild(row);
			};

			let btn = aE.target.triggerNode;
			AR(rows, btn.name, "", "ID = " + btn.id.slice(20));
			aN.appendChild($.$CNS("separator")); // Empty space
			aN.appendChild($.$CNS("label", {
				value: "Scheduled backups location:",
				style: "font-weight: bold;",
			}));
			let desc1 = $.$CNS("description");
			desc1.textContent = ($._pAutoBUExpLoc === "" ? $._defaultExpLoc : $._pAutoBUExpLoc);
			aN.appendChild(desc1);
			aN.appendChild($.$CNS("separator")); // Empty space
			aN.appendChild($.$CNS("label", {
				value: "Startup backups location:",
				style: "font-weight: bold;",
			}));
			let desc2 = $.$CNS("description");
			desc2.textContent = ($._pStartupBUExpLoc === "" ? $._defaultExpLoc : $._pStartupBUExpLoc);
			aN.appendChild(desc2);
			aN.appendChild($.$CNS("separator")); // Empty space
			aN.appendChild($.$CNS("label", {
				value: "Manual backups location:",
				style: "font-weight: bold;",
			}));
			let desc3 = $.$CNS("description");
			desc3.textContent = ($._pManualBUExpLoc === "" ? $._defaultExpLoc : $._pManualBUExpLoc);
			aN.appendChild(desc3);
			aN.appendChild($.$CNS("separator")); // Empty space
			let desc4 = $.$CNS("description", {
				style: "font-weight:bold;",
			});
			desc4.textContent = "Read this button help for very important usage notes.\n" +
				"Right click on button, \"Button Help\" item.";
			aN.appendChild(desc4);
			return true;
		}
		return true;
	},

	onMainMenuPopupHiding: function(aE) {
		if (window[$._UID]["SavePrefFile"]) {
			window[$._UID]["SavePrefFile"] = false;
			Services.prefs.savePrefFile(null);
			$.selfDestroy(); // Can you believe this SHEET?!?!?!!?
		}
		aE.stopPropagation();
	},

	CreateMainMenu: function() {
		var pSet = $.$("mainPopupSet") || $.$($._UID + "_popupset");
		let that = "document.getElementById('" + self.id + "').CBBackupAllButtons";
		if (!pSet) {
			try {
				pSet = $.$("msgcomposeWindow").appendChild($.$CNS("popupset", {
					id: $._UID + "_popupset",
					cb_id: $._UID
				}));
			} catch (aErr) {}
		}
		if (pSet && !$.dynamicTT) {
			pSet.appendChild($.$CNS("tooltip", {
				id: $._UID + "_dynamic_tooltip",
				cb_id: $._UID,
				onpopupshowing: that + ".populateDynamicTooltip(event, this);event.stopPropagation();",
				onpopuphiding: "event.stopPropagation();",
				flex: 1,
			}));
		}
		var m = $.mPop;
		if (pSet && !m) {
			let dHS = "margin: 0 !important;padding: 2px 5px 2px 5px !important;border:none" +
				" !important;font-size:smaller !important;";
