				label: "Set a custom timestamp",
				tooltiptext: "Set a custom format for the timestamp for files/folders names.",
				oncommand: that + ".SetCustomTimestamp();event.stopPropagation();",
			}));
			menu4.appendChild($.$CNS("menuitem", {
				label: "Set name for HTML files",
				tooltiptext: "Set custom name for HTML files.",
				oncommand: that + ".SetHTMLBaseName();event.stopPropagation();",
			}));
			menu4.appendChild($.$CNS("menuitem", {
				label: "Convert menu into panel",
				type: "checkbox",
				_pref: "pConvertToPanel",
				tooltiptext: "Change the appearance of the main menu into a panel.",
				checked: $._pConvertToPanel,
				oncommand: that + ".sPref('pConvertToPanel', !" + that + ".gPref('pConvertToPanel'));" +
					that + ".selfDestroy();event.stopPropagation();",
			}));
			menu4.appendChild($.$CNS("menuitem", {
				label: "Show detailed tooltip",
				type: "checkbox",
				_pref: "pShowDetailedTooltip",
				tooltiptext: "If enabled/checked, the locations for the different backup types will " +
					"be displayed on this button tooltip.",
				checked: $._pShowDetailedTooltip,
				oncommand: that + ".sPref('pShowDetailedTooltip', !" + that + ".gPref('pShowDetailedTooltip'));" +
					that + ".selfDestroy();event.stopPropagation();",
			}));
			menu4.appendChild($.$CNS("menuitem", {
				label: "Show button dropmarker",
				type: "checkbox",
				_pref: "pShowDropmarker",
				tooltiptext: "Show/Hide this button dropmarker.",
				checked: $._pShowDropmarker,
				oncommand: that + ".sPref('pShowDropmarker', !" + that + ".gPref('pShowDropmarker'));" +
					that + ".selfDestroy();event.stopPropagation();",
			}));
		}
	},

	HandleOptionsMenu: function(aNode) {
		Array.prototype.slice.call(aNode.getElementsByAttribute("type", "radio")).forEach(function(aR) {
			if (aR && aR.hasAttribute("_custom_ts"))
				aR.setAttribute("tooltiptext", $._pTimeStampForFilesCustom);
			aR && aR.setAttribute("checked", parseInt(aR.value, 10) === $["_" + aR.parentNode.getAttribute("_pref")]);
		});
		let days = $._pAutoBUDays.split(",").map(function(aVal) {
			if (aVal !== "")
				return parseInt(aVal, 10);
		});
		Array.prototype.slice.call(aNode.getElementsByAttribute("type", "checkbox")).forEach(function(aC) {
			switch (aC.getAttribute("_pref")) {
				case "pAutoBUDays":
					aC && (aC.setAttribute("checked", days.indexOf(parseInt(aC.value, 10)) !== -1));
					break;
				case "pConvertToPanel":
					aC && aC.setAttribute("checked", $._pConvertToPanel);
					break;
			}

		});
		Array.prototype.slice.call(aNode.getElementsByTagName("textbox")).forEach(function(aT) {
			if (aT) {
				let pref = aT.getAttribute("_pref");
				aT.value = $["_" + pref];
				aT.onchange = function(aE) {
					setTimeout(function() {
						$.sPref(pref, parseInt(aT.value, 10));
					}, 10);
				};
			}
		});
	},

	HandleAutoBackDays: function(aDayNum, aCheck) {
		let days = $._pAutoBUDays.split(",").map(function(aVal) {
			if (aVal !== "")
				return parseInt(aVal, 10);
		});
		if (aCheck) {
			if (days.indexOf(aDayNum) !== -1)
				return true;
			else
				return false;
		} else {
			if (days.indexOf(aDayNum) !== -1) {
				while (days.indexOf(aDayNum) !== -1) {
					days.splice(days.indexOf(aDayNum), 1);
				}
			} else
				days.push(aDayNum);
			$.sPref("pAutoBUDays", days.join(","));
		}
	},

	SetCustomTimestamp: function() {
		let msg = "Set a custom timestamp format for files/folders names.\n" +
			"YYYY: year\nMM: month\nDD: day\nhh: hour\nmm: minute\nss: second\n\n" +
			"Note: The choosen format will be automatically stripped of ilegal characters for file names.";
		let value = $.promptme(msg, $._pTimeStampForFilesCustom);
		if (value)
			$.sPref("pTimeStampForFilesCustom", value);
	},

	SetBackupsCustomPath: function(aType) {
		let type;
		switch (aType) {
			case "pStartupBUExpLoc":
				type = "Startup backups";
				break;
			case "pAutoBUExpLoc":
				type = "Scheduled backups";
				break;
			case "pManualBUExpLoc":
				type = "Manual backups";
				break;
		}
		var nsIFilePicker = Ci.nsIFilePicker;
		var fp = Cc["@mozilla.org/filepicker;1"].createInstance(nsIFilePicker);
		fp.init(window, "Select export location for " + type, nsIFilePicker.modeGetFolder);
		fp.appendFilters(fp.filterAll);
		fp.displayDirectory = $._getExpStorage(aType);
		if (fp.show() === fp.returnOK && fp.file.isDirectory())
			$.sPref(aType, fp.file.path);
		else
			return;
	},

	SetHTMLBaseName: function() {
		let value = $.promptme("Set a custom base name for HTML files.", $._pHTMLDefaultFileName);
		if (value)
			$.sPref("pHTMLDefaultFileName", value);
	},

	HandleOverlay: function(aType, aWhichStorage, aCompress) {
		$._whichStorage = $._getExpStorage(aWhichStorage);
		$._compressFiles = aCompress;
		let overlayFile = OS.Path.join(OS.Constants.Path.profileDir, "custombuttons", "buttonsoverlay.xul");
		$._exportType = aType;
		if ($._exportType === "Overlay") {
			let dateForFileNames = $._getTimeStamp(new Date().getTime());
			let fileName = dateForFileNames + "-buttonsoverlay.xul";
			if ($._compressFiles) {
				$.compress(overlayFile, $._whichStorage.path, fileName);
			} else {
				OS.File.copy(overlayFile, OS.Path.join($._whichStorage.path,
					fileName));
				$.notifyme("Custom Buttons overlay backed up",
					"Click here to open export location.",
					$.getImg("AlertImg"),
					$.alertCallback);
			}
			return true;
		}
		OS.File.read(overlayFile).then(function onSuccess(aReadData) {
			let data = new TextDecoder().decode(aReadData).replace(/\t/gm, "").replace(/>\s+</g, "><");
			let overlayDoc = false;
			try {
				overlayDoc = (new DOMParser()).parseFromString(data, "application/xml").documentElement;
			} catch (aErr) {
				overlayDoc = false;
			} finally {
				if (!overlayDoc) {
					$.alertme("Something went wrong!!!\nYou may need to restart " +
						Services.appinfo.name + " and reattempt the backup.");
					return false;
				}
				let allPalettes = overlayDoc.getElementsByTagName("toolbarpalette");
				let allUsedPalettes = [];
				try {
					for (let i = allPalettes.length - 1; i >= 0; i--) {
						if (allPalettes[i].childNodes.length > 0)
							allUsedPalettes.push(allPalettes[i]);
					}
				} finally {
					setTimeout(function() {
						Array.prototype.slice.call(allUsedPalettes).forEach(function(aPalette) {
							$.saveAllButtonsFromOverlay(aPalette, aPalette.id);
						});
					}, 200);
				}
			}
		}, function onFailure(aRejectReason) {
			Cu.reportError(self.name + ": " + [aType, aWhichStorage, aRejectReason, aRejectReason.stack].join("\n"));
		});
	},

	ZipWriter: Components.Constructor("@mozilla.org/zipwriter;1", "nsIZipWriter"),
	_compressFiles: null,
	compress: function(aSrcDirOrFile, aStorage, aFileName) {
		let src = aSrcDirOrFile;
		let zipW;

		// Init routine
		let init = function(aDestDir, aZipName) {

			let zipFile = new $._nsIFile(aDestDir);
			zipFile.append(aZipName);
			if (zipFile.exists()) {
				try {
					zipFile.remove(true);
				} catch (aErr) {
					Cu.reportError(self.name + ": " + [aDestDir, aZipName, aErr, aErr.stack].join("\n"));
					return false;
				}
			}
			zipW = new $.ZipWriter();
			zipW.open(zipFile, 0x04 | 0x08 | 0x20);
		};

		// Define the zip routine
		let zip = function(aSrc) {
			let oldPerms, changedPerms = false;
			let nsIFile = new $._nsIFile(aSrc);
			if (!nsIFile)
				return false;
			if (nsIFile.exists()) {
				oldPerms = nsIFile.permissions;
				changedPerms = false;
				if (oldPerms !== 511) {
					nsIFile.permissions = 511;
					changedPerms = true;
				}
			}
			if (nsIFile.isDirectory()) {
				let entries = nsIFile.directoryEntries;
				while (entries.hasMoreElements()) {
					let entry = entries.getNext();
					entry.QueryInterface(Ci.nsIFile);
					let file = entry.leafName;
					if (entry.isDirectory()) {
						continue; // Shouldn't exist any directory inside!!
					} else {
						file = file.replace(/\\/g, "/");
						try {
							zipW.addEntryFile(file, Ci.nsIZipWriter.COMPRESSION_BEST, entry, true);
						} catch (aErr) {
							if (aErr.toString().indexOf("NS_BASE_STREAM_CLOSED") !== -1 ||
								aErr.toString().indexOf("NS_ERROR_NOT_INITIALIZED") !== -1) {
								continue;
							}
							if (aErr.toString().indexOf("NS_ERROR_FILE_ALREADY_EXISTS") !== -1) {
								zipW.removeEntry(file, false);
								zipW.addEntryFile(file, Ci.nsIZipWriter.COMPRESSION_BEST, entry, true);
								continue;
							}
							return false;
						}
					}
				}
			} else {
				try {
					zipW.addEntryFile(nsIFile.leafName, Ci.nsIZipWriter.COMPRESSION_BEST, nsIFile, true);
				} catch (aErr) {
					Cu.reportError(self.name + ": " + [aSrc, aErr, aErr.stack].join("\n"));
					return false;
				}
			}
			if (changedPerms) {
				nsIFile.permissions = oldPerms;
			}
		};

		// nsIRequestObserver methods
		let observer = {
			onStartRequest: function(aReq, aCtx) {},
			onStopRequest: function(aReq, aCtx, aStatus) {
				let msg;
				switch ($._exportType) {
					case "HTML":
						msg = "All buttons exported as HTML";
						if (/.html$/.test(aSrcDirOrFile)) {
							OS.File.remove(aSrcDirOrFile, {
								ignoreAbsent: true
							});
						}
						break;
					case "XML":
						msg = "All buttons exported as XML";
						if (aSrcDirOrFile.indexOf("XML Output") !== -1) {
							OS.File.removeDir(aSrcDirOrFile, {
								ignoreAbsent: true,
								ignorePermissions: true
							});
						}
						break;
					case "Images":
						msg = "All Custom Buttons images saved";
						if (aSrcDirOrFile.indexOf("Saved Images") !== -1) {
							OS.File.removeDir(aSrcDirOrFile, {
								ignoreAbsent: true,
								ignorePermissions: true
							});
						}
						break;
					case "Overlay":
						msg = "Custom Buttons overlay backed up";
						break;
				}
				$.notifyme(msg, "Click here to open export location.",
					$.getImg("AlertImg"),
					$.alertCallback);
				zipW.close();
			}
		};
		init(aStorage, aFileName + ".zip");
		zip(src);
		zipW.comment = "Zipped by " + self.name;
		zipW.processQueue(observer, null);
		return true;
	},

	saveAllButtonsFromOverlay: function(aPalette, aPaletteName) {
		if (!$._exportType || $._exportType === "Overlay")
			return true;
		let paletteStorage = OS.Path.join($._whichStorage.path, aPaletteName);
		OS.File.makeDir(paletteStorage, {
			ignoreExisting: true
		});
		var dateForFileNames = $._getTimeStamp(new Date().getTime());
		var XMLFileName = "XML Output " + dateForFileNames;
		var XMLExportFolder = OS.Path.join(paletteStorage, XMLFileName);
		if ($._exportType === "XML") {
			OS.File.makeDir(XMLExportFolder, {
				ignoreExisting: true
			});
		}
		var outPut = [];
		var p = 0,
			pLen = aPalette.childNodes.length;
		try {
			let handleButton = function(aImage, aBtn, aCount) {
				$.img_to_b64_cback(aImage, function(aImg) {
					if ($._exportType !== "Images") {
						let name = aBtn.getAttribute("label") || aBtn.getAttribute("name") || "";
						(name === "") && (name = "Name me!!");
						let id = aBtn.getAttribute("id").slice(20),
							init = aBtn.getAttribute("cb-init"),
							key = aBtn.getAttribute("accelkey") || "",
							code = aBtn.getAttribute("cb-oncommand"),
							help = aBtn.getAttribute("Help"),
							mode = aBtn.getAttribute("mode") || "";
						let URI = $.createURI(name, aImg, code, help, init, key, mode);
						if (!URI && pLen === (p + 1)) {
							$.alertme("Something went wrong!!!\nYou may need to restart " + Services.appinfo.name +
								" and reattempt the backup.");
							return false;
						}
						switch ($._exportType) {
							case "HTML":
								outPut.push("<li><span><span><input class=\"spoiler_button\" type=\"button\"" +
									" value=\"Show Source\" onclick=\"self.spr(this);\"><a href=\"" + URI +
									"\"><img alt=\"\" src=\"" + aImg + "\">" + name + "</a><label>#ID:" + id +
									"</label></span><span><span class=\"spoiler\" style=\"display: none;\">" +
									"</span></span></span></li>");
								break;
							case "XML":
								$.saveXML({
									btnName: name,
									btnId: id,
									btnURI: URI,
									btnImage: aImg,
									doCompression: aCount,
									XMLExportFolder: XMLExportFolder,
									zipName: XMLFileName,
									storage: paletteStorage
								});
								break;
						}
					} else
						outPut.push(aImg);
				});
			};
			let count = pLen;
			for (; p < pLen; p++) {
				count--;
				let btn = aPalette.childNodes[p];
				let img = btn.getAttribute("image");
				img || (img = $.getImg(btn.getAttribute("cb-stdicon")));
				handleButton(img, btn, count === 0);
			}
		} catch (aErr) {
			Cu.reportError(self.name + ": " + [aPalette, aPaletteName, aErr, aErr.stack].join("\n"));
		} finally {
			let buttonCount = outPut.length;
			setTimeout(function() {
				if ($._exportType === "XML") {
					if (!$._compressFiles)
						$.notifyme("All buttons exported as XML", "Click here to open export location.",
							$.getImg("AlertImg"),
							$.alertCallback);
				} else if ($._exportType === "Images") {
					if (!("Task" in window))
						Cu.import("resource://gre/modules/Task.jsm");
					if (!("Downloads" in window))
						Cu.import("resource://gre/modules/Downloads.jsm");
					let imgs = outPut.filter(function(v, i, a) { // Remove duplicated
						return a.indexOf(v) === i;
					});
					let imgFolderName = "Saved Images " + dateForFileNames;
					let imgPath = OS.Path.join(paletteStorage, imgFolderName);
					OS.File.makeDir(imgPath, {
						ignoreExisting: true
					});
					let count = imgs.length;
					let doDownload = "fetch" in Downloads ? Downloads.fetch : Downloads.simpleDownload;
					Array.prototype.slice.call(imgs).forEach(function(aImg) {
						if (aImg.search(/^data:image/) !== -1) {
							let imgExt;
							count--;
							switch (aImg.substring(0, aImg.indexOf(";"))) {
								case "data:image/svg+xml":
									imgExt = ".svg";
									break;
								case "data:image/x-icon":
									imgExt = ".ico";
									break;
								case "data:image/gif":
									imgExt = ".gif";
									break;
								case "data:image/jpeg":
									imgExt = ".jpg";
									break;
								case "data:image/bmp":
									imgExt = ".bmp";
									break;
								default:
									imgExt = ".png";
							}
							Task.spawn(function() {
								doDownload.call(Downloads, aImg, OS.Path.join(imgPath,
									"Image-" + $.padding(count, 3) + imgExt));
							}).then(function onSuccess() {
								if ($._compressFiles && count === 0) {
									setTimeout(function() {
										$.compress(imgPath, paletteStorage, imgFolderName);
									}, 3000);
								} else {
									$.notifyme("All Custom Buttons images saved", "Click here to open export location.",
										$.getImg("AlertImg"),
										$.alertCallback);
								}
							}, function onFailure(aRejectReason) {
								Cu.reportError([self.name + ": " + self.id, aRejectReason, aRejectReason.stack].join("\n"));
							});
						}
					});
				} else if ($._exportType !== "XML" && $._exportType !== "Images") {
					outPut = outPut.sort(function sortByString(a, b) {
						return a.localeCompare(b);
					});
					var html = $.getHelp("HTMLForExport");
					html = html
						.replace("<!--_T_I_T_L_E_-->", "There are a total of " + buttonCount + " <title>")
						.replace("<!--_B_U_T_T_O_N_L_I_S_T_-->", new Date() + "\n" + "<ol>" + "\n" + outPut.join("\n"));
					setTimeout(function() {
						let HTMLFileName = $._pHTMLDefaultFileName + dateForFileNames + ".html";
						let HTMLFilePath = OS.Path.join(paletteStorage, HTMLFileName);
						OS.File.writeAtomic(HTMLFilePath, html, {
							tmpPath: paletteStorage + ".tmp",
							encoding: "utf-8"
						}).then(function onSuccess() {
							if ($._compressFiles) {
								setTimeout(function() {
									$.compress(HTMLFilePath, paletteStorage, HTMLFileName);
								}, 2000);
							} else {
								$.notifyme("All buttons exported as HTML", "Click here to open export location.",
									$.getImg("AlertImg"),
									$.alertCallback);
							}
						}, function onFailure(aRejectReason) {
							Cu.reportError([self.name + ": " + self.id, aRejectReason, aRejectReason.stack].join("\n"));
						});
					}, 500);
				}
			}, parseInt(buttonCount * 10, 10));
		}
	},

	saveXML: function(aDataObj) {
		if (!aDataObj.btnURI || !/^custombutton\:\/\//.test(aDataObj.btnURI))
			return;
		var cbXML = aDataObj.btnURI.replace(/^custombutton\:\/\//, "");
		var data = unescape(cbXML);
		var header = "<custombutton xmlns:cb=\"http://xsms.nm.ru/custombuttons/\"" +
			" xmlns:html=\"http://www.w3.org/1999/xhtml\">\n" +
			"<html:head>\n" +
			"	<html:title>\x3C![CDATA[" + $.escHTML(aDataObj.btnName) + "]]\x3e</html:title>\n" +
			"	<html:link rel=\"shortcut icon\" href=\"" + aDataObj.btnImage + "\"/>\n" +
			"	<html:style type=\"text/css\">\x3C![CDATA[" + $.getHelp("StyleForXMLFiles") +
			"]]\x3e</html:style>\n" +
			"</html:head>\n" +
			"<html:body>\n" +
			"	<html:div id=\"wrapper\">\n" +
			"		<html:div id=\"button\">\n" +
			"			<html:a href=\"" + aDataObj.btnURI + "\" rel=\"nofollow\" title=\"Install " +
			$.escHTML(aDataObj.btnName) + "\">\n" +
			"				\x3C![CDATA[Install this button]]\x3e\n" +
			"			</html:a>\n" +
			"		</html:div>\n" +
			"		<html:a id=\"about\" href=\"https://addons.mozilla.org/addon/custom-buttons/\">\n" +
			"			\x3C![CDATA[About CustomButtons]]\x3e\n" +
			"		</html:a>\n" +
			"		<html:div id=\"credits\">\n" +
			"			<html:a href=\"http://custombuttons.sourceforge.net/forum/viewtopic.php?t=3647\">\n" +
			"				\x3C![CDATA[XML created with]]\x3e\n" +
			"				<html:br/>\n" +
			"				\x3C![CDATA[Backup all Custom Buttons]]\x3e\n" +
			"			</html:a>\n" +
			"		</html:div>\n" +
			"	</html:div>\n" +
			"</html:body>";
		data = data
			.replace(/<custombutton xmlns\:cb=\"http\:\/\/xsms\.nm\.ru\/custombuttons\/\">/, header);
		let filePath = OS.Path.join(aDataObj.XMLExportFolder, $.legalize(aDataObj.btnName) + "-ID" + aDataObj.btnId + ".xml");
		OS.File.writeAtomic(filePath, data, {
			tmpPath: filePath + ".tmp",
			encoding: "utf-8"
		}).then(function onSuccess() {
			if (aDataObj.doCompression) {
				setTimeout(function() {
					$.compress(aDataObj.XMLExportFolder, aDataObj.storage, aDataObj.zipName);
				}, 5000);
			}
		}, function onFailure(aRejectReason) {
			Cu.reportError([self.name + ": " + self.id, aRejectReason, aRejectReason.stack].join("\n"));
		});
	},

	/**
	 * Using a parsed string for the template variable it's infinitely faster than calling
	 * the template file with XMLHttpRequest (IF DOMParser happens to effing work!!!!!).
	 */
	createURI: function(aName, aImg, aCode, aHelp, aInit, aKey, aMode) {
		let temp = false;
		try {
			temp = (new DOMParser()).parseFromString('<?xml version="1.0"?><custombutton xmlns:cb=' +
				'"http://xsms.nm.ru/custombuttons/"><name/><image/><mode/><initcode/><code/><accelke' +
				'y/><help/><attributes/></custombutton>', "text/xml");
		} catch (aErr) {
			temp = false;
		}
		if (!temp) {
			return false;
		}
		let sTxt = custombutton.buttonSetText;
		sTxt(temp, "name", aName, 0);
		sTxt(temp, "image", aImg, 1);
		sTxt(temp, "mode", aMode, 0);
		sTxt(temp, "initcode", $.escCDATA(aInit), 1);
		sTxt(temp, "code", $.escCDATA(aCode), 1);
		sTxt(temp, "accelkey", aKey, 1);
		sTxt(temp, "help", $.escCDATA(aHelp), 1);
		return "custombutton:\x2F\x2F" + escape(new XMLSerializer().serializeToString(temp));
	},

	escCDATA: function(aStr) { // Unescaped CDATA tags can break buttons in XML format.
		return aStr
			.replace(/]]\x3e/g, "\\" + "x5D" + "\\" + "x5D" + "\\" + "x3e")
			.replace(/]]&gt\;/g, "\\" + "x5D" + "\\" + "x5D" + "\\" + "x3e");
	},

	img_to_b64_cback: function(aURL, aCallback) {
		if (!aURL)
			aCallback($.getImg("DefaultImg"));
		else if (aURL.search(/^data:image/) === 0)
			aCallback(aURL);
		else {
			let xhr = new XMLHttpRequest(),
				nsIMIMEService = Cc["@mozilla.org/mime;1"].getService(Ci.nsIMIMEService);
			try {
				xhr.open("GET", aURL, true);
			} catch (aErr) {
				aCallback($.getImg("DefaultImg"));
				return true;
			}
			xhr.responseType = "arraybuffer";
			xhr.onload = function() {
				let mediaType,
					dataURI,
					binary,
					bytes;
				bytes = new Uint8Array(xhr.response);
				binary = [].map.call(bytes, function(aByte) {
					return String.fromCharCode(aByte);
				}).join("");
				mediaType = xhr.getResponseHeader("Content-Type");
				let base64 = btoa(binary);
				if (mediaType.indexOf("image/") !== 0) {
					try {
						mediaType = nsIMIMEService.getTypeFromURI(Services.io.newURI(aURL, null, null));
					} catch (aError) {
						mediaType = "image/png";
					}
				}
				dataURI = "data:" + mediaType + ";base64," + base64;
				aCallback(dataURI);
			};
			xhr.onerror = function() {
				aCallback($.getImg("DefaultImg"));
			};
			xhr.send(null);
		}
	},

	_getTimeStamp: function(aDate) {
		let ts;
		switch ($._pTimeStampForFiles) {
			case 0:
				ts = $.legalize($._pTimeStampForFilesCustom); // Custom
				break;
			case 1:
				ts = "YYYY MM-DD hh.mm.ss"; // ISO8601
				break;
			case 2:
				ts = "YYYY DD.MM hh.mm.ss"; // European
				break;
		}
		let dte = new Date(parseInt(aDate));
		let YYYY = String(dte.getFullYear());
		let MM = String(dte.getMonth() + 1);
		if (MM.length === 1) {
			MM = "0" + MM;
		}
		let DD = String(dte.getDate());
		if (DD.length === 1)
			DD = "0" + DD;
		let hh = String(dte.getHours());
		if (hh.length === 1)
			hh = "0" + hh;
		let mm = String(dte.getMinutes());
		if (mm.length === 1)
			mm = "0" + mm;
		let ss = String(dte.getSeconds());
		if (ss.length === 1)
			ss = "0" + ss;
		ts = ts.replace("YYYY", YYYY);
		ts = ts.replace("MM", MM);
		ts = ts.replace("DD", DD);
		ts = ts.replace("hh", hh);
		ts = ts.replace("mm", mm);
		ts = ts.replace("ss", ss);
		return ts;
	},

	/**
	 * NEVER CHANGE TO "BRANCH OBSERVER"!!!!
	 */
	HandlePrefObserver: function(aRem) {
		var prefs = $.DefPrefs;
		for (let key in prefs) {
			let pref = $.Branch + key;
			if (prefs.hasOwnProperty(key)) {
				if (aRem)
					Services.prefs.removeObserver(pref, $.prefObserver);
				else
					Services.prefs.addObserver(pref, $.prefObserver, false);
			}
		}
	},

	prefObserver: {
		observe: function(aSubject, aTopic, aPStr) {
			if (aTopic === "nsPref:changed") {
				window[$._UID]["SavePrefFile"] = true;
				let prefName = aPStr.slice($.Branch.length);
				$["_" + prefName] = $.gPref(prefName);
				switch (prefName) {
					case "pHTMLDefaultFileName":
						if ($.gPref(prefName) === "") {
							$.notifyme("Default name for HTML files can't be blank!!!",
								"Value has been reseted to its default.",
								$.getImg("WarningImg"));
							Services.prefs.clearUserPref($.Branch + prefName);
						}
						break;
					case "pAutoBUType":
					case "pAutoBUDays":
					case "pAutoBUHours":
					case "pAutoBUMinutes":
						$.HandleScheduledBackups();
						break;
				}
			}
		}
	},

	getImg: function(aSrc) {
		switch (aSrc) {
			case "custombuttons-stdicon-1":
				return "chrome://custombuttons/skin/button.png";
			case "custombuttons-stdicon-2":
				return "chrome://custombuttons/skin/stdicons/rbutton.png";
			case "custombuttons-stdicon-3":
				return "chrome://custombuttons/skin/stdicons/gbutton.png";
			case "custombuttons-stdicon-4":
				return "chrome://custombuttons/skin/stdicons/bbutton.png";
			default:
				return $.getHelp(aSrc);
		}
	},

	getHelp: function(aPlaceholder) { // Get specific data, surrounded by "placeholders", from the Help tab.
		return self.getAttribute("Help").split("<!--" + aPlaceholder + "-->")[1];
	},

	promptme: function(aMsg, aVal) {
		let retVal = {
			value: aVal
		};
		if (Services.prompt.prompt(null, (self.label || self.name), aMsg, retVal, null, {}))
			return retVal.value;
		else
			return false;
		return false;
	},

	$CNS: function(aElName, aAttrs, aNameSpace) { // Creates elements
		var el = aNameSpace ?
			document.createElementNS(aNameSpace, aElName) :
			document.createElement(aElName);
		if (aAttrs)
			Object.keys(aAttrs).forEach(function(n) {
				if (aAttrs[n] !== "")
					el.setAttribute(n, aAttrs[n]);
			});
		return el;
	},

	padding: function(aNum, aLength) {
		var str = '' + aNum;
		while (str.length < aLength)
			str = '0' + str;
		return str;
	},

	legalize: function(aStr) { // Removes illegal characters in file names.
		return aStr.replace(/[\/:\"\*\?<>\|\\]/g, "");
	},

	escHTML: function(aStr) {
		aStr = String(aStr)
			.replace(/&/g, "&amp;")
			.replace(/</g, "&lt;")
			.replace(/>/g, "&gt;")
			.replace(/"/g, "&quot;")
			.replace(/'/g, "&apos;");
		return aStr;
	},

	notifyme: function(aTitle, aMsg, aImg, aCallback) { // Notification function
		let origin = (Services.prefs.getPrefType("ui.alertNotificationOrigin") !== 0) ?
			Services.prefs.getIntPref("ui.alertNotificationOrigin") :
			null;
		let win = Services.ww.openWindow(null, "chrome://global/content/alerts/alert.xul",
			"_blank", "chrome,titlebar=no,popup=yes", null);
		win.arguments = [aImg, aTitle, aMsg, !!aCallback, "", origin, "", "", null, (!!aCallback ? aCallback : null)];
	},

	alertme: function(aMsg) {
		Services.prompt.alert(null, self.name, aMsg);
	},

	$: function(aId) { // Return elements by Id
		return document.getElementById(aId);
	},

	alertCallback: {
		observe: function(aSubject, aTopic, aData) {
			let alt = Services.wm.getMostRecentWindow("alert:alert");
			setTimeout(function() {
				alt.close();
			}, 5000);
			if (aTopic === "alertclickcallback") {
				alt.close();
				$._whichStorage.reveal();
			}
		}
	},

	_getExpStorage: function(aWhich) {
		let dir;
		try {
			dir = new $._nsIFile($["_" + aWhich]);
			if (!dir.exists() || !dir.isDirectory())
				throw new Error("Non existent path.\nUsing default path: %HOME%/Custom Buttons Backups");
		} catch (aErr) {
			dir = new $._nsIFile($._defaultExpLoc);
			if (!dir.exists() || !dir.isDirectory())
				dir.create(Ci.nsIFile.DIRECTORY_TYPE, 0x1FF);
		}
		return dir;
	},

	selfDestroy: function() {
		window[$._UID]["SavePrefFile"] = false;
		setTimeout(function() {
			self.destroy("update");
			setTimeout(function() {
				self.init();
			}, 100);
		}, 100);
	},

	appID: function(aId) { // App ID boolean return.
		var app = {
			"SM": "{92650c4d-4b8e-4d2a-b7eb-24ecf4f6b63a}",
			"FX": "{ec8030f7-c20a-464f-9b0e-13a3a9e97384}",
			"TB": "{3550f703-e582-4d05-9a08-453d09bdfdc6}",
			"PM": "{8de7fcbb-c55c-4fbe-bfc5-fc555c87dbc4}"
		};
		return (Services.appinfo.ID === app[aId]);
	},

	_UID: "__cb_backup_all_buttons" + self.id.slice(20),
	_exportType: null,
	_autoBackTimer: 0,
	_startupBackTimer: 0,
	_autoBackCheck: 0,
	_nsIFile: Components.Constructor("@mozilla.org/file/local;1", "nsIFile", "initWithPath"),
	_XMLExportFolder: null,
	_defaultExpLoc: OS.Path.join(Services.dirsvc.get("Home", Ci.nsIFile).path, "Custom Buttons Backups"),
	get _curTime() { // getTime() in hours
		return parseInt((new Date()).getTime() / 3600000, 10);
	},
	get mPop() {
		return $.$($._UID + "_CBBackupAllButtonsPopup");
	},
	get dynamicTT() {
		return $.$($._UID + "_dynamic_tooltip");
	},

};

this.CBBackupAllButtons.Branch = "CB.CBBackupAllButtons.Id" + self.id.slice(20) + ".";

this.CBBackupAllButtons.DefPrefs = {
	pManualBUExpLoc: "",
	pHTMLDefaultFileName: "Custom Buttons ",
	pAutoBUExpLoc: "",
	pAutoBUType: 0,
	pAutoBUDays: "0,1,2,3,4,5,6",
	pAutoBUHours: 12,
	pAutoBUMinutes: 30,
	pStartupBUExpLoc: "",
	pStartupBUType: 0,
	pStartupBUDelay: 5,
	pStartupBUElapsedHours: 23,
	pStartupBULastTime: 0,
	pTimeStampForFilesCustom: "YYYY-MM-DD hh.mm.ss",
	pTimeStampForFiles: 1,
	pConvertToPanel: true,
	pStartupBUZipFiles: false,
	pAutoBUZipFiles: false,
	pManualBUZipFiles: false,
	pShowDetailedTooltip: true,
	pShowDropmarker: true,
};

this.CBBackupAllButtons.gPref = function(aKey) {
	let branch = Services.prefs.getBranch(self.CBBackupAllButtons.Branch);
	switch (typeof self.CBBackupAllButtons.DefPrefs[aKey]) {
		case "boolean":
			return branch.getBoolPref(aKey);
		case "number":
			return branch.getIntPref(aKey);
		case "string":
			return branch.getComplexValue(aKey, Ci.nsISupportsString).data;
	}
	return null;
};

this.CBBackupAllButtons.sPref = function(aKey, aVal) {
	let branch = Services.prefs.getBranch(self.CBBackupAllButtons.Branch);
	switch (typeof aVal) {
		case "boolean":
			branch.setBoolPref(aKey, aVal);
			break;
		case "number":
			branch.setIntPref(aKey, aVal);
			break;
		case "string":
			let str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
			str.data = aVal;
			branch.setComplexValue((aKey), Ci.nsISupportsString, str);
			break;
	}
};

this.CBBackupAllButtons.InitPrefs = function() {
	let branch = Services.prefs.getDefaultBranch(self.CBBackupAllButtons.Branch),
		defaults = $.DefPrefs;
	for (let key in defaults) {
		if (defaults.hasOwnProperty(key)) {
			let val = defaults[key];
			switch (typeof val) {
				case "boolean":
					branch.setBoolPref(key, val);
					break;
				case "number":
					branch.setIntPref(key, val);
					break;
				case "string":
					let str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
					str.data = val;
					branch.setComplexValue((key),
						Ci.nsISupportsString, str);
					break;
			}
		}
	}
};

this.CBBackupAllButtons.Init();

custombutton.code:
if(!event.target && !this.disabled) { // If button hotkey is pressed.
	this.CBBackupAllButtons.TogglePopup();
}
