	_ANI: function(aMPop, aEl, aInsertPoint, aSuffix, aAfter, aRemID) { // Append New Items
		let el = aEl.cloneNode(true);
		el.setAttribute("_placed", $._UID + "placed-items");
		if (aRemID) // ONLY FOR SEPARATORS!!!
			el.removeAttribute("id");
		else
			el.setAttribute("id", el.getAttribute("id") + aSuffix);
		if (el.hasChildNodes()) {
			for (let i = el.childNodes.length - 1; i >= 0; i--) {
				let cEl = el.childNodes[i];
				cEl.hasAttribute("id") && cEl.setAttribute("id", cEl.getAttribute("id") + aSuffix);
			}
		}
		switch (aInsertPoint) {
			case 0: // Insert first or after first
				aMPop.insertBefore(el, (aAfter ?
					aMPop.firstChild.nextSibling :
					aMPop.firstChild));
				break;
			case 1: // Insert last
				aMPop.appendChild(el);
				break;
			default: // Insert after or before specific element
				try {
					aMPop.insertBefore(el, (aAfter ?
						$.$(aInsertPoint).nextSibling :
						$.$(aInsertPoint)));
				} catch (aErr) {
					aMPop.appendChild(el);
					$.reportError(aErr);
				}
		}
	},

	createElements: function() {
		let popSet = $.popSet;
		if (popSet && !$.optionsPop) {
			let m = popSet.appendChild($.$CNS("menupopup", {
				id: $._UID + "_optionsPopup",
				cb_id: $._UID,
				position: "after_start",
				consumeoutsideclicks: false,
				onpopupshowing: $.that + "populateOptionsPopup(this);event.stopPropagation();",
				onpopuphiding: "event.stopPropagation();",
				onpopupshown: "this.handleOpenState(true);event.stopPropagation();",
				onpopuphidden: "this.handleOpenState(false);event.stopPropagation();",
				oncommand: $.that + "togglePref(event.target.getAttribute('value'));event.stopPropagation();",
				onclick: "event.stopPropagation();",
			}));
			m.handleOpenState = function(aState) {
				if (aState)
					self.setAttribute("open", true);
				else
					self.removeAttribute("open");
			};
		}
		if (popSet && !$.dynamicTT) {
			popSet.appendChild($.$CNS("tooltip", {
				id: $._UID + "_dynamic_tooltip",
				cb_id: $._UID,
				onpopupshowing: $.that + "populateDynamicTooltip(event, this);event.stopPropagation();",
				onpopuphiding: "event.stopPropagation();",
				flex: 1,
			}));
		}
	},

	populateDynamicTooltip: function(aE, aN) {
		aN.textContent = "";

		if (aE.target.triggerNode.id === self.id) {
			var grid = aN.appendChild($.$CNS("grid"));
			var cols = grid.appendChild($.$CNS("columns"));
			cols.appendChild($.$CNS("column", {
				flex: 2
			}));
			cols.appendChild($.$CNS("column", {
				flex: 3
			}));
			cols.appendChild($.$CNS("column", {
				flex: 1
			}));
			var rows = grid.appendChild($.$CNS("rows"));
			// AppendRow
			let AR = function(aRowsNode, aLabel1, aLabel2, aLabel3, aEmpty) {
				let row = $.$CNS("row");
				if (aEmpty) {
					row.appendChild($.$CNS("label", {
						value: " ",
						width: 0,
					}));
				} else {
					row.appendChild($.$CNS("label", {
						value: aLabel1,
						style: "font-weight: bold;",
					}));
					row.appendChild($.$CNS("label", {
						value: aLabel2,
						style: "font-weight: bold;",
					}));
					let desc = $.$CNS("description", {
						maxwidth: 250,
					});
					desc.textContent = aLabel3; // Use of "textContent" to achieve word wrap. ¬¬
					row.appendChild(desc);
				}
				aRowsNode.appendChild(row);
			};

			let btn = aE.target.triggerNode;
			AR(rows, btn.name, "→", "ID = " + btn.id.slice(20));
			AR(rows, null, null, null, true);
			AR(rows, "Left click", "→", "Open Options menu");
			AR(rows, null, null, null, true);
			let desc = $.$CNS("description", {
				style: "font-weight:bold;",
			});
			// Use of "textContent" to achieve word wrap. ¬¬
			desc.textContent = "Read this button help for very important usage notes.\n" +
				"Right click on button, \"Button Help\" item.";
			aN.appendChild(desc);
			return true;
		}
	},

	togglePref: function(aP) {
		$.sPref(aP, !$.gPref(aP));
	},

	populateOptionsPopup: function(aNode) {
