	setCustomPath: function() {
		var nsIFilePicker = Ci.nsIFilePicker;
		var fp = Cc["@mozilla.org/filepicker;1"].createInstance(nsIFilePicker);
		fp.init(window, "Select a custom export location", nsIFilePicker.modeGetFolder);
		fp.appendFilters(fp.filterAll);
		fp.displayDirectory = $._getExpStorage();
		if (fp.show() === fp.returnOK && fp.file.isDirectory())
			$.sPref("pImpExpCustomLocation", fp.file.path);
		else
			return;
	},

	handlePrefObserver: function(aRem) {
		var prefs = $.DefPrefs;
		for (let key in prefs) {
			let pref = $.Branch + key;
			if (prefs.hasOwnProperty(key)) {
				if (aRem)
					Services.prefs.removeObserver(pref, $.PrefObserver, false);
				else
					Services.prefs.addObserver(pref, $.PrefObserver, false);
			}
		}
	},

	PrefObserver: {
		observe: function(aSubject, aTopic, aPStr) {
			if (aTopic === "nsPref:changed") {
				let pN = aPStr.slice($.Branch.length);
				window[$._UID]["Preferences"][pN] = $.gPref(pN);
				switch (pN) {
					case "pCustomStyleSheet":
					case "pCustomStyleSheetEnabled":
						$.CSSLoader();
						break;
					case "pInvertIconsColor":
					case "pInfoHeader":
					case "pMoveGMI":
					case "pMoveGMIInTC":
					case "pEditInTabMI":
					case "pRemoveToPaletteMI":
					case "pToggleInitMI":
					case "pEditGMI":
					case "pDeleteGMI":
					case "pCopyAsBBCodeMI":
					case "pCopyAsHTMLMI":
					case "pCopyImageMI":
					case "pCopyGMI":
					case "pAddGMI":
					case "pAddGMIInTC":
					case "pOtherActionsGMI":
					case "pXMLExportMI":
					case "pXMLImportLeftMI":
					case "pXMLImportRightMI":
					case "pOverlayImportLeftMI":
					case "pOverlayImportRightMI":
					case "pXMLExpImpGMI":
					case "pXMLExpImpGMIInTC":
					case "pElementsForIntegration":
						$.handleNewItems();
						if (pN === "pEditGMI" ||
							pN === "pDeleteGMI" ||
							pN === "pCopyGMI" ||
							pN === "pXMLExpImpGMI") {
							let optM = $.optionsPop;
							optM && $.populateOptionsPopup(optM);
						}
						if (pN === "pInvertIconsColor")
							$.handleInversion();
						break;
				}
				if (!$._.pMoveGMIInTC &&
					!$._.pAddGMIInTC &&
					!$._.pXMLExpImpGMIInTC) {
					$.handleTBContext(true);
				}
				$._ = window[$._UID]["Preferences"];
			}
		}
	},

	handleNewItems: function() {
		clearTimeout(window[$._UID]["HandleNewItemsTimer"]);
		window[$._UID]["HandleNewItemsTimer"] = setTimeout(function() {
			$.removeNewItems();
			setTimeout(function() {
				$.insertNewItems(false);
			}, 250);
		}, 250);
	},

	editCustomStyleSheet: function(aPref) {
		var em = Services.ww.getWindowEnumerator();
		var winName = $._UID + "_custom_style_sheet_window";
		var index = 1;
		while (em.hasMoreElements()) {
			let win = em.getNext();
			if (win.name === winName) {
				win.focus();
				return;
			}
			index++;
		}
		let storage = {
			button: self,
		};
		let xul = $.getHelp("%CustomStyleSheetWindow%");
		xul = xul.replace(/>\s+</g, "><");
		xul = xul.split("%U_I_D%").join($._UID);
		let url = "data:application/vnd.mozilla.xul+xml;text/plain," + encodeURIComponent(xul);
		let optWin = openDialog(url, winName, "chrome, dialog=yes, centerscreen, close");
		optWin._storage = storage;
	},

	CSSLoader: function() {
		let css;
		try {
			css = "@namespace url(" + xulns + ");" + $.getHelp("%StyleSheet%")
				.replace(/_B_A_S_E_I_D_/g, $._ID + $._UID)
				.replace(/_U_I_D_/g, $._UID)
				.replace(/\n/g, "");
		} catch (aErr) {
			css = false;
		}
		if (!css)
			return;

		if ($._.pCustomStyleSheetEnabled)
			css += decodeURIComponent($._.pCustomStyleSheet);

		if (!window[$._UID]["StyleSheet"]) {
			window[$._UID]["StyleSheet"] = css;
			$.styleSheet(css);
		} else if (window[$._UID]["StyleSheet"] && window[$._UID]["StyleSheet"] !== css) {
			$.styleSheet(window[$._UID]["StyleSheet"], true);
			window[$._UID]["StyleSheet"] = css;
			$.styleSheet(css);
		}
	},

	$CNS: function(aElName, aAttrs, aNameSpace) { // Creates elements
		var el = aNameSpace ?
			document.createElementNS(aNameSpace, aElName) :
			document.createElement(aElName);
		if (aElName === "menuitem")
			el.setAttribute("class", "menuitem-iconic");
		if (aAttrs)
			Object.keys(aAttrs).forEach(function(n) {
				if (aAttrs[n] !== "")
					el.setAttribute(n, aAttrs[n]);
			});
		return el;
	},

	$: function(aID, aParent) {
		if (!aParent)
			return document.getElementById(aID);
		return aParent.getElementById(aID);
	},

	parseXML: function(aXML) { // Return parsed XML
		aXML = aXML.replace(/>\s+</g, "><"); // Linearize XML
		return (new DOMParser()).parseFromString(aXML, "application/xml").documentElement;
	},

	getHelp: function(aPlaceholder) { // Get specific data, surrounded by "placeholders", from the Help tab.
		return document.getElementById(self.id).getAttribute("Help").split("<!--" + aPlaceholder + "-->")[1];
	},

	styleSheet: function(aCSS, aRemFlag) {
		var sss = Cc["@mozilla.org/content/style-sheet-service;1"].getService(Ci.nsIStyleSheetService);
		var uri = Services.io.newURI("data:text/css," + encodeURIComponent(aCSS), null, null);
		if (sss.sheetRegistered(uri, sss.USER_SHEET))
			sss.unregisterSheet(uri, sss.USER_SHEET);
		if (aRemFlag)
			return;
		sss.loadAndRegisterSheet(uri, sss.USER_SHEET);
	},

	notifyme: function(aTitle, aMsg, aImg, aCallback) { // Notification function
		let origin = (Services.prefs.getPrefType("ui.alertNotificationOrigin") !== 0) ?
			Services.prefs.getIntPref("ui.alertNotificationOrigin") :
			null;
		let win = Services.ww.openWindow(null, "chrome://global/content/alerts/alert.xul",
			"_blank", "chrome,titlebar=no,popup=yes", null);
		win.arguments = [aImg, aTitle, aMsg, !!aCallback, "", origin, "", "", null, (!!aCallback ? aCallback : null)];
	},

	getStdImage: function(aIId) {
		if (/^custombuttons-stdicon-(\d)$/.test(aIId))
			switch (+RegExp.$1) {
				case 1:
					return "chrome://custombuttons/skin/button.png";
				case 2:
					return "chrome://custombuttons/skin/stdicons/rbutton.png";
				case 3:
					return "chrome://custombuttons/skin/stdicons/gbutton.png";
				case 4:
					return "chrome://custombuttons/skin/stdicons/bbutton.png";
			}
		return aIId || "chrome://custombuttons/skin/button.png";
	},

	stringToDOM: function(aStr) {
		var parser = new DOMParser(),
			dom = parser.parseFromString(aStr, "text/xml");
		if (dom.documentElement.nodeName === "parsererror") {
			$.reportError("Parse error!!! Not a valid XML string(?)!!!\n" +
				"Source:\n\n" + aStr);
			return null;
		} else {
			return dom;
		}
	},

	getImg: function(aSrc) {
		switch (aSrc) {
			case "custombuttons-stdicon-1":
				return "chrome://custombuttons/skin/button.png";
			case "custombuttons-stdicon-2":
				return "chrome://custombuttons/skin/stdicons/rbutton.png";
			case "custombuttons-stdicon-3":
				return "chrome://custombuttons/skin/stdicons/gbutton.png";
			case "custombuttons-stdicon-4":
				return "chrome://custombuttons/skin/stdicons/bbutton.png";
		}
	},

	saveXML: function(aName, aId, aURI, aImg) {
		if (!aURI || !/^custombutton\:\/\//.test(aURI))
			return;
		var cbXML = aURI.replace(/^custombutton\:\/\//, "");
		var data = unescape(cbXML);
		var header = "<custombutton xmlns:cb=\"http://xsms.nm.ru/custombuttons/\"" +
			" xmlns:html=\"http://www.w3.org/1999/xhtml\">\n" +
			"<html:head>\n" +
			"	<html:title>\x3C![CDATA[" + $.escHTML(aName) + "]]\x3e</html:title>\n" +
			"	<html:link rel=\"shortcut icon\" href=\"" + aImg + "\"/>\n" +
			"	<html:style type=\"text/css\">\x3C![CDATA[" + $.getHelp("%StyleForXMLFiles%") +
			"]]\x3e</html:style>\n" +
			"</html:head>\n" +
			"<html:body>\n" +
			"	<html:div id=\"wrapper\">\n" +
			"		<html:div id=\"button\">\n" +
			"			<html:a href=\"" + aURI + "\" rel=\"nofollow\" title=\"Install " +
			$.escHTML(aName) + "\">\n" +
			"				\x3C![CDATA[Install this button]]\x3e\n" +
			"			</html:a>\n" +
			"		</html:div>\n" +
			"		<html:a id=\"about\" href=\"https://addons.mozilla.org/addon/custom-buttons/\">\n" +
			"			\x3C![CDATA[About CustomButtons]]\x3e\n" +
			"		</html:a>\n" +
			"		<html:div id=\"credits\">\n" +
			"			<html:a href=\"http://custombuttons.sourceforge.net/forum/viewtopic.php?t=3599\">\n" +
			"				\x3C![CDATA[XML created with]]\x3e\n" +
			"				<html:br/>\n" +
			"				\x3C![CDATA[CB Context Menu Extended]]\x3e\n" +
			"			</html:a>\n" +
			"		</html:div>\n" +
			"	</html:div>\n" +
			"</html:body>";
		data = data
			.replace(/<custombutton xmlns\:cb=\"http\:\/\/xsms\.nm\.ru\/custombuttons\/\">/, header);
		setTimeout(function() {
			let file = $._getExpStorage();
			file.append(($.legalize(aName) + "-ID" + ($._.pShowIDOnExportedButtons ? aId : "") + ".xml").trim());
			$.writeFile(file.path, data, "unique");
			setTimeout(function() {
				$.alertCallback.opt = {
					footer: 1,
					centerScreen: false,
				};
				$.notifyme("Button \"" + aName + "\"",
					"Exported successfully.",
					aImg,
					$.alertCallback);
			}, 100);
		}, 100);
	},

	writeFile: function(aPath, aData, aSaveType) {
		try {
			var file = new $._nsIFile(aPath);
			var fos = Cc["@mozilla.org/network/file-output-stream;1"].createInstance(Ci.nsIFileOutputStream);
			switch (aSaveType) {
				case "append":
					fos.init(file, 0x02 | 0x08 | 0x10, 0x1B6, 0);
					break;
				case "unique":
					file.createUnique(Ci.nsIFile.NORMAL_FILE_TYPE, 0x1B6);
					fos.init(file, 0x02 | 0x08 | 0x20, 0x1B6, 0);
					break;
				default:
					fos.init(file, 0x02 | 0x08 | 0x20, 0x1B6, 0);
			}
			var cos = Cc["@mozilla.org/intl/converter-output-stream;1"].createInstance(Ci.nsIConverterOutputStream);
			cos.init(fos, "UTF-8", 0, 0);
			cos.writeString(aData);
			cos.close();
			fos.close();
		} catch (aErr) {
			$.reportError([self.name + ": ", aPath, aData, aSaveType, aErr, aErr.stack].join("\n"));
		}
	},

	readFile: function(aPath) {
		var data = "";
		var file = null;
		try {
			file = new this._nsIFile(aPath);
			var fis = Cc["@mozilla.org/network/file-input-stream;1"].createInstance(Ci.nsIFileInputStream);
			fis.init(file, -1, 0, 0);
			var cis = Cc["@mozilla.org/intl/converter-input-stream;1"].createInstance(Ci.nsIConverterInputStream);
			cis.init(fis, "UTF-8", 0, Ci.nsIConverterInputStream.DEFAULT_REPLACEMENT_CHARACTER);
			let str = {},
				read = 0;
			do {
				read = cis.readString(0xffffffff, str);
				data += str.value;
			} while (read !== 0);
			cis.close();
			fis.close();
		} catch (aErr) {
			$.reportError([self.name + ": ", aPath, aErr, aErr.stack].join("\n"));
			return null;
		}
		return data;
	},

	/**
	 * Using a parsed string for the template variable it's infinitely faster than calling
	 * the template file with XMLHttpRequest.
	 */
	createURI: function(aName, aImg, aCode, aHelp, aInit, aKey, aMode) {
		let template = (new DOMParser()).parseFromString('<?xml version="1.0"?><custombutton xmlns:cb=' +
			'"http://xsms.nm.ru/custombuttons/"><name/><image/><mode/><initcode/><code/><accelke' +
			'y/><help/><attributes/></custombutton>', "application/xml");
		let sTxt = custombutton.buttonSetText;
		sTxt(template, "name", aName, 0);
		sTxt(template, "image", aImg, 1);
		sTxt(template, "mode", aMode, 0);
		sTxt(template, "initcode", $.escCDATA(aInit), 1);
		sTxt(template, "code", $.escCDATA(aCode), 1);
		sTxt(template, "accelkey", aKey, 1);
		sTxt(template, "help", $.escCDATA(aHelp), 1);
		return "custombutton:\x2F\x2F" + escape(new XMLSerializer().serializeToString(template));
	},

	escCDATA: function(aStr) { // Unescaped CDATA tags can break buttons in XML format.
		return aStr
			.replace(/]]\x3e/g, "\\" + "x5D" + "\\" + "x5D" + "\\" + "x3e")
			.replace(/]]&gt\;/g, "\\" + "x5D" + "\\" + "x5D" + "\\" + "x3e");
	},

	escHTML: function(aStr) {
		aStr = String(aStr)
			.replace(/&/g, "&amp;")
			.replace(/</g, "&lt;")
			.replace(/>/g, "&gt;")
			.replace(/"/g, "&quot;")
			.replace(/'/g, "&apos;");
		return aStr;
	},

	promptme: function(aMsg, aVal) {
		let retVal = {
			value: aVal
		};
		if (Services.prompt.prompt(null, (self.label || self.name), aMsg, retVal, null, {}))
			return retVal.value;
		else
			return false;
		return false;
	},

	hasMod: function(aE, aMod) {
		switch (aMod) {
			case 0:
				return !aE.shiftKey && !$.accel(aE) && !aE.altKey;
			case "a":
				return !aE.shiftKey && !$.accel(aE) && aE.altKey;
			case "c":
				return !aE.shiftKey && $.accel(aE) && !aE.altKey;
			case "s":
				return aE.shiftKey && !$.accel(aE) && !aE.altKey;
			case "ca":
				return !aE.shiftKey && $.accel(aE) && aE.altKey;
			case "sc":
				return aE.shiftKey && $.accel(aE) && !aE.altKey;
			case "sa":
				return aE.shiftKey && !$.accel(aE) && aE.altKey;
			case "sca":
				return aE.shiftKey && $.accel(aE) && aE.altKey;
		}
	},

	_getExpStorage: function() {
		let d;
		try {
			d = new $._nsIFile($._.pImpExpCustomLocation);
			if (!d.exists() || !d.isDirectory())
				throw new Error("");
		} catch (aErr) {
			d = Services.dirsvc.get("Home", Ci.nsIFile);
			d.append("CB Context Menu Extended");
			if (!d.exists() || !d.isDirectory())
				d.create(Ci.nsIFile.DIRECTORY_TYPE, 0x1FF);
		}
		return d;
	},

	getNotificationFooter: function(aType) {
		switch (aType) {
			case 1: // Open storage.
				return "𝗖𝗹𝗶𝗰𝗸 𝗺𝗲 𝘁𝗼 𝗼𝗽𝗲𝗻 𝗲𝘅𝗽𝗼𝗿𝘁 𝗹𝗼𝗰𝗮𝘁𝗶𝗼𝗻";
			default:
				return "𝗖𝗹𝗶𝗰𝗸 𝗺𝗲 𝘁𝗼 𝗰𝗹𝗼𝘀𝗲 𝗺𝗲";
		}
	},

	alertCallback: {
		opt: {
			footer: null,
			centerScreen: null,
		},
		observe: function(aSubject, aTopic, aData) {
			let alt = Services.wm.getMostRecentWindow("alert:alert");
			let tBox = alt.document.getElementById("alertTextBox");
			if (aTopic === "alertshow") {
				tBox.appendChild($.$CNS("label", {
					flex: 1,
					value: $.getNotificationFooter(this.opt.footer),
				}));
				if (this.opt.centerScreen) {
					alt.moveTo((screen.availWidth / 2) - (alt.outerWidth / 2),
						(screen.availHeight / 2) - (alt.outerHeight / 2));
				}
				setTimeout(function() {
					alt.close();
				}, (this.opt.centerScreen ? 10000 : 3000));
			}
			if (aTopic === "alertclickcallback") {
				alt.close();
				switch (this.opt.footer) {
					case 1:
						$._getExpStorage().reveal();
						break;
				}
			}
		}
	},

	selfDestroy: function() {
		setTimeout(function() {
			self.destroy("update");
			setTimeout(function() {
				self.init();
			}, 100);
		}, 100);
	},

	reportError: function(aError) {
		try {
			console.error(aError);
		} catch (aErr) {
			Cu.reportError([self.name + ": " + self.id, aError, aError.stack].join("\n"));
		}
	},

	legalize: function(aStr) { // Removes illegal characters in file names.
		return aStr.replace(/[\{\=\}\@\!\%\$\+\/:\‘\"\`\*\?<&#>\|\\]/g, "");
	},

	accel: function(aE) {
		if (aE)
			return $.isOSX ? aE.metaKey : aE.ctrlKey;
		return $.isOSX ? "Command" : "Control";
	},

	isOSX: Services.appinfo.OS.toLowerCase().startsWith("darwin"),
	_UID: "__cb_context_menu_extended_" + self.id.slice(20),
	_nsIFile: Components.Constructor("@mozilla.org/file/local;1", "nsIFile", "initWithPath"),
	_ID: "custombuttons-contextpopup",
	_showCursorFeedback: true,
	_cbs: custombuttons.cbService,
	_normalInstall: null,
	_toRight: null,
	_moveButtonID: null,
	_anchor: null,
	_startURI: /custombutton\x3A\x2F\x2F/,
	initCodeRegExOld: /^\s*return;?(?:\s*\/\/[^\n\r]*)?\n+/,
	initCodeRegExNew: /^\s*(?:if\s*\(true\)\s*)?return;?(?:\s*\/\/[^\n\r]*)?\n+/,
	that: "document.getElementById('" + self.id + "').CBCME.",

	get initcodeDefValRegEx() {
		return new RegExp("^\s*(?:\/\*" + $._.pDefaultInitTabValue + "\*\/\s*)?$");
	},
	get mainPop() {
		return $.$($._ID);
	},
	get dynamicTT() {
		return $.$($._UID + "_dynamic_tooltip");
	},
	get optionsPop() {
		return $.$($._UID + "_optionsPopup");
	},
	get subPop() {
		return $.$($._ID + "-sub");
	},
	get popSet() {
		let pSet = $.$("mainPopupSet") || $.$($._UID + "_popupset");
		try {
			if (!pSet)
				pSet = $.$("msgcomposeWindow").appendChild($.$CNS("popupset", {
					id: $._UID + "_popupset",
					cb_id: $._UID
				}));
		} catch (aErr) {} finally {
			return pSet;
		}
	},
};

