<!--%ImportFromOverlayWindow%--><?xml version="1.0" encoding="utf-8"?>
<?xml-stylesheet href="chrome://global/skin" type="text/css"?>
<?xml-stylesheet href="data:text/css;charset=utf-8,
toolbarbutton {
	padding: 2px !important;
	margin: 2px !important;
}
toolbarbutton > .toolbarbutton-text {
	text-align: left !important;
}
toolbarbutton .toolbarbutton-icon {
	width: 16px !important;
	max-width: 16px !important;
	height: 16px !important;
	max-height: 16px !important;
}
toolbarbutton[_checked=&quot;true&quot;] {
	font-weight: bold !important;
	text-shadow: 1px 0px 0px darkorange !important;
	box-shadow: 0 0 2px 0 darkorange !important;
}
" type="text/css" ?>
<window xmlns="http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul"
	    orient="vertical"
	    windowtype="overlay:importer"
	    onload="$.onLoad();">
<script><![CDATA[
var storage = window._storage;
var cb_btn = storage.button;

function $id(id) {
	return document.getElementById(id);
}

var $ = {
	MaxColumnWidth: 180,
	_btnsPerCol: 0,

	onLoad: function() {
		$.populateTabs();
		$.setDisabled();
	},

	populateTabs: function() {
		var palettes = storage.allUsedPalettes;
		Array.prototype.slice.call(palettes).forEach(function(aPal) {
			$id(aPal.id + "_tab").hidden = false;
			$.appendToTab(aPal);
		});
	},

	appendToTab: function(aPal) {
		var btns1 = aPal.getElementsByTagName("toolbarbutton");
		var btns2 = [];
		for (let a = btns1.length - 1; a >= 0; a--) {
			btns2.push([btns1[a].getAttribute("label") || btns1[a].getAttribute("id"),
				btns1[a].getAttribute("image") || cb_btn.CBCME.getImg(btns1[a].getAttribute("cb-stdicon")),
				btns1[a].getAttribute("tooltiptext"),
				btns1[a]
			]);
		}
		btns2.sort(function(a, b) {
			return a[0].toLowerCase() > b[0].toLowerCase();
		});
		var btnPerRow = 6;
		var breakPoint = 0;
		var btnCol = [];
		var hbox = $id(aPal.id + "_tabpanel").appendChild(cb_btn.CBCME.$CNS("hbox"));
		var createCol = function(aArr) {
			let vbox = hbox.appendChild(cb_btn.CBCME.$CNS("vbox", {
				maxwidth: ($.MaxColumnWidth === 0) ? "" : $.MaxColumnWidth,
				onclick: "event.stopPropagation();",
				oncommand: "event.target.checked = !event.target.checked;$.HandleItems(event.target);event.stopPropagation();",
			}));
			var i = 0,
				iLen = aArr.length;
			for (; i < iLen; i++) {
				let btn = vbox.appendChild(cb_btn.CBCME.$CNS("toolbarbutton", {
					label: aArr[i][0],
					image: aArr[i][1],
					crop: "end",
					tooltiptext: aArr[i][2],
				}));
				btn._btn = aArr[i][3];
				vbox.appendChild(btn);
			}
		};
		$._btnsPerCol = parseInt((btns2.length / btnPerRow) + 1, 10);
		var b = 0,
			tLen = btns2.length;
		for (; b < tLen; b++) {
			breakPoint++;
			btnCol.push([btns2[b][0],
				btns2[b][1],
				btns2[b][2],
				btns2[b][3]
			]);
			if ((btnCol.length === $._btnsPerCol) || (b + 1) === btns2.length) {
				breakPoint = 0;
				createCol(btnCol);
				btnCol = [];
				continue;
			}
		}
	},

	HandleItems: function(aEl) {
		if (aEl.hasAttribute("_checked"))
			aEl.removeAttribute("_checked");
		else
			aEl.setAttribute("_checked", "true");
		setTimeout(function() {
			$.setDisabled();
		}, 100);
	},

	setDisabled: function() {
		let checked = document.getElementsByAttribute("_checked", "*").length;
		$id("uncheck-all").disabled = (checked === 0);
		$id("install-all").disabled = (checked === 0);
	},

	uncheckAll: function() {
		Array.prototype.slice.call(document.getElementsByAttribute("_checked", "*")).forEach(function(aEl) {
			aEl.removeAttribute("_checked");
			aEl.checked = false;
		});
		setTimeout(function() {
			$.setDisabled();
		}, 100);
	},
};
]]></script>
	<tabbox flex="1">
		<tabs>
			<tab id="BrowserToolbarPalette_tab" label=" BrowserToolbarPalette " hidden="true" />
			<tab id="MailToolbarPalette_tab" label=" MailToolbarPalette " hidden="true" />
			<tab id="MsgComposeToolbarPalette_tab" label=" MsgComposeToolbarPalette " hidden="true" />
			<tab id="calendarToolbarPalette_tab" label=" calendarToolbarPalette " hidden="true" />
			<tab id="NvuToolbarPalette_tab" label=" NvuToolbarPalette " hidden="true" />
		</tabs>
		<tabpanels flex="1">
			<tabpanel id="BrowserToolbarPalette_tabpanel"/>
			<tabpanel id="MailToolbarPalette_tabpanel"/>
			<tabpanel id="MsgComposeToolbarPalette_tabpanel"/>
			<tabpanel id="calendarToolbarPalette_tabpanel"/>
			<tabpanel id="NvuToolbarPalette_tabpanel"/>
		</tabpanels>
	</tabbox>
	<hbox>
		<spacer flex="1"/>
		<button id="uncheck-all"
			    label="Uncheck all selected buttons"
			    oncommand="$.uncheckAll();"/>
		<button id="install-all"
			    label="Install all selected buttons"
			    oncommand="cb_btn.CBCME.contextFunctions.exportImportButtons.installFromOverlay(document.getElementsByAttribute('_checked', '*'), storage.anchor, storage.toRight);close();"/>
		<button label="Cancel"
			    oncommand="close();"/>
		<spacer flex="1"/>
	</hbox>
</window><!--%ImportFromOverlayWindow%-->

