custombutton.initcode:
// To avoid initialization while moving the button in "Australis customize mode". Â¬Â¬
if (self.parentNode && /^wrapper-/.test(self.parentNode.id))
	return;

var $ = this.CustomButtonsForumNotifications = {
	init: function() {
		if (!("Services" in window))
			Cu.import("resource://gre/modules/Services.jsm");

		if (!($._UID in window)) {
			window[$._UID] = {
				CustomizableUI: false,
				checkInterval: null,
				startupTimeOut: null,
				GlowerTimer: null,
				currentPostsCount: 0,
				currentTopicsCount: 0,
			};
		}

		$.initPrefs();

		self.onclick = function(aE) {
			switch (aE.button) {
				case 0:
					aE.preventDefault();
					if (!aE.shiftKey && !$.accel(aE) && !aE.altKey)
						$.openForumPage($.gPref("pWhereToOpenForumPage"));
					else if (!aE.shiftKey && $.accel(aE) && !aE.altKey)
						$.openOptions();
					else if (aE.shiftKey && !$.accel(aE) && !aE.altKey)
						$.markAllAsRead();
					else if (aE.shiftKey && $.accel(aE) && !aE.altKey)
						$.checkNow();
					break;
				case 1:
					aE.preventDefault();
					if (!aE.shiftKey && !$.accel(aE) && !aE.altKey)
						$.openOptions();
					break;
			}
			aE.stopPropagation();
		};

		$.checkForum(true);
		$.handlePrefObserver();
		$.setButtonTooltip();

		if ("CustomizableUI" in window) {
			if (!window[$._UID]["CustomizableUI"]) {
				window[$._UID]["CustomizableUI"] = true;
				CustomizableUI.addListener({
					onCustomizeEnd: function(aWindow) {
						var thisBtn = aWindow.document.getElementById(self.id);
						setTimeout(function() {
							aWindow[$._UID]["CustomizableUI"] && thisBtn.CustomButtonsForumNotifications.selfDestroy();
						}, Math.floor(Math.random() * (1 + 2000 - 200)) + 200);
					}
				});
			}
		}

		addDestructor(function(aReason) {
			if (aReason === "delete" || aReason === "update") {
				clearTimeout(window[$._UID]["startupTimeOut"]);
				clearInterval(window[$._UID]["checkInterval"]);
				$.handlePrefObserver(true);
			}
			if (aReason === "delete") {
				(Services.prefs.getBranch($.Branch)).deleteBranch("");
				Services.prefs.savePrefFile(null);
				delete window[$._UID];
			}
		});
	},

	checkNow: function() {
		$.CB_xmlhttpRequest({
			method: "GET",
			overrideMimeType: "text/html; charset=UTF-8",
			url: $.forumMainURL,
			data: null,
			headers: {
				"Content-Type": "application/x-www-form-urlencoded"
			},
			onload: function(aRes) {
				if (aRes.readyState === 4 && aRes.status === 200) {
					try {
						let date = new Date();
						$.sPref("pzLastChecked", date.toDateString() + " - " + date.toLocaleTimeString());
						var totalPostsRegEx = new RegExp("(Total posts <strong>)\\d+(<\/strong>)", "i");
						var totalTopicsRegEx = new RegExp("(Total topics <strong>)\\d+(<\/strong>)", "i");
						window[$._UID]["currentPostsCount"] = Number(totalPostsRegEx.exec(aRes.responseText)[0].replace(/\D+/g, ""));
						window[$._UID]["currentTopicsCount"] = Number(totalTopicsRegEx.exec(aRes.responseText)[0].replace(/\D+/g, ""));
						$.reportFindings();
					} catch (aErr) {
						$.reportError(aErr);
					}
				} else {
					$.reportError(aRes);
				}
			},
			onerror: function(aErr) {
				$.reportError(aErr);
			},
			ontimeout: function(aErr) {
				$.reportError(aErr);
			}
		});
	},

	setButtonTooltip: function() {
		self.tooltipText = self.name + "  == ID: " + self.id.slice(20) + "\n\n" +
			"ğ—Ÿğ—®ğ˜€ğ˜ ğ—°ğ—µğ—²ğ—°ğ—¸ğ—²ğ—±: " + $.gPref("pzLastChecked") + "\n" +
			this.unreadPosts + " unread posts\n" +
			this.unreadTopics + " unread topics\n\n" +
			"ğ—Ÿğ—²ğ—³ğ˜ ğ—°ğ—¹ğ—¶ğ—°ğ—¸: Visit Custom Buttons forum\n" +
			"ğ— ğ—¶ğ—±ğ—±ğ—¹ğ—² ğ—¼ğ—¿ " + $.accel() + " + ğ—Ÿğ—²ğ—³ğ˜ ğ—°ğ—¹ğ—¶ğ—°ğ—¸: Open options\n" +
			$.accel() + " + ğ—¦ğ—µğ—¶ğ—³ğ˜ + ğ—Ÿğ—²ğ—³ğ˜ ğ—°ğ—¹ğ—¶ğ—°ğ—¸: Check for new posts/topics now\n" +
			"ğ—¦ğ—µğ—¶ğ—³ğ˜ + ğ—Ÿğ—²ğ—³ğ˜ ğ—°ğ—¹ğ—¶ğ—°ğ—¸: Mark all as read\n\n" +
			"ğ—¥ğ—²ğ—®ğ—± ğ˜ğ—µğ—¶ğ˜€ ğ—¯ğ˜‚ğ˜ğ˜ğ—¼ğ—» ğ—µğ—²ğ—¹ğ—½ ğ—³ğ—¼ğ—¿ ğ˜ƒğ—²ğ—¿ğ˜† ğ—¶ğ—ºğ—½ğ—¼ğ—¿ğ˜ğ—®ğ—»ğ˜ ğ˜‚ğ˜€ğ—®ğ—´ğ—² ğ—»ğ—¼ğ˜ğ—²ğ˜€.\n" +
			"ğ—¥ğ—¶ğ—´ğ—µğ˜ ğ—°ğ—¹ğ—¶ğ—°ğ—¸ ğ—¼ğ—» ğ—¯ğ˜‚ğ˜ğ˜ğ—¼ğ—», \"ğ—•ğ˜‚ğ˜ğ˜ğ—¼ğ—» ğ—›ğ—²ğ—¹ğ—½\" ğ—¶ğ˜ğ—²ğ—º.";
	},

	markAllAsRead: function() {
		if (window[$._UID]["currentPostsCount"] !== 0)
			$.sPref("pzTotalPostsLastCount", window[$._UID]["currentPostsCount"]);
		if (window[$._UID]["currentTopicsCount"] !== 0)
			$.sPref("pzTotalTopicsLastCount", window[$._UID]["currentTopicsCount"]);
		this.unreadPosts = 0;
		this.unreadTopics = 0;
		$.glower.stop();
		$.setButtonTooltip();
	},

	checkForum: function(aForce) {
		if (window[$._UID]["startupTimeOut"]) {
			clearTimeout(window[$._UID]["startupTimeOut"]);
			window[$._UID]["startupTimeOut"] = null;
		}

		window[$._UID]["startupTimeOut"] = setTimeout(function() {
			if (aForce) {
				clearInterval(window[$._UID]["checkInterval"]);
				window[$._UID]["checkInterval"] = null;
				if ($.gPref("pCheckForNewPostsTopicsOnBrowserStartup"))
					$.checkNow();
			}

			if (window[$._UID]["checkInterval"])
				return true;

			window[$._UID]["checkInterval"] = setInterval(function() {
				$.checkNow();
			}, $.gPref("pRefreshRate") * 60000);
		}, $.gPref("pDelayAfterBrowserStartUp") * 1000);
	},

	reportFindings: function() {
		let initialLoad = Number($.gPref("pzTotalPostsLastCount") +
			$.gPref("pzTotalTopicsLastCount")) === 0;
		this.unreadPosts = initialLoad ?
			window[$._UID]["currentPostsCount"] :
			window[$._UID]["currentPostsCount"] - $.gPref("pzTotalPostsLastCount");
		this.unreadTopics = initialLoad ?
			window[$._UID]["currentTopicsCount"] :
			window[$._UID]["currentTopicsCount"] - $.gPref("pzTotalTopicsLastCount");

		let notify = Number(this.unreadPosts + this.unreadTopics) !== 0;

		if (notify && $.gPref("pNotificationEnabled")) {
			$.notifyme(self.name,
				"ğ—£ğ—¼ğ˜€ğ˜ğ˜€: " + this.unreadPosts + " / " + window[$._UID]["currentPostsCount"] + "\n" +
				"ğ—§ğ—¼ğ—½ğ—¶ğ—°ğ˜€: " + this.unreadTopics + " / " + window[$._UID]["currentTopicsCount"],
				$.getHelp("%AlertImg%"),
				$.alertCallback);
		}

		if (notify && $.gPref("pSoundNotificationEnabled")) {
			let sound = Cc["@mozilla.org/sound;1"].createInstance(Ci.nsISound);
			sound.init();

			sound.playSystemSound($.gPref("pSoundNotificationPathToFile"));
		}

		if (notify && $.gPref("pHighlightButtonOnNewPostsTopincsFound")) {
			$.glower.start();
		}

		$.setButtonTooltip();
	},

	glower: {
		start: function() {
			self._glowing = !self._glowing;
			$.glower.stop();
			if (self._glowing)
				self.style.setProperty("outline", "3px solid " + $.gPref("pHighlightingColor1"), "important");
			else
				self.style.setProperty("outline", "3px solid " + $.gPref("pHighlightingColor2"), "important");
			self.style.setProperty("outline-offset", "-3px", "important");
			window[$._UID]["GlowerTimer"] = setTimeout(function() {
				$.glower.start();
			}, 500);
		},

		stop: function() {
			clearTimeout(window[$._UID]["GlowerTimer"]);
			self.style.removeProperty("outline");
			self.style.removeProperty("outline-offset");
		},
	},

	openForumPage: function(aWhere) {
		if ($.gPref("pMarkAllAsReadWhenOpeningForumPageFromButton"))
			$.markAllAsRead();

		switch (aWhere) {
			case 0: // Current
				window.openUILinkIn($.forumMainURL, "current", {
					inBackground: false
				});
				break;
			case 1: // Next to current
				let pos = gBrowser.selectedTab._tPos || gBrowser.tabContainer.selectedIndex; // Firefox || SeaMonkey
				gBrowser.moveTabTo(gBrowser.selectedTab = gBrowser.addTab($.forumMainURL), pos + 1);
				break;
			case 2: // New tab in background
				window.openUILinkIn($.forumMainURL, "tab", {
					inBackground: true
				});
				break;
			case 3: // New tab in foreground
				window.openUILinkIn($.forumMainURL, "tab", {
					inBackground: false
				});
				break;
		}
	},

	req: null,
	CB_xmlhttpRequest: function(aObj) {
		if (typeof(aObj) !== "object" || (typeof(aObj.url) !== "string" && !(aObj.url instanceof String)))
			return;
		if (!$.req)
			$.req = new XMLHttpRequest();
		var req = $.req;
		req.abort();
		req.timeout = 10000;

		req.reqestUrl = aObj.url;
		req.open(aObj.method || "GET", aObj.url, true);

		if (typeof(aObj.headers) === "object") {
			for (var i in aObj.headers)
				req.setRequestHeader(i, aObj.headers[i], false);
		}

		["onload", "onerror", "onreadystatechange", "ontimeout"].forEach(function(k) {
			if (aObj[k] && (typeof(aObj[k]) === "function" || aObj[k] instanceof Function))
				req[k] = function() {
					aObj[k]({
						status: (req.readyState === 4) ? req.status : 0,
						statusText: (req.readyState === 4) ? req.statusText : "",
						responseHeaders: (req.readyState === 4) ? req.getAllResponseHeaders() : "",
						responseText: req.responseText,
						readyState: req.readyState,
						finalUrl: (req.readyState === 4) ? req.channel.URI.spec : ""
					});
				};
		});

		if (aObj.overrideMimeType)
			req.overrideMimeType(aObj.overrideMimeType);
		if (typeof aObj.data === "string")
			req.send(aObj.data); // For POST
		else
			req.send(null); // For GET
	},

	$CNS: function(aElName, aAttrs, aNameSpace) { // Creates elements
		var el = aNameSpace ?
			document.createElementNS(aNameSpace, aElName) :
			document.createElement(aElName);
		if (aAttrs)
			Object.keys(aAttrs).forEach(function(n) {
				if (aAttrs[n] !== "")
					el.setAttribute(n, aAttrs[n]);
			});
		return el;
	},

	openOptions: function() {
		var url = "about:config?filter=" + $.Branch;
		if ($.gPref("pOpenOptionsOnNewTab") && ("gBrowser" in window)) {
			let tab = gBrowser.visibleTabs.find(tab => tab.linkedBrowser.currentURI.spec === url);
			gBrowser.selectedTab = tab || gBrowser.addTab(url);
		} else
			toOpenWindowByType("Preferences:ConfigManager", url);
	},

	getHelp: function(aPlaceholder) { // Get specific data, surrounded by "placeholders", from the Help tab.
		return $.$(self.id).getAttribute("Help").split("<!--" + aPlaceholder + "-->")[1];
	},

	alertCallback: {
		observe: function(aSubject, aTopic, aData) {
			let alt = Services.wm.getMostRecentWindow("alert:alert");
			let tBox = alt.document.getElementById("alertTextBox");
			if (aTopic === "alertshow") {
				tBox.appendChild($.$CNS("label", {
					flex: 1,
					value: "ğ—–ğ—¹ğ—¶ğ—°ğ—¸ ğ˜ğ—¼ ğ—¼ğ—½ğ—²ğ—» ğ—³ğ—¼ğ—¿ğ˜‚ğ—º",
				}));
				setTimeout(function() {
					alt.close();
				}, $.gPref("pNotificationAutoCloseDelay") * 1000);
			}
			if (aTopic === "alertclickcallback") {
				$.openForumPage($.gPref("pWhereToOpenForumPage"));
				alt.close();
			}
		}
	},

	notifyme: function(aTitle, aMsg, aImg, aCallback) { // Notification function
		let origin = (Services.prefs.getPrefType("ui.alertNotificationOrigin") !== 0) ?
			Services.prefs.getIntPref("ui.alertNotificationOrigin") :
			null;
		let win = Services.ww.openWindow(null, "chrome://global/content/alerts/alert.xul",
			"_blank", "chrome,titlebar=no,popup=yes", null);
		win.arguments = [aImg,
			aTitle,
			aMsg, !!aCallback,
			"",
			origin,
			"",
			"",
			null,
			(!!aCallback ? aCallback : null)
		];
	},

	/**
	 * NEVER CHANGE TO "BRANCH OBSERVER"!!!!
	 * Works like absolute SHEET on SeaMonkey!!!!
	 */
	handlePrefObserver: function(aRem) {
		var prefs = $.DefPrefs;
		for (let key in prefs) {
			let pref = $.Branch + key;
			if (prefs.hasOwnProperty(key)) {
				if (aRem)
					Services.prefs.removeObserver(pref, $.prefObserver, false);
				else
					Services.prefs.addObserver(pref, $.prefObserver, false);
			}
		}
	},

	prefObserver: {
		observe: function(aSubject, aTopic, aPStr) {
			if (aTopic === "nsPref:changed") {
				let prefName = aPStr.slice($.Branch.length);
				switch (prefName) {
					case "pNotificationEnabled":
					case "pNotificationAutoCloseDelay":
					case "pCheckForNewPostsTopicsOnBrowserStartup":
					case "pSoundNotificationEnabled":
					case "pSoundNotificationPathToFile":
					case "pHighlightButtonOnNewPostsTopincsFound":
					case "pHighlightingColor1":
					case "pHighlightingColor2":
					case "pWhereToOpenForumPage":
					case "pRefreshRate":
					case "pMarkAllAsReadWhenOpeningForumPageFromButton":
					case "pDelayAfterBrowserStartUp":
						$.selfDestroy();
						break;
				}
			}
		}
	},

	selfDestroy: function() {
		self.destroy("update");
		setTimeout(function() {
			self.init();
		}, 100);
	},

	reportError: function(aError) {
		try {
			console.error(aError);
		} catch (aErr) {
			Cu.reportError(["Multi Translator" + ": " + self.id, aError, aError.stack].join("\n"));
		}
	},

	$: function(aId) { // Return elements by Id
		return document.getElementById(aId);
	},

	_UID: "__custom_buttons_forum_notifications_" + self.id.slice(20),
	forumMainURL: "http://custombuttons.sourceforge.net/forum/index.php",
	isOSX: Services.appinfo.OS.toLowerCase().startsWith("darwin"),
	unreadPosts: 0,
	unreadTopics: 0,
	accel: function(aE) {
		if (aE)
			return $.isOSX ? aE.metaKey : aE.ctrlKey;
		return $.isOSX ? "ğ—–ğ—¼ğ—ºğ—ºğ—®ğ—»ğ—±" : "ğ—–ğ—¼ğ—»ğ˜ğ—¿ğ—¼ğ—¹";
	},
};

this.CustomButtonsForumNotifications.Branch = "CB.CustomButtonsForumNotifications.Id" + self.id.slice(20) + ".";

this.CustomButtonsForumNotifications.gPref = function(aKey) {
	let branch = Services.prefs.getBranch($.Branch);
	switch (typeof $.DefPrefs[aKey]) {
		case "boolean":
			return branch.getBoolPref(aKey);
		case "number":
			return branch.getIntPref(aKey);
		case "string":
			return branch.getComplexValue(aKey, Ci.nsISupportsString).data;
	}
	return null;
};

this.CustomButtonsForumNotifications.sPref = function(aKey, aVal) {
	let branch = Services.prefs.getBranch($.Branch);
	switch (typeof aVal) {
		case "boolean":
			branch.setBoolPref(aKey, aVal);
			break;
		case "number":
			branch.setIntPref(aKey, aVal);
			break;
		case "string":
			let str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
			str.data = aVal;
			branch.setComplexValue((aKey), Ci.nsISupportsString, str);
			break;
	}
};

this.CustomButtonsForumNotifications.initPrefs = function() {
	let branch = Services.prefs.getDefaultBranch($.Branch),
		defaults = $.DefPrefs;
	for (let key in defaults) {
		if (defaults.hasOwnProperty(key)) {
			let val = defaults[key];
			switch (typeof val) {
				case "boolean":
					branch.setBoolPref(key, val);
					break;
				case "number":
					branch.setIntPref(key, val);
					break;
				case "string":
					let str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
					str.data = val;
					branch.setComplexValue((key), Ci.nsISupportsString, str);
					break;
			}
		}
	}
};

this.CustomButtonsForumNotifications.DefPrefs = {
	pNotificationEnabled: true,
	pNotificationAutoCloseDelay: 5,
	pCheckForNewPostsTopicsOnBrowserStartup: true,
	pSoundNotificationEnabled: false,
	pSoundNotificationPathToFile: "",
	pHighlightButtonOnNewPostsTopincsFound: true,
	pMarkAllAsReadWhenOpeningForumPageFromButton: false,
	pHighlightingColor1: "yellow",
	pHighlightingColor2: "orange",
	pWhereToOpenForumPage: 3,
	pRefreshRate: 30,
	pDelayAfterBrowserStartUp: 10,
	pzTotalPostsLastCount: 0,
	pzTotalTopicsLastCount: 0,
	pzLastChecked: "",
};

this.CustomButtonsForumNotifications.init();
