custombutton.initcode:
if (self.parentNode && self.parentNode.id.match("wrapper-"))
	return;

var $ = this.CustomIconsForCBEditor = {
	init: function() {
		if (!("Services" in window))
			Cu.import("resource://gre/modules/Services.jsm");

		if (!("OS" in window))
			Cu.import("resource://gre/modules/osfile.jsm");

		$.icons = self.getAttribute("Help").split("[,]")[1].split("\n");

		$.initPrefs();

		if (Services.prefs.getPrefType($.sharedBranch + "HideStdIcons") === 0)
			Services.prefs.setBoolPref($.sharedBranch + "HideStdIcons", false);

		self.style.opacity = $.gPref("pEnabled") ? "1" : "0.65";
		self.checked = $.gPref("pAllowChecked") && $.gPref("pEnabled");

		self.tooltipText = self.name + "\n\n" +
			"ð—Ÿð—²ð—³ð˜ ð—°ð—¹ð—¶ð—°ð—¸ : Show/Hide new icons on CB Editor\t\n" +
			"ð— ð—¶ð—±ð—±ð—¹ð—² or " + $.accel() + " + ð—Ÿð—²ð—³ð˜ ð—°ð—¹ð—¶ð—°ð—¸: Open options menu\n\n" +
			"Read this button help for very important usage notes.\n" +
			"Right click on button, \"Button Help\" item.";

		$.handlePrefObserver();
		$.handleObservers();
		$.createElements();

		self.onclick = function(aE) {
			if (!aE.shiftKey && !$.accel(aE) && !aE.altKey && aE.button === 0) { // Left click
				aE.preventDefault();
				$.switchIcons();
				aE.stopPropagation();
			} else if (!aE.shiftKey && !$.accel(aE) && !aE.altKey && aE.button === 1 ||
				!aE.shiftKey && $.accel(aE) && !aE.altKey && aE.button === 0) { // Middle click
				aE.preventDefault();
				let optPop = $.optPop;
				optPop && optPop.openPopup(aE.target, "after_start", 0, 0, false, false);
				aE.stopPropagation();
			}
		};

		if ("CustomizableUI" in window) {
			let _cui = $._UID + "customizable_ui_";
			if (!window[_cui]) {
				window[_cui] = true;
				CustomizableUI.addListener({
					onCustomizeEnd: function(aWindow) {
						let thisBtn = aWindow.document.getElementById(self.id);
						setTimeout(function() {
							if (window[_cui]) {
								thisBtn.destroy("update");
								setTimeout(function() {
									thisBtn.init();
								}, 100);
							}
						}, Math.floor(Math.random() * (1 + 2000 - 200)) + 200);
					}
				});
			}
		}

		addDestructor(function(aReason) {
			if (aReason === "update" || aReason === "delete") {
				$.handlePrefObserver(true);
				let selfEls = document.getElementsByAttribute("cb_id", $._UID);
				Array.prototype.slice.call(selfEls).forEach(function(aEl) {
					aEl && aEl.parentNode.removeChild(aEl);
				});
			}
			if (aReason === "delete") {
				(Services.prefs.getBranch($.Branch)).deleteBranch("");
			}
		});
	},

	switchIcons: function() {
		$.sPref("pEnabled", !$.gPref("pEnabled"));
	},

	switchSharedPref: function() {
		Services.prefs.setBoolPref($.sharedBranch + "HideStdIcons", !Services.prefs.getBoolPref($.sharedBranch + "HideStdIcons"));
		$.selfDestroy();
	},

	populateOptionsMenu: function(aN) {
		aN.textContent = "";
		aN.appendChild($.$CNS("menuitem", {
			label: "Allow checked",
			type: "checkbox",
			checked: $.gPref("pAllowChecked"),
			oncommand: $.that + "sPref('pAllowChecked', !" + $.that +
				"gPref('pAllowChecked'));event.stopPropagation();",
			tooltiptext: "If checked/enabled, " + self.name + " will also reflect the enabled " +
				"state by its pressed/checked state.",
		}));
		aN.appendChild($.$CNS("menuitem", {
			label: "Hide standard icons",
			type: "checkbox",
			checked: Services.prefs.getBoolPref($.sharedBranch + "HideStdIcons"),
			oncommand: $.that + "switchSharedPref();event.stopPropagation();",
			tooltiptext: "If checked/enabled, the CB standard icons will be hidden from the " +
				"dropdown menu &quot;Image&quot; of the CB Editor window.",
		}));
		aN.appendChild($.$CNS("menuitem", {
			label: "Add images from folder",
			oncommand: $.that + "createListing();event.stopPropagation();",
			tooltiptext: "Select a folder containing images. Each image found will be added " +
				" to the Help tab of this button in Base64 format or as a file URI."
		}));
	},

	createElements: function() {
		let popSet = $.popSet;
		if (popSet && !$.optPop) {
			popSet.appendChild($.$CNS("menupopup", {
				id: $._UID + "-options-mpop",
				cb_id: $._UID,
				onclick: "event.stopPropagation();",
				oncommand: $.that + "event.stopPropagation();",
				onpopupshowing: $.that + "populateOptionsMenu(this);",
			}));
		}
	},

	handleObservers: function() {
		if ($.gPref("pEnabled")) {
			Services.obs.addObserver($.observer, "domwindowopened", false);
			(typeof gBrowser === "object") && window.addEventListener("DOMContentLoaded", $.alsoForTabs, false);
			$.setForEachWin(false);
			(typeof gBrowser === "object") && $.setForEachTab(false);
			addDestructor(function(aReason) {
				if (aReason === "update" || aReason === "delete") {
					$.setForEachWin(true);
					(typeof gBrowser === "object") && $.setForEachTab(true);
					Services.obs.removeObserver($.observer, "domwindowopened");
					(typeof gBrowser === "object") && window.removeEventListener("DOMContentLoaded", $.alsoForTabs, false);
				}
			});
		}
	},

	/**
	 * NEVER CHANGE TO "BRANCH OBSERVER"!!!!
	 * Works like absolute SHEET on SeaMonkey!!!!
	 */
	handlePrefObserver: function(aRem) {
		var prefs = $.DefPrefs;
		for (let key in prefs) {
			let pref = $.Branch + key;
			if (prefs.hasOwnProperty(key)) {
				if (aRem)
					Services.prefs.removeObserver(pref, $.prefObserver, false);
				else
					Services.prefs.addObserver(pref, $.prefObserver, false);
			}
		}
	},

	prefObserver: {
		observe: function(aSubject, aTopic, aPStr) {
			if (aTopic === "nsPref:changed") {
				let prefName = aPStr.slice($.Branch.length);
				switch (prefName) {
					case "pAllowChecked":
					case "pEnabled":
						$.selfDestroy();
						break;
				}
			}
		}
	},

	selfDestroy: function() {
		setTimeout(function() {
			self.destroy("update");
			setTimeout(function() {
				self.init();
			}, 100);
		}, 100);
	},

	moveIcons: function(aDoc, aRem) {
		var mPop = aDoc.getElementById("image").firstChild;
		let h = 0;
		for (; h < 5; h++) {
			var mi = mPop.children[h];
			if (!mi)
				break;
			var val = mi.getAttribute("value");
			var isStd = val.substring(0, 22) === "custombuttons-stdicon-";
			if (Services.prefs.getBoolPref($.sharedBranch + "HideStdIcons") && isStd)
				mi.hidden = true;
			if (!Services.prefs.getBoolPref($.sharedBranch + "HideStdIcons") || !isStd)
				mi.hidden = false;
		}
		if (aRem) {
			Array.prototype.slice.call(mPop.getElementsByAttribute("data-from" + $._UID, "*")).forEach(function(aEl) {
				aEl && mPop.removeChild(aEl);
			});
			return true;
		}
		let i = 0,
			iLen = $.icons.length;
		for (; i < iLen; i++) {
			let icn = $.icons[i];
			if (icn === "")
				continue;
			let mI = mPop.appendChild($.$CNS("menuitem", {
				"label": icn,
				"value": icn,
				"src": icn,
			}));
			mI.setAttribute("data-from" + $._UID, true);
		}
	},

	setForEachWin: function(aRem) {
		var em = Services.ww.getWindowEnumerator();
		var win = true;
		while (win) {
			win = em.getNext();
			if (!win)
				return;
			var doc = win.document;
			if (doc.location.href.substring(0, 41) !== $.editorURL)
				continue;
			if (win.opener !== window)
				continue;
			$.moveIcons(doc, aRem);
		}
	},

	observer: {
		observe: function(aSubject, aTopic, aData) {
			aSubject.addEventListener("load", this, false);
		},
		handleEvent: function(aE) {
			var doc = aE.target;
			var win = doc.defaultView;
			win.removeEventListener("load", this, false);
			if (doc.location.href.substring(0, 41) !== $.editorURL)
				return;
			$.moveIcons(doc, false);
		}
	},

	setForEachTab: function(aRem) {
		var tabContainer = gBrowser.mTabContainer;
		var tabs = tabContainer.childNodes;
		for (var t = 0; t < tabContainer.itemCount; t++) {
			var doc = tabs[t].linkedBrowser.contentDocument;
			if (doc.location.href.substring(0, 41) !== $.editorURL)
				continue;
			$.moveIcons(doc, aRem);
		}
	},

	alsoForTabs: function(evt) {
		var doc = evt.target;
		if (doc.location.href.substring(0, 41) !== $.editorURL)
			return;
		$.moveIcons(doc, false);
	},

	img_to_b64_cback: function(aURL, aCallback) {
		if (!aURL)
			aCallback(false);
		else if (aURL.search(/^data:image/) === 0)
			aCallback(aURL);
		else {
			let xhr = new XMLHttpRequest(),
				nsIMIMEService = Cc["@mozilla.org/mime;1"].getService(Ci.nsIMIMEService);
			try {
				xhr.open("GET", aURL, true);
			} catch (aErr) {
				aCallback(false);
				return true;
			}
			xhr.responseType = "arraybuffer";
			xhr.onload = function() {
				let mediaType,
					dataURI,
					binary,
					bytes;
				bytes = new Uint8Array(xhr.response);
				binary = [].map.call(bytes, function(aByte) {
					return String.fromCharCode(aByte);
				}).join("");
				mediaType = xhr.getResponseHeader("Content-Type");
				let base64 = btoa(binary);
				if (mediaType.indexOf("image/") !== 0) {
					try {
						mediaType = nsIMIMEService.getTypeFromURI(Services.io.newURI(aURL, null, null));
					} catch (aError) {
						mediaType = "image/png";
					}
				}
				dataURI = "data:" + mediaType + ";base64," + base64;
				aCallback(dataURI);
			};
			xhr.onerror = function() {
				aCallback(false);
			};
			xhr.send(null);
		}
	},

	addToHelpTabSwitch: function(aEntries) {
		var cBox = custombuttons.confirmBox3(self.name, "Save list of images " +
			"into the Help tab of " + self.name + ". \n\n" +
			"ð—ªð—”ð—¥ð—¡ð—œð—¡ð—š!!!\nOnce you make your choice, changes can't be undone! Choose wisely!",
			"ð—”ð—½ð—½ð—²ð—»ð—± to Help tab content", "Cancel", "ð—¢ð˜ƒð—²ð—¿ð˜„ð—¿ð—¶ð˜ð—² Help tab content");
		if (cBox === 0)
			$.addToHelpTab(aEntries, true);
		else if (cBox === 2)
			$.addToHelpTab(aEntries);
		else
			return;
	},

	addToHelpTab: function(aEntries, aAppend) {
		var helpContent = self.getAttribute("Help").split("[,]"),
			btnURL = custombuttons.makeButtonLink("edit", self.id),
			cbService = custombuttons.cbService;
		var param = cbService.getButtonParameters(btnURL);
		param = param.wrappedJSObject || param;
		if (aAppend)
			param.help = helpContent[0] + "[,]" + helpContent[1] + "\n" + aEntries.join("\n");
		else
			param.help = helpContent[0] + "[,]\n" + aEntries.join("\n");
		cbService.installButton(param); // Triggers onDestroy()
	},

	getIconsFromFolder: function(aPath) {
		let iterator = new OS.File.DirectoryIterator(aPath),
			imgPattern = /\.(gif|jpg|jpeg|tiff|png|ico|svg|bmp)$/i,
			entries = [];
		iterator.forEach(function(aEntry) {
			if (!aEntry.isDir && imgPattern.test(aEntry.path))
				entries.push("file:///" + aEntry.path);
		});
		return entries;
	},

	createListing: function() {
		var fp = Cc["@mozilla.org/filepicker;1"].createInstance(Ci.nsIFilePicker);
		fp.init(window, "Folder selection", Ci.nsIFilePicker.modeGetFolder);
		if (fp.show() === Ci.nsIFilePicker.returnOK && fp.file.isDirectory()) {
			var entries = $.getIconsFromFolder(fp.file.path),
				b64Entries = [];
			setTimeout(function() {
				setTimeout(function() {
					var cBox = custombuttons.confirmBox3(self.name, entries.length +
						" images were found.\n\nConvert images to Base64?",
						"Yes", "Cancel", "No");
					if (cBox === 0) {
						Array.prototype.slice.call(entries).forEach(function(aPath) {
							if (aPath.length > 0) {
								$.img_to_b64_cback(aPath, function(aImg) {
									aImg && b64Entries.push(aImg);
								});
							}
						});
						setTimeout(function() {
							setTimeout(function() {
								$.addToHelpTabSwitch(b64Entries);
							}, b64Entries.length);
						}, 3000);
					} else if (cBox === 2)
						$.addToHelpTabSwitch(entries);
					else
						return;
				}, entries.length);
			}, 3000);
		} else
			return;
	},

	$CNS: function(aElName, aAttrs, aNameSpace) { // Creates elements
		var el = aNameSpace ?
			document.createElementNS(aNameSpace, aElName) :
			document.createElement(aElName);
		if (aAttrs)
			Object.keys(aAttrs).forEach(function(n) {
				if (aAttrs[n] !== "")
					el.setAttribute(n, aAttrs[n]);
			});
		return el;
	},

	_UID: "__custom_icons_for_cb_editor_" + self.id.slice(20),
	editorURL: "chrome://custombuttons/content/editor.xul",
	sharedBranch: "CB.CustomIconsForCBEditorShared.",
	icons: null,
	that: "document.getElementById('" + self.id + "').CustomIconsForCBEditor.",
	isOSX: Services.appinfo.OS.toLowerCase().startsWith("darwin"),
	get popSet() {
		let pSet = document.getElementById("mainPopupSet") || document.getElementById($._UID + "_popupset");
		if (!pSet) {
			try {
				pSet = document.getElementById("msgcomposeWindow").appendChild($.$CNS("popupset", {
					id: $._UID + "_popupset",
					cb_id: $._UID
				}));
			} catch (aErr) {}
		}
		return pSet;
	},
	get optPop() {
		return document.getElementById($._UID + "-options-mpop");
	},
	accel: function(aE) {
		if (aE)
			return $.isOSX ? aE.metaKey : aE.ctrlKey;
		return $.isOSX ? "ð—–ð—¼ð—ºð—ºð—®ð—»ð—±" : "ð—–ð—¼ð—»ð˜ð—¿ð—¼ð—¹";
	},
};

this.CustomIconsForCBEditor.Branch = "CB.CustomIconsForCBEditor.Id" + self.id.slice(20) + ".";

this.CustomIconsForCBEditor.gPref = function(aKey) {
	let branch = Services.prefs.getBranch($.Branch);
	switch (typeof $.DefPrefs[aKey]) {
		case "boolean":
			return branch.getBoolPref(aKey);
		case "number":
			return branch.getIntPref(aKey);
		case "string":
			return branch.getComplexValue(aKey, Ci.nsISupportsString).data;
	}
	return null;
};

this.CustomIconsForCBEditor.sPref = function(aKey, aVal) {
	let branch = Services.prefs.getBranch($.Branch);
	switch (typeof aVal) {
		case "boolean":
			branch.setBoolPref(aKey, aVal);
			break;
		case "number":
			branch.setIntPref(aKey, aVal);
			break;
		case "string":
			let str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
			str.data = aVal;
			branch.setComplexValue((aKey), Ci.nsISupportsString, str);
			break;
	}
};

this.CustomIconsForCBEditor.initPrefs = function() {
	let branch = Services.prefs.getDefaultBranch($.Branch),
		defaults = $.DefPrefs;
	for (let key in defaults) {
		if (defaults.hasOwnProperty(key)) {
			let val = defaults[key];
			switch (typeof val) {
				case "boolean":
					branch.setBoolPref(key, val);
					break;
				case "number":
					branch.setIntPref(key, val);
					break;
				case "string":
					let str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
					str.data = val;
					branch.setComplexValue((key), Ci.nsISupportsString, str);
					break;
			}
		}
	}
};

this.CustomIconsForCBEditor.DefPrefs = {
	pAllowChecked: true,
	pEnabled: false
};

this.CustomIconsForCBEditor.init();
