		$.tBox.style.setProperty("font-size", newSize + "px", "important");
		cb_btn.FormattedTextGenerator.sPref("pTextBoxFontSize", newSize);
	},
	convertStr: function(aE) {
		$.tBox.focus();
		if (!aE.target.hasAttribute("data-style"))
			return true;
		let style = aE.target.getAttribute("data-style");
		let str = $._getSelection();
		str = cb_btn.FormattedTextGenerator.converterSwitch(str, style);
		setTimeout(function() {
			$.textInserter(str);
		}, 250);
	},
	_getSelection: function() {
		let fWin = {};
		let fEl = Services.focus.getFocusedElementForWindow(window, true, fWin);
		fWin = fWin.value;
		sel = fWin.getSelection().toString();
		if (!sel && fEl instanceof Ci.nsIDOMNSEditableElement) {
			if (fEl instanceof Ci.nsIDOMHTMLTextAreaElement ||
				(fEl instanceof Ci.nsIDOMHTMLInputElement &&
					fEl.mozIsTextField(true))) {
				sel = fEl.editor.selection.toString();
			}
		}
		return sel;
	},
	textInserter: function(aText) {
		let el = $.tBox;
		var text = aText;
		var selectionEnd = el.selectionStart + text.length;
		var currentValue = el.value || el.textContent;
		var beforeText = currentValue.substring(0, el.selectionStart);
		var afterText = currentValue.substring(el.selectionEnd, currentValue.length);
		el.value = beforeText + text + afterText;
		el.focus();
		el.selectionStart = el.selectionEnd = selectionEnd;
	},
	createBookmarks: function() {
		let bookBox = $id("bookmarks-box");
		let bookList = cb_btn.FormattedTextGenerator.gPref("pBookmarksList").split(",").filter(aBook =>
			aBook !== "");
		bookList.sort(function(a, b) {
			return a.toLowerCase() > b.toLowerCase();
		});
		Array.prototype.slice.call(bookList).forEach(function(aStyle) {
			if (aStyle !== "") {
				let mI = document.getElementsByAttribute("data-style", aStyle)[0];
				bookBox.appendChild($.$CNS("toolbarbutton", {
					label: mI.getAttribute("label"),
					id: aStyle,
					tooltiptext: mI.getAttribute("tooltiptext"),
					"data-style": aStyle,
				}));
			}
		});
	},
