custombutton.initcode:
var $ = this.FormattedTextGenerator = {

	Init: function() {
		if (!("Services" in window))
			Cu.import("resource://gre/modules/Services.jsm");

		$.InitPrefs();

		if (!($._UID in window)) {
			window[$._UID] = {
				CustomizableUI: false,
				Initialized: false
			};
		}

		for (var p in $.DefPrefs) {
			if ($.DefPrefs.hasOwnProperty(p))
				$["_" + p] = $.gPref(p);
		}

		if (!window[$._UID]["Initialized"]) {
			window[$._UID]["Initialized"] = true;
			$.HandlePrefObserver();
		}

		self.tooltipText = "𝗙𝗼𝗿𝗺𝗮𝘁𝘁𝗲𝗱 𝗧𝗲𝘅𝘁 𝗚𝗲𝗻𝗲𝗿𝗮𝘁𝗼𝗿 \t\t 𝗜𝗗: " + self.id.slice(20) +
			"\t\n\n𝗟𝗲𝗳𝘁 𝗖𝗹𝗶𝗰𝗸: Open main window\n";
		self.onclick = function(aE) {
			if ((aE.button === 0 && !aE.shiftKey && !$.accel(aE) && !aE.altKey)) {
				aE.preventDefault();
				$.openFormaterWindow();
			}
			aE.stopPropagation();
		};

		if ("CustomizableUI" in window) {
			if (!window[$._UID]["CustomizableUI"]) {
				window[$._UID]["CustomizableUI"] = true;
				CustomizableUI.addListener({
					onCustomizeEnd: function(aWindow) {
						var thisBtn = aWindow.document.getElementById(self.id);
						setTimeout(function() {
							aWindow[$._UID]["CustomizableUI"] && thisBtn.init();
						}, Math.floor(Math.random() * (1 + 2000 - 200)) + 200);
					}
				});
			}
		}

		addDestructor(function(aReason) {
			if (aReason === "update" || aReason === "delete") {
				// Delete all elements created by this button.
				let selfEls = document.getElementsByAttribute("cb_id", $._UID);
				Array.prototype.slice.call(selfEls).forEach(function(aEl) {
					aEl.parentNode.removeChild(aEl);
				});
			}
			if (aReason === "delete") {
				(Services.prefs.getBranch($.Branch)).deleteBranch("");
				Services.prefs.savePrefFile(null);
				delete window[$._UID];
			}
		});
	},

	openFormaterWindow: function() {
		var em = Services.ww.getWindowEnumerator();
		var winName = "formatted_text_generator_window" + $._UID;
		var index = 1;
		while (em.hasMoreElements()) {
			let win = em.getNext();
			if (win.name === winName) {
				win.focus();
				return;
			}
			index++;
		}
		let xul = $.getHelp("%FormaterWindow%");
		let storage = {
			gClipboard: gClipboard,
			goDoCommand: goDoCommand,
			button: self
		};
		xul = xul.replace(/>\s+</g, "><");
		xul = xul.split("%S_E_L_F_I_D%").join(self.id);
		xul = xul.split("%U_I_D%").join($._UID);
		xul = xul.split("%A_C_C_E_L%").join($.accel());
		let url = "data:application/vnd.mozilla.xul+xml;text/plain," + encodeURIComponent(xul);
		let win = openDialog(url, winName, "chrzome,resizable,centerscreen,dialog=no,close");
		win._storage = storage;
	},

	converterSwitch: function(aStr, aStyle) {
		switch (aStyle) {
			/**
			 * Generic method
			 */
			case "italicSansSerif":
			case "boldSerif":
			case "boldItalicSerif":
			case "boldSansSerif":
			case "greek":
			case "upperAngles":
			case "squiggle":
			case "doubleStruck":
			case "medieval":
			case "cursive":
			case "cursiveBold":
			case "oldEnglish":
			case "wideText":
			case "subscript":
			case "superscript":
			case "boldItalicSansSerif":
			case "monospace":
			case "italicSerif":
				return $.genericMapping1(aStr, aStyle);
				/**
				 * Simple methods
				 */
			case "firework":
				return aStr.split("").join("҉") + "҉";
			case "weirdBox":
				return aStr.replace(/([^\s])/g, "[̲̅$1]");
			case "littleSparkles":
				return "˜”*°•.˜”*°• " + aStr + " •°*”˜.•°*”˜";
			case "kirbyHug":
				return "(っ◔◡◔)っ ♥" + aStr + " ♥";
			case "dottyJoiner":
				return "░" + aStr.split("").join("░") + "░";
			case "thickBlockFramed":
				return aStr.replace(/([^\s])/g, "【$1】");
			case "wavyJoiner":
				return "≋" + aStr.split("").join("≋") + "≋";
			case "diametricAngleFrame":
				return aStr.replace(/([^\s])/g, "『$1』");
			case "reverser":
				return $.reverser(aStr);
			case "toUpperCase":
				return aStr.toUpperCase();
			case "toLowerCase":
				return aStr.toLowerCase();
				/**
				 * Deferred methods
				 */
			case "underline3":
				return $.overlaping(aStr, "underline3");
			case "underline2":
				return $.overlaping(aStr, "underline2");
			case "underline":
				return $.overlaping(aStr, "underline");
			case "strikethroughText1":
				return $.overlaping(aStr, "strikethroughText1");
			case "strikethroughText2":
				return $.overlaping(aStr, "strikethroughText2");
			case "xtoBubbles":
				$.bubbles.init();
				return $.bubbles.encode(aStr);
			case "xfromBubbles":
				$.bubbles.init();
				return $.bubbles.decode(aStr);
			case "ytoBinary":
				return $.binary.textToBin(aStr);
			case "yfromBinary":
				return $.binary.binToText(aStr);
			case "zfromUnicode":
				return $.unicode.fromUnicode(aStr);
			case "ztoUnicode":
				return $.unicode.toUnicode(aStr);
		}
	},

