		"_": ["", "", "", "", "", "", "", "", "", "", "", "", "_", "", "", "", "", ""],
		"+": ["", "", "", "", "", "", "", "", "", "", "", "", "＋", "₊", "⁺", "", "", ""],
	},

	genericMapping1: function(aStr, aStyle) {
		let idx;
		switch (aStyle) {
			case "italicSansSerif":
				idx = 0;
				break;
			case "boldSerif":
				idx = 1;
				break;
			case "boldItalicSerif":
				idx = 2;
				break;
			case "boldSansSerif":
				idx = 3;
				break;
			case "greek":
				idx = 4;
				break;
			case "upperAngles":
				idx = 5;
				break;
			case "squiggle":
				idx = 6;
				break;
			case "doubleStruck":
				idx = 7;
				break;
			case "medieval":
				idx = 8;
				break;
			case "cursive":
				idx = 9;
				break;
			case "cursiveBold":
				idx = 10;
				break;
			case "oldEnglish":
				idx = 11;
				break;
			case "wideText":
				idx = 12;
				break;
			case "subscript":
				idx = 13;
				break;
			case "superscript":
				idx = 14;
				break;
			case "boldItalicSansSerif":
				idx = 15;
				break;
			case "monospace":
				idx = 16;
				break;
			case "italicSerif":
				idx = 17;
				break;
		}
		idx = parseInt(idx, 10); // Just in case.
		var cArr = aStr.split("");
		var i = 0,
			iLen = cArr.length;
		for (; i < iLen; i++) {
			if ($._map[cArr[i].toLowerCase()]) {
				if ($._map[cArr[i]][idx] === "")
					continue;
				cArr[i] = $._map[cArr[i]][idx];
			}
		}
		aStr = cArr.join("");
		return aStr;
	},

	unicode: {
		isWhitespace: function(a) {
			if (a == 9 || a == 10 || a == 13 || a == 32) {
				return true;
			}
			return false;
		},

		padLeft: function(b, a) {
			if (!a || b.length >= a) {
				return b;
			}
			return Math.pow(10, a - b.length).toString().slice(1) + b;
		},

		toUnicode: function(b) {
			var d = "";
			var c = b.length;
			for (var a = 0; a < c; a++) {
				if (this.isWhitespace(b.charCodeAt(a))) {
					d += b.charAt(a);
				} else {
					d += "\\u";
					d += this.padLeft(b.charCodeAt(a).toString(16), 4);
				}
			}
			return d;
		},

		fromUnicode: function(a) {
			return a.replace(new RegExp("\\\\u" + "([0-9a-fA-F]{4})", "g"), function(c, d) {
				return String.fromCharCode(parseInt(d, 16));
			});
		},
	},

	binary: {
		binToText: function(aStr) {
			var binString = "";
			var chunks = aStr.match(/.{1,8}/g);
			this.padLastChunk(chunks);
			chunks.map(function(aBin) {
				binString += String.fromCharCode(parseInt(aBin, 2));
			});
			return binString;
		},

		textToBin: function(text) {
			var length = text.length,
				output = [];
			for (var i = 0; i < length; i++) {
				var bin = text[i].charCodeAt().toString(2);
				output.push(Array(8 - bin.length + 1).join("0") + bin);
			}
			return output.join("");
		},

		padLastChunk: function(chunks) {
			let last = chunks[chunks.length - 1];
			while (last.length < 8)
				last = "0" + last;
		},
	},

	/**
	 * [From https://github.com/combatwombat/Lunicode.js]
	 * Circles around Letters. Uses special circle characters for some letters and combining characters for the rest
	 * Thanks to
	 * - Alan Wood: http://www.alanwood.net/unicode/enclosed_alphanumerics.html
	 */
	bubbles: {
		init: function() {
			// Numbers
			for (let i = 49; i <= 57; i++) {
				this.map[String.fromCharCode(i)] = String.fromCharCode(i + 9263);
			}
			this.map['0'] = '\u24ea';
			// Capital letters
			for (let i = 65; i <= 90; i++) {
				this.map[String.fromCharCode(i)] = String.fromCharCode(i + 9333);
			}
			// Lower letters
			for (let i = 97; i <= 122; i++) {
				this.map[String.fromCharCode(i)] = String.fromCharCode(i + 9327);
			}
			// invert the map
			for (let i in this.map) {
				this.mapInverse[this.map[i]] = i;
			}

		},

		encode: function(aStr) {
			aStr = aStr.split("");
			var ret = "",
				ch,
				first = true;

			for (let i in aStr) {
				ch = this.map[aStr[i]];
				// No dedicated circled character available? Use a Combining Diacritical Mark surrounded
				// with non-breaking spaces, so it doesn't overlap
				if ((typeof(ch) == "undefined")) {
					if (aStr[i].charCodeAt(0) >= 33) {
						ch = aStr[i] + String.fromCharCode(8413);
						if (!first) {
							ch = String.fromCharCode(8239) + String.fromCharCode(160) + String.fromCharCode(160) + String.fromCharCode(8239) + ch;
						}
					} else {
						ch = aStr[i];
					}
				}
				ret += ch;
				first = (ch == '\n');
			}
			return ret;
		},

		decode: function(aStr) {
			aStr = aStr.split("");
			var ret = "",
				ch,
				newRet = '';

			for (let i in aStr) {
				ch = this.mapInverse[aStr[i]];
				ret += ((typeof(ch) == "undefined") ? aStr[i] : ch);
			}

			ret = ret.split("");

			for (let i in ret) {
				ch = ret[i].charCodeAt(0);
				if (ch != 160 && ch != 8239 && ch != 8413) {
					newRet += ret[i];
				}
			}

			return newRet;
		},

		map: {},
		mapInverse: {}
	},

	regexSymbolWithCombiningMarks: /([\0-\u02FF\u0370-\u1AAF\u1B00-\u1DBF\u1E00-\u20CF\u2100-\uD7FF\uE000-\uFE1F\uFE30-\uFFFF]|[\uD800-\uDBFF][\uDC00-\uDFFF]|[\uD800-\uDBFF](?![\uDC00-\uDFFF])|(?:[^\uD800-\uDBFF]|^)[\uDC00-\uDFFF])([\u0300-\u036F\u1AB0-\u1AFF\u1DC0-\u1DFF\u20D0-\u20FF\uFE20-\uFE2F]+)/g,
	regexSurrogatePair: /([\uD800-\uDBFF])([\uDC00-\uDFFF])/g,
	reverser: function(aStr) {
		// Step 1: deal with combining marks and astral symbols (surrogate pairs)
		let string = aStr
			// Swap symbols with their combining marks so the combining marks go first
			.replace($.regexSymbolWithCombiningMarks, function($0, $1, $2) {
				// Reverse the combining marks so they will end up in the same order
				// later on (after another round of reversing)
				return $.reverse($2) + $1;
			})
			// Swap high and low surrogates so the low surrogates go first
			.replace($.regexSurrogatePair, "$2$1");
		// Step 2: reverse the code units in the string
		var result = "";
		var index = string.length;
		while (index--) {
			result += string.charAt(index);
		}
		return result;
	},

	overlaping: function(aStr, aOpt) {
		let rep;
		switch (aOpt) {
			case "strikethroughText1":
				rep = "\u0336";
				break;
			case "strikethroughText2":
				rep = "\u0335";
				break;
			case "underline":
				rep = "\u0332";
				break;
			case "underline2":
				rep = "\u035F";
				break;
			case "underline3":
				rep = "\u0333";
				break;
		}
		var charArray = aStr.split("");
		var i = 0,
			iLen = charArray.length;
		for (; i < iLen; i++) {
			if (charArray[i] !== "\n")
				charArray[i] = charArray[i] + rep + "";
		}
		aStr = charArray.join("");
		return aStr;
	},

	/**
	 * NEVER CHANGE TO "BRANCH OBSERVER"!!!!
	 */
	HandlePrefObserver: function(aRem) {
		var prefs = $.DefPrefs;
		for (let key in prefs) {
			let pref = $.Branch + key;
			if (prefs.hasOwnProperty(key)) {
				if (aRem)
					Services.prefs.removeObserver(pref, $.prefObserver);
				else
					Services.prefs.addObserver(pref, $.prefObserver, false);
			}
		}
	},

	prefObserver: {
		observe: function(aSubject, aTopic, aPStr) {
			if (aTopic === "nsPref:changed") {
				$["_" + aPStr] = $.gPref(aPStr);
			}
		}
	},

	getHelp: function(aPlaceholder) { // Get specific data, surrounded by "placeholders", from the Help tab.
		return self.getAttribute("Help").split("<!--" + aPlaceholder + "-->")[1];
	},

	_UID: "__formatted_text_generator" + self.id.slice(20),
	accel: function(aE) {
		let isOSX = Services.appinfo.OS.toLowerCase().startsWith("darwin");
		if (aE)
			return isOSX ? aE.metaKey : aE.ctrlKey;
		return isOSX ? "Command" : "Control";
	},
};

this.FormattedTextGenerator.Branch = "CB.FormattedTextGenerator.Id" + self.id.slice(20) + ".";

this.FormattedTextGenerator.DefPrefs = {
	pTextBoxFontSize: 12,
	pBookmarksList: "",
	pInvertDarkIcons: false,
};

this.FormattedTextGenerator.InitPrefs = function() {
	var branch = Services.prefs.getDefaultBranch($.Branch),
		defaults = self.FormattedTextGenerator.DefPrefs;
	for (var key in defaults) {
		if (defaults.hasOwnProperty(key)) {
			var val = defaults[key];
			switch (typeof val) {
				case "boolean":
					branch.setBoolPref(key, val);
					break;
				case "number":
					branch.setIntPref(key, val);
					break;
				case "string":
					var str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
					str.data = val;
					branch.setComplexValue((key),
						Ci.nsISupportsString, str);
					break;
			}
		}
	}
};

this.FormattedTextGenerator.gPref = function(aKey) {
	var branch = Services.prefs.getBranch($.Branch);
	switch (typeof self.FormattedTextGenerator.DefPrefs[aKey]) {
		case "boolean":
			return branch.getBoolPref(aKey);
		case "number":
			return branch.getIntPref(aKey);
		case "string":
			return branch.getComplexValue(aKey, Ci.nsISupportsString).data;
	}
	return null;
};

this.FormattedTextGenerator.sPref = function(aKey, aVal) {
	var branch = Services.prefs.getBranch($.Branch);
	switch (typeof aVal) {
		case "boolean":
			branch.setBoolPref(aKey, aVal);
			break;
		case "number":
			branch.setIntPref(aKey, aVal);
			break;
		case "string":
			var str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
			str.data = aVal;
			branch.setComplexValue((aKey), Ci.nsISupportsString, str);
			break;
	}
};

this.FormattedTextGenerator.Init();

