		var text = aText;
		var selectionEnd = el.selectionStart + text.length;
		var currentValue = el.value || el.textContent;
		var beforeText = currentValue.substring(0, el.selectionStart);
		var afterText = currentValue.substring(el.selectionEnd, currentValue.length);
		el.value = beforeText + text + afterText;
		el.focus();
		el.selectionStart = el.selectionEnd = selectionEnd;
	},
	createBookmarks: function() {
		let bookBox = $id("bookmarks-box");
		let bookList = cb_btn.FormattedTextGenerator.gPref("pBookmarksList").split(",").filter(aBook =>
			aBook !== "");
		bookList.sort(function(a, b) {
			return a.toLowerCase() > b.toLowerCase();
		});
		Array.prototype.slice.call(bookList).forEach(function(aStyle) {
			if (aStyle !== "") {
				let mI = document.getElementsByAttribute("data-style", aStyle)[0];
				bookBox.appendChild($.$CNS("toolbarbutton", {
					label: mI.getAttribute("label"),
					id: aStyle,
					tooltiptext: mI.getAttribute("tooltiptext"),
					"data-style": aStyle,
				}));
			}
		});
	},
	handleBookmark: function(aE) {
		let el = aE.target;
		if ((!aE.shiftKey && $.accel(aE) && !aE.altKey && aE.button === 0) ||
			(!aE.shiftKey && !$.accel(aE) && !aE.altKey && aE.button === 1)) {
			aE.preventDefault();
			this.handleBookmarksList({
				style: el.getAttribute("data-style"),
				label: el.getAttribute("label"),
				tooltiptext: el.getAttribute("tooltiptext"),
			});
		}
		aE.stopPropagation();
	},
	handleBookmarksList: function(aBookObj) {
		let bookList = cb_btn.FormattedTextGenerator.gPref("pBookmarksList").split(",").filter(function(aVal) {
			if (aVal !== "")
				return true;
		});
		let book = aBookObj.style;
		if (bookList.indexOf(book) !== -1) {
			while (bookList.indexOf(book) !== -1) {
				bookList.splice(bookList.indexOf(book), 1);
			}
			let bookEl = $id(book);
			while (bookEl && bookEl.parentNode) {
				bookEl.parentNode.removeChild(bookEl);
			}
		} else {
			bookList.push(book);
			let bookBox = $id("bookmarks-box");
			bookBox.appendChild($.$CNS("toolbarbutton", {
				label: aBookObj.label,
				tooltiptext: aBookObj.tooltiptext,
				id: book,
				"data-style": book,
			}));
		}
		cb_btn.FormattedTextGenerator.sPref("pBookmarksList", bookList.join(","));
	},
	saveToFile: function() {
		try {
			let data = $.tBox.value;
			var nsIFilePicker = Ci.nsIFilePicker;
			var fp = Cc["@mozilla.org/filepicker;1"].createInstance(nsIFilePicker);
			let fpCallback = function(aResult) {
				if (aResult === nsIFilePicker.returnOK ||
					aResult === nsIFilePicker.returnReplace) {
					OS.File.writeAtomic(fp.file.path, data, {
						tmpPath: fp.file.path + ".tmp",
						encoding: "utf-8"
					});
				} else
					return false;
			};
			fp.init(window, "Save to text file", nsIFilePicker.modeSave);
			fp.appendFilters(fp.filterText);
			fp.appendFilters(fp.filterAll);
			fp.defaultExtension = ".txt";
			fp.defaultString = "Name me.txt";
			fp.open(fpCallback);

		} catch (ex) {}
	},
	openFile: function() {
		try {
			var nsIFilePicker = Ci.nsIFilePicker;
			let fp = Cc["@mozilla.org/filepicker;1"].createInstance(nsIFilePicker);
			let fpCallback = function(aResult) {
				if (aResult === nsIFilePicker.returnOK) {
					OS.File.read(fp.file.path).then(function onSuccess(aReadData) {
						let data = new TextDecoder().decode(aReadData);
						$.tBox.value = data;
					}, function onFailure(aRejectReason) {
						Cu.reportError([aRejectReason, aRejectReason.stack].join("\n"));
					});
				} else
					return false;
			};
			fp.init(window, "Load from a text file", nsIFilePicker.modeOpen);
			fp.appendFilters(fp.filterText);
			fp.appendFilters(fp.filterAll);
			fp.open(fpCallback);
		} catch (ex) {}
	},
	unDo: function() {
		let cmd = "cmd_undo";
		let ctrlr = tBoxCtrlrs.getControllerForCommand(cmd);

		if (ctrlr && ctrlr.isCommandEnabled(cmd)) {
			ctrlr = ctrlr.QueryInterface(Ci.nsICommandController);
			ctrlr.doCommand(cmd);
		}
	},
	reDo: function() {
		let cmd = "cmd_redo";
		let ctrlr = tBoxCtrlrs.getControllerForCommand(cmd);

		if (ctrlr && ctrlr.isCommandEnabled(cmd)) {
			ctrlr = ctrlr.QueryInterface(Ci.nsICommandController);
			ctrlr.doCommand(cmd);
		}
	},
	onInput: function(aE) {
		let cmds = ["cmd_undo", "cmd_redo"];
		cmds.forEach(function(aCmd) {
			let ctrlr = tBoxCtrlrs.getControllerForCommand(aCmd);
			if (ctrlr)
				$id(aCmd + "-btn").disabled = !ctrlr.isCommandEnabled(aCmd);
		});
		aE && aE.stopPropagation();
	},
	openPage: function(aURL, aE) {
		try {
			window.opener.gBrowser.selectedTab = window.opener.gBrowser.addTab(aURL);
		} catch (aEr) {
			try {
				window.opener.openContentTab(aURL);
			} catch (aError) {
				window.opener.openUILink(aURL, aE);
			}
		}
	},
	$CNS: function(aElName, aAttrs, aNameSpace) { // Creates elements
		var el = aNameSpace ?
			document.createElementNS(aNameSpace, aElName) :
			document.createElement(aElName);
		if (aAttrs)
			Object.keys(aAttrs).forEach(function(n) {
				if (aAttrs[n] !== "")
					el.setAttribute(n, aAttrs[n]);
			});
		return el;
	},
	get tBox() {
		return $id("input-textbox");
	},
	accel: function(aE) {
		let isOSX = Services.appinfo.OS.toLowerCase().startsWith("darwin");
		if (aE)
			return isOSX ? aE.metaKey : aE.ctrlKey;
		return isOSX ? "Command" : "Control";
	},
};
]]></script>
