<prefwindow id="multi_snippets_inserter_settings_window_%S_E_L_F_I_D%"
			 xmlns:html="http://www.w3.org/1999/xhtml"
			 xmlns="http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul"
			 onload="$.onLoad();"
			 onunload="$.onUnload();"
			 buttons="accept,cancel,extra1,extra2"
			 buttonlabelextra1="Apply"
			 buttonlabelextra2="Reset to defaults"
			 ondialogextra1="$.onApply();"
			 ondialogextra2="$.resetToDefaults();"
			 title="Multi Snippets Inserter settings">
				<script>
		<![CDATA[
var Cc = Components.classes;
var Ci = Components.interfaces;
var Cu = Components.utils;
var storage = window._storage;
var cb_btn = storage.button;
function $id(aId) {
	return document.getElementById(aId);
}
var $ = {
	_initialized : false,
	onLoad : function () {
		this._initialized = true;
		window.addEventListener("change", this, false);
		window.addEventListener("focus", this, false);
		window.addEventListener("blur", this, false);
		var docElt = document.documentElement;
		this.instantApply = docElt.instantApply;
		docElt.setAttribute("title",
		 "Multi Snippets Inserter settings - Button #ID: " + cb_btn.id.slice(20));
		docElt.getButton("extra1").setAttribute("icon", "save");
		let extra2 = docElt.getButton("extra2");
		extra2.setAttribute("icon", "clear");
		extra2.setAttribute("tooltip", "tt_18");
		docElt.setAttribute("cancelbuttonlabel", docElt.mStrBundle.GetStringFromName("button-cancel"));
		this.setButtons(true);
		this.setItemsVisibility();
	},
	handleEvent: function(aE) {
		switch (aE.type) {
			case "change":
				if (aE.target.localName !== "preference")
					return;
				if (!this.instantApply)
					this.updateApplyButton(aE);
				this.setItemsVisibility();
				break;
			case "focus":
				cb_btn.style.setProperty("outline", "4px groove green", "important");
				cb_btn.style.setProperty("outline-offset", "-3px", "important");
				break;
			case "blur":
				cb_btn.style.setProperty("outline", "2px dotted green", "important");
				cb_btn.style.setProperty("outline-offset", "-3px", "important");
				break;
		}
	},
	setItemsVisibility: function() {
		setTimeout(function() {
			$id("flip_attr_radiobox").disabled = !$id("pConvertMenuToPanel").value;
			$id("open_on_hover_close_delay").disabled = !$id("pOpenOnHover").value;
			$id("open_on_hover_open_delay").disabled = !$id("pOpenOnHover").value;
			$id("evaluator_textbox").disabled = !$id("pEvaluatorEnabled").value;
		}, 100);
	},
	changes: [],
	updateApplyButton: function(aE) {
		var item = aE.target;
		if (item.localName != "preference")
			return;
		var valueChanged = item.value != item.valueFromPreferences;
		var index = this.changes.indexOf(item);
		if (valueChanged && index == -1)
			this.changes.push(item);
		else if (!valueChanged && index > -1)
			this.changes.splice(index, 1);
		this.setButtons(!this.changes.length);
	},
	onApply : function () {
		this.setButtons(true);
		while (this.changes.length) {
			var preference = this.changes.shift();
			preference.batching = true;
			preference.valueFromPreferences = preference.value;
			preference.batching = false;
		}
		this.onLoad();
		cb_btn.MultiSnippetsInserter.selfDestroy();
	},
	setButtons: function(aDisable) {
		var docElt = document.documentElement;
		// when in instantApply mode apply and accept buttons are hidden except when user
		// change min/max width value
		var applyButton = docElt.getButton("extra1");
		applyButton.disabled = aDisable;
		applyButton.hidden = this.instantApply && aDisable;
		docElt.getButton("accept").hidden = aDisable;

		var action = aDisable ? "close" : "cancel";
		var cancelButton = docElt.getButton("cancel");
		cancelButton.label = docElt.getAttribute(action + "buttonlabel");
		cancelButton.setAttribute("icon", action);
	},
	setDisabled: function(aItemOrId, aVal) {
		var item = typeof(aItemOrId) == "string" ? $id(aItemOrId) : aItemOrId;
		if (!item)
			return;
		if (item.hasAttribute("inverseDependency"))
			aVal = !aVal;
		// remove disabled when the value is false
		this.setItem(item, "disabled", aVal || null);
	},
	setItem: function(aItemOrId, aAttr, aVal) {
		var el = typeof(aItemOrId) == "string" ? $id(aItemOrId) : aItemOrId;
		if (el) {
			if (aVal === null || aVal === undefined) {
				el.removeAttribute(aAttr);
				return;
			}
			if (typeof(aVal) == "boolean")
				aVal = aVal ? "true" : "false";

			if (!el.hasAttribute(aAttr) || el.getAttribute(aAttr) != aVal)
				el.setAttribute(aAttr, aVal);
		}
	},
	onUnload : function () {
		cb_btn.style.removeProperty("outline", "4px groove green", "important");
		cb_btn.style.removeProperty("outline-offset", "-3px", "important");
		window.removeEventListener("change", this, false);
		cb_btn.MultiSnippetsInserter.selfDestroy();
	},
	resetToDefaults : function () {
		cb_btn.MultiSnippetsInserter.resetToDefaults(window);
	},
};
]]></script>
