custombutton.initcode:
// To avoid initialization while moving the button in "Australis customize mode". ¬¬
if (self.parentNode && /^wrapper-/.test(self.parentNode.id))
	return;

$ = this.Sanitizer = {
	Init: function() {
		// If button is located inside App Button or in Vertical Toolbar Addon
		// Put it right here due to those sheets of Palemoon and SeaMonkey.
		if (self.parentNode && (self.parentNode.id.match("PanelUI") ||
				self.parentNode.id.match("vertical-") ||
				self.parentNode.id === "aios-toolbar")) {
			self.orient = "vertical";
		} else
			self.orient = "horizontal";

		if (!("Services" in window))
			Cu.import("resource://gre/modules/Services.jsm");

		if (!($._UID in window)) {
			window[$._UID] = {
				CustomizableUI: false,
			};
		}

		$.InitPrefs();

		for (let p in $.DefPrefs) {
			if ($.DefPrefs.hasOwnProperty(p))
				$["_" + p] = $.gPref(p);
		}

		$.createElements();
		$.HandlePrefListener();

		self.type = "menu";
		self.value = -1;
		self.tooltipText = "";
		self.setAttribute("tooltip", $._UID + "-dynamic-tooltip");
		self.oncontextmenu = function(aE) {
			if (aE.shiftKey || aE.ctrlKey || aE.altKey)
				aE.preventDefault();
		};
		self.onclick = function(aE) {
			(!aE.shiftKey && aE.ctrlKey && !aE.altKey && aE.button === 0) &&
			(aE.preventDefault() | aE.stopPropagation() | $.clickSwitch("CLeftC"));
			(aE.shiftKey && aE.ctrlKey && !aE.altKey && aE.button === 0) &&
			(aE.preventDefault() | aE.stopPropagation() | $.clickSwitch("CSLeftC"));
			(!aE.shiftKey && !aE.ctrlKey && !aE.altKey && aE.button === 1) &&
			(aE.preventDefault() | aE.stopPropagation() | $.clickSwitch("MiddleC"));
			(!aE.shiftKey && !aE.ctrlKey && aE.altKey && aE.button === 2) &&
			(aE.preventDefault() | aE.stopPropagation() | $.clickSwitch("SRightC"));
			(!aE.shiftKey && aE.ctrlKey && !aE.altKey && aE.button === 2) &&
			(aE.preventDefault() | aE.stopPropagation() | $.clickSwitch("CRightC"));
			(aE.shiftKey && aE.ctrlKey && !aE.altKey && aE.button === 2) &&
			(aE.preventDefault() | aE.stopPropagation() | $.clickSwitch("CSRightC"));
		};

		if (!$._showOptions) { // Fail safe in the event that the Options menu item is hidden.
			if ($._CLeftC !== 1 && $._CSLeftC !== 1 && $._MiddleC !== 1 &&
				$._SRightC !== 1 && $._CRightC !== 1 && $._CSRightC !== 1)
				$.sPref("CRightC", 1);
		}

		if ("CustomizableUI" in window) {
			if (!window[$._UID]["CustomizableUI"]) {
				window[$._UID]["CustomizableUI"] = true;
				CustomizableUI.addListener({
					onCustomizeEnd: function(aWindow) {
						setTimeout(function() {
							aWindow[$._UID]["CustomizableUI"] && $.selfDestroy();
						}, Math.floor(Math.random() * (1 + 2000 - 200)) + 200);
					}
				});
			}
		}

		(function dropmarkerVisibility() {
			if (self.parentNode &&
				(!self.parentNode.id.match("PanelUI") || !self.parentNode.id.match("vertical-"))) {
				let dropMarker = document.getAnonymousElementByAttribute(self, "class",
					"toolbarbutton-menu-dropmarker");
				if (dropMarker)
					dropMarker.setAttribute("hidden", !$._showDropmarker);
				else
					setTimeout(function() {
						dropmarkerVisibility();
					}, 100);
			}
		})();

		if ($._openOnHover) {
			self.onmouseover = function(aE) {
				clearTimeout($.openOnHoverTimer);
				$.openOnHoverTimer = setTimeout(function() {
					$.selfPop && $.selfPop.openPopup(self, "after_start", 0, 0, false, null, null);
				}, $._openOnHoverOpenDelay);
				aE.stopPropagation();
			};
			self.onmouseout = function(aE) {
				clearTimeout($.openOnHoverTimer);
				$.openOnHoverTimer = setTimeout(function() {
					if ($.selfPop && $.selfPop.state === "open")
						$.selfPop.hidePopup();
				}, $._openOnHoverCloseDelay);
				aE.stopPropagation();
			};
		} else {
			self.onmouseover = null;
			self.onmouseout = null;
		}

		$.selfPop && $.populateMainPopup($.selfPop);

		addDestructor(function(aReason) {
			if (aReason === "update" || aReason === "delete") {
				$.HandlePrefListener(true);
				// Delete all elements created by this button.
				let selfEls = document.getElementsByAttribute("cb_id", $._UID);
				Array.prototype.slice.call(selfEls).forEach(function(aEl) {
					aEl.parentNode.removeChild(aEl);
				});
			}
			if (aReason === "delete") {
				(Services.prefs.getBranch($.Branch)).deleteBranch("");
				delete window[$._UID];
				Services.prefs.savePrefFile(null);
			}
		});
	},

	createElements: function() {
		if (!$.selfPop) {
			let m = self.appendChild($.$CNS("menupopup", {
				id: $._UID + "-mpop",
				cb_id: $._UID,
				orient: "vertical",
				position: $._PopupPosition,
				tooltip: $._UID + "-dynamic-tooltip",
				onclick: "document.getElementById('" + self.id +
					"').Sanitizer.menuItemsProxy(event);event.stopPropagation();",
				// To run items without closing menupopup while pressing Shift key.
				onmouseup: "if(event.shiftKey)event.preventDefault();",
				oncontextmenu: "event.preventDefault();event.stopPropagation();",
				onpopupshowing: "event.stopPropagation();",
				onpopuphiding: "event.stopPropagation();",
			}));
			if ($._ConvertToPanel) {
				m.style.MozAppearance = "none";
				m.style.MozBinding = "url('chrome://global/content/bindings/popup.xml#arrowpanel')";
				m.style.background = "transparent";
				m.style.border = "none";
				m.style.MozImageRegion = "auto";
			}
		}
		if (!($.$($._UID + "-dynamic-tooltip"))) {
			$.$("mainPopupSet").appendChild($.$CNS("tooltip", {
				id: $._UID + "-dynamic-tooltip",
				cb_id: $._UID,
				onpopupshowing: "event.stopPropagation();document.getElementById('" + self.id +
					"').Sanitizer.populateDynamicTooltip(event, this);",
				onpopuphiding: "event.stopPropagation();",
				flex: 1,
			}));
		}
	},

	populatePositioningTooltip: function(aE, aN) {
		aN.textContent = "";

		let pos = aE.target.triggerNode.getAttribute("value");
		if (pos === "") {
			aE.preventDefault();
			return false;
		}
		let box = aN.appendChild($.$CNS("hbox"));
		box.appendChild($.$CNS("spacer", {
			flex: 1
		}));
		box.appendChild($.$CNS("label", {
			value: "Preview",
			style: "font-weight:bold;",
		}));
		box.appendChild($.$CNS("spacer", {
			flex: 1
		}));
		aN.appendChild($.$CNS("image", {
			src: $.getHelp(pos),
			width: 185,
			height: 120,
		}));
	},

	populateDynamicTooltip: function(aE, aN) {
		aN.textContent = "";
		let trigger = aE.target.triggerNode;
		if (trigger.nodeName !== "menuitem" && trigger.nodeName !== "toolbarbutton") {
			aE.preventDefault();
			return false;
		}

		if (trigger.nodeName === "menuitem") {
			let desc = $.$CNS("description");
			// Use of "textContent" to achieve word wrap. ¬¬
			desc.textContent = trigger.getAttribute("_tt");
			aN.appendChild(desc);
			aN.appendChild($.$CNS("separator")); // Empty space
		}

		var grid = aN.appendChild($.$CNS("grid"));
		var cols = grid.appendChild($.$CNS("columns"));
		cols.appendChild($.$CNS("column", {
			flex: 2
		}));
		cols.appendChild($.$CNS("column", {
			flex: 3
		}));
		cols.appendChild($.$CNS("column", {
			flex: 1
		}));
		var rows = grid.appendChild($.$CNS("rows"));
		// AppendRow
		let AR = function(aRowsNode, aLabel1, aLabel2, aLabel3, aEmpty) {
			let row = $.$CNS("row");
			if (aEmpty) {
				row.appendChild($.$CNS("label", {
					value: " ",
					width: 0,
				}));
			} else {
				row.appendChild($.$CNS("label", {
					value: aLabel1,
					style: "font-weight: bold;",
				}));
				row.appendChild($.$CNS("label", {
					value: aLabel2,
					style: "font-weight: bold;",
				}));
				row.appendChild($.$CNS("label", {
					value: aLabel3,
				}));
			}
			aRowsNode.appendChild(row);
		};

		if (trigger.nodeName === "menuitem") {
			AR(rows, "Clicks on menuitems", "", "");
			AR(rows, "Left click", "→", "Run item");
			AR(rows, "Shift + Left click", "→", "Run item without closing menu");
			AR(rows, "Ctrl + Right click", "→", "Hide menuitem");
			return true;
		}
		if (trigger.id === self.id) {
			AR(rows, trigger.name, "→", "ID = " + trigger.id.slice(20));
			AR(rows, null, null, null, true);
			AR(rows, "Clicks on button", "", "");
			AR(rows, "Left click", "→", "Open/Close main menu");

			let eventList = ["CLeftC", "CSLeftC", "MiddleC", "SRightC", "CRightC", "CSRightC", "HotKey"];
			for (let evt in eventList) {
				if ($["_" + eventList[evt]] !== 0) {
					let _event = eventList[evt],
						timeRange = eventList[evt] + "_TimeRange",
						TabH = eventList[evt] + "_TabHndr",
						_eventAbbrTxt,
						_actionTxt,
						_timeRangeTxt,
						_tabHandlerTxt;
					switch (_event) {
						case "CLeftC":
							_eventAbbrTxt = "\tCtrl + Left click: ";
							break;
						case "CSLeftC":
							_eventAbbrTxt = "\tCtrl + Shift + Left click: ";
							break;
						case "MiddleC":
							_eventAbbrTxt = "\tMiddle click: ";
							break;
						case "SRightC":
							_eventAbbrTxt = "\tShift + Right click: ";
							break;
						case "CRightC":
							_eventAbbrTxt = "\tCtrl + Right click: ";
							break;
						case "CSRightC":
							_eventAbbrTxt = "\tCtrl + Shift + Right click: ";
							break;
						case "HotKey":
							_eventAbbrTxt = "\t" + ($.$(self.id).getAttribute("cb-accelkey")) + ": ";
							break;
					}
					switch ($["_" + _event]) {
						case 0:
							break;
						case 1:
							_actionTxt = "Open Options";
							break;
						case 2:
							_actionTxt = "Clear All Data";
							break;
						case 3:
							_actionTxt = "Clear All Data (VO)";
							break;
						case 4:
							_actionTxt = "Clear All & Restart";
							break;
						case 5:
							_actionTxt = "Clear All & Restart (VO)";
							break;
						case 6:
							_actionTxt = "Clear All & Exit";
							break;
						case 7:
							_actionTxt = "Clear All & Exit (VO)";
							break;
					}
					switch ($["_" + timeRange]) {
						case 0:
							_timeRangeTxt = " | Everything | ";
							break;
						case 1:
							_timeRangeTxt = " | Last Hour | ";
							break;
						case 2:
							_timeRangeTxt = " | Last Two Hours | ";
							break;
						case 3:
							_timeRangeTxt = " | Last Four Hours | ";
							break;
						case 4:
							_timeRangeTxt = " | Today | ";
							break;
						case 5:
							_timeRangeTxt = " | Default | ";
							break;
					}
					switch ($["_" + TabH]) {
						case 1:
							_tabHandlerTxt = "\t\t\n";
							break;
						case 2:
							_tabHandlerTxt = "Close all tabs\t\t\n";
							break;
						case 3:
							_tabHandlerTxt = "Close other tabs\t\t\n";
							break;
						case 4:
							_tabHandlerTxt = "Reload all tabs\t\t\n";
							break;
						case 5:
							_tabHandlerTxt = "Reload current tab\t\t\n";
							break;
						case 6:
							_tabHandlerTxt = "Close others & Rel.\t\t\n";
							break;
					}
					if ($["_" + _event] === 1 && _event !== "HotKey")
						AR(rows, _eventAbbrTxt, "→", _actionTxt);
					else if ($["_" + _event] !== 1 && _event !== "HotKey")
						AR(rows, _eventAbbrTxt, "→", _actionTxt + _timeRangeTxt + _tabHandlerTxt);
					if ($._UseHotKey && ($.$(self.id).getAttribute("cb-accelkey") !== "") &&
						($._HotKey === 1) && (_event === "HotKey")) {
						AR(rows, null, null, null, true);
						AR(rows, "Button Hotkey", "", "");
						AR(rows, _eventAbbrTxt, "→", _actionTxt);
					} else if ($._UseHotKey && ($.$(self.id).getAttribute("cb-accelkey") !== "") &&
						($._HotKey !== 1) && (_event === "HotKey")) {
						AR(rows, null, null, null, true);
						AR(rows, "Button Hotkey", "", "");
						AR(rows, _eventAbbrTxt, "→", _actionTxt + _timeRangeTxt + _tabHandlerTxt);
					}
				}
			}

			aN.appendChild($.$CNS("separator")); // Empty space
			let desc2 = $.$CNS("description", {
				style: "font-weight:bold;",
			});
			// Use of "textContent" to achieve word wrap. ¬¬
			desc2.textContent = "Read this button help for very important usage notes.\n" +
				"Right click on button, \"Button Help\" item.";
			aN.appendChild(desc2);
			return true;
		}
	},

	InitPrefs: function() {
		let branch = Services.prefs.getDefaultBranch(self.Sanitizer.Branch),
			defaults = self.Sanitizer.DefPrefs;
		for (let key in defaults) {
			if (defaults.hasOwnProperty(key)) {
				let val = defaults[key];
				switch (typeof val) {
					case "boolean":
						branch.setBoolPref(key, val);
						break;
					case "number":
						branch.setIntPref(key, val);
						break;
					case "string":
						let str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
						str.data = val;
						branch.setComplexValue((key),
							Ci.nsISupportsString, str);
						break;
				}
			}
		}
	},

	selfDestroy: function() {
		setTimeout(function() {
			self.destroy("update");
			setTimeout(function() {
				self.init();
			}, 50);
		}, 50);
	},

	HandlePrefListener: function(aRem) {
		var prefs = $.DefPrefs;
		for (let key in prefs) {
			let pref = $.Branch + key;
			if (prefs.hasOwnProperty(key)) {
				if (aRem)
					Services.prefs.removeObserver(pref, $.PrefListener, false);
				else
					Services.prefs.addObserver(pref, $.PrefListener, false);
			}
		}
	},

	PrefListener: {
		observe: function(aSubject, aTopic, aPStr) {
			if (aTopic === "nsPref:changed") {
				let prefName = aPStr.slice($.Branch.length);
				$["_" + prefName] = $.gPref(prefName);
			}
		}
	},

	populateMainPopup: function(aNode) {
		aNode.textContent = "";
		let icn01 = "";
		let icn02 = icn03 = icn04 = icn05 = icn06 = icn07 = icn08 = icn09 = icn10 = icn11 = icn12 = icn13 = icn14 = icn01;
		if ($._IconsColor === 1 && $._showMItemsIcon) {
			icn01 = $.getHelp("CIcon01");
			icn02 = $.getHelp("CIcon02");
			icn03 = $.getHelp("CIcon03");
			icn04 = $.getHelp("CIcon04");
			icn05 = $.getHelp("CIcon05");
			icn06 = $.getHelp("CIcon06");
			icn07 = $.getHelp("CIcon07");
			icn08 = $.getHelp("CIcon08");
			icn09 = $.getHelp("CIcon09");
			icn10 = $.getHelp("CIcon10");
			icn11 = $.getHelp("CIcon11");
			icn12 = $.getHelp("CIcon12");
			icn13 = $.getHelp("CIcon13");
			icn14 = $.getHelp("CIcon14");
		} else if ($._IconsColor === 2 && $._showMItemsIcon) {
			icn01 = $.getHelp("BIcon01");
			icn02 = icn03 = icn04 = icn05 = icn06 = icn07 = icn08 = icn01;
			icn09 = $.getHelp("BIcon09");
			icn10 = $.getHelp("BIcon10");
			icn11 = $.getHelp("BIcon11");
			icn12 = $.getHelp("BIcon12");
			icn13 = $.getHelp("BIcon13");
			icn14 = $.getHelp("BIcon14");
		} else if ($._IconsColor === 3 && $._showMItemsIcon) {
			icn01 = $.getHelp("DIcon01");
			icn02 = icn03 = icn04 = icn05 = icn06 = icn07 = icn08 = icn01;
			icn09 = $.getHelp("DIcon09");
			icn10 = $.getHelp("DIcon10");
			icn11 = $.getHelp("DIcon11");
			icn12 = $.getHelp("DIcon12");
			icn13 = $.getHelp("DIcon13");
			icn14 = $.getHelp("DIcon14");
		}

		let tHM, tHPop, tHMI1, tHMI2, tHMI3, sep4, sep5, sep6;
		if ($._tabHndgMenuType === 1 || $._tabHndgMenuType === 2) {
			tHM = $.$CNS("menu", {
				label: "Tabs handling",
				class: "menu-iconic",
				image: icn12
			});
			tHPop = tHM.appendChild($.$CNS("menupopup", {
				orient: "vertical",
				tooltip: $._UID + "-dynamic-tooltip",
				onmouseup: "if(event.shiftKey)event.preventDefault();",
				oncontextmenu: "event.preventDefault();event.stopPropagation();",
				onpopupshowing: "event.stopPropagation();",
				onpopuphiding: "event.stopPropagation();"
			}));
		}
		if ($._tabHndgMenuType !== 0) {
			tHMI1 = $.$CNS("menuitem", {
				label: "Close all tabs",
				class: "menuitem-iconic",
				_value: "closeAllTabs",
				_prefValue: "showTabHndgCATabs",
				image: icn09
			});
			tHMI2 = $.$CNS("menuitem", {
				label: "Close other tabs",
				class: "menuitem-iconic",
				_value: "closeOtherTabs",
				_prefValue: "showTabHndgCOTabs",
				image: icn09
			});
			tHMI3 = $.$CNS("menuitem", {
				label: "Reload all tabs",
				class: "menuitem-iconic",
				_value: "reloadAllTabs",
				_prefValue: "showTabHndgRATabs",
				image: icn11
			});
			sep4 = $.$CNS("menuseparator", {
				_prefValue: "sep4"
			});
			sep5 = $.$CNS("menuseparator", {
				_prefValue: "sep5"
			});
			sep6 = $.$CNS("menuseparator", {
				_prefValue: "sep6"
			});
		}

		if ($._tabHndgMenuType === 1) {
			$._showTabHndgCATabs && tHPop.appendChild(tHMI1);
			$._showTabHndgCOTabs && tHPop.appendChild(tHMI2);
			$._showTabHndgRATabs && tHPop.appendChild(tHMI3);
			aNode.appendChild(tHM);
		}

		if ($._tabHndgMenuType === 3) {
			$._showTabHndgCATabs && aNode.appendChild(tHMI1);
			$._sep4 && aNode.appendChild(sep4);
			$._showTabHndgCOTabs && aNode.appendChild(tHMI2);
			$._sep5 && aNode.appendChild(sep5);
			$._showTabHndgRATabs && aNode.appendChild(tHMI3);
			$._sep6 && aNode.appendChild(sep6);
		}

		if ($._showClearHistory) {
			aNode.appendChild($.$CNS("menuitem", {
				label: "Browsing & Download",
				class: "menuitem-iconic",
				_tt: "Clear Browsing & Download History.",
				_value: "clearHistory",
				_prefValue: "showClearHistory",
				image: icn01,
			}));
		}
		if ($._showClearFormdata) {
			aNode.appendChild($.$CNS("menuitem", {
				label: "Form & Search",
				class: "menuitem-iconic",
				_tt: "Clear Form & Search History.",
				_value: "clearFormdata",
				_prefValue: "showClearFormdata",
				image: icn02,
			}));
		}
		if ($._showClearCookies) {
			aNode.appendChild($.$CNS("menuitem", {
				label: "Cookies",
				class: "menuitem-iconic",
				_tt: "Clear Cookies.",
				_value: "clearCookies",
				_prefValue: "showClearCookies",
				image: icn03,
			}));
		}
		if ($._showClearCache) {
			aNode.appendChild($.$CNS("menuitem", {
				label: "Cache",
				class: "menuitem-iconic",
				_tt: "Clear Cache.",
				_value: "clearCache",
				_prefValue: "showClearCache",
				image: icn04,
			}));
		}
		if ($._showClearActiveLogins) {
			aNode.appendChild($.$CNS("menuitem", {
				label: "Active Logins",
				class: "menuitem-iconic",
				_tt: "Clear Active Logins.",
				_value: "clearActiveLogins",
				_prefValue: "showClearActiveLogins",
				image: icn05,
			}));
		}
		if ($._showClearOfflineWebsiteData) {
			aNode.appendChild($.$CNS("menuitem", {
				label: "Offline Website Data",
				class: "menuitem-iconic",
				_tt: "Clear Offline Website Data.",
				_value: "clearOfflineWebsiteData",
				_prefValue: "showClearOfflineWebsiteData",
				image: icn06,
			}));
		}
		if ($._showClearSitePreferences && !$.appID("SM")) {
			aNode.appendChild($.$CNS("menuitem", {
				label: "Site Preferences",
				class: "menuitem-iconic",
				_tt: "Clear Site Preferences.",
				_value: "clearSitePreferences",
				_prefValue: "showClearSitePreferences",
				image: icn07,
			}));
		}

		$._sep7 && aNode.appendChild($.$CNS("menuseparator", {
			_prefValue: "sep7"
		}));

		let cad_tt, cadr_tt;
		if ($._clearAllData_Bhvr === 1) {
			cad_tt = "Performs the actions of ONLY VISIBLE menu items SIMULTANEOUSLY.";
		} else if ($._clearAllData_Bhvr === 2) {
			cad_tt = "Performs the actions of ALL (HIDDEN ITEMS INCLUDED) menu items SIMULTANEOUSLY.";
		}
		if ($._clearAllDataResEx_Bhvr === 1) {
			cadr_tt = "Performs the actions of ONLY VISIBLE menu items SIMULTANEOUSLY.";
		} else if ($._clearAllDataResEx_Bhvr === 2) {
			cadr_tt = "Performs the actions of ALL (HIDDEN ITEMS INCLUDED) menu items SIMULTANEOUSLY.";
		}

		if ($._showClearAllData) {
			aNode.appendChild($.$CNS("menuitem", {
				label: "Clear all data",
				class: "menuitem-iconic",
				_tt: cad_tt + "\n\n(See options to change behaivor)",
				_value: "clearAllData",
				_prefValue: "showClearAllData",
				image: icn08,
			}));
		}
		$._sep1 && aNode.appendChild($.$CNS("menuseparator", {
			_prefValue: "sep1"
		}));
		if ($._showAllDataRestart) {
			aNode.appendChild($.$CNS("menuitem", {
				label: "Clear and RESTART",
				class: "menuitem-iconic",
				_tt: cadr_tt + "\nThe browser is restarted after cleaning.\n\n(See options to change behaivor)",
				_value: "clearAllDataRestart",
				_prefValue: "showAllDataRestart",
				image: icn13,
			}));
		}
		$._sep2 && aNode.appendChild($.$CNS("menuseparator", {
			_prefValue: "sep2"
		}));
		if ($._showAllDataExit) {
			aNode.appendChild($.$CNS("menuitem", {
				label: "Clear and EXIT",
				class: "menuitem-iconic",
				_tt: cadr_tt + "\nThe browser is closed after cleaning.\n\n(See options to change behaivor)",
				_value: "clearAllDataExit",
				_prefValue: "showAllDataExit",
				image: icn13,
			}));
		}
		$._sep3 && aNode.appendChild($.$CNS("menuseparator", {
			_prefValue: "sep3"
		}));

		if ($._tabHndgMenuType === 2) {
			$._showTabHndgCATabs && tHPop.appendChild(tHMI1);
			$._showTabHndgCOTabs && tHPop.appendChild(tHMI2);
			$._showTabHndgRATabs && tHPop.appendChild(tHMI3);
			aNode.appendChild(tHM);
		}

		if ($._tabHndgMenuType === 4) {
			$._showTabHndgCATabs && aNode.appendChild(tHMI1);
			$._sep4 && aNode.appendChild(sep4);
			$._showTabHndgCOTabs && aNode.appendChild(tHMI2);
			$._sep5 && aNode.appendChild(sep5);
			$._showTabHndgRATabs && aNode.appendChild(tHMI3);
			$._sep6 && aNode.appendChild(sep6);
		}

		if ($._showOptions) {
			aNode.appendChild($.$CNS("menuitem", {
				label: "Options",
				_tt: "Configure this button.",
				_value: "openOptions",
				_prefValue: "showOptions",
				image: icn10,
			}));
		}
	},

	clickSwitch: function(aPseudoEvent) {
		switch ($["_" + aPseudoEvent]) {
			case 0:
				break;
			case 1:
				$.openOptions();
				break;
			case 2:
				$.clickSProxy("All", aPseudoEvent + "_TabHndr", aPseudoEvent + "_TimeRange");
				break;
			case 3:
				$.clickSProxy("Vis", aPseudoEvent + "_TabHndr", aPseudoEvent + "_TimeRange");
				break;
			case 4:
				$.clickSProxy("AllRes", aPseudoEvent + "_TabHndr", aPseudoEvent + "_TimeRange");
				break;
			case 5:
				$.clickSProxy("VisRes", aPseudoEvent + "_TabHndr", aPseudoEvent + "_TimeRange");
				break;
			case 6:
				$.clickSProxy("AllExit", aPseudoEvent + "_TabHndr", aPseudoEvent + "_TimeRange");
				break;
			case 7:
				$.clickSProxy("VisExit", aPseudoEvent + "_TabHndr", aPseudoEvent + "_TimeRange");
				break;
		}
	},

	clickSProxy: function(aCond, aTabHandler, aTimeSpan) {
		if ($._showWarningsOnClicks) {
			var msg;
			switch (aCond) {
				case "All":
					msg = "Clear all navigation data?\n\nWARNING!!! Hidden menu items are included!!!";
					break;
				case "Vis":
					msg = "Clear all navigation data?\n\nNOTICE: Hidden menu items are NOT included.";
					break;
				case "AllRes":
					msg = "Clear all navigation data and RESTART the browser?\n\nWARNING!!! Hidden menu items are included!!!";
					break;
				case "VisRes":
					msg = "Clear all navigation data and RESTART the browser?\n\nNOTICE: Hidden menu items are NOT included.";
					break;
				case "AllExit":
					msg = "Clear all navigation data and EXIT the browser?\n\nWARNING!!! Hidden menu items are included!!!";
					break;
				case "VisExit":
					msg = "Clear all navigation data and RESTART the browser?\n\nNOTICE: Hidden menu items are NOT included.";
					break;
			}
			let cBox = custombuttons.confirmBox(self.name, msg, "Yes", "Cancel");
			if (cBox) {
				$.clickS(aCond, aTabHandler, aTimeSpan);
			} else
				return;
		} else if (!$._showWarningsOnClicks) {
			$.clickS(aCond, aTabHandler, aTimeSpan);
		}
	},

	clickS: function(aCond, aTabHandler, aTimeSpan) {
		var _timeSpan;
		switch ($["_" + aTimeSpan]) {
			case 0:
				_timeSpan = 0;
				break;
			case 1:
				_timeSpan = 1;
				break;
			case 2:
				_timeSpan = 2;
				break;
			case 3:
				_timeSpan = 3;
				break;
			case 4:
				_timeSpan = 4;
				break;
			case 5:
				_timeSpan = null;
				break;
		}
		switch ($["_" + aTabHandler]) {
			case 1:
				break;
			case 2:
				$.tabHandlerCaller(2);
				break;
			case 3:
				$.tabHandlerCaller(3);
				break;
			case 6:
				(aCond === "All" || aCond === "Vis") && $.tabHandlerCaller(6);
				break;
		}
		setTimeout(function() {
			var nsIAppStartup = Cc["@mozilla.org/toolkit/app-startup;1"].getService(Ci.nsIAppStartup);
			switch (aCond) {
				case "All":
					$.clearAllData(_timeSpan);
					break;
				case "Vis":
					$.clearAllDataVisOnly(_timeSpan);
					break;
				case "AllRes":
					$.clearAllData(_timeSpan);
					setTimeout(function() {
						nsIAppStartup.quit(nsIAppStartup.eAttemptQuit | nsIAppStartup.eRestart);
					}, 2000);
					break;
				case "VisRes":
					$.clearAllDataVisOnly(_timeSpan);
					setTimeout(function() {
						nsIAppStartup.quit(nsIAppStartup.eAttemptQuit | nsIAppStartup.eRestart);
					}, 2000);
					break;
				case "AllExit":
					$.clearAllData(_timeSpan);
					setTimeout(function() {
						setTimeout(function() {
							nsIAppStartup.quit(nsIAppStartup.eAttemptQuit);
						}, 500);
					}, 1500);
					break;
				case "VisExit":
					$.clearAllDataVisOnly(_timeSpan);
					setTimeout(function() {
						setTimeout(function() {
							nsIAppStartup.quit(nsIAppStartup.eAttemptQuit);
						}, 500);
					}, 1500);
					break;
			}
			if (aCond === "All" || aCond === "Vis") {
				switch ($["_" + aTabHandler]) {
					case 1:
						break;
					case 4:
						$.tabHandlerCaller(4);
						break;
					case 5:
						$.tabHandlerCaller(5);
						break;
					case 6:
						BrowserStop();
						$.tabHandlerCaller(6);
						break;
				}
				setTimeout(function() {
					$._showNotifications && $.notifyme("Finished",
						"All navigation data cleared.",
						$.getHelp("NotImg01"),
						$.alertCallback);
				}, 1000);
			}
		}, 500);
	},

	menuItemsProxy: function(aE) {
		if (!aE.shiftKey && aE.ctrlKey && !aE.altKey && aE.button === 2) {
			aE.preventDefault();
			if (aE.target.nodeName === "menuitem" || aE.target.nodeName === "menuseparator") {
				$.sBP(aE.target.getAttribute("_prefValue"), false);
				$.selfDestroy();
				setTimeout(function() {
					$.selfPop && $.selfPop.openPopup(self, "after_start", 0, 0, false, false)
				}, 200);
			}
		} else if (aE.button === 0) {
			let msg;
			let val = aE.target.getAttribute("_value");
			switch (val) {
				case "closeAllTabs":
					$.closeAllTabs();
					return;
				case "closeOtherTabs":
					$.closeOtherTabs();
					return;
				case "reloadAllTabs":
					$.reloadAllTabs();
					return;
				case "openOptions":
					$.openOptions();
					return;
				case "clearHistory":
					msg = "Browsing and Download History";
					break;
				case "clearFormdata":
					msg = "Form and Search History";
					break;
				case "clearCookies":
					msg = "Cookies";
					break;
				case "clearCache":
					msg = "Cache";
					break;
				case "clearActiveLogins":
					msg = "Active logins";
					break;
				case "clearOfflineWebsiteData":
					msg = "Offline Website Data";
					break;
				case "clearSitePreferences":
					msg = "Site Preferences";
					break;
				case "clearAllData":
					msg = "All navigation data";
					break;
				case "clearAllDataRestart":
					msg = "all data and RESTART browser";
					break;
				case "clearAllDataExit":
					msg = "all data and EXIT browser";
					break;
				default:
					return;
			}
			if ($._showGlobalWarnings) {
				let cBox = custombuttons.confirmBox(self.name, "Clear " + msg + "?", "Yes", "Cancel");
				if (cBox) {
					$.runMenuItem(val, msg);
				} else
					return;
			} else
				$.runMenuItem(val, msg);
		}
		aE.stopPropagation();
	},

	runMenuItem: function(aMenuItem, aMsg) {
		if (!aMenuItem)
			return true;
		switch ($["_" + aMenuItem + "_TabHndr"]) {
			case 1:
				break;
			case 2:
				$.tabHandlerCaller(2);
				break;
			case 3:
				$.tabHandlerCaller(3);
				break;
			case 6:
				$.tabHandlerCaller(6);
				break;
		}
		var nsIAppStartup = Cc["@mozilla.org/toolkit/app-startup;1"].getService(Ci.nsIAppStartup);
		var _timeSpan = null;
		setTimeout(function() {
			switch (aMenuItem) {
				case "clearHistory":
					$.resetPREF_DOM();
					setTimeout(function() {
						$.sBP($.PREF_DOM + "history", true, true);
						$.sBP($.PREF_DOM + "downloads", true, true);
						$.startSanitizing(_timeSpan);
					}, 200);
					break;
				case "clearFormdata":
					$.resetPREF_DOM();
					setTimeout(function() {
						$.sBP($.PREF_DOM + "formdata", true, true);
						$.sBP($.PREF_DOM + "urlbar", true, true);
						$.startSanitizing(_timeSpan);
					}, 200);
					break;
				case "clearCookies":
					$.resetPREF_DOM();
					setTimeout(function() {
						$.sBP($.PREF_DOM + "cookies", true, true);
						$.startSanitizing(_timeSpan);
					}, 200);
					break;
				case "clearCache":
					$.resetPREF_DOM();
					setTimeout(function() {
						$.sBP($.PREF_DOM + "cache", true, true);
						$.startSanitizing(_timeSpan);
					}, 200);
					break;
				case "clearActiveLogins":
					$.resetPREF_DOM();
					setTimeout(function() {
						$.sBP($.PREF_DOM + "sessions", true, true);
						$.startSanitizing(_timeSpan);
					}, 200);
					break;
				case "clearOfflineWebsiteData":
					$.resetPREF_DOM();
					setTimeout(function() {
						$.sBP($.PREF_DOM + "offlineApps", true, true);
						$.startSanitizing(_timeSpan);
					}, 200);
					break;
				case "clearSitePreferences":
					$.resetPREF_DOM();
					setTimeout(function() {
						$.sBP($.PREF_DOM + "siteSettings", true, true);
						$.startSanitizing(_timeSpan);
					}, 200);
					break;
				case "clearAllData":
					switch ($._clearAllData_Bhvr) {
						case 1:
							$.clearAllDataVisOnly(_timeSpan);
							break;
						case 2:
							$.clearAllData(_timeSpan);
							break;
					}
					break;
				case "clearAllDataRestart":
					switch ($._clearAllDataResEx_Bhvr) {
						case 1:
							$.clearAllDataVisOnly(_timeSpan);
							break;
						case 2:
							$.clearAllData(_timeSpan);
							break;
					}
					setTimeout(function() {
						nsIAppStartup.quit(nsIAppStartup.eAttemptQuit | nsIAppStartup.eRestart);
					}, 2000);
					break;
				case "clearAllDataExit":
					switch ($._clearAllDataResEx_Bhvr) {
						case 1:
							$.clearAllDataVisOnly(_timeSpan);
							break;
						case 2:
							$.clearAllData(_timeSpan);
							break;
					}
					setTimeout(function() {
						nsIAppStartup.quit(nsIAppStartup.eAttemptQuit);
					}, 2000);
					break;
			}
			if (aMenuItem !== "clearAllDataRestart" && aMenuItem !== "clearAllDataExit") {
				switch ($["_" + aMenuItem + "_TabHndr"]) {
					case 4:
						$.tabHandlerCaller(4);
						break;
					case 5:
						$.tabHandlerCaller(5);
						break;
					case 6:
						BrowserStop();
						$.tabHandlerCaller(6);
						break;
				}
				setTimeout(function() {
					$._showNotifications && $.notifyme("Finished",
						aMsg + " cleared.",
						$.getHelp("NotImg01"),
						$.alertCallback);
				}, 1000);
			}
		}, 500);
	},

	clearAllData: function(aTimeSpan) {
		$.resetPREF_DOM();
		setTimeout(function() {
			$.sBP($.PREF_DOM + "history", true, true);
			$.sBP($.PREF_DOM + "downloads", true, true);
			$.sBP($.PREF_DOM + "formdata", true, true);
			$.appID("SM") && $.sBP($.PREF_DOM + "urlbar", true, true);
			$.sBP($.PREF_DOM + "cookies", true, true);
			$.sBP($.PREF_DOM + "cache", true, true);
			$.sBP($.PREF_DOM + "sessions", true, true);
			$.sBP($.PREF_DOM + "offlineApps", true, true);
			$.appID("SM") || $.sBP($.PREF_DOM + "siteSettings", true, true);
			setTimeout(function() {
				$.startSanitizing(aTimeSpan);
			}, 500);
		}, 200);
	},

	clearAllDataVisOnly: function(aTimeSpan) {
		$.resetPREF_DOM();
		setTimeout(function() {
			$._showClearHistory && ($.sBP($.PREF_DOM + "history", true, true) |
				$.sBP($.PREF_DOM + "downloads", true, true) | ($.appID("SM") && ($.sBP($.PREF_DOM +
					"urlbar", true, true))));
			$._showClearFormdata && $.sBP($.PREF_DOM + "formdata", true, true);
			$._showClearCookies && $.sBP($.PREF_DOM + "cookies", true, true);
			$._showClearCache && $.sBP($.PREF_DOM + "cache", true, true);
			$._showClearActiveLogins && $.sBP($.PREF_DOM + "sessions", true, true);
			$._showClearOfflineWebsiteData && $.sBP($.PREF_DOM + "offlineApps", true, true);
			(!$.appID("SM") && $._showClearSitePreferences) && $.sBP($.PREF_DOM + "siteSettings", true, true);
			setTimeout(function() {
				$.startSanitizing(aTimeSpan);
			}, 500);
		}, 200);
	},

	startSanitizing: function(aTimeSpan) {
		Services.scriptloader.loadSubScript($.appID("SM") ?
			"resource://gre/modules/Sanitizer.jsm" :
			"chrome://browser/content/sanitize.js");
		var promptOnSanitize = "privacy.sanitize.promptOnSanitize";
		var tmp1 = $.Branch + "temp1";
		$.appID("SM") && ($.sBP(tmp1, $.gBP(promptOnSanitize), true) | $.sBP(promptOnSanitize, false, true));
		setTimeout(function() {
			var s = $.appID("SM") ? Sanitizer : new Sanitizer();
			(!$.appID("SM")) && ((s.ignoreTimespan = (aTimeSpan === 0) ? true : false) |
				(s.range = (aTimeSpan !== null) ? Sanitizer.getClearRange(aTimeSpan) : null) |
				(s.prefDomain = $.PREF_DOM));
			s.sanitize();
			setTimeout(function() {
				$.appID("SM") && ((Services.prefs.getPrefType(tmp1) !== 0) &&
					($.sBP(promptOnSanitize, true, true) | Services.prefs.clearUserPref(tmp1)));
			}, 100);
		}, 500);
	},

	resetPREF_DOM: function() {
		var prefDomain = Services.prefs.getChildList($.PREF_DOM);
		for (var prefName in prefDomain) {
			(Services.prefs.getPrefType(prefDomain[prefName]) === 128) &&
			$.sBP(prefDomain[prefName], false, true);
		}
	},

	tabHandlerCaller: function(aTabHandler) {
		switch (aTabHandler) {
			case 1:
				break;
			case 2:
				$.closeAllTabs();
				break;
			case 3:
				$.closeOtherTabs();
				break;
			case 4:
				$.reloadAllTabs();
				break;
			case 5:
				$.reloadCurrentTab();
				break;
			case 6:
				$.closeOtherTabsReload();
				break;
		}
	},

	closeAllTabs: function() {
		gBrowser.selectedTab = gBrowser.addTab($._lastPageStanding);
		for (var i = gBrowser.tabContainer.childNodes.length - 1; i >= 0; i--) {
			if (gBrowser.tabContainer.childNodes[i] !== gBrowser.selectedTab) {
				gBrowser.removeTab(gBrowser.tabContainer.childNodes[i]);
			}
		}
	},

	closeOtherTabs: function() {
		var WOCOT = $.appID("SM") ?
			$.gBP("browser.tabs.warnOnCloseOther") :
			$.gBP("browser.tabs.warnOnCloseOtherTabs");
		if (typeof Tabmix === "object") {
			var TMPTWOC = $.gBP("extensions.tabmix.tabs.warnOnClose");
			$.sBP("browser.tabs.warnOnCloseOtherTabs", false, true);
			$.sBP("extensions.tabmix.tabs.warnOnClose", false, true);
			gBrowser.removeAllTabsBut(gBrowser.selectedTab);
			setTimeout(function() {
				$.sBP("browser.tabs.warnOnCloseOtherTabs", WOCOT, true);
				$.sBP("extensions.tabmix.tabs.warnOnClose", TMPTWOC, true);
			}, 150);
		} else {
			$.appID("SM") ?
				$.sBP("browser.tabs.warnOnCloseOther", false, true) :
				$.sBP("browser.tabs.warnOnCloseOtherTabs", false, true);
			gBrowser.removeAllTabsBut(gBrowser.selectedTab);
			setTimeout(function() {
				$.appID("SM") ?
					$.sBP("browser.tabs.warnOnCloseOther", WOCOT, true) :
					$.sBP("browser.tabs.warnOnCloseOtherTabs", WOCOT, true);
			}, 150);
		}
	},

	reloadAllTabs: function() {
		gBrowser.reloadAllTabs();
	},

	reloadCurrentTab: function() {
		gBrowser.reloadTab(gBrowser.selectedTab);
	},

	closeOtherTabsReload: function() {
		$.closeOtherTabs();
		setTimeout(function() {
			gBrowser.removeAllTabsBut(gBrowser.selectedTab);
			gBrowser.reloadTab(gBrowser.selectedTab);
		}, 150);
	},

	openOptions: function() {
		var em = Services.ww.getWindowEnumerator("CB:Sanitizer:Settings:Window");
		var index = 1;
		while (em.hasMoreElements()) {
			let win = em.getNext();
			if (win.document.documentElement.getAttribute("windowtype") === "CB:Sanitizer:Settings:Window") {
				win.focus();
				return;
			}
			index++
		}
		var x = $.getHelp("OptionsWindow").replace(/>\s+</g, "><");
		x = x.replace("_self_id_", self.id);
		x = x.split("_RoOt_").join($.Branch);
		var xul = "data:application/vnd.mozilla.xul+xml;text/plain," + encodeURIComponent(x);
		openDialog(xul, "", "chrome, resizable, dependant, dialog, centerscreen, close, alwaysRaised");
	},

	HotKeyPressed: function() {
		$._UseHotKey && $.clickSwitch("HotKey");
	},

	notifyme: function(aTitle, aMsg, aImg, aCallback) { // Notification function
		let origin = (Services.prefs.getPrefType("ui.alertNotificationOrigin") !== 0) ?
			Services.prefs.getIntPref("ui.alertNotificationOrigin") :
			null;
		let win = Services.ww.openWindow(null, "chrome://global/content/alerts/alert.xul",
			"_blank", "chrome,titlebar=no,popup=yes", null);
		win.arguments = [aImg, aTitle, aMsg, !!aCallback, "", origin, "", "", null, (!!aCallback ? aCallback : null)];
	},

	getHelp: function(aPlaceholder) { // Get specific data, surrounded by "placeholders", from the Help tab.
		return $.$(self.id).getAttribute("Help").split("<!--" + aPlaceholder + "-->")[1];
	},

	appID: function(aId) { // App ID boolean return.
		let app = {
			"SM": "{92650c4d-4b8e-4d2a-b7eb-24ecf4f6b63a}",
			"FX": "{ec8030f7-c20a-464f-9b0e-13a3a9e97384}",
			"TB": "{3550f703-e582-4d05-9a08-453d09bdfdc6}",
			"PM": "{8de7fcbb-c55c-4fbe-bfc5-fc555c87dbc4}"
		};
		return (Services.appinfo.ID === app[aId]);
	},

	$: function(aId) {
		return document.getElementById(aId);
	},

	$CNS: function(aElName, aAttrs, aNS) {
		// Holy crap!!! createElementNS is absolute sheet!!
		// "key" elements are useless for one!! ¬¬
		var el = aNS ?
			document.createElementNS(aNS, aElName) :
			document.createElement(aElName);
		if (aAttrs)
			Object.keys(aAttrs).forEach(function(n) {
				if (aAttrs[n] !== "")
					el.setAttribute(n, aAttrs[n])
			});
		return el;
	},

	gBP: function(aKey) { //Get Boolean Pref
		return Services.prefs.getBoolPref(aKey);
	},

	gIP: function(aKey) { //Get Integer Pref
		return Services.prefs.getIntPref(aKey);
	},

	sBP: function(aKey, aVal, aBranch) { //Set Boolean Pref
		Services.prefs.setBoolPref((!aBranch) ? $.Branch + aKey : aKey, aVal);
	},

	gCV: function(aKey) { // Get string preference with unicode support.
		return Services.prefs.getComplexValue(aKey, Ci.nsISupportsString).data;
	},

	sCV: function(aKey, aVal) { // Set string preference with unicode support.
		var str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
		str.data = aVal;
		Services.prefs.setComplexValue(aKey, Ci.nsISupportsString, str);
	},

	//START// Preferences handling functions
	prefHandling: {
		resetP: function(aPrefWin) {
			var msg = "You're about to reset Multi Search preferences to their default values​​.\t\t\n" +
				"This process is immediate and irreversible.\t\n" +
				"It is advisable to make a backup of the preferences before proceeding.\t\n\n" +
				"Do you want to continue?\n";
			var cBox = custombuttons.confirmBox(self.name, msg, "Yes", "Cancel");
			if (cBox) {
				(Services.prefs.getBranch($.Branch)).deleteBranch("");
				aPrefWin.close();
				$.selfDestroy();
			} else
				return;
		},
		exportP: function() {
			var out = [],
				value,
				prefList = (Services.prefs.getBranch($.Branch)).getChildList("", {});
			for (let pref in prefList) {
				switch (Services.prefs.getPrefType($.Branch + prefList[pref])) {
					case 0:
						break;
					case 32:
						value = $.gCV($.Branch + prefList[pref]);
						break;
					case 64:
						value = $.gIP($.Branch + prefList[pref]);
						break;
					case 128:
						value = $.gBP($.Branch + prefList[pref]) ? "true" : "false";
						break;
				}
				out.push(prefList[pref] + "|" + value);
			}
			var file = Cc["@mozilla.org/file/local;1"].createInstance(Ci.nsIFile);
			file.initWithPath(Services.dirsvc.get("Desk", Ci.nsIFile).path);
			file.append("SanitizerPrefs" + ".js");
			file.createUnique(Ci.nsIFile.NORMAL_FILE_TYPE, 0x1B6);
			var fos = Cc["@mozilla.org/network/file-output-stream;1"]
				.createInstance(Ci.nsIFileOutputStream);
			fos.init(file, 0x02 | 0x08 | 0x20, 0x1B6, 0);
			var cos = Cc["@mozilla.org/intl/converter-output-stream;1"]
				.createInstance(Ci.nsIConverterOutputStream);
			cos.init(fos, "UTF-8", 0, 0);
			cos.writeString(out.join("\n"));
			cos.close();
			$.notifyme("Multi Search Preferences",
				"Successfully exported to your Desktop.",
				$.getHelp("WebSearch"),
				$.alertCallback);
		},
		importP: function(aPrefWin) {
			let msg = "You're about to import new preferences to Sanitizer.\t\t\n" +
				"This process is immediate and irreversible.\t\n" +
				"It is advisable to make a backup of the preferences before proceeding.\t\n\n" +
				"Do you want to continue?\n";
			let cBox = custombuttons.confirmBox((self.label || self.name), msg, "Yes", "Cancel");
			if (cBox) {
				aPrefWin.close();
				var localDir = Cc["@mozilla.org/file/local;1"].createInstance(Ci.nsIFile);
				localDir.initWithPath(Services.dirsvc.get("Desk", Ci.nsIFile).path);
				var fp = Cc["@mozilla.org/filepicker;1"].createInstance(Ci.nsIFilePicker);
				fp.init(window, "Import Sanitizer Preferences", Ci.nsIFilePicker.modeOpen);
				fp.appendFilter("SanitizerPrefs*.js", "SanitizerPrefs*.js");
				fp.displayDirectory = localDir;
				if (fp.show() === Ci.nsIFilePicker.returnOK) {
					if (fp.file && fp.file.exists()) {
						localDir = fp.file.parent.QueryInterface(Ci.nsIFile);
					}
				} else
					return;
				OS.File.read(fp.file.path).then(function onSuccess(aReadData) {
					let prefFile = new TextDecoder().decode(aReadData);
					prefFile = prefFile.replace(/\r\n/g, "\n").split("\n");
					for (let p in prefFile) {
						let pair = prefFile[p].split("|");
						var prefName = $.Branch + pair[0],
							prefValue = pair[1];
						if (Services.prefs.getPrefType(prefName)) {
							switch (Services.prefs.getPrefType(prefName)) {
								case 0:
									break;
								case 32:
									let str = Cc["@mozilla.org/supports-string;1"]
										.createInstance(Ci.nsISupportsString);
									str.data = prefValue;
									Services.prefs.setComplexValue((prefName),
										Ci.nsISupportsString, str);
									break;
								case 64:
									Services.prefs.setIntPref(prefName, prefValue);
									break;
								case 128:
									Services.prefs.setBoolPref(prefName, ((prefValue === "true") ? "true" : ""));
									break;
							}
						}
					}
					setTimeout(function() {
						$.selfDestroy();
					}, 1000);
				}, function onFailure(aRejectReason) {
					Cu.reportError(aRejectReason);
				});
			} else
				return;
		}
	},
	//END// Preferences handling functions
	_UID: "__sanitizer" + self.id.slice(20),
	openOnHoverTimer: null,
	alertCallback: { // Thanks to srazzano!!!
		observe: function(aSubject, aTopic, aData) {
			let alt = Services.wm.getMostRecentWindow("alert:alert")
			setTimeout(function() {
				alt.close();
			}, 3000);
			if (aTopic === "alertclickcallback")
				alt.close();
		}
	},
	get PREF_DOM() {
		return $.appID("SM") ? "privacy.item." : "privacy.cpd.";
	},
	get selfPop() {
		return $.$($._UID + "-mpop");
	},
};

this.Sanitizer.Branch = "CB.Sanitizer.Id" + self.id.slice(20) + ".";

