this.Sanitizer.gPref = function(aKey) {
	let branch = Services.prefs.getBranch(self.Sanitizer.Branch);
	switch (typeof self.Sanitizer.DefPrefs[aKey]) {
		case "boolean":
			return branch.getBoolPref(aKey);
			break;
		case "number":
			return branch.getIntPref(aKey);
			break;
		case "string":
			return branch.getComplexValue(aKey, Ci.nsISupportsString).data;
			break;
	}
	return null;
};

this.Sanitizer.sPref = function(aKey, aVal) {
	let branch = Services.prefs.getBranch(self.Sanitizer.Branch);
	switch (typeof aVal) {
		case "boolean":
			branch.setBoolPref(aKey, aVal);
			break;
		case "number":
			branch.setIntPref(aKey, aVal);
			break;
		case "string":
			let str = Cc["@mozilla.org/supports-string;1"].createInstance(Ci.nsISupportsString);
			str.data = aVal;
			branch.setComplexValue((aKey), Ci.nsISupportsString, str);
			break;
	}
};

this.Sanitizer.Init();

