function appID(aId) {
	let app = {
		"SM": "{92650c4d-4b8e-4d2a-b7eb-24ecf4f6b63a}"
	};
	return (Components.classes["@mozilla.org/xre/app-info;1"]
		.getService(Components.interfaces.nsIXULAppInfo).ID === app[aId]);
}

function $(id) document.getElementById(id);
var fromButton = opener.document.getElementById("_self_id_");
var gSanitizerWindow = {
	_initialized: false,
	init: function() {
		this._initialized = true;
		var docElt = document.documentElement;
		this.instantApply = docElt.instantApply;
		window.addEventListener("change", this, false);
		window.addEventListener("beforeaccept", this, false);
		docElt.getButton("extra1").setAttribute("icon", "apply");
		docElt.getButton("extra2").setAttribute("popup", "sanitizer-settings");
		if (appID("SM")) {
			var notForSeaMonkey = document.getElementsByClassName("not_for_seamonkey");
			[].forEach.call(notForSeaMonkey, function(item) {
				item.style.display = "none";
			});
		}
	},
	handleEvent: function(aEvent) {
		switch (aEvent.type) {
			case "change":
				if (aEvent.target.localName != "preference")
					return;
				this.updateBroadcasters(aEvent.target);
				if (!this.instantApply)
					this.updateApplyButton(aEvent);
				break;
			case "beforeaccept":
				break;
		}
	},
	changes: [],
	updateApplyButton: function(aEvent) {
		var item = aEvent.target;
		if (item.localName != "preference")
			return;
		var valueChanged = item.value != item.valueFromPreferences;
		var index = this.changes.indexOf(item);
		if (valueChanged && index == -1)
			this.changes.push(item);
		else if (!valueChanged && index > -1)
			this.changes.splice(index, 1);
	},
	onApply: function() {
		while (this.changes.length) {
			var preference = this.changes.shift();
			preference.batching = true;
			preference.valueFromPreferences = preference.value;
			preference.batching = false;
		}
		fromButton.init();
	},
	onUnload: function() {
		fromButton.init();
	},
	updateBroadcasters: function(aPreference) {
		if (aPreference.type != "bool")
			return;
		var obs = $(aPreference.id.replace("pref_", "obs_"));
		if (obs)
			this.setDisabled(obs, !aPreference.value);
	},
	setDisabled: function(itemOrId, val) {
		var item = typeof(itemOrId) == "string" ? $(itemOrId) : itemOrId;
		if (!item)
			return;
	},
	initMenulists: function() {
		let actionPMList = $("action-primary-menulist"),
			tabHandPMList = $("tabhand-primary-menulist");
		var actionMPop = actionPMList.firstChild,
			tabHandMPop = tabHandPMList.firstChild;
		var actionSMLists = document.getElementsByClassName("secundary-action-menulist"),
			tabHandSMLists = document.getElementsByClassName("secundary-tabhand-menulist");
		for (let a = 0; a < actionSMLists.length; a++) {
			actionSMLists[a].appendChild(actionMPop.cloneNode(true));
			gSanitizerWindow.setMenulistsCheckedState(actionSMLists[a]);
		}
		if (!appID("SM")) {
			let timerangePMList = $("timerange-primary-menulist");
			var timerangeMPop = timerangePMList.firstChild,
				timerangeSMLists = document.getElementsByClassName("secundary-timerange-menulist");
			for (let t = 0; t < timerangeSMLists.length; t++) {
				timerangeSMLists[t].appendChild(timerangeMPop.cloneNode(true));
				gSanitizerWindow.setMenulistsCheckedState(timerangeSMLists[t]);
			}
		}
		for (let h = 0; h < tabHandSMLists.length; h++) {
			tabHandSMLists[h].appendChild(tabHandMPop.cloneNode(true));
			gSanitizerWindow.setMenulistsCheckedState(tabHandSMLists[h]);
		}
	},
	setMenulistsCheckedState: function(aMList) {
		let prefID = aMList.getAttribute("preference");
		let val = $(prefID).value;
		if (val !== -1)
			aMList.selectedIndex = val;
	}
};
