custombutton.initcode:
var $ = this.TabsManager = {
	init: function() {
		if (!("Services" in window))
			Cu.import("resource://gre/modules/Services.jsm");

		$.initPrefs();

		if (!($._UID in window)) {
			window[$._UID] = {
				CustomizableUI: false,
				StyleSheet: false,
				SelfDestroyerListener: false,
				ListOfHotSpots: false,
				TabsContextEvaluated: false,
				FrameScriptLoaded: false,
				RandomID: false,
				TabsContextHandled: false,
				Preferences: {},
				HoverOpenPreviewTimer: null,
				HoverOpenMainPanelTimer: null,
				AppendTabsToTabsBoxTimer: null,
				PanelSelectTabOnHoverTimer: null,
				CleanSlateTimer: null,
				UpdatePreviewTimer: null,
			};
		}

		for (let p in $.DefPrefs) {
			if ($.DefPrefs.hasOwnProperty(p))
				window[$._UID]["Preferences"][p] = $.gPref(p);
		}

		$._ = window[$._UID]["Preferences"];

		self.tooltipText = ""; // Set to blank. Otherwise the tooltip attribute doesn't work.
		self.setAttribute("tooltip", $._UID + "_dynamic_tooltip");
		self.setAttribute("overflows", false);
		$.setSelfImage();
		self.onclick = function(aE) {
			if (aE.button === 0 && !aE.shiftKey && !$.accel(aE) && !aE.altKey) {
				aE.preventDefault();
				$.togglePanel();
			} else if (aE.button === 0 && !aE.shiftKey && $.accel(aE) && !aE.altKey) {
				aE.preventDefault();
				$.openOptions();
			}
			aE.stopPropagation();
		};

		$.CSSLoader();
		$.createMainPanel();
		$.createDynamicTooltip();
		$.handleTabsContext();
		$.createTabsTooltip();
		$.handlePrefObserver();
		$.handleOpenOnHover();
		$.handleHotSpots();

		$._tooltipAllowHovering = ($._.pTabPreviewEnabled || $._.pTabPreviewShowToolbar);

		if (!window[$._UID]["RandomID"]) {
			window[$._UID]["RandomID"] = parseInt(Math.random() * 10000, 10);
		}
		$._randomID = window[$._UID]["RandomID"];

		if (!window[$._UID]["FrameScriptLoaded"]) {
			window[$._UID]["FrameScriptLoaded"] = true;
			$._frameScript = "data:application/javascript," + encodeURIComponent($.getHelp("%FrameScript%")
				.replace(/%Update_Preview_ID%/g, $._updatePreviewMsgID)
				.replace(/%Scroll_Preview_ID%/g, $._scrollMsgID)
				.replace(/%Emulate_Preview_ID%/g, $._emulateMsgID)
				.replace(/%Cleanup_ID%/g, $._cleanUpMsgID)
				.replace(/%Response_ID%/g, $._responseMsgID));
			$._globalMM.loadFrameScript($._frameScript, true);
			$._globalMM.addMessageListener($._responseMsgID, $._messageResponse);
		}

		if (!(window[$._UID]["TabsContextEvaluated"])) {
			window[$._UID]["TabsContextEvaluated"] = true;
			let originalFunc = TabContextMenu.updateContextMenu;
			eval("TabContextMenu.updateContextMenu=" + originalFunc.toString().replace(": gBrowser.selectedTab;",
				": (aPopupMenu.triggerNode.localName === \"toolbarbutton\" && aPopupMenu.triggerNode.hasOwnProperty(\"tab\")) ? aPopupMenu.triggerNode.tab : gBrowser.selectedTab;"));
		}

		if ("CustomizableUI" in window) {
			if (!window[$._UID]["CustomizableUI"]) {
				window[$._UID]["CustomizableUI"] = true;
				CustomizableUI.addListener({
					onCustomizeEnd: function(aWindow) {
						var thisBtn = aWindow.document.getElementById(self.id);
						setTimeout(function() {
							aWindow[$._UID]["CustomizableUI"] && thisBtn.TabsManager.selfDestroy();
						}, Math.floor(Math.random() * (1 + 2000 - 200)) + 200);
					}
				});
			}
		}

		addDestructor(function(aReason) {
			if (aReason === "update" || aReason === "delete") {
				$.handleHotSpots(true);
				$.panelFunctions._removeListeners();
				$.handlePrefObserver(true);
				let selfEls = document.getElementsByAttribute("cb_id", $._UID);
				Array.prototype.slice.call(selfEls).forEach(function(aEl) {
					aEl && aEl.parentNode.removeChild(aEl);
				});
			}
			if (aReason === "delete" || aReason === "destructor") {
				$._mm.sendAsyncMessage($._cleanUpMsgID);
				$._globalMM.removeDelayedFrameScript($._frameScript);
				$._globalMM.removeMessageListener($._responseMsgID, $._messageResponse);
			}
			if (aReason === "delete") {
				$.styleSheet(window[$._UID]["StyleSheet"], true);
				delete window[$._UID];
				(Services.prefs.getBranch($.Branch)).deleteBranch("");
				Services.prefs.savePrefFile(null);
			}
		});

		$.handleInvertedImages();
	},

	_messageResponse: {
		receiveMessage: function(aMsg) {
			if (aMsg.name !== $._responseMsgID)
				return;
			$.canvas._scaleX = aMsg.data.scaleX;
			$.canvas._scaleY = aMsg.data.scaleY;
			$.canvas._scale = aMsg.data.scale;
			$.canvas.width = aMsg.data.width;
			$.canvas.height = aMsg.data.height;
			$._lastCanvasWidth = aMsg.data.width;
			$._lastCanvasHeight = aMsg.data.height;
			$.canvas.getContext("2d").putImageData(aMsg.data.imgData, 0, 0);
			$.tooltipLabel.style.maxWidth = aMsg.data.width + "px";
		},
	},

	handleTabsContext: function() {
		if (!(window[$._UID]["TabsContextHandled"])) {
			window[$._UID]["TabsContextHandled"] = true;
			let tCtx = $.$("tabContextMenu");
			let trigger = function(aE) {
				let isShowing = (aE.type === "popupshowing");
				isShowing && clearTimeout(window[$._UID]["HoverOpenPreviewTimer"]);
				isShowing && $.tooltip.hidePopup();
				Array.prototype.slice.call(this.getElementsByAttribute("_multisel", "*")).forEach(function(aMI) {
					if ($.selectedTabs.length > 0)
						aMI.hidden = false;
					else
						aMI.hidden = true;
				});
			};
			tCtx.addEventListener("popupshowing", trigger, false);
			tCtx.addEventListener("popuphidden", trigger, false);
			tCtx.appendChild($.$CNS("separator", {
				_multisel: true,
				hidden: true,
			}));
			if ("toggleMuteAudio" in gBrowser.selectedTab) {
				let muteAll = tCtx.appendChild($.$CNS("menuitem", {
					id: $._UID + "-mute_unmute_selected_tabs",
					label: "Mute/Unmute selected tabs",
					_multisel: true,
					// image: "chrome://browser/skin/tabbrowser/tab-audio.svg#tab-audio-backgroundTab-pressed",
					class: "menuitem-iconic marked-for-invertion",
					hidden: true,
					oncommand: "event.preventDefault();event.stopPropagation();",
				}));
				muteAll.onclick = function(aE) {
					Array.prototype.slice.call($.selectedTabs).forEach(function(aTabItem) {
						aTabItem.tab.toggleMuteAudio();
					});
				};
			}
			let pinAll = tCtx.appendChild($.$CNS("menuitem", {
				id: $._UID + "-pin_unpin_selected_tabs",
				label: "Pin/Unpin selected tabs",
				_multisel: true,
				class: "menuitem-iconic marked-for-invertion",
				hidden: true,
				oncommand: "event.preventDefault();event.stopPropagation();",
			}));
			pinAll.onclick = function(aE) {
				Array.prototype.slice.call($.selectedTabs).forEach(function(aTabItem) {
					gBrowser[aTabItem.tab.pinned ? "unpinTab" : "pinTab"](aTabItem.tab);
				});
			};
			let reloadAll = tCtx.appendChild($.$CNS("menuitem", {
				id: $._UID + "-reload_selected_tabs",
				label: "Reload selected tabs",
				_multisel: true,
				class: "menuitem-iconic marked-for-invertion",
				hidden: true,
				oncommand: "event.preventDefault();event.stopPropagation();",
			}));
			reloadAll.onclick = function(aE) {
				Array.prototype.slice.call($.selectedTabs).forEach(function(aTabItem) {
					gBrowser.reloadTab(aTabItem.tab);
				});
			};
			let closeAll = tCtx.appendChild($.$CNS("menuitem", {
				id: $._UID + "-close_selected_tabs",
				label: "Close selected tabs",
				_multisel: true,
				hidden: true,
				oncommand: "event.preventDefault();event.stopPropagation();",
			}));
			closeAll.onclick = function(aE) {
				Array.prototype.slice.call($.selectedTabs).forEach(function(aTabItem) {
					gBrowser.removeTab(aTabItem.tab, {
						animate: true
					});
				});
			};
		}
	},

	createTabsTooltip: function() {
		if (!$.tooltip) {
			let t = $.$("mainPopupSet").appendChild($.$CNS("panel", {
				id: $._UID + "-tabs-box-tooltip",
				"xmlns:html": "http://www.w3.org/1999/xhtml",
				cb_id: $._UID,
				orient: "vertical",
				position: "after_start",
				flip: "slide",
				level: "top",
				noautofocus: "true",
				consumeoutsideclicks: "false",
				oncontextmenu: "event.stopPropagation();",
				onmouseout: $.that + "onTooltipOut(event, this);event.stopPropagation();",
				onpopuphiding: $.that + "onTooltipHiding(event, this);event.stopPropagation();",
				onpopupshowing: $.that + "populateTabsTooltip(event, this);event.stopPropagation();",
			}));
			let vB = t.appendChild($.$CNS("vbox", {
				id: $._UID + "-tabs-preview-container"
			}));
			vB.appendChild($.$CNS("label", {
				id: $._UID + "-tabs-preview-title",
				crop: "end"
			}));
			let tB = vB.appendChild($.$CNS("toolbar", {
				id: $._UID + "-tabs-preview-toolbar",
				fullscreentoolbar: "true",
				onclick: "event.stopPropagation();",
				oncommand: $.that + "previewFunctions.onCommand(event);event.stopPropagation();",
			}));
			tB.appendChild($.$CNS("toolbarbutton", {
				id: $._UID + "-tabs-preview-back-button",
				class: "marked-for-invertion",
				tooltiptext: "Go backwards one page",
				label: "back"
			}));
			tB.appendChild($.$CNS("toolbarbutton", {
				id: $._UID + "-tabs-preview-forward-button",
				class: "marked-for-invertion",
				tooltiptext: "Go forward one page",
				label: "forward"
			}));
			tB.appendChild($.$CNS("toolbarbutton", {
				id: $._UID + "-tabs-preview-reload-button",
				class: "marked-for-invertion",
				tooltiptext: "Reload page",
				label: "reload"
			}));
			tB.appendChild($.$CNS("toolbarbutton", {
				id: $._UID + "-tabs-preview-pin-button",
				class: "marked-for-invertion",
				tooltiptext: "Pin tab",
				label: "pin"
			}));
			tB.appendChild($.$CNS("toolbarbutton", {
				id: $._UID + "-tabs-preview-zoom-button",
				class: "marked-for-invertion",
				tooltiptext: "Zoom preview",
				label: "zoom"
			}));
			tB.appendChild($.$CNS("spacer", {
				flex: "1"
			}));
			tB.appendChild($.$CNS("toolbarbutton", {
				id: $._UID + "-tabs-preview-close-button",
				class: "marked-for-invertion",
				tooltiptext: "Close tab",
				label: "close"
			}));
			vB.appendChild($.$CNS("html:canvas", {
				id: $._UID + "-tabs-preview-canvas"
			}, xhtmlns));
		}
	},

	onTooltipOut: function(aE, aNode) {
		if (!$.tooltip.mozMatchesSelector(":hover"))
			$.tooltip.hidePopup();
		aE.stopPropagation();
	},

	onTooltipHiding: function(aE, aNode) {
		$._tab.linkedBrowser.removeEventListener("MozAfterPaint", $.previewFunctions.onMozAfterPaint, false);
		$._tab = null;
		$._resetPreview();
		$._resetTitle();
		clearTimeout(window[$._UID]["HoverOpenPreviewTimer"]);
		$.tooltip.removeAttribute("_open");
		aE.stopPropagation();
	},

	populateTabsTooltip: function(aE, aNode) {
		if ($._tab.localName !== "tab")
			aE.preventDefault();
		var b = $._tab.linkedBrowser;
		if (!b)
			aE.preventDefault();
		if ($._prevDoNotAllow)
			aE.preventDefault();

		$._updateTitle();
		let tBar = $.toolbar;
		tBar.hidden = true;
		let canvas = $.canvas;
		canvas.hidden = true;
		aNode.setAttribute("_open", "true");

		if ($._.pTabPreviewShowToolbar)
			tBar.hidden = false;
		if (!$._.pTabPreviewEnabled)
			return true;
		if ($._.pTabPreviewRequireControlKey && !$._accelKeyPressed)
			return true;
		if ($._.pTabPreviewEnabled)
			canvas.hidden = false;
		else
			return true;
		$._tab.linkedBrowser.addEventListener("MozAfterPaint", $.previewFunctions.onMozAfterPaint, false);
		$._ensureTabIsRestored();
		$._updatePreview();
		return true;
	},

	_openDelayedTooltip: function(aE) {
		if (!$._.pTabPreviewRequireControlKey && $._accelKeyPressed)
			return;
		var anotherPopupOpen = !!document.popupNode;
		if (!aE.target.mozMatchesSelector(":hover") || anotherPopupOpen)
			return;
		if (document.documentElement.mozMatchesSelector(":-moz-window-inactive"))
			return;

		if (!window[$._UID]["HoverOpenPreviewTimer"] && $.tooltip.state === "open") {
			if ($._tab.hasAttribute("pending"))
				$._updatePreview();
			return;
		}
		clearTimeout(window[$._UID]["HoverOpenPreviewTimer"]);
		window[$._UID]["HoverOpenPreviewTimer"] = setTimeout(function() {
			$.tooltip.openPopup(aE.target, "", 0, 0, false, null, null);
		}, $._.pTabPreviewShowDelay);
	},

	_ensureTabIsRestored: function() {
		let ensure = $._.pTabPreviewLoadUnloadedTabs;
		let reload = function(aEnsure) {
			if (aEnsure)
				$._tab.linkedBrowser.reload();
			else
				$.canvas.height = 0;
		};
		if ("unloadTabObj" in window && $._tab.hasAttribute("uT_tabUnload"))
			reload(ensure);
		if ($._tab.linkedBrowser.__SS_restoreState === 1)
			reload(ensure);
	},

	_updatePreview: function() {
		// The timer is to avoid triggering _updatePreview() a million times per second!!
		clearTimeout(window[$._UID]["UpdatePreviewTimer"]);
		window[$._UID]["UpdatePreviewTimer"] = setTimeout(function() {
			if (!$.tooltip)
				return false;
			if (!$._tab)
				return false;
			let zoomFactor = $._prevZoomedIn ? 1 + parseInt($._.pTabPreviewZoomFactor / 100, 10) : 1;
			let cW = parseInt($._.pTabPreviewWidth, 10) * zoomFactor;
			let cH = parseInt($._.pTabPreviewHeight, 10) * zoomFactor;
			// $.tooltipLabel.style.maxWidth = cW + "px"; // "Fix" to avoid large label to resize the entire preview.
			if ($._tab.getAttribute("pending") == "true") {
				$._resetPreview();
				if ($._.pTabPreviewLoadUnloadedTabs) {
					let temp = $._.pTabPreviewAutomaticSizePercentage / 100;
					$.canvas.width = $._lastCanvasWidth || window.content.innerWidth * temp;
					$.canvas.height = $._lastCanvasHeight || window.content.innerHeight * temp;
				}
				return;
			}
			if (!$._tab.linkedBrowser)
				return;
			$._tab.linkedBrowser.messageManager.sendAsyncMessage($._updatePreviewMsgID, {
				msgID: $._updatePreviewMsgID,
				auto: $._.pTabPreviewAutomaticSize,
				autoPercentage: $._.pTabPreviewAutomaticSizePercentage,
				zoomFactor: zoomFactor,
				width: cW,
				height: cH
			});
			if ($._.pTabPreviewShowToolbar)
				$.previewFunctions._updateToolbar();
		}, 15);
	},

	_resetPreview: function() {
		$.tooltip.removeAttribute("width");
		$.tooltip.removeAttribute("height");
		var canvas = $.canvas;
		var ctx = canvas.getContext("2d");
		ctx.clearRect(0, 0, canvas.width, canvas.height);
		canvas.removeAttribute("width");
		canvas.height = 0;
	},

	_updateTitle: function(aBtn) {
		let lbl = $.tooltipLabel;
		let uri = $._tab.linkedBrowser.currentURI.spec || "";
		if (aBtn) {
			aBtn.setAttribute("tooltiptext", aBtn.label + "\n" + uri);
			return true;
		}
		lbl.value = $._tab.label;
		lbl.setAttribute("tooltiptext", lbl.value + "\n" + uri);
		lbl.style.maxWidth = $._.pTabPreviewWidth.toString() + "px";
	},

	_resetTitle: function() {
		let lbl = $.tooltipLabel;
		lbl.value = "";
		lbl.removeAttribute("tooltiptext");
		lbl.style.maxWidth = "0px";
		XULBrowserWindow.setOverLink("", null);
	},

	tabsProgressListenerForPanel: {
		onStateChange: function(aBrowser) {
			// gBrowser.getTabForBrowser not defined on Palemoon. 
			let tab = gBrowser.getTabForBrowser ?
				gBrowser.getTabForBrowser(aBrowser) :
				gBrowser._getTabForBrowser(aBrowser);
			if (!tab)
				return;
			if (!$.panel || $.panel.state !== "open")
				return;
			$._setButtonAttributes(tab.mCorrespondingMenuitem, tab);
		},
	},

	tabsProgressListenerForPreview: {
		onStateChange: function(aBrowser) {
			if (!$._tab)
				return;
			if (aBrowser !== $._tab.linkedBrowser)
				return;
			if (!$.tooltip || $.tooltip.state !== "open")
				return;
			$._updatePreview();
		},

		onLocationChange: function(aBrowser) {
			if (!$._tab)
				return;
			if (aBrowser !== $._tab.linkedBrowser)
				return;
			if (!$.tooltip || $.tooltip.state !== "open")
				return;
			$.previewFunctions._updateToolbar();
		},
	},

	handleOpenOnHover: function() {
		if ($._.pPanelOpenOnHover) {
			self.onmouseover = function(aE) {
				$.onHotSpotMouseOver(aE);
			};
			self.onmouseout = function(aE) {
				$.onHotSpotMouseOut(aE);
			};
		} else {
			self.onmouseover = null;
			self.onmouseout = null;
		}
	},

	handleOptionsBox: function() {
		let hasNodes = $.optionsBox.hasChildNodes();
		if (hasNodes) {
			$.populateOptionsBox(true);
			$.optBtn.setAttribute("_checked", false);
		} else {
			$.populateOptionsBox(false);
			$.optBtn.setAttribute("_checked", true);
		}
	},

	populateOptionsBox: function(aDel) {
		let optB = $.optionsBox;
		optB.textContent = "";
		if (aDel)
			return false;
		try {
			let mainHBox = optB.appendChild($.$CNS("hbox"));
			mainHBox.style.overflowX = "auto"; // "overflow" alone doesn't work! 
			mainHBox.style.overflowY = "auto";
			let vBoxA = mainHBox.appendChild($.$CNS("vbox"));
			let hBoxA1 = vBoxA.appendChild($.$CNS("hbox", {
				align: "center"
			}));
			hBoxA1.appendChild($.$CNS("label", {
				value: "Vertical offset:"
			}));
			hBoxA1.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			hBoxA1.appendChild($.$CNS("textbox", {
				type: "number",
				"data-pref": "pPanelVerticalOffset",
				tooltiptext: "Vertical offset for panel in relation to its anchor.\nMinimum: " +
					"-300px.\nMaximum: 300px.",
				size: 3,
				min: -300,
				max: 300,
			}));
			vBoxA.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			let hBoxA2 = vBoxA.appendChild($.$CNS("hbox", {
				align: "center"
			}));
			hBoxA2.appendChild($.$CNS("label", {
				value: "Icon size:"
			}));
			hBoxA2.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			hBoxA2.appendChild($.$CNS("textbox", {
				type: "number",
				"data-pref": "pMaxIconSizeForTabButtonsInPanel",
				tooltiptext: "Each 'tab item' in the grid will display the site's favicon. This " +
					"option allows to set a custom fixed size for those icons.\nMinimum: 16px.\nMaximum: 64px.",
				size: 3,
				min: 16,
				max: 64,
			}));
			vBoxA.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			let hBoxA3 = vBoxA.appendChild($.$CNS("hbox", {
				align: "center",
				"data-auto-tab": false
			}));
			hBoxA3.appendChild($.$CNS("label", {
				value: "Tabs per row:"
			}));
			hBoxA3.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			hBoxA3.appendChild($.$CNS("textbox", {
				type: "number",
				"data-pref": "pPanelButtonTabsPerRow",
				tooltiptext: "How many tabs per row should display the panel.\nMinimum: 1 tab per" +
					" row.\nMaximum: 50 tabs per row.",
				size: 2,
				min: 1,
				max: 50,
			}));
			vBoxA.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			let hBoxA4 = vBoxA.appendChild($.$CNS("hbox", {
				align: "center",
				"data-auto-tab": true
			}));
			hBoxA4.appendChild($.$CNS("label", {
				value: "Tabs width:"
			}));
			hBoxA4.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			hBoxA4.appendChild($.$CNS("textbox", {
				type: "number",
				"data-pref": "pPanelButtonTabsPerRowAutoWidth",
				tooltiptext: "Width used to calculate the amount of tabs per row inside the main " +
					"panel.\nMinimum: 100px.\nMaximum: 250px.",
				size: 3,
				increment: 5,
				min: 100,
				max: 250,
			}));
			vBoxA.appendChild($.$CNS("checkbox", {
				label: "Tabs per row auto",
				"data-pref": "pPanelButtonTabsPerRowAuto",
				tooltiptext: "The amount of tabs per row will be automatically calculated based on" +
					" the 'Tabs width' option.",
				oncommand: "event.stopPropagation();"
			}));

			let vBoxB = mainHBox.appendChild($.$CNS("vbox"));
			let hBoxB1 = vBoxB.appendChild($.$CNS("hbox", {
				align: "center",
				"data-preview": "true",
				"data-preview-size": "false",
			}));
			hBoxB1.appendChild($.$CNS("label", {
				value: "Tabs preview width:"
			}));
			hBoxB1.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			hBoxB1.appendChild($.$CNS("textbox", {
				type: "number",
				"data-pref": "pTabPreviewWidth",
				tooltiptext: "Tabs preview width in pixels.\nMinimum: 100 pixels.\nMaximum: 2000 pixels.",
				size: 4,
				increment: 10,
				min: 100,
				max: 2000,
			}));
			let hBoxB2 = vBoxB.appendChild($.$CNS("hbox", {
				align: "center",
				"data-preview": "true",
				"data-preview-size": "true",
			}));
			hBoxB2.appendChild($.$CNS("label", {
				value: "Tabs preview size:"
			}));
			hBoxB2.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			hBoxB2.appendChild($.$CNS("textbox", {
				type: "number",
				"data-pref": "pTabPreviewAutomaticSizePercentage",
				tooltiptext: "Tabs preview size percentage in relation to the page content size." +
					"\nMinimum: 10%.\nMaximum: 100%.",
				size: 4,
				increment: 5,
				min: 10,
				max: 100,
			}));
			vBoxB.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			let hBoxB3 = vBoxB.appendChild($.$CNS("hbox", {
				align: "center",
				"data-preview": "true",
				"data-preview-size": "false",
			}));
			hBoxB3.appendChild($.$CNS("label", {
				value: "Tabs preview height:"
			}));
			hBoxB3.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			hBoxB3.appendChild($.$CNS("textbox", {
				type: "number",
				"data-pref": "pTabPreviewHeight",
				tooltiptext: "Tabs preview height in pixels.\nMinimum: 100 pixels.\nMaximum: 2000 pixels.",
				size: 4,
				increment: 10,
				min: 100,
				max: 2000,
			}));
			vBoxB.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			let hBoxB4 = vBoxB.appendChild($.$CNS("hbox", {
				align: "center",
				"data-preview": "true",
			}));
			hBoxB4.appendChild($.$CNS("label", {
				value: "Tabs preview zoom:"
			}));
			hBoxB4.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			hBoxB4.appendChild($.$CNS("textbox", {
				type: "number",
				"data-pref": "pTabPreviewZoomFactor",
				tooltiptext: "Zoom factor in % for tabs preview.\nMinimum: 5%\nMaximum: 500%",
				size: 4,
				increment: 5,
				min: 100,
				max: 500,
			}));
			vBoxB.appendChild($.$CNS("checkbox", {
				label: "Auto size preview",
				"data-pref": "pTabPreviewAutomaticSize",
				tooltiptext: "Ignore the fixed preview width/height and size it based on a " +
					"percentage of the actual page content size.",
				"data-preview": "true",
				oncommand: "event.stopPropagation();"
			}));
			let vBoxC = mainHBox.appendChild($.$CNS("vbox"));
			[
				["Invert dark icons",
					"pPanelInvertDarkIcons",
					"Almost all icons used by this button are dark/black. With this option enabled," +
					" a white filter will be applied to those icons so they are actually seen on dark themes."
				],
				["Select tab on hover",
					"pPanelSelectTabOnHover",
					"Auto select tabs on the panel on mouse over."
				],
				["Show pinned tabs",
					"pPanelShowPinnedTabs",
					"Show pinned tabs row."
				],
				["Tabs preview",
					"pTabPreviewEnabled",
					"Enable tabs preview."
				],
				["Show toolbar",
					"pTabPreviewShowToolbar",
					"Show toolbar inside the preview panel. The toolbar is standalone, so it can" +
					" be used even if the tabs preview is disabled."
				]
			].forEach(function(aP) {
				vBoxC.appendChild($.$CNS("checkbox", {
					label: aP[0],
					"data-pref": aP[1],
					oncommand: "event.stopPropagation();",
					tooltiptext: aP[2],
				}));
			});
			let vBoxD = mainHBox.appendChild($.$CNS("vbox"));
			[
				["Send clicks to preview",
					"pTabPreviewSendClicks",
					"Enable sending click events to the page content currently displayed on the tab preview."
				],
				["Send scroll to preview",
					"pTabPreviewSendScroll",
					"If enabled, it will allow you to scroll the page content &quot;through the " +
					"preview&quot; and the preview will be updated.\nScroll with no modifiers:" +
					" Scroll page content vertically by lines.\nCtrl + Scroll: Scroll page" +
					" content vertically by pages.\nShift + Scroll: Scroll page content " +
					"horizontally.\nCtrl + Shift + Scroll: Scroll page content horizontally " +
					"by content width. This is an intent to emulate the vertical Scroll by pages."
				],
				["Load unloaded tabs",
					"pTabPreviewLoadUnloadedTabs",
					"Automatically load unloaded tabs so they are visible when tabs preview is enabled."
				],
				["Require Control key",
					"pTabPreviewRequireControlKey",
					"Require the Control key to be pressed to display the tab preview."
				],
			].forEach(function(aP) {
				vBoxD.appendChild($.$CNS("checkbox", {
					label: aP[0],
					"data-pref": aP[1],
					"data-preview": "true",
					oncommand: "event.stopPropagation();",
					tooltiptext: aP[2],
				}));
			});
			let gBoxA = mainHBox.appendChild($.$CNS("vbox", {
				orient: "vertical"
			}));
			gBoxA.appendChild($.$CNS("caption", {
				label: "Middle click action",
				tooltiptext: "Action triggered when middle-clicking a tab item.",
			}));
			let rGA = gBoxA.appendChild($.$CNS("radiogroup", {
				"data-pref": "pPanelMiddleClickOnTabAction",
				orient: "vertical",
			}));
			rGA.appendChild($.$CNS("radio", {
				label: "Close tab",
				value: 0,
			}));
			rGA.appendChild($.$CNS("radio", {
				label: "Reload tab",
				value: 1,
			}));
			rGA.appendChild($.$CNS("radio", {
				label: "Multi select tabs",
				value: 2,
				tooltiptext: "Select more than one tab to perform in-bulk actions.\nMulti-selection" +
					" can also be done with Ctrl + Left click.",
			}));
			let gBoxB = mainHBox.appendChild($.$CNS("vbox", {
				orient: "vertical"
			}));
			gBoxB.appendChild($.$CNS("caption", {
				label: "Button image",
				tooltiptext: "Choose this button image.",
			}));
			let rGB = gBoxB.appendChild($.$CNS("radiogroup", {
				"data-pref": "pButtonImage",
				orient: "vertical",
			}));
			rGB.appendChild($.$CNS("radio", {
				label: "Custom",
				value: 0,
				tooltiptext: "Set an image by going to 'Edit Button...' &gt; 'Image' section.",
			}));
			rGB.appendChild($.$CNS("radio", {
				label: "Dark image",
				value: 1,
			}));
			rGB.appendChild($.$CNS("radio", {
				label: "Light image",
				value: 2,
			}));
		} finally {
			Array.prototype.slice.call(optB.getElementsByTagName("textbox")).forEach(function(aTBox) {
				let pref = aTBox.getAttribute("data-pref");
				aTBox.value = $._[pref];
				aTBox.style.listStyleImage = "none"; // Remove stupid wrong icons. 
				aTBox.onchange = function(aE) {
					let tBox = this;
					if (pref === "pPanelVerticalOffset") {
						let panel = $.panel;
						let curVal = $._.pPanelVerticalOffset;
						let tBoxVal = tBox.value;
						let deltaY = parseInt(tBoxVal, 10) - parseInt(curVal, 10);
						panel.moveTo(parseInt(panel.boxObject.screenX, 10), parseInt(panel.boxObject.screenY + deltaY, 10));
					}
					setTimeout(function() {
						$.sPref(pref, parseInt(tBox.value, 10));
					}, 10);
				};
			});

			Array.prototype.slice.call(optB.getElementsByTagName("checkbox")).forEach(function(aCBox) {
				aCBox.checked = $._[aCBox.getAttribute("data-pref")];
				aCBox.onclick = function(aE) {
					let pref = aE.target.getAttribute("data-pref");
					let val = $._[pref];
					$.sPref(pref, !val);
					$._checkPrevOptVis(optB);
					$._checkTabsPerRowOptVis(optB);
					aE.stopPropagation();
				};
			});

			$._checkPrevOptVis(optB);
			$._checkTabsPerRowOptVis(optB);

			Array.prototype.slice.call(optB.getElementsByTagName("radiogroup")).forEach(function(aRadG) {
				let pref = aRadG.getAttribute("data-pref");
				aRadG.selectedIndex = $._[pref];
				aRadG.onselect = function(aE) {
					setTimeout(function() {
						$.sPref(pref, aE.target.selectedIndex);
					}, 10);
				};
			});
		}
	},

	_checkTabsPerRowOptVis: function(aOptB) {
		Array.prototype.slice.call(aOptB.getElementsByAttribute("data-auto-tab", "*")).forEach(function(aOptEl) {
			let auto = $._.pPanelButtonTabsPerRowAuto;
			if (aOptEl.getAttribute("data-auto-tab") == "true")
				aOptEl.hidden = !auto;
			else
				aOptEl.hidden = auto;
		});
	},

	_checkPrevOptVis: function(aOptB) {
		let prevEnabled = $._.pTabPreviewEnabled;
		Array.prototype.slice.call(aOptB.getElementsByAttribute("data-preview", "*")).forEach(function(aOptEl) {
			aOptEl.hidden = !prevEnabled;
		});
		if (!prevEnabled)
			return true;
		Array.prototype.slice.call(aOptB.getElementsByAttribute("data-preview-size", "*")).forEach(function(aOptEl) {
			let auto = $._.pTabPreviewAutomaticSize;
			if (aOptEl.getAttribute("data-preview-size") == "true")
				aOptEl.hidden = !auto;
			else
				aOptEl.hidden = auto;
		});
	},

	createMainPanel: function() {
		if (!$.panel) {
			let p = $.$("mainPopupSet").appendChild($.$CNS("panel", {
				id: $._UID + "-main-panel",
				orient: "vertical",
				flip: "slide",
				cb_id: $._UID,
				level: "parent",
				consumeoutsideclicks: "false",
				noautohide: $._.pPanelKeepItOpen,
				onmouseout: "event.stopPropagation();",
				onclick: "event.stopPropagation();",
				oncommand: "event.stopPropagation();",
				onpopupshowing: $.that + "panelFunctions.onPanelShowing(event, this);event.stopPropagation();",
				onpopuphiding: $.that + "panelFunctions.onPanelHidden(event, this);event.stopPropagation();",
			}));
			let hB = p.appendChild($.$CNS("hbox", {
				id: $._UID + "-main-panel-box"
			}));
			let vB1 = hB.appendChild($.$CNS("vbox"));
			vB1.appendChild($.$CNS("vbox", {
				id: $._UID + "-tabs-box",
				onclick: $.that + "panelFunctions.onItemClick(event);event.stopPropagation();",
				oncommand: "event.stopPropagation();",
				oncontextmenu: "event.stopPropagation();",
				onmousemove: $.that + "panelFunctions.onMouseMoveItem(event);",
				onmouseover: $.that + "panelFunctions.onMouseOverItem(event);",
				onmouseout: $.that + "panelFunctions.onMouseOutItem(event);",
				ondragstart: $.that + "dragAndDropHandler.onDragStart(event);",
				ondragover: $.that + "dragAndDropHandler.onDragOver(event);",
				ondrop: $.that + "dragAndDropHandler.onDrop(event);",
			}));
			hB.appendChild($.$CNS("spacer", {
				flex: 1
			}));
			let vB2 = hB.appendChild($.$CNS("vbox", {
				maxwidth: 48,
				class: "tabs-manager-options-buttons"
			}));
			vB2.appendChild($.$CNS("box", {
				flex: 1
			})).appendChild($.$CNS("toolbarbutton", {
				id: $._UID + "-new-tab-button",
				maxwidth: 500,
				tooltiptext: "Open New tab",
				class: "marked-for-invertion",
				oncommand: "BrowserOpenTab();event.stopPropagation();",
			}));
			vB2.appendChild($.$CNS("toolbarbutton", {
				id: $._UID + "-open-options-button",
				tooltiptext: "Open Tabs Manager options window.",
				class: "marked-for-invertion",
				oncommand: $.that + "openOptions();event.stopPropagation();",
			}));
			vB2.appendChild($.$CNS("toolbarbutton", {
				id: $._UID + "-inline-options-button",
				tooltiptext: "Show inline options.",
				class: "marked-for-invertion",
				oncommand: $.that + "handleOptionsBox();",
			}));

			p.appendChild($.$CNS("vbox", {
				id: $._UID + "-options-box"
			}));
		}
	},

	_reloadTab: function(aTab, aE) {
		if (aE.target.getAttribute("_loading") == "true") {
			aTab.linkedBrowser.stop();
			return;
		}
		if (aE.shiftKey) {
			let flags = Ci.nsIWebNavigation.LOAD_FLAGS_BYPASS_PROXY |
				Ci.nsIWebNavigation.LOAD_FLAGS_BYPASS_CACHE;
			aTab.linkedBrowser.reloadWithFlags(flags);
		} else {
			aTab.linkedBrowser.reload();
		}
	},

	previewFunctions: {
		onClick: function(aE) {
			if (aE.target.localName === "canvas")
				$.previewFunctions._performAction("emulate", aE);
		},

		onCommand: function(aE) {
			$.previewFunctions._performAction(aE.target.label, aE);
		},

		onMozAfterPaint: function(aE) {
			if (aE.clientRects.length > 0)
				$._updatePreview();
		},

		onDOMScroll: function(aE) {
			aE.preventDefault();
			aE.stopPropagation();
			if ($._.pTabPreviewAutomaticSize && !$.canvas._scaleX && !$.canvas._scaleY)
				return;
			if (!$._.pTabPreviewAutomaticSize && !$.canvas._scale)
				return;
			let rect = $.canvas.getBoundingClientRect();
			$._tab.linkedBrowser.messageManager.sendAsyncMessage($._scrollMsgID, {
				x: (aE.clientX - rect.left) / ($._.pTabPreviewAutomaticSize ? $.canvas._scaleX : $.canvas._scale),
				y: (aE.clientY - rect.top) / ($._.pTabPreviewAutomaticSize ? $.canvas._scaleY : $.canvas._scale),
				lines: aE.detail,
				ctrlKey: $.accel(aE),
				shiftKey: aE.shiftKey,
			});
			$._updatePreview();
		},

		_performAction: function(aCmd, aE) {
			switch (aCmd) {
				case "back":
					$._tab.linkedBrowser.goBack();
					return;
				case "forward":
					$._tab.linkedBrowser.goForward();
					return;
				case "reload":
					$._reloadTab($._tab, aE);
					return;
				case "pin":
					gBrowser[$._tab.pinned ? "unpinTab" : "pinTab"]($._tab);
					$._appendTabsToTabsBox($.$($._UID + "-tabs-box"));
					break;
				case "close":
					gBrowser.removeTab($._tab, {
						animate: true
					});
					break;
				case "zoom":
					$._prevZoomedIn = !$._prevZoomedIn;
					$._resetPreview();
					$._updatePreview();
					return;
				case "emulate":
					var elt;
					try {
						elt = $.previewFunctions._elementFromPointOnPreview(aE);
					} catch (aErr) {
						elt = null;
					}
					if (elt && elt.namespaceURI === xulns) { // For XUL content only.
						let view = elt.ownerDocument.defaultView;
						if (elt.localName === "button")
							elt.doCommand();
						else if (elt.localName === "checkbox")
							elt.checked = !elt.checked;
						else {
							elt.dispatchEvent(new view.MouseEvent("mousedown", { // "click" event doesn't work on XUL content. 
								view: view,
								cancelable: true,
								bubbles: true,
								button: aE.button,
								ctrlKey: aE.ctrlKey,
								altKey: aE.altKey,
								shiftKey: aE.shiftKey,
								metaKey: aE.metaKey
							}));
						}
					} else { // For web page content only.
						if ($._.pTabPreviewAutomaticSize && !$.canvas._scaleX && !$.canvas._scaleY)
							return;
						if (!$._.pTabPreviewAutomaticSize && !$.canvas._scale)
							return;
						var rect = $.canvas.getBoundingClientRect();
						var x = (aE.clientX - rect.left) / ($._.pTabPreviewAutomaticSize ? $.canvas._scaleX : $.canvas._scale);
						var y = (aE.clientY - rect.top) / ($._.pTabPreviewAutomaticSize ? $.canvas._scaleY : $.canvas._scale);
						$._tab.linkedBrowser.messageManager.sendAsyncMessage($._emulateMsgID, {
							x: x,
							y: y,
							type: "click", // "mousedown" event doesn't work on web content. 
							button: aE.button,
							ctrlKey: aE.ctrlKey,
							altKey: aE.altKey,
							shiftKey: aE.shiftKey,
							metaKey: aE.metaKey
						});
					}
					$._updatePreview();
					return;
				default:
					return;
			}
			$.tooltip.hidePopup();
		},

		_updateToolbar: function() {
			var toolbar = $.toolbar;
			if (toolbar.hidden)
				return;
			if (toolbar.collapsed)
				toolbar.collapsed = false;
			var browser = $._tab.linkedBrowser;
			$.$($._UID + "-tabs-preview-back-button").disabled = !browser.canGoBack;
			$.$($._UID + "-tabs-preview-forward-button").disabled = !browser.canGoForward;
			var button = $.$($._UID + "-tabs-preview-reload-button");
			if (!button.hidden)
				button.setAttribute("_loading", browser.webProgress.isLoadingDocument);
			button = $.$($._UID + "-tabs-preview-pin-button");
			if (!button.hidden)
				button.setAttribute("_active", $._tab.pinned);
			button = $.$($._UID + "-tabs-preview-zoom-button");
			if (!button.hidden)
				button.setAttribute("_active", $._prevZoomedIn);
		},

		_elementFromPointOnPreview: function(aE) {
			var rect = $.canvas.getBoundingClientRect();
			var x = (aE.clientX - rect.left) / ($._.pTabPreviewAutomaticSize ? $.canvas._scaleX : $.canvas._scale);
			var y = (aE.clientY - rect.top) / ($._.pTabPreviewAutomaticSize ? $.canvas._scaleY : $.canvas._scale);
			var win = $._tab.linkedBrowser.contentWindow;
			var elt = win.document.elementFromPoint(x, y);
			if (!elt)
				elt = win.QueryInterface(Ci.nsIInterfaceRequestor).
			getInterface(Ci.nsIDOMWindowUtils).
			elementFromPoint(x, y, true, false);
			if (!elt)
				elt = win.document.body || win.document.documentElement;
			while (/^i?frame$/.test(elt.localName.toLowerCase())) {
				x -= elt.getBoundingClientRect().left;
				y -= elt.getBoundingClientRect().top;
				elt = elt.contentDocument.elementFromPoint(x, y);
			}
			return elt;
		},
	},

	panelFunctions: {
		onPanelHidden: function(aE, aNode) {
			aNode.height = 0;
			aNode.width = 0;
			$.panelFunctions._removeListeners();
			$.tooltip.hidePopup();
			aE.stopPropagation();
		},

		_removeListeners: function() { // Outside of onPanelHidden so I can call it from onDestroy().
			let tabcontainer = gBrowser.tabContainer;
			tabcontainer.removeEventListener("TabAttrModified", $.tabContainerListener, false);
			tabcontainer.removeEventListener("TabClose", $.tabContainerListener, false);
			tabcontainer.removeEventListener("TabOpen", $.tabContainerListener, false);
			tabcontainer.removeEventListener("TabPinned", $.tabContainerListener, false);
			tabcontainer.removeEventListener("TabUnpinned", $.tabContainerListener, false);
			$.canvas.removeEventListener("click", $.previewFunctions.onClick, true);
			$.canvas.removeEventListener("DOMMouseScroll", $.previewFunctions.onDOMScroll, false);
			if ($._tab)
				$._tab.linkedBrowser &&
				$._tab.linkedBrowser.removeEventListener("MozAfterPaint", $.previewFunctions.onMozAfterPaint, false);
			gBrowser.removeTabsProgressListener($.tabsProgressListenerForPanel);
			gBrowser.removeTabsProgressListener($.tabsProgressListenerForPreview);
		},

		onPanelShowing: function(aE, aPanel) {
			aPanel.width = $.panelWidth;
			aPanel.maxWidth = $.panelWidth;
			let tabcontainer = gBrowser.tabContainer;
			tabcontainer.addEventListener("TabAttrModified", $.tabContainerListener, false);
			tabcontainer.addEventListener("TabClose", $.tabContainerListener, false);
			tabcontainer.addEventListener("TabOpen", $.tabContainerListener, false);
			tabcontainer.addEventListener("TabPinned", $.tabContainerListener, false);
			tabcontainer.addEventListener("TabUnpinned", $.tabContainerListener, false);
			gBrowser.addTabsProgressListener($.tabsProgressListenerForPanel);
			if ($._.pTabPreviewEnabled) {
				// Doesn't matter wich event to listen to. "click" doesn't work on XUL content and "mousedown" doesn't work on web content.
				// The correct event to use is defined in the "emulate" case of _performAction function.
				if ($._.pTabPreviewSendClicks)
					$.canvas.addEventListener("click", $.previewFunctions.onClick, true);
				if ($._.pTabPreviewSendScroll)
					$.canvas.addEventListener("DOMMouseScroll", $.previewFunctions.onDOMScroll, false);
				gBrowser.addTabsProgressListener($.tabsProgressListenerForPreview);
			}
			$._appendTabsToTabsBox($.$($._UID + "-tabs-box"));
			let optB = $.optionsBox.hasChildNodes();
			$.optBtn.setAttribute("_checked", optB);
			optB && $.populateOptionsBox(false);
			aE.stopPropagation();
		},

		onItemClick: function(aE) {
			clearTimeout(window[$._UID]["PanelSelectTabOnHoverTimer"]);
			clearTimeout(window[$._UID]["HoverOpenPreviewTimer"]);
			$.tooltip.hidePopup();
			if (aE.button === 2)
				return true;
			var btn = aE.target;
			var tab = btn.tab;
			if (btn.localName !== "toolbarbutton" || !tab)
				return false;
			if (aE.button === 0 && !aE.shiftKey && $.accel(aE) && !aE.altKey)
				$.panelFunctions._multiSelectTab(btn, tab);
			else if (aE.button === 0 && !aE.shiftKey && !$.accel(aE) && !aE.altKey)
				gBrowser.tabContainer._notifyBackgroundTab(gBrowser.selectedTab = tab);
			else if (aE.button === 1 && !aE.shiftKey && !$.accel(aE) && !aE.altKey) {
				switch ($._.pPanelMiddleClickOnTabAction) {
					case 0: // Close tab
						gBrowser.removeTab(tab);
						break;
					case 1: // Reload tab
						$._reloadTab(tab, aE);
						setTimeout(function() {
							$._setButtonAttributes(btn, tab);
						}, 10);
						break;
					case 2: // Multi-select tab
						$.panelFunctions._multiSelectTab(btn, tab);
						break;
					default:
						Services.prompt.alert(null, self.name, "You manually changed the preference" +
							" called \"pPanelMiddleClickOnTabAction\" to an invalid value!!!\n" +
							"Go to " + self.name + " options page and reset that preference, then" +
							"choose the action you want from the in-line options inside the main panel.");
						break;
				}
			}
			aE.stopPropagation();
		},

		_multiSelectTab: function(aBtn, aTab) {
			if (aBtn.hasAttribute("_multisel")) {
				aBtn.removeAttribute("_multisel");
				if (gBrowser.selectedTab !== aTab)
					aBtn.removeAttribute("checked");
			} else {
				aBtn.removeAttribute("type"); // Remove to avoid checked state. 
				aBtn.removeAttribute("group"); // Remove to avoid checked state. 
				aBtn.setAttribute("_multisel", true);
				if (gBrowser.selectedTab !== aTab)
					aBtn.removeAttribute("checked");
			}
		},

		onMouseMoveItem: function(aE) {
			var btn = aE.target;
			var tab = btn.tab;
			if (btn.localName !== "toolbarbutton" || !tab)
				return false;
			$._tab = tab;
			if (!$._.pTabPreviewEnabled && !$._.pTabPreviewShowToolbar)
				return false;
			$.tooltip.removeAttribute("width");
			$.tooltip.removeAttribute("height");
			$._accelKeyPressed = $.accel(aE);
			$._openDelayedTooltip(aE);
			aE.stopPropagation();
		},

		onMouseOverItem: function(aE) {
			var btn = aE.target;
			var tab = btn.tab;
			if (btn.localName !== "toolbarbutton" || !tab)
				return false;
			try {
				XULBrowserWindow.setOverLink(tab.linkedBrowser.currentURI.spec, null);
			} catch (aErr) {}
			if (gBrowser._removingTabs.indexOf(tab) > -1)
				return true;
			if ($._.pPanelSelectTabOnHover) {
				clearTimeout(window[$._UID]["PanelSelectTabOnHoverTimer"]);
				window[$._UID]["PanelSelectTabOnHoverTimer"] = setTimeout(function() {
					gBrowser.selectedTab = tab;
				}, $._.pPanelSelectTabOnHoverDelay);
				return true;
			}
			$._tab = tab;
			if (!$._.pTabPreviewEnabled && !$._.pTabPreviewShowToolbar) {
				$._updateTitle(btn);
				return false;
			}
			if (!$._.pTabPreviewEnabled)
				return true;
			tab.linkedBrowser.removeEventListener("MozAfterPaint", $.previewFunctions.onMozAfterPaint, false);
			if (!window[$._UID]["HoverOpenPreviewTimer"] && $.tooltip.state === "open")
				$.tooltip.hidePopup();
			$._accelKeyPressed = $.accel(aE);
			tab.linkedBrowser.addEventListener("MozAfterPaint", $.previewFunctions.onMozAfterPaint, false);
			$._updatePreview();
			aE.stopPropagation();
		},

		onMouseOutItem: function(aE) {
			aE.preventDefault();
			var btn = aE.target;
			var tab = btn.tab;
			if (btn.localName !== "toolbarbutton" || !tab)
				return false;
			$._accelKeyPressed = $.accel(aE);
			XULBrowserWindow.setOverLink("", null);
			clearTimeout(window[$._UID]["PanelSelectTabOnHoverTimer"]);
			clearTimeout(window[$._UID]["HoverOpenPreviewTimer"]);
			var x = aE.screenX,
				y = aE.screenY;
			if ($._tooltipAllowHovering && $.tooltip.state === "open") {
				let box = $.tooltip.boxObject;
				if (box.screenX <= x && x < box.screenX + box.width &&
					box.screenY <= y && y < box.screenY + box.height)
					return;
			}
			$.tooltip.hidePopup();
			aE.stopPropagation();
		},
	},

	togglePanel: function() {
		var panel = $.panel;
		if (panel) {
			if (panel.state === "open")
				panel.hidePopup();
			else
				panel.openPopup($.panelAnchor, $._.pPanelPosition, 0, $._.pPanelVerticalOffset, false, false, null);
		}
	},

	openOptions: function() {
		var em = Services.ww.getWindowEnumerator();
		var winName = $._UID + "_settings_window";
		var index = 1;
		while (em.hasMoreElements()) {
			let win = em.getNext();
			if (win.name === winName) {
				win.focus();
				return;
			}
			index++;
		}
		let xul = $.getHelp("%OptionsWindow%");
		let storage = {
			button: self,
		};
		xul = xul.replace(/\t+/gm, "").replace(/>\s+</g, "><");
		xul = xul.split("%U_I_D%").join($._UID);
		xul = xul.split("%B_R_A_N_C_H%").join($.Branch);
		let url = "data:application/vnd.mozilla.xul+xml;text/plain," + encodeURIComponent(xul);
		let win = openDialog(url, winName, "chrome, resizable, dependant, dialog, centerscreen, close, alwaysRaised");
		win._storage = storage;
	},

	_appendTabsToTabsBox: function(aBox) {
		let panel = $.panel;
		if (!panel)
			return false;
		if (panel && panel.state === "closed")
			return false;
		aBox.textContent = "";
		let tabs = gBrowser.visibleTabs;
		let boxWidth = $.panelWidth - 48;
		let btnPerRow = $._.pPanelButtonTabsPerRowAuto ?
			Math.round(boxWidth / $._.pPanelButtonTabsPerRowAutoWidth) :
			$._.pPanelButtonTabsPerRow;
		let colWidth = (boxWidth / btnPerRow);
		let breakPoint = 0;
		let tabRow = [];
		let pinnedTabs = [];

		let grid = aBox.appendChild($.$CNS("grid", {
			id: $._UID + "tabs-box-grid",
			maxwidth: boxWidth,
			minwidth: boxWidth,
		}));

		let cols = grid.appendChild($.$CNS("columns"));

		while (breakPoint !== btnPerRow) {
			breakPoint++;
			cols.appendChild($.$CNS("column", {
				flex: 1,
				width: colWidth
			}));
		}

		let rows = grid.appendChild($.$CNS("rows"));
		let createRow = function(aArr) {
			let row = rows.appendChild($.$CNS("row"));
			let i = 0,
				iLen = aArr.length;
			for (; i < iLen; i++) {
				let btn = $.$CNS("toolbarbutton", {
					type: "radio",
					context: "tabContextMenu",
					autocheck: false,
					group: $._UID + "-tabs-radio-group",
					oncontextmenu: "event.stopPropagation();",
					oncommand: "event.stopPropagation();",
				});
				row.appendChild(btn);
				$._setButtonAttributes(btn, aArr[i]);
				aArr[i].mCorrespondingMenuitem = btn;
				btn.tab = aArr[i];
			}
			if (aArr.length !== btnPerRow) {
				let dummies = btnPerRow - (aArr.length % btnPerRow || btnPerRow);
				while (dummies) {
					dummies--;
					row.appendChild($.$CNS("toolbaritem", {
						//width: parseInt(btnWidth),
						dummy: true,
						disabled: true,
						image: "",
					}));
				}
			}
		};
		var t = 0,
			tLen = tabs.length;
		for (; t < tLen; t++) {
			if (tabs[t].pinned) {
				if ($._.pPanelShowPinnedTabs)
					pinnedTabs.push(tabs[t]);
				continue;
			}
			breakPoint++;
			tabRow.push(tabs[t]);
			if ((tabRow.length === btnPerRow) || (t + 1) === tabs.length) {
				breakPoint = 0;
				createRow(tabRow);
				tabRow = [];
				continue;
			}
		}

		if (pinnedTabs.length > 0 && $._.pPanelShowPinnedTabs) {
			let pinBox = aBox.insertBefore($.$CNS("hbox", {
				id: $._UID + "-pinned-tabs-box",
			}), aBox.childNodes[0]);

			pinBox.appendChild($.$CNS("spacer", {
				flex: 1
			}));

			let appendPinnedTab = function(aPTab) {
				let btn = pinBox.appendChild($.$CNS("toolbarbutton", {
					type: "radio",
					context: "tabContextMenu",
					autocheck: false,
					group: $._UID + "-tabs-radio-group",
					oncontextmenu: "event.stopPropagation();",
					oncommand: "event.stopPropagation();",
				}));
				$._setButtonAttributes(btn, aPTab);
				aPTab.mCorrespondingMenuitem = btn;
				btn.tab = aPTab;
			};
			var p = 0,
				pLen = pinnedTabs.length;
			for (; p < pLen; p++) {
				appendPinnedTab(pinnedTabs[p]);
			}
			pinBox.appendChild($.$CNS("spacer", {
				flex: 1
			}));
		}
	},

	tabContainerListener: function(aE) {
		var tab = aE.target;
		var btn = tab.mCorrespondingMenuitem;
		if ($.panel.state !== "open" || $.tooltip.hasAttribute("_open"))
			return true;
		switch (aE.type) {
			case "TabAttrModified":
				if (btn)
					$._setButtonAttributes(btn, tab);
				break;
			case "TabClose":
			case "TabOpen":
			case "TabPinned":
			case "TabUnpinned":
				clearTimeout(window[$._UID]["AppendTabsToTabsBoxTimer"]);
				window[$._UID]["AppendTabsToTabsBoxTimer"] = setTimeout(function() {
					$._appendTabsToTabsBox($.$($._UID + "-tabs-box"));
				}, 50);
				break;
		}
	},

	_setButtonAttributes: function(aBtn, aTab) {
		if (!aBtn || !aTab)
			return true;
		aBtn.setAttribute("label", aTab.label);
		aBtn.setAttribute("crop", aTab.getAttribute("crop"));

		if (aTab.hasAttribute("busy"))
			aBtn.setAttribute("image", "chrome://global/skin/icons/loading_16.png");
		else
			aBtn.setAttribute("image", aTab.getAttribute("image") || "chrome://branding/content/icon16.png");

		if (aTab.selected)
			aBtn.setAttribute("checked", "true");
		else
			aBtn.removeAttribute("checked");

		["muted", "soundplaying", "pinned", "pending"].forEach(aAttr => {
			if (aTab.hasAttribute(aAttr))
				aBtn.setAttribute(aAttr, true);
			else
				aBtn.removeAttribute(aAttr);
		});
	},

	CSSLoader: function() {
		let css;
		try {
			$.styleSheet(window[$._UID]["StyleSheet"], true);
			window[$._UID]["StyleSheet"] = false;
		} catch (aErr) {} finally {
			css = "@namespace url(" + xulns + ");\n" + $.getHelp("%StyleSheet%")
				.replace(/%U_I_D%/g, $._UID)
				.replace(/%I_C_O_N_S_I_Z_E%/g, $._.pMaxIconSizeForTabButtonsInPanel)
				.replace(/%S_E_L_F_I_D%/g, self.id);

			css += decodeURIComponent($._.pCustomStyleSheet)
				.replace(/%U_I_D%/g, $._UID)
				.replace(/%I_C_O_N_S_I_Z_E%/g, $._.pMaxIconSizeForTabButtonsInPanel)
				.replace(/%S_E_L_F_I_D%/g, self.id);

			if (!window[$._UID]["StyleSheet"]) {
				window[$._UID]["StyleSheet"] = css;
				$.styleSheet(css);
			}
		}
	},

	handleInvertedImages: function() {
		let invImg = document.getElementsByClassName("marked-for-invertion");
		for (let i = invImg.length - 1; i >= 0; i--)
			invImg[i].classList.toggle("inverted-color-for-this-image", $._.pPanelInvertDarkIcons);
	},

	dragAndDropHandler: {
		onDragStart: function(aE) {
			this._ensureTTClosing();
			if (!this._isDraggable(aE.target))
				return false;
			aE.dataTransfer.setData("text/plain", aE.target.tab._tPos);
			aE.dataTransfer.effectAllowed = "copy";
			aE.stopPropagation();
		},

		_ensureTTClosing: function() {
			clearTimeout(window[$._UID]["HoverOpenPreviewTimer"]);
			$.tooltip.hidePopup();
			$._prevDoNotAllow = true;
		},

		_isDraggable: function(aNode) {
			return aNode.localName === "toolbarbutton" && !aNode.hasAttribute("dummy");
		},

		onDragOver: function(aE) {
			this._ensureTTClosing();
			aE.preventDefault();
			aE.stopPropagation();
		},

		onDrop: function(aE) {
			this._ensureTTClosing();
			if (!this._isDraggable(aE.target))
				return false;
			let tabIndex = parseInt(aE.dataTransfer.getData("text/plain"), 10);
			let target = aE.composedTarget;
			let targetTab = target.tab;
			if (targetTab) {
				if (tabIndex === targetTab._tPos)
					return false;
				gBrowser.moveTabTo(gBrowser.mTabContainer.children[tabIndex], targetTab._tPos);
			} else
				return false;
			clearTimeout(window[$._UID]["AppendTabsToTabsBoxTimer"]);
			window[$._UID]["AppendTabsToTabsBoxTimer"] = setTimeout(function() {
				$._appendTabsToTabsBox($.$($._UID + "-tabs-box"));
				$._prevDoNotAllow = false;
			}, 100);
			aE.stopPropagation();
		}
	},

	onHotSpotMouseOver: function(aE) {
		clearTimeout(window[$._UID]["HoverOpenMainPanelTimer"]);
		if (gBrowser.visibleTabs.length < $._.pPanelOpenOnHoverOverHotSpotsTrigger ||
			$.panel.state === "open")
			return true;
		window[$._UID]["HoverOpenMainPanelTimer"] = setTimeout(function() {
			$.panel.openPopup($.panelAnchor, $._.pPanelPosition, 0, $._.pPanelVerticalOffset, false, false, null);
		}, $._.pPanelOpenOnHoverDelay);
		aE.stopPropagation();

	},

	onHotSpotMouseOut: function(aE) {
		if ($.panel.state !== "open")
			clearTimeout(window[$._UID]["HoverOpenMainPanelTimer"]);
		aE.stopPropagation();
	},

	handleHotSpots: function(aRem) {
		let curHP = window[$._UID]["ListOfHotSpots"];
		if (curHP && curHP.length > 0) {
			curHP.forEach(function(aHPID) {
				let el = $.$(aHPID);
				el && el.removeEventListener("mouseover", $.onHotSpotMouseOver, false);
				el && el.removeEventListener("mouseout", $.onHotSpotMouseOut, false);
			});
		}
		if (aRem)
			return;
		if (!$._.pPanelListOfHotSpotsIDs)
			return true;
		let HPs = $._.pPanelListOfHotSpotsIDs.split(",").filter(function(aVal) {
			if (aVal !== "")
				return true;
		});
		if (HPs && HPs.length > 0) {
			window[$._UID]["ListOfHotSpots"] = HPs;
			setTimeout(function() {
				window[$._UID]["ListOfHotSpots"].forEach(function(aHPID) {
					let el = $.$(aHPID);
					el && el.addEventListener("mouseover", $.onHotSpotMouseOver, false);
					el && el.addEventListener("mouseout", $.onHotSpotMouseOut, false);
				});
			}, 10);
		}
	},

	handlePrefObserver: function(aRem) { // Can't add obs. to brach in here.
		var prefs = $.DefPrefs;
		for (let key in prefs) {
			let pref = $.Branch + key;
			if (prefs.hasOwnProperty(key)) {
				if (aRem)
					Services.prefs.removeObserver(pref, $.prefObserver);
				else
					Services.prefs.addObserver(pref, $.prefObserver, false);
			}
		}
	},

	prefObserver: {
		observe: function(aSubject, aTopic, aPStr) { // aPStr is the pref. name minus the branch name.
			if (aTopic === "nsPref:changed") {
				let prefName = aPStr.slice($.Branch.length);
				window[$._UID]["Preferences"][prefName] = $.gPref(prefName);
				switch (prefName) {
					case "pButtonImage":
					case "pPanelKeepItOpen":
						$.selfDestroy();
						break;
					case "pPanelInvertDarkIcons":
						$.handleInvertedImages();
						break;
					case "pTabPreviewSendScroll":
						let TPSSaddRem = $._.pTabPreviewSendScroll ? "addEventListener" : "removeEventListener";
						$.canvas[TPSSaddRem]("DOMMouseScroll", $.previewFunctions.onDOMScroll, false);
						break;
					case "pTabPreviewSendClicks":
						let TPSCaddRem = $._.pTabPreviewSendClicks ? "addEventListener" : "removeEventListener";
						$.canvas[TPSCaddRem]("click", $.previewFunctions.onClick, true);
						break;
					case "pTabPreviewEnabled":
					case "pTabPreviewShowToolbar":
						setTimeout(function() {
							let val = !!$._.pTabPreviewShowToolbar || !!$._.pTabPreviewEnabled || false;
							$._tooltipAllowHovering = val;
						}, 10);
						break;
					case "pPanelOpenOnHover":
					case "pPanelOpenOnHoverDelay":
						$.handleOpenOnHover();
						break;
					case "pPanelListOfHotSpotsIDs":
					case "pPanelOpenOnHoverOverHotSpotsTrigger":
						$.handleHotSpots();
						break;
					case "pMaxIconSizeForTabButtonsInPanel":
						$.CSSLoader();
						break;
					case "pPanelVerticalOffset": // Destroy the freaking entire button so the vertical offset is respected. 
						if (!window[$._UID]["SelfDestroyerListener"]) {
							window[$._UID]["SelfDestroyerListener"] = true;
							$.panel.addEventListener("popuphidden", function _(aE) {
								this.removeEventListener("popuphidden", _, false);
								window[$._UID]["SelfDestroyerListener"] = false;
								$.selfDestroy();
							}, false);
						}
						break;
					case "pPanelButtonTabsPerRow":
						clearTimeout(window[$._UID]["AppendTabsToTabsBoxTimer"]);
						window[$._UID]["AppendTabsToTabsBoxTimer"] = setTimeout(function() {
							$._appendTabsToTabsBox($.$($._UID + "-tabs-box"));
						}, 100);
						break;
				}
				$._ = window[$._UID]["Preferences"];
			}
		}
	},

	populatePositioningTooltip: function(aE, aN) {
		aN.textContent = "";
		let box = aN.appendChild($.$CNS("hbox"));
		let pos = aE.target.triggerNode.getAttribute("value");
		let isWide = /^end_/.test(pos) || /^start_/.test(pos);
		box.appendChild($.$CNS("spacer", {
			flex: 1
		}));
		box.appendChild($.$CNS("label", {
			value: "Preview",
			style: "font-weight:bold;",
		}));
		box.appendChild($.$CNS("spacer", {
			flex: 1
		}));
		aN.appendChild($.$CNS("image", {
			src: $.getHelp("%" + pos + "%"),
			width: isWide ? 180 : 130,
			height: isWide ? 91 : 122,
		}));
	},

	createDynamicTooltip: function() {
		if (!$.dynamicTT) {
			$.$("mainPopupSet").appendChild($.$CNS("tooltip", {
				id: $._UID + "_dynamic_tooltip",
				cb_id: $._UID,
				onpopupshowing: $.that + "populateDynamicTooltip(event, this);event.stopPropagation();",
				onpopuphiding: "event.stopPropagation();",
				flex: 1,
			}));
		}
	},

	populateDynamicTooltip: function(aE, aN) {
		aN.textContent = "";

		if (aE.target.triggerNode.id === self.id) {
			var grid = aN.appendChild($.$CNS("grid"));
			var cols = grid.appendChild($.$CNS("columns"));
			cols.appendChild($.$CNS("column", {
				flex: 2
			}));
			cols.appendChild($.$CNS("column", {
				flex: 3
			}));
			cols.appendChild($.$CNS("column", {
				flex: 1
			}));
			var rows = grid.appendChild($.$CNS("rows"));
			// AppendRow
			let AR = function(aRowsNode, aLabel1, aLabel2, aLabel3, aEmpty) {
				let row = $.$CNS("row");
				if (aEmpty) {
					row.appendChild($.$CNS("label", {
						value: " ",
						width: 0,
					}));
				} else {
					row.appendChild($.$CNS("label", {
						value: aLabel1,
						style: "font-weight: bold;",
					}));
					row.appendChild($.$CNS("label", {
						value: aLabel2,
						style: "font-weight: bold;",
					}));
					let desc = $.$CNS("description", {
						maxwidth: 250,
					});
					desc.textContent = aLabel3; // Use of "textContent" to achieve word wrap. 
					row.appendChild(desc);
				}
				aRowsNode.appendChild(row);
			};

			let btn = aE.target.triggerNode;
			AR(rows, btn.name, "", "ID = " + btn.id.slice(20));
			aN.appendChild($.$CNS("separator")); // Empty space
			let desc2 = $.$CNS("description", {
				style: "font-weight:bold;",
			});
			// Use of "textContent" to achieve word wrap. 
			desc2.textContent = "Read this button help for very important usage notes.\n" +
				"Right click on button, \"Button Help\" item.";
			aN.appendChild(desc2);
			return true;
		}
	},

	selfDestroy: function() {
		clearTimeout(window[$._UID]["CleanSlateTimer"]);
		window[$._UID]["CleanSlateTimer"] = setTimeout(function() {
			self.destroy("update");
			setTimeout(function() {
				self.init();
			}, 100);
		}, 100);
	},

	$: function(aId) { // Return elements by Id
		return document.getElementById(aId);
	},

	notifyme: function(aTitle, aMsg, aImg, aCallback) { // Notification function
		let origin = (Services.prefs.getPrefType("ui.alertNotificationOrigin") !== 0) ?
			Services.prefs.getIntPref("ui.alertNotificationOrigin") :
			null;
		let win = Services.ww.openWindow(null, "chrome://global/content/alerts/alert.xul",
			"_blank", "chrome,titlebar=no,popup=yes", null);
		win.arguments = [aImg, aTitle, aMsg, !!aCallback, "", origin, "", "", null, (!!aCallback ? aCallback : null)];
	},

	alertCallback: {
		opt: {
			centerScreen: null,
		},
		observe: function(aSubject, aTopic, aData) {
			let alt = Services.wm.getMostRecentWindow("alert:alert");
			let tBox = alt.document.getElementById("alertTextBox");
			if (aTopic === "alertshow") {
				tBox.appendChild($.$CNS("label", {
					flex: 1,
					value: "    ",
				}));
				if (this.opt.centerScreen) {
					alt.moveTo((screen.availWidth / 2) - (alt.outerWidth / 2),
						(screen.availHeight / 2) - (alt.outerHeight / 2));
				}
				setTimeout(function() {
					alt.close();
				}, (this.opt.centerScreen ? 10000 : 3000));
			}
			if (aTopic === "alertclickcallback")
				alt.close();
		}
	},

