	accel: function(aE) {
		if (aE)
			return $.isOSX ? aE.metaKey : aE.ctrlKey;
		return $.isOSX ? "Command" : "Control";
	},
	get panelWidth() {
		switch ($._.pPanelWidth) {
			case 0:
				return $._.pPanelWidthCustom;
			case 1:
				return window.innerWidth;
			case 2:
				return screen.width;
		}
	},
	get panelAnchor() {
		let el = null;
		let id = $._.pPanelAnchor;
		if (id === "self")
			return self;
		else if (id === "")
			return $.$("nav-bar");
		else
			el = $.$(id);
		if (!el) {
			el = $.$("nav-bar");
			$.alertCallback.opt = {
				centerScreen: true,
			};
			$.notifyme(self.name,
				"ID of anchor element not valid!!!\nChoose the ID of an existent element.\n" +
				"Switching to default anchor for the panel.",
				$.getHelp("%WarningImg%"),
				$.alertCallback);
		}
		return el;
	},
	get _responseMsgID() {
		return "CB:TabsManager:Response:" + self.id.slice(14) + "-" + $._randomID;
	},
	get _updatePreviewMsgID() {
		return "CB:TabsManager:Preview:" + self.id.slice(14) + "-" + $._randomID;
	},
	get _scrollMsgID() {
		return "CB:TabsManager:Scroll:" + self.id.slice(14) + "-" + $._randomID;
	},
	get _cleanUpMsgID() {
		return "CB:TabsManager:Cleanup:" + self.id.slice(14) + "-" + $._randomID;
	},
	get _emulateMsgID() {
		return "CB:TabsManager:Emulate:" + self.id.slice(14) + "-" + $._randomID;
	},
	get canvas() {
		return $.$($._UID + "-tabs-preview-canvas");
	},
	get optionsBox() {
		return $.$($._UID + "-options-box");
	},
	get optBtn() {
		return $.$($._UID + "-inline-options-button");
	},
	get panel() {
		return $.$($._UID + "-main-panel");
	},
	get tooltip() {
		return $.$($._UID + "-tabs-box-tooltip");
	},
	get tooltipLabel() {
		return $.$($._UID + "-tabs-preview-title");
	},
	get toolbar() {
		return $.$($._UID + "-tabs-preview-toolbar");
	},
	get selectedTabs() {
		return $.$($._UID + "-tabs-box").getElementsByAttribute("_multisel", "*");
	},
	get dynamicTT() {
		return $.$($._UID + "_dynamic_tooltip");
