custombutton.initcode:
/*Initialization Code*/
var scriptName = "User Script Name";
var enabledImage = "data:image/png;base64,";
var disabledImage = "data:image/png;base64,";

this.tooltipText = self.name + "\n\n" +
	"\tLeft clcik : Toggle script status\t\n" +
	"\tMiddle or " + accel() + " + Left click : Open script options\t\n" +
	"\tControl + Shift + Left click : Edit script\t\n\n" +
	"Read this button help for very important usage notes.\n" +
	"Right click on button, \"Button Help\" item.";

if (!("Services" in window))
	Cu.import("resource://gre/modules/Services.jsm");

if (!("AddonManager" in window))
	Cu.import("resource://gre/modules/AddonManager.jsm");

this._GM_script = null;

this.onclick = function(aE) {
	let addon = self._GM_script;
	if (!addon && (aE.button === 0 || aE.button === 1)) {
		notifyme(self.name,
			"Wrong script name or not set yet.\nClick this notification to re-try to " +
			"\"catch\" the script name. Or edit the button and set the correct script name",
			getHelp("%WarningImg%"),
			self.alertCallback);
		return true;
	}
	if (!aE.shiftKey && !accel(aE) && !aE.altKey && aE.button === 0) { // Left click
		addon.userDisabled = !addon.userDisabled;
		setTimeout(function() {
			setImage(!addon.userDisabled);
		}, 10);
	} else if (!aE.shiftKey && !accel(aE) && !aE.altKey && aE.button === 1 ||
		!aE.shiftKey && accel(aE) && !aE.altKey && aE.button === 0) { // Middle click or Control + Left click
		openAddonOptions(addon);
	} else if (aE.shiftKey && accel(aE) && !aE.altKey && aE.button === 0) {
		GM_util.openInEditor(addon._script);
	}
};

function openAddonOptions(aAddon) {
	var optURL = aAddon.optionsURL;
	let windows = Services.wm.getEnumerator(null);
	while (windows.hasMoreElements()) {
		let win = windows.getNext();
		if (win.document.documentURI == optURL) {
			win.focus();
			return true;
		}
	}
	window.openDialog(optURL, "", "resizeble,chrome,titlebar,toolbar,centerscreen,dialog=no");
	return true;
}

function setImage(aEnabled) {
	switch (aEnabled) {
		case true:
			self.image = enabledImage;
			break;
		case false:
			self.image = disabledImage;
			break;
	}
}

AddonManager.getAddonsByTypes(["greasemonkey-user-script", "userscript"], function(aList) {
	for (var i = aList.length - 1; i >= 0; i--) {
		if (aList[i].name === scriptName) {
			let addon = aList[i];
			self._GM_script = addon;
			setImage(!addon.userDisabled);
			break;
		}
	}
});

this.alertCallback = {
	observe: function(aSubject, aTopic, aData) {
		let alt = Services.wm.getMostRecentWindow("alert:alert");
		if (aTopic === "alertshow") {
			alt.moveTo((screen.availWidth / 2) - (alt.outerWidth / 2),
				(screen.availHeight / 2) - (alt.outerHeight / 2));
			setTimeout(function() {
				alt.close();
			}, 10000);
		}
		if (aTopic === "alertclickcallback") {
			alt.close();
			self.init();
		}
	}
};

function notifyme(aTitle, aMsg, aImg, aCallback) { // Notification function
	let origin = (Services.prefs.getPrefType("ui.alertNotificationOrigin") !== 0) ?
		Services.prefs.getIntPref("ui.alertNotificationOrigin") :
		null;
	let win = Services.ww.openWindow(null, "chrome://global/content/alerts/alert.xul",
		"_blank", "chrome,titlebar=no,popup=yes", null);
	win.arguments = [aImg, aTitle, aMsg, !!aCallback, "", origin, "", "", null, (!!aCallback ? aCallback : null)];
}

function getHelp(aPlaceholder) { // Get specific data, surrounded by "placeholders", from the Help tab.
	return document.getElementById(self.id).getAttribute("Help").split("<!--" + aPlaceholder + "-->")[1];
}

function accel(aE) {
	let isOSX = Services.appinfo.OS.toLowerCase().startsWith("darwin");
	if (aE)
		return isOSX ? aE.metaKey : aE.ctrlKey;
	return isOSX ? "Command" : "Control";
}
